INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Acolyte', 'You have spent your life in the service of a temple to a specific god or pantheon of gods. You act as an intermediary between the realm of the holy and the mortal world, performing sacred rites and offering sacrifices in order to conduct worshipers into the presence of the divine. You are not necessarily a cleric—performing sacred rites is not the same thing as channeling divine power.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Acolyte'), 'Feature: Shelter of the Faithful', 'As an acolyte, you command the respect of those who share your faith, and you can perform the religious ceremonies of your deity. You and your adventuring companions can expect to receive free healing and care at a temple, shrine, or other established presence of your faith, though you must provide any material components needed for spells. Those who share your religion will support you (but only you) at a modest lifestyle.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Acolyte'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I idolize a particular hero of my faith, and constantly refer to that person''s deeds and example.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I can find common ground between the fiercest enemies, empathizing with them and always working toward peace.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I see omens in every event and action. The gods try to speak to us, we just need to listen.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Nothing can shake my optimistic attitude.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I quote (or misquote) sacred texts and proverbs in almost every situation.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I am tolerant (or intolerant) of other faiths and respect (or condemn) the worship of other gods.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I''ve enjoyed fine food, drink, and high society among my temple''s elite. Rough living grates on me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I''ve spent so long in the temple that I have little practical experience dealing with people in the outside world.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Tradition. The ancient traditions of worship and sacrifice must be preserved and upheld. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Charity. I always try to help those in need, no matter what the personal cost. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Change. We must help bring about the changes the gods are constantly working in the world. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Power. I hope to one day rise to the top of my faith''s religious hierarchy. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Faith. I trust that my deity will guide my actions. I have faith that if I work hard, things will go well. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Aspiration. I seek to prove myself worthy of my god''s favor by matching my actions against his or her teachings. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I would die to recover an ancient relic of my faith that was lost long ago.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I will someday get revenge on the corrupt temple hierarchy who branded me a heretic.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I owe my life to the priest who took me in when my parents died.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Everything I do is for the common people.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I will do anything to protect the temple where I served.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I seek to preserve a sacred text that my enemies consider heretical and seek to destroy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I judge others harshly, and myself even more severely.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I put too much trust in those who wield power within my temple''s hierarchy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'My piety sometimes leads me to blindly trust those that profess faith in my god.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I am inflexible in my thinking.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I am suspicious of strangers and expect the worst of them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Once I pick a goal, I become obsessed with it to the detriment of everything else in my life.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Charlatan', 'You have always had a way with people. You know what makes them tick, you can tease out their hearts'' desires after a few minutes of conversation, and with a few leading questions you can read them like they were children''s books. It''s a useful talent, and one that you''re perfectly willing to use for your advantage.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Charlatan'), 'Favorite Schemes', 'Scam', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Charlatan') AND title = 'Scam' ORDER BY id DESC LIMIT 1), '1', 'I cheat at games of chance.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Charlatan') AND title = 'Scam' ORDER BY id DESC LIMIT 1), '2', 'I shave coins or forge documents.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Charlatan') AND title = 'Scam' ORDER BY id DESC LIMIT 1), '3', 'I insinuate myself into people''s lives to prey on their weakness and secure their fortunes.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Charlatan') AND title = 'Scam' ORDER BY id DESC LIMIT 1), '4', 'I put on new identities like clothes.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Charlatan') AND title = 'Scam' ORDER BY id DESC LIMIT 1), '5', 'I run sleight-of-hand cons on street corners.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Charlatan') AND title = 'Scam' ORDER BY id DESC LIMIT 1), '6', 'I convince people that worthless junk is worth their hard-earned money.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Charlatan'), 'Feature: False Identity', 'You have created a second identity that includes documentation, established acquaintances, and disguises that allow you to assume that persona. Additionally, you can forge documents including official papers and personal letters, as long as you have seen an example of the kind of document or the handwriting you are trying to copy.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Charlatan'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Charlatan') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I fall in and out of love easily, and am always pursuing someone.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Charlatan') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I have a joke for every occasion, especially occasions where humor is inappropriate.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Charlatan') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Flattery is my preferred trick for getting what I want.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Charlatan') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I''m a born gambler who can''t resist taking a risk for a potential payoff.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Charlatan') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I lie about almost everything, even when there''s no good reason to.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Charlatan') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Sarcasm and insults are my weapons of choice.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Charlatan') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I keep multiple holy symbols on me and invoke whatever deity might come in useful at any given moment.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Charlatan') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I pocket anything I see that might have some value.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Charlatan'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Charlatan') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Independence. I am a free spirit — no one tells me what to do. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Charlatan') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Fairness. I never target people who can''t afford to lose a few coins. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Charlatan') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Charity. I distribute the money I acquire to the people who really need it. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Charlatan') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Creativity. I never run the same con twice. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Charlatan') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Friendship. Material goods come and go.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Charlatan'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Charlatan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'Aspiration. I''m determined to make something of myself. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Charlatan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I fleeced the wrong person and must work to ensure that this individual never crosses paths with me or those I care about.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Charlatan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I owe everything to my mentor — a horrible person who''s probably rotting in jail somewhere.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Charlatan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'Somewhere out there, I have a child who doesn''t know me. I''m making the world better for him or her.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Charlatan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I come from a noble family, and one day I''ll reclaim my lands and title from those who stole them from me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Charlatan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'A powerful person killed someone I love. Some day soon, I''ll have my revenge.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Charlatan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I swindled and ruined a person who didn''t deserve it. I seek to atone for my misdeeds but might never be able to forgive myself.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Charlatan'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Charlatan') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I can''t resist a pretty face.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Charlatan') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I''m always in debt. I spend my ill-gotten gains on decadent luxuries faster than I bring them in.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Charlatan') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I''m convinced that no one could ever fool me the way I fool others.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Charlatan') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I''m too greedy for my own good. I can''t resist taking a risk if there''s money involved.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Charlatan') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I can''t resist swindling people who are more powerful than me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Charlatan') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I hate to admit it and will hate myself for it, but I''ll run and preserve my own hide if the going gets tough.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Criminal', 'You are an experienced criminal with a history of breaking the law. You have spent a lot of time among other criminals and still have contacts within the criminal underworld. You''re far closer than most people to the world of murder, theft, and violence that pervades the underbelly of civilization, and you have survived up to this point by flouting the rules and regulations of society.', 'Deception, Stealth', 'One type of gaming set, thieves'' tools', '', 'A crowbar, a set of dark common clothes including a hood, and a belt pouch containing 15 gp', (SELECT id FROM core_book_sources WHERE raw_source = 'Player''s Handbook (2014) p. 129'));
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Criminal'), 'Criminal Specialty', 'Specialty', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Criminal') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '1', 'Blackmailer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Criminal') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '2', 'Burglar');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Criminal') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '3', 'Enforcer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Criminal') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '4', 'Fence');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Criminal') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '5', 'Highway robber');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Criminal') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '6', 'Hired killer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Criminal') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '7', 'Pickpocket');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Criminal') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '8', 'Smuggler');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Criminal'), 'Feature: Criminal Contact', 'You have a reliable and trustworthy contact who acts as your liaison to a network of other criminals. You know how to get messages to and from your contact, even over great distances; specifically, you know the local messengers, corrupt caravan masters, and seedy sailors who can deliver messages for you.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Criminal'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Criminal') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I always have a plan for what to do when things go wrong.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Criminal') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I am always calm, no matter what the situation. I never raise my voice or let my emotions control me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Criminal') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'The first thing I do in a new place is note the locations of everything valuable — or where such things could be hidden.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Criminal') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I would rather make a new friend than a new enemy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Criminal') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I am incredibly slow to trust. Those who seem the fairest often have the most to hide.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Criminal') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I don''t pay attention to the risks in a situation. Never tell me the odds.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Criminal') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'The best way to get me to do something is to tell me I can''t do it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Criminal') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I blow up at the slightest insult.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Criminal'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Criminal') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Honor. I don''t steal from others in the trade. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Criminal') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Freedom. Chains are meant to be broken, as are those who would forge them. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Criminal') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Charity. I steal from the wealthy so that I can help people in need. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Criminal') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Greed. I will do whatever it takes to become wealthy. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Criminal') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'People. I''m loyal to my friends, not to any ideals, and everyone else can take a trip down the Styx for all I care. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Criminal') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Redemption. There''s a spark of good in everyone. (Good)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Criminal'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Criminal') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I''m trying to pay off an old debt I owe to a generous benefactor.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Criminal') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'My ill-gotten gains go to support my family.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Criminal') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'Something important was taken from me, and I aim to steal it back.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Criminal') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I will become the greatest thief that ever lived.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Criminal') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I''m guilty of a terrible crime. I hope I can redeem myself for it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Criminal') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'Someone I loved died because of 1 mistake I made. That will never happen again.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Criminal'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Criminal') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'When I see something valuable, I can''t think about anything but how to steal it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Criminal') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'When faced with a choice between money and my friends, I usually choose the money.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Criminal') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'If there''s a plan, I''ll forget it. If I don''t forget it, I''ll ignore it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Criminal') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I have a "tell" that reveals when I''m lying.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Criminal') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I turn tail and run when things look bad.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Criminal') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'An innocent person is in prison for a crime that I committed. I''m okay with that.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Entertainer', 'You thrive in front of an audience. You know how to entrance them, entertain them, and even inspire them. Your poetics can stir the hearts of those who hear you, awakening grief or joy, laughter or anger. Your music raises their spirits or captures their sorrow. Your dance steps captivate, your humor cuts to the quick. Whatever techniques you use, your art is your life.', 'Acrobatics, Performance', 'Disguise kit, one type of musical instrument', '', 'A musical instrument (one of your choice), the favor of an admirer (love letter, lock of hair, or trinket), a costume, and a belt pouch containing 15 gp', (SELECT id FROM core_book_sources WHERE raw_source = 'Player''s Handbook (2014) p. 130'));
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Entertainer'), 'Entertainer Routines', 'Routines', '1d10');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Routines' ORDER BY id DESC LIMIT 1), '1', 'Actor');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Routines' ORDER BY id DESC LIMIT 1), '2', 'Dancer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Routines' ORDER BY id DESC LIMIT 1), '3', 'Fire-eater');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Routines' ORDER BY id DESC LIMIT 1), '4', 'Jester');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Routines' ORDER BY id DESC LIMIT 1), '5', 'Juggler');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Routines' ORDER BY id DESC LIMIT 1), '6', 'Instrumentalist');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Routines' ORDER BY id DESC LIMIT 1), '7', 'Poet');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Routines' ORDER BY id DESC LIMIT 1), '8', 'Singer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Routines' ORDER BY id DESC LIMIT 1), '9', 'Storyteller');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Routines' ORDER BY id DESC LIMIT 1), '10', 'Tumbler');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Entertainer'), 'Feature: By Popular Demand', 'You can always find a place to perform, usually in an inn or tavern but possibly with a circus, at a theater, or even in a noble''s court. At such a place, you receive free lodging and food of a modest or comfortable standard (depending on the quality of the establishment), as long as you perform each night. In addition, your performance makes you something of a local figure. When strangers recognize you in a town where you have performed, they typically take a liking to you.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Entertainer'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I know a story relevant to almost every situation.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Whenever I come to a new place, I collect local rumors and spread gossip.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I''m a hopeless romantic, always searching for that "special someone".');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Nobody stays angry at me or around me for long, since I can defuse any amount of tension.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I love a good insult, even one directed at me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I get bitter if I''m not the center of attention.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I''ll settle for nothing less than perfection.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I change my mood or my mind as quickly as I change key in a song.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Beauty. When I perform, I make the world better than it was. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Tradition. The stories, legends, and songs of the past must never be forgotten, for they teach us who we are. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Creativity. The world is in need of new ideas and bold action. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Greed. I''m only in it for the money and fame. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'People. I like seeing the smiles on people''s faces when I perform. That''s all that matters. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Honesty. Art should reflect the soul; it should come from within and reveal who we really are. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Entertainer'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'My instrument is my most treasured possession, and it reminds me of someone I love.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'Someone stole my precious instrument, and someday I''ll get it back.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I want to be famous, whatever it takes.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I idolize a hero of the old tales and measure my deeds against that person''s.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I will do anything to prove myself superior to my hated rival.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I would do anything for the other members of my old troupe.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Entertainer'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I''ll do anything to win fame and renown.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I''m a sucker for a pretty face.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'A scandal prevents me from ever going home again. That kind of trouble seems to follow me around.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I once satirized a noble who still wants my head. It was a mistake that I will likely repeat.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I have trouble keeping my true feelings hidden. My sharp tongue lands me in trouble.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Entertainer') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'Despite my best efforts, I am unreliable to my friends.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Folk Hero', 'You come from a humble social rank, but you are destined for so much more. Already the people of your home village regard you as their champion, and your destiny calls you to stand against the tyrants and monsters that threaten the common folk everywhere.', 'Animal Handling, Survival', 'One type of artisan''s tools, vehicles (land)', '', 'A set of artisan''s tools (one of your choice), a shovel, an iron pot, a set of common clothes, and a belt pouch containing 10 gp', (SELECT id FROM core_book_sources WHERE raw_source = 'Player''s Handbook (2014) p. 131'));
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Folk Hero'), 'Defining Event', 'Defining', '1d10');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Defining' ORDER BY id DESC LIMIT 1), '1', 'I stood up to a tyrant''s agents.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Defining' ORDER BY id DESC LIMIT 1), '2', 'I saved people during a natural disaster.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Defining' ORDER BY id DESC LIMIT 1), '3', 'I stood alone against a terrible monster.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Defining' ORDER BY id DESC LIMIT 1), '4', 'I stole from a corrupt merchant to help the poor.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Defining' ORDER BY id DESC LIMIT 1), '5', 'I led a militia to fight off an invading army.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Defining' ORDER BY id DESC LIMIT 1), '6', 'I broke into a tyrant''s castle and stole weapons to arm the people.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Defining' ORDER BY id DESC LIMIT 1), '7', 'I trained the peasantry to use farm implements as weapons against a tyrant''s soldiers.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Defining' ORDER BY id DESC LIMIT 1), '8', 'A lord rescinded an unpopular decree after I led a symbolic act of protest against it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Defining' ORDER BY id DESC LIMIT 1), '9', 'A celestial, fey, or similar creature gave me a blessing or revealed my secret origin.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Defining' ORDER BY id DESC LIMIT 1), '10', 'Recruited into a lord''s army, I rose to leadership and was commended for my heroism.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Folk Hero'), 'Feature: Rustic Hospitality', 'Since you come from the ranks of the common folk, you fit in among them with ease. You can find a place to hide, rest, or recuperate among other commoners, unless you have shown yourself to be a danger to them. They will shield you from the law or anyone else searching for you, though they will not risk their lives for you.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Folk Hero'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I judge people by their actions, not their words.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'If someone is in trouble, I''m always ready to lend help.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'When I set my mind to something, I follow through no matter what gets in my way.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I have a strong sense of fair play and always try to find the most equitable solution to arguments.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I''m confident in my own abilities and do what I can to instill confidence in others.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Thinking is for other people. I prefer action.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I misuse long words in an attempt to sound smarter.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I get bored easily. When am I going to get on with my destiny?');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Folk Hero'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Respect. People deserve to be treated with dignity and respect. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Fairness. No one should get preferential treatment before the law, and no one is above the law. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Freedom. Tyrants must not be allowed to oppress the people. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Might. If I become strong, I can take what I want — what I deserve. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Sincerity. There''s no good in pretending to be something I''m not. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Destiny. Nothing and no one can steer me away from my higher calling. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Folk Hero'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I have a family, but I have no idea where they are. One day, I hope to see them again.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I worked the land, I love the land, and I will protect the land.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'A proud noble once gave me a horrible beating, and I will take my revenge on any bully I encounter.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'My tools are symbols of my past life, and I carry them so that I will never forget my roots.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I protect those who cannot protect themselves.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I wish my childhood sweetheart had come with me to pursue my destiny.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Folk Hero'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'The tyrant who rules my land will stop at nothing to see me killed.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I''m convinced of the significance of my destiny, and blind to my shortcomings and the risk of failure.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'The people who knew me when I was young know my shameful secret, so I can never go home again.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I have a weakness for the vices of the city, especially hard drink.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'Secretly, I believe that things would be better if I were a tyrant lording over the land.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Folk Hero') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I have trouble trusting in my allies.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Guild Artisan', 'You are a member of an artisan''s guild, skilled in a particular field and closely associated with other artisans. You are a well-established part of the mercantile world, freed by talent and wealth from the constraints of a feudal social order. You learned your skills as an apprentice to a master artisan, under the sponsorship of your guild, until you became a master in your own right.', 'Insight, Persuasion', 'One type of artisan''s tools', 'One of your choice', 'A set of artisan''s tools (one of your choice), a letter of introduction from your guild, a set of traveler''s clothes, and a belt pouch containing 15 gp', (SELECT id FROM core_book_sources WHERE raw_source = 'Player''s Handbook (2014) p. 132'));
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Guild Artisan'), 'Guild Business', 'Guild Business', '1d20');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '1', 'Alchemists and apothecaries');
