INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '4', 'Calligraphers, scribes, and scriveners');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '5', 'Carpenters, roofers, and plasterers');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '6', 'Cartographers, surveyors, and chart-makers');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '7', 'Cobblers and shoemakers');
