INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '9', 'Glassblowers and glaziers');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '10', 'Jewelers and gemcutters');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '11', 'Leatherworkers, skinners, and tanners');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '12', 'Masons and stonecutters');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '13', 'Painters, limners, and sign-makers');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '14', 'Potters and tile-makers');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '15', 'Shipwrights and sailmakers');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '16', 'Smiths and metal-forgers');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '17', 'Tinkers, pewterers, and casters');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '18', 'Wagon-makers and wheelwrights');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '19', 'Weavers and dyers');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '20', 'Woodcarvers, coopers, and bowyers');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Guild Artisan'), 'Feature: Guild Membership', 'As an established and respected member of a guild, you can rely on certain benefits that membership provides. Your fellow guild members will provide you with lodging and food if necessary, and pay for your funeral if needed. In some cities and towns, a guildhall offers a central place to meet other members of your profession, which can be a good place to meet potential patrons, allies, or hirelings.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Guild Artisan'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I believe that anything worth doing is worth doing right. I can''t help it — I''m a perfectionist.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I''m a snob who looks down on those who can''t appreciate fine art.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I always want to know how things work and what makes people tick.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I''m full of witty aphorisms and have a proverb for every occasion.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I''m rude to people who lack my commitment to hard work and fair play.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I like to talk at length about my profession.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I don''t part with my money easily and will haggle tirelessly to get the best deal possible.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I''m well known for my work, and I want to make sure everyone appreciates it. I''m always taken aback when people haven''t heard of me.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Guild Artisan'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Community. It is the duty of all civilized people to strengthen the');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Guild Artisan'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'Generosity. My talents were given to me so that I could use them to benefit the world. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'Freedom. Everyone should be free to pursue his or her own livelihood. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'Greed. I''m only in it for the money. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'People. I''m committed to the people I care about, not to ideals. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'Aspiration. I work hard to be the best there is at my craft. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'The workshop where I learned my trade is the most important place in the world to me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I created a great work for someone, and then found them unworthy to receive it. I''m still looking for someone worthy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I owe my guild a great debt for forging me into the person I am today.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I pursue wealth to secure someone''s love.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'One day I will return to my guild and prove that I am the greatest artisan of them all.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I will get revenge on the evil forces that destroyed my place of business and ruined my livelihood.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Guild Artisan'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I''ll do anything to get my hands on something rare or priceless.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I''m quick to assume that someone is trying to cheat me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'No one must ever learn that I once stole money from guild coffers.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I''m never satisfied with what I have — I always want more.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I would kill to acquire a noble title.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Artisan') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I''m horribly jealous of anyone who can outshine my handiwork. Everywhere I go, I''m surrounded by rivals.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Hermit', 'You lived in seclusion — either in a sheltered community such as a monastery, or entirely alone — for a formative part of your life. In your time apart from the clamor of society, you found quiet, solitude, and perhaps some of the answers you were looking for.', 'Medicine, Religion', 'Herbalism kit', 'One of your choice', 'A scroll case stuffed full of notes from your studies or prayers, a winter blanket, a set of common clothes, an herbalism kit, and 5 gp', (SELECT id FROM core_book_sources WHERE raw_source = 'Player''s Handbook (2014) p. 134'));
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Hermit'), 'Life of Seclusion', 'Life', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hermit') AND title = 'Life' ORDER BY id DESC LIMIT 1), '1', 'I was searching for spiritual enlightenment.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hermit') AND title = 'Life' ORDER BY id DESC LIMIT 1), '2', 'I was partaking of communal living in accordance with the dictates of a religious order.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hermit') AND title = 'Life' ORDER BY id DESC LIMIT 1), '3', 'I was exiled for a crime I didn''t commit.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hermit') AND title = 'Life' ORDER BY id DESC LIMIT 1), '4', 'I retreated from society after a life-altering event.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hermit') AND title = 'Life' ORDER BY id DESC LIMIT 1), '5', 'I needed a quiet place to work on my art, literature, music, or manifesto.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hermit') AND title = 'Life' ORDER BY id DESC LIMIT 1), '6', 'I needed to commune with nature, far from civilization.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hermit') AND title = 'Life' ORDER BY id DESC LIMIT 1), '7', 'I was the caretaker of an ancient ruin or relic.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hermit') AND title = 'Life' ORDER BY id DESC LIMIT 1), '8', 'I was a pilgrim in search of a person, place, or relic of spiritual significance.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Hermit'), 'Feature: Discovery', 'The quiet seclusion of your extended hermitage gave you access to a unique and powerful discovery. The exact nature of this revelation depends on the nature of your seclusion. It might be a great truth about the cosmos, the deities, the powerful beings of the outer planes, or the forces of nature. It could be a site that no one else has ever seen. You might have uncovered a fact that has long been forgotten, or unearthed some relic of the past that could rewrite history. It might be information that would be damaging to the people who or consigned you to exile, and hence the reason for your return to society.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Hermit'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hermit') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I''ve been isolated for so long that I rarely speak, preferring gestures and the occasional grunt.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hermit') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I am utterly serene, even in the face of disaster.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hermit') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'The leader of my community had something wise to say on every topic, and I am eager to share that wisdom.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hermit') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I feel tremendous empathy for all who suffer.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hermit') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I''m oblivious to etiquette and social expectations.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hermit') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I connect everything that happens to me to a grand, cosmic plan.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hermit') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I often get lost in my own thoughts and contemplation, becoming oblivious to my surroundings.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hermit') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I am working on a grand philosophical theory and love sharing my ideas.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hermit') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Greater Good. My gifts are meant to be shared with all, not used for my own benefit. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hermit') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Logic. Emotions must not cloud our sense of what is right and true, or our logical thinking. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hermit') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Free Thinking. Inquiry and curiosity are the pillars of progress. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hermit') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Power. Solitude and contemplation are paths toward mystical or magical power. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hermit') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Live and Let Live. Meddling in the affairs of others only causes trouble. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hermit') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Self-Knowledge. If you know yourself, there''s nothing left to know. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Hermit'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hermit') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'Nothing is more important than the other members of my hermitage, order, or association.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hermit') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I entered seclusion to hide from the ones who might still be hunting me. I must someday confront them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hermit') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I''m still seeking the enlightenment I pursued in my seclusion, and it still eludes me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hermit') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I entered seclusion because I loved someone I could not have.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hermit') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'Should my discovery come to light, it could bring ruin to the world.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hermit') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'My isolation gave me great insight into a great evil that only I can destroy.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Hermit'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hermit') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'Now that I''ve returned to the world, I enjoy its delights a little too much.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hermit') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I harbor dark, bloodthirsty thoughts that my isolation and meditation failed to quell.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hermit') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I am dogmatic in my thoughts and philosophy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hermit') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I let my need to win arguments overshadow friendships and harmony.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hermit') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I''d risk too much to uncover a lost bit of knowledge.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hermit') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I like keeping secrets and won''t share them with anyone.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Noble', 'You understand wealth, power, and privilege. You carry a noble title, and your family owns land, collects taxes, and wields significant political influence. You might be a pampered aristocrat unfamiliar with work or discomfort, a former merchant just elevated to the nobility, or a disinherited scoundrel with a disproportionate sense of entitlement. Or you could be an honest, hard-working landowner who cares deeply about the people who live and work on your land, keenly aware of your responsibility to them.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Noble'), 'Feature: Position of Privilege', 'Thanks to your noble birth, people are inclined to think the best of you. You are welcome in high society, and people assume you have the right to be wherever you are. The common folk make every effort to accommodate you and avoid your displeasure, and other people of high birth treat you as a member of the same social sphere. You can secure an audience with a local noble if you need to.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Noble'), 'Variant Feature: Retainers', 'You have the service of three retainers loyal to your family. These retainers can be attendants or messengers, and one might be a majordomo. Your retainers are commoners who can perform mundane tasks for you, but they do not fight for you, will not follow you into obviously dangerous areas (such as dungeons), and will leave if they are frequently endangered or abused.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Noble'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'My eloquent flattery makes everyone I talk to feel like the most wonderful and important person in the world.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'The common folk love me for my kindness and generosity.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'No one could doubt by looking at my regal bearing that I am a cut above the unwashed masses.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I take great pains to always look my best and follow the latest fashions.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I don''t like to get my hands dirty, and I won''t be caught dead in unsuitable accommodations.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Despite my noble birth, I do not place myself above other folk. We all have the same blood.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'My favor, once lost, is lost forever.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'If you do me an injury, I will crush you, ruin your name, and salt your fields.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Noble'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Noble') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Respect. Respect is due to me because of my position, but all people regardless of station deserve to be treated with dignity. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Noble') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Responsibility. It is my duty to respect the authority of those above me, just as those below me must respect mine. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Noble') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Independence. I must prove that I can handle myself without the coddling of my family. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Noble') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Power. If I can attain more power, no one will tell me what to do. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Noble') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Family. Blood runs thicker than water. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Noble') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Noble Obligation. It is my duty to protect and care for the people beneath me. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Noble') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'I will face any challenge to win the approval of my family.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Noble') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'My house''s alliance with another noble family must be sustained at all costs.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Noble') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Nothing is more important than the other members of my family.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Noble') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'I am in love with the heir of a family that my family despises.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Noble') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'My loyalty to my sovereign is unwavering.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Noble') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'The common folk must see me as a hero of the people.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Noble'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Noble') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I secretly believe that everyone is beneath me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Noble') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I hide a truly scandalous secret that could ruin my family forever.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Noble') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I too often hear veiled insults and threats in every word addressed to me, and I''m quick to anger.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Noble') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I have an insatiable desire for carnal pleasures.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Noble') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'In fact, the world does revolve around me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Noble') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'By my words and actions, I often bring shame to my family.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Outlander', 'You grew up in the wilds, far from civilization and the comforts of town and technology. You''ve witnessed the migration of herds larger than forests, survived weather more extreme than any city-dweller could comprehend, and enjoyed the solitude of being the only thinking creature for miles in any direction. The wilds are in your blood, whether you were a nomad, an explorer, a recluse, a hunter-gatherer, or even a marauder. Even in places where you don''t know the specific features of the terrain, you know the ways of the wild.', 'Athletics, Survival', 'One type of musical instrument', 'One of your choice', 'A staff, a hunting trap, a trophy from an animal you killed, a set of traveler''s clothes, and a belt pouch containing 10 gp', (SELECT id FROM core_book_sources WHERE raw_source = 'Player''s Handbook (2014) p. 136'));
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Outlander'), 'Origin', 'Origin', '1d10');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '1', 'Forester');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '2', 'Trapper');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '3', 'Homesteader');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '4', 'Guide');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '5', 'Exile or outcast');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '6', 'Bounty hunter');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '7', 'Pilgrim');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '8', 'Tribal nomad');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '9', 'Hunter-gatherer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '10', 'Tribal marauder');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Outlander'), 'Feature: Wanderer', 'You have an excellent memory for maps and geography, and you can always recall the general layout of terrain, settlements, and other features around you. In addition, you can find food and fresh water for yourself and up to five other people each day, provided that the land offers berries, small game, water, and so forth.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Outlander'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I''m driven by a wanderlust that led me away from home.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I watch over my friends as if they were a litter of newborn pups.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I once ran twenty-five miles without stopping to warn to my clan of an approaching orc horde. I''d do it again if I had to.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I have a lesson for every situation, drawn from observing nature.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I place no stock in wealthy or well-mannered folk. Money and manners won''t save you from a hungry owlbear.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I''m always picking things up, absently fiddling with them, and sometimes accidentally breaking them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I feel far more comfortable around animals than people.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I was, in fact, raised by wolves.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Outlander'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Change. Life is like the seasons, in constant change, and we must change with it. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Greater Good. It is each person''s responsibility to make the most happiness for the whole tribe. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Honor. If I dishonor myself, I dishonor my whole clan. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Might. The strongest are meant to rule. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Nature. The natural world is more important than all the constructs of civilization. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Glory. I must earn glory in battle, for myself and my clan. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'My family, clan, or tribe is the most important thing in my life, even when they are far from me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'An injury to the unspoiled wilderness of my home is an injury to me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'I will bring terrible wrath down on the evildoers who destroyed my homeland.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'I am the last of my tribe, and it is up to me to ensure their names enter legend.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'I suffer awful visions of a coming disaster and will do anything to prevent it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'It is my duty to provide children to sustain my tribe.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Outlander'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I am too enamored of ale, wine, and other intoxicants.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'There''s no room for caution in a life lived to the fullest.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I remember every insult I''ve received and nurse a silent resentment toward anyone who''s ever wronged me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I am slow to trust members of other races, tribes, and societies.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'Violence is my answer to almost any challenge.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Outlander') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'Don''t expect me to save those who can''t save themselves. It is nature''s way that the strong thrive and the weak perish.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Sage', 'You spent years learning the lore of the multiverse. You scoured manuscripts, studied scrolls, and listened to the greatest experts on the subjects that interest you. Your efforts have made you a master in your fields of study.', 'Arcana, History', '', 'Two of your choice', 'A bottle of black ink, a quill, a small knife, a letter from a dead colleague posing a question you have not yet been able to answer, a set of common clothes, and a belt pouch containing 10 gp', (SELECT id FROM core_book_sources WHERE raw_source = 'Player''s Handbook (2014) p. 137'));
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Sage'), 'Specialty', 'Field of Study', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sage') AND title = 'Field of Study' ORDER BY id DESC LIMIT 1), '1', 'Alchemist');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sage') AND title = 'Field of Study' ORDER BY id DESC LIMIT 1), '2', 'Astronomer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sage') AND title = 'Field of Study' ORDER BY id DESC LIMIT 1), '3', 'Discredited academic');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sage') AND title = 'Field of Study' ORDER BY id DESC LIMIT 1), '4', 'Librarian');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sage') AND title = 'Field of Study' ORDER BY id DESC LIMIT 1), '5', 'Professor');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sage') AND title = 'Field of Study' ORDER BY id DESC LIMIT 1), '6', 'Researcher');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sage') AND title = 'Field of Study' ORDER BY id DESC LIMIT 1), '7', 'Wizard''s apprentice');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sage') AND title = 'Field of Study' ORDER BY id DESC LIMIT 1), '8', 'Scribe');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Sage'), 'Feature: Researcher', 'When you attempt to learn or recall a piece of lore, if you do not know that information, you often know where and from whom you can obtain it. Usually, this information comes from a library, scriptorium, university, or a sage or other learned person or creature. Your DM might rule that the knowledge you seek is secreted away in an almost inaccessible place, or that it simply cannot be found. Unearthing the deepest secrets of the multiverse can require an adventure or even a whole campaign.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Sage'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sage') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I use polysyllabic words that convey the impression of great erudition.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sage') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I''ve read every book in the world''s greatest libraries — or I like to boast that I have.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sage') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I''m used to helping out those who aren''t as smart as I am, and I patiently explain anything and everything to others.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sage') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'There''s nothing I like more than a good mystery.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sage') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I''m willing to listen to every side of an argument before I make my own judgment.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sage') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I... speak... slowly... when talking... to idiots,... which... almost... everyone... is... compared... to me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sage') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I am horribly, horribly awkward in social situations.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sage') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I''m convinced that people are always trying to steal my secrets.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sage') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Knowledge. The path to power and self-improvement is through knowledge. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sage') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Beauty. What is beautiful points us beyond itself toward what is true. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sage') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Logic. Emotions must not cloud our logical thinking. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sage') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'No Limits. Nothing should fetter the infinite possibility inherent in all existence. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sage') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Power. Knowledge is the path to power and domination. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sage') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Self-Improvement. The goal of a life of study is the betterment of oneself. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Sage'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sage') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'It is my duty to protect my students.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sage') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I have an ancient text that holds terrible secrets that must not fall into the wrong hands.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sage') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I work to preserve a library, university, scriptorium, or monastery.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sage') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'My life''s work is a series of tomes related to a specific field of lore.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sage') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I''ve been searching my whole life for the answer to a certain question.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sage') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I sold my soul for knowledge. I hope to do great deeds and win it back.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Sage'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sage') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I am easily distracted by the promise of information.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sage') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'Most people scream and run when they see a demon. I stop and take notes on its anatomy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sage') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'Unlocking an ancient mystery is worth the price of a civilization.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sage') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I overlook obvious solutions in favor of complicated ones.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sage') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I speak without really thinking through my words, invariably insulting others.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sage') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I can''t keep a secret to save my life, or anyone else''s.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Sailor', 'You sailed on a seagoing vessel for years. In that time, you faced down mighty storms, monsters of the deep, and those who wanted to sink your craft to the bottomless depths. Your first love is the distant line of the horizon, but the time has come to try your hand at something new.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Sailor'), 'Feature: Ship''s Passage', 'When you need to, you can secure free passage on a sailing ship for yourself and your adventuring companions. You might sail on the ship you served on, or another ship you have good relations with (perhaps one captained by a former crewmate). Because you''re calling in a favor, you can''t be certain of a schedule or route that will meet your every need. Your Dungeon Master will determine how long it takes to get where you need to go. In return for your free passage, you and your companions are expected to assist the crew during the voyage.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Sailor'), 'Variant Feature: Bad Reputation', 'If your character has a sailor background, you may select this background feature instead of Ship''s Passage.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Sailor'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sailor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'My friends know they can rely on me, no matter what.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sailor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I work hard so that I can play hard when the work is done.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sailor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I enjoy sailing into new ports and making new friends over a flagon of ale.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sailor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I stretch the truth for the sake of a good story.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sailor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'To me, a tavern brawl is a nice way to get to know a new city.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sailor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I never pass up a friendly wager.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sailor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'My language is as foul as an otyugh nest.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sailor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I like a job well done, especially if I can convince someone else to do it.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Sailor'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sailor') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Respect. The thing that keeps a ship together is mutual respect between captain and crew. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sailor') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Fairness. We all do the work, so we all share in the rewards. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sailor') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Freedom. The sea is freedom — the freedom to go anywhere and do anything. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sailor') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Mastery. I''m a predator, and the other ships on the sea are my prey. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sailor') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'People. I''m committed to my crewmates, not to ideals. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sailor') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Aspiration. Someday I''ll own my own ship and chart my own destiny. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Sailor'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sailor') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I''m loyal to my captain first, everything else second.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sailor') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'The ship is most important — crewmates and captains come and go.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sailor') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I''ll always remember my first ship.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sailor') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'In a harbor town, I have a paramour whose eyes nearly stole me from the sea.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sailor') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I was cheated out of my fair share of the profits, and I want to get my due.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sailor') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'Ruthless pirates murdered my captain and crewmates, plundered our ship, and left me to die. Vengeance will be mine.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Sailor'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sailor') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I follow orders, even if I think they''re wrong.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sailor') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I''ll say anything to avoid having to do extra work.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sailor') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'Once someone questions my courage, I never back down no matter how dangerous the situation.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sailor') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'Once I start drinking, it''s hard for me to stop.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sailor') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I can''t help but pocket loose coins and other trinkets I come across.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Sailor') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'My pride will probably lead to my destruction.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Soldier', 'War has been your life for as long as you care to remember. You trained as a youth, studied the use of weapons and armor, learned basic survival techniques, including how to stay alive on the battlefield. You might have been part of a standing national army or a mercenary company, or perhaps a member of a local militia who rose to prominence during a recent war.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Soldier'), 'Specialty', 'Role', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Soldier') AND title = 'Role' ORDER BY id DESC LIMIT 1), '1', 'Officer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Soldier') AND title = 'Role' ORDER BY id DESC LIMIT 1), '2', 'Scout');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Soldier') AND title = 'Role' ORDER BY id DESC LIMIT 1), '3', 'Infantry');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Soldier') AND title = 'Role' ORDER BY id DESC LIMIT 1), '4', 'Cavalry');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Soldier') AND title = 'Role' ORDER BY id DESC LIMIT 1), '5', 'Healer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Soldier') AND title = 'Role' ORDER BY id DESC LIMIT 1), '6', 'Quartermaster');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Soldier') AND title = 'Role' ORDER BY id DESC LIMIT 1), '7', 'Standard bearer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Soldier') AND title = 'Role' ORDER BY id DESC LIMIT 1), '8', 'Support staff (cook, blacksmith, or the like)');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Soldier'), 'Feature: Military Rank', 'You have a military rank from your career as a soldier. Soldiers loyal to your former military organization still recognize your authority and influence, and they defer to you if they are of a lower rank. You can invoke your rank to exert influence over other soldiers and requisition simple equipment or horses for temporary use. You can also usually gain access to friendly military encampments and fortresses where your rank is recognized.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Soldier'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I''m always polite and respectful.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I''m haunted by memories of war. I can''t get the images of violence out of my mind.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I''ve lost too many friends, and I''m slow to make new ones.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I''m full of inspiring and cautionary tales from my military experience relevant to almost every combat situation.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I can stare down a hell hound without flinching.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I enjoy being strong and like breaking things.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I have a crude sense of humor.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I face problems head-on. A simple, direct solution is the best path to success.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Greater Good. Our lot is to lay down our lives in defense of others. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Responsibility. I do what I must and obey just authority. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Independence. When people follow orders blindly, they embrace a kind of tyranny. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Might. In life as in war, the stronger force wins. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Live and Let Live. Ideals aren''t worth killing over or going to war for. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Nation. My city, nation, or people are all that matter. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I would still lay down my life for the people I served with.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Someone saved my life on the battlefield. To this day, I will never leave a friend behind.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'My honor is my life.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I''ll never forget the crushing defeat my company suffered or the enemies who dealt it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Those who fight beside me are those worth dying for.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I fight for those who cannot fight for themselves.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Soldier'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Soldier') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'The monstrous enemy we faced in battle still leaves me quivering with fear.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Soldier') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I have little respect for anyone who is not a proven warrior.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Soldier') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I made a terrible mistake in battle that cost many lives, and I would do anything to keep that mistake secret.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Soldier') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'My hatred of my enemies is blind and unreasoning.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Soldier') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I obey the law, even if the law causes misery.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Soldier') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I''d rather eat my armor than admit when I''m wrong.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Urchin', 'You grew up on the streets alone, orphaned, and poor. You had no one to watch over you or to provide for you, so you learned to provide for yourself. You fought fiercely over food and kept a constant watch out for other desperate souls who might steal from you. You slept on rooftops and in alleyways, exposed to the elements, and endured sickness without the advantage of medicine or a place to recuperate. You''ve survived despite all odds, and did so through cunning, strength, speed, or some combination of each.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Urchin'), 'Feature: City Secrets', 'You know the secret patterns and flow to cities and can find passages through the urban sprawl that others would miss. When you are not in combat, you (and companions you lead) can travel between any two locations in the city twice as fast as your speed would normally allow.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Urchin'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urchin') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I hide scraps of food and trinkets away in my pockets.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urchin') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I ask a lot of questions.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urchin') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I like to squeeze into small places where no one else can get to me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urchin') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I sleep with my back to a wall or tree, with everything I own wrapped in a bundle in my arms.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urchin') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I eat like a pig and have bad manners.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urchin') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I think anyone who''s nice to me is hiding evil intent.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urchin') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I don''t like to bathe.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urchin') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I bluntly say what other people are hinting at or hiding.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Urchin'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urchin') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Respect. All people, rich or poor, deserve respect. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urchin') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Community. We have to take care of each other, because no one else is going to do it. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urchin') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Change. The low are lifted up, and the high and mighty are brought down. Change is the nature of things. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urchin') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Retribution. The rich need to be shown what life and death are like in the gutters. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urchin') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'People. I help the people who help me — that''s what keeps us alive. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urchin') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Aspiration. I''m going to prove that I''m worthy of a better life. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Urchin'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urchin') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'My town or city is my home, and I''ll fight to defend it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urchin') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I sponsor an orphanage to keep others from enduring what I was forced to endure.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urchin') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I owe my survival to another urchin who taught me to live on the streets.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urchin') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I owe a debt I can never repay to the person who took pity on me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urchin') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I escaped my life of poverty by robbing an important person, and I''m wanted for it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urchin') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'No one else should have to endure the hardships I''ve been through.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Urchin'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urchin') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'If I''m outnumbered, I will run away from a fight.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urchin') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'Gold seems like a lot of money to me, and I''ll do just about anything for more of it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urchin') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I will never fully trust anyone other than myself.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urchin') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I''d rather kill someone in their sleep than fight fair.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urchin') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'It''s not stealing if I need it more than someone else.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urchin') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'People who can''t take care of themselves get what they deserve.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Spy', 'Although your capabilities are not much different from those of a burglar or smuggler, you learned and practiced them in a very different context: As an espionage agent. You might have been an officially sanctioned agent of the crown, or perhaps you sold the secrets you uncovered to the highest bidder.', 'Deception, Stealth', 'One type of gaming set, thieves'' tools', '', 'A crowbar, a set of dark common clothes including a hood, and a belt pouch containing 15 gp', (SELECT id FROM core_book_sources WHERE raw_source = 'Player''s Handbook (2014) p. 130'));
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Spy'), 'Spy Specialty', 'Specialty', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Spy') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '1', 'Blackmailer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Spy') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '2', 'Burglar');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Spy') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '3', 'Enforcer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Spy') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '4', 'Fence');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Spy') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '5', 'Highway robber');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Spy') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '6', 'Hired killer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Spy') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '7', 'Pickpocket');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Spy') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '8', 'Smuggler');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Spy'), 'Feature: Spy Contact', 'You have a reliable and trustworthy contact who acts as your liaison to a network of other spies. You know how to get messages to and from your contact, even over great distances; specifically, you know the local messengers, corrupt caravan masters, and seedy sailors who can deliver messages for you.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Spy'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Spy') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I always have a plan for what to do when things go wrong.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Spy') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I am always calm, no matter what the situation. I never raise my voice or let my emotions control me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Spy') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'The first thing I do in a new place is note the locations of everything valuable — or where such things could be hidden.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Spy') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I would rather make a new friend than a new enemy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Spy') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I am incredibly slow to trust. Those who seem the fairest often have the most to hide.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Spy') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I don''t pay attention to the risks in a situation. Never tell me the odds.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Spy') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'The best way to get me to do something is to tell me I can''t do it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Spy') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I blow up at the slightest insult.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Spy'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Spy') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Honor. I don''t steal from others in the trade. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Spy') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Freedom. Chains are meant to be broken, as are those who would forge them. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Spy') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Charity. I steal from the wealthy so that I can help people in need. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Spy') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Greed. I will do whatever it takes to become wealthy. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Spy') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'People. I''m loyal to my friends, not to any ideals, and everyone else can take a trip down the Styx for all I care. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Spy') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Redemption. There''s a spark of good in everyone. (Good)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Spy'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Spy') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I''m trying to pay off an old debt I owe to a generous benefactor.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Spy') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'My ill-gotten gains go to support my family.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Spy') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'Something important was taken from me, and I aim to steal it back.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Spy') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I will become the greatest thief that ever lived.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Spy') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I''m guilty of a terrible crime. I hope I can redeem myself for it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Spy') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'Someone I loved died because of 1 mistake I made. That will never happen again.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Spy'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Spy') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'When I see something valuable, I can''t think about anything but how to steal it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Spy') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'When faced with a choice between money and my friends, I usually choose the money.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Spy') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'If there''s a plan, I''ll forget it. If I don''t forget it, I''ll ignore it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Spy') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I have a "tell" that reveals when I''m lying.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Spy') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I turn tail and run when things look bad.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Spy') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'An innocent person is in prison for a crime that I committed. I''m okay with that.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Gladiator', 'A gladiator is as much an entertainer as any minstrel or circus performer, trained to make the arts of combat into a spectacle the crowd can enjoy. This kind of flashy combat is your entertainer routine, though you might also have some skills as a tumbler or actor. Using your By Popular Demand feature, you can find a place to perform in any place that features combat for entertainment — perhaps a gladiatorial arena or secret pit fighting club.', 'Acrobatics, Performance', 'Disguise kits, one type of musical instrument', '', 'An inexpensive but unusual weapon such as a trident or net, the favor of an admirer (love letter, lock of hair, or trinket), a costume, and a belt pouch containing 15 gp', (SELECT id FROM core_book_sources WHERE raw_source = 'Player''s Handbook (2014) p. 131'));
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Gladiator'), 'Feature: By Popular Demand', 'You can always find a place to perform, usually in an inn or tavern but possibly with a circus, at a theater, or even in a noble''s court. At such a place, you receive free lodging and food of a modest or comfortable standard (depending on the quality of the establishment), as long as you perform each night. In addition, your performance makes you something of a local figure. When strangers recognize you in a town where you have performed, they typically take a liking to you.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Gladiator'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gladiator') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I know a story relevant to almost every situation.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gladiator') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Whenever I come to a new place, I collect local rumors and spread gossip.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gladiator') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I''m a hopeless romantic, always searching for that "special someone".');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gladiator') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Nobody stays angry at me or around me for long, since I can defuse any amount of tension.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gladiator') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I love a good insult, even one directed at me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gladiator') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I get bitter if I''m not the center of attention.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gladiator') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I''ll settle for nothing less than perfection.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gladiator') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I change my mood or my mind as quickly as I change key in a song.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gladiator') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Beauty. When I perform, I make the world better than it was. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gladiator') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Tradition. The stories, legends, and songs of the past must never be forgotten, for they teach us who we are. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gladiator') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Creativity. The world is in need of new ideas and bold action. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gladiator') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Greed. I''m only in it for the money and fame. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gladiator') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'People. I like seeing the smiles on people''s faces when I perform. That''s all that matters. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gladiator') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Honesty. Art should reflect the soul; it should come from within and reveal who we really are. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Gladiator'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gladiator') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'My instrument is my most treasured possession, and it reminds me of someone I love.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gladiator') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'Someone stole my precious instrument, and someday I''ll get it back.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gladiator') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I want to be famous, whatever it takes.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gladiator') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I idolize a hero of the old tales and measure my deeds against that person''s.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gladiator') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I will do anything to prove myself superior to my hated rival.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gladiator') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I would do anything for the other members of my old troupe.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Gladiator'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gladiator') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I''ll do anything to win fame and renown.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gladiator') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I''m a sucker for a pretty face.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gladiator') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'A scandal prevents me from ever going home again. That kind of trouble seems to follow me around.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gladiator') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I once satirized a noble who still wants my head. It was a mistake that I will likely repeat.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gladiator') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I have trouble keeping my true feelings hidden. My sharp tongue lands me in trouble.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gladiator') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'Despite my best efforts, I am unreliable to my friends.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Guild Merchant', 'Instead of an artisans'' guild, you might belong to a guild of traders, caravan masters, or shopkeepers. You don''t craft items yourself but earn a living by buying and selling the works of others (or the raw materials artisans need to practice their craft). Your guild might be a large merchant consortium (or family) with interests across the region. Perhaps you transported goods from one place to another, by ship, wagon, or caravan, or bought them from traveling traders and sold them in your own little shop. In some ways, the traveling merchant''s life lends itself to adventure far more than the life of an artisan.', 'Insight, Persuasion', 'Navigator''s tools', 'One of your choice', 'A mule and a cart, a letter of introduction from your guild, a set of traveler''s clothes, and a belt pouch containing 15 gp', (SELECT id FROM core_book_sources WHERE raw_source = 'Player''s Handbook (2014) p. 133'));
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Guild Merchant'), 'Guild Business', 'Guild Business', '1d20');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '1', 'Alchemists and apothecaries');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '2', 'Armorers, locksmiths, and finesmiths');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '3', 'Brewers, distillers, and vintners');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '4', 'Calligraphers, scribes, and scriveners');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '5', 'Carpenters, roofers, and plasterers');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '6', 'Cartographers, surveyors, and chart-makers');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '7', 'Cobblers and shoemakers');
