INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '10', 'Jewelers and gemcutters');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '11', 'Leatherworkers, skinners, and tanners');
