INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '13', 'Painters, limners, and sign-makers');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '14', 'Potters and tile-makers');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '15', 'Shipwrights and sailmakers');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '16', 'Smiths and metal-forgers');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '17', 'Tinkers, pewterers, and casters');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '18', 'Wagon-makers and wheelwrights');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '19', 'Weavers and dyers');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '20', 'Woodcarvers, coopers, and bowyers');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Guild Merchant'), 'Feature: Guild Membership', 'As an established and respected member of a guild, you can rely on certain benefits that membership provides. Your fellow guild members will provide you with lodging and food if necessary, and pay for your funeral if needed. In some cities and towns, a guildhall offers a central place to meet other members of your profession, which can be a good place to meet potential patrons, allies, or hirelings.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Guild Merchant'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I believe that anything worth doing is worth doing right. I can''t help it — I''m a perfectionist.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I''m a snob who looks down on those who can''t appreciate fine art.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I always want to know how things work and what makes people tick.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I''m full of witty aphorisms and have a proverb for every occasion.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I''m rude to people who lack my commitment to hard work and fair play.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I like to talk at length about my profession.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I don''t part with my money easily and will haggle tirelessly to get the best deal possible.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I''m well known for my work, and I want to make sure everyone appreciates it. I''m always taken aback when people haven''t heard of me.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Guild Merchant'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Community. It is the duty of all civilized people to strengthen the');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Guild Merchant'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'Generosity. My talents were given to me so that I could use them to benefit the world. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'Freedom. Everyone should be free to pursue his or her own livelihood. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'Greed. I''m only in it for the money. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'People. I''m committed to the people I care about, not to ideals. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'Aspiration. I work hard to be the best there is at my craft. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'The workshop where I learned my trade is the most important place in the world to me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I created a great work for someone, and then found them unworthy to receive it. I''m still looking for someone worthy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I owe my guild a great debt for forging me into the person I am today.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I pursue wealth to secure someone''s love.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'One day I will return to my guild and prove that I am the greatest artisan of them all.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I will get revenge on the evil forces that destroyed my place of business and ruined my livelihood.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Guild Merchant'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I''ll do anything to get my hands on something rare or priceless.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I''m quick to assume that someone is trying to cheat me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'No one must ever learn that I once stole money from guild coffers.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I''m never satisfied with what I have — I always want more.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I would kill to acquire a noble title.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Guild Merchant') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I''m horribly jealous of anyone who can outshine my handiwork. Everywhere I go, I''m surrounded by rivals.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Knight', 'A knighthood is among the lowest noble titles in most societies, but it can be a path to higher status. One of your commoner retainers is replaced by a noble who serves as your squire, aiding you in exchange for training on his or her own path to knighthood. Your two remaining retainers might include a groom to care for your horse and a servant who polishes your armor (and even helps you put it on).
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Knight'), 'Feature: Retainers', 'You have the service of three retainers loyal to your family. These retainers can be attendants or messengers, and one might be a majordomo. Your retainers are commoners who can perform mundane tasks for you, but they do not fight for you, will not follow you into obviously dangerous areas (such as dungeons), and will leave if they are frequently endangered or abused.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Knight'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'My eloquent flattery makes everyone I talk to feel like the most wonderful and important person in the world.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'The common folk love me for my kindness and generosity.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'No one could doubt by looking at my regal bearing that I am a cut above the unwashed masses.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I take great pains to always look my best and follow the latest fashions.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I don''t like to get my hands dirty, and I won''t be caught dead in unsuitable accommodations.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Despite my noble birth, I do not place myself above other folk. We all have the same blood.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'My favor, once lost, is lost forever.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'If you do me an injury, I will crush you, ruin your name, and salt your fields.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Knight'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Respect. Respect is due to me because of my position, but all people regardless of station deserve to be treated with dignity. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Responsibility. It is my duty to respect the authority of those above me, just as those below me must respect mine. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Independence. I must prove that I can handle myself without the coddling of my family. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Power. If I can attain more power, no one will tell me what to do. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Family. Blood runs thicker than water. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Noble Obligation. It is my duty to protect and care for the people beneath me. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'I will face any challenge to win the approval of my family.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'My house''s alliance with another noble family must be sustained at all costs.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Nothing is more important than the other members of my family.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'I am in love with the heir of a family that my family despises.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'My loyalty to my sovereign is unwavering.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'The common folk must see me as a hero of the people.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Knight'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I secretly believe that everyone is beneath me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I hide a truly scandalous secret that could ruin my family forever.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I too often hear veiled insults and threats in every word addressed to me, and I''m quick to anger.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I have an insatiable desire for carnal pleasures.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'In fact, the world does revolve around me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'By my words and actions, I often bring shame to my family.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Pirate', 'You spent your youth under the sway of a dread pirate, a ruthless cutthroat who taught you how to survive in a world of sharks and savages. You''ve indulged in larceny on the high seas and sent more than one deserving soul to a briny grave. Fear and bloodshed are no strangers to you, and you''ve garnered a somewhat unsavory reputation in many a port town.', 'Athletics, Perception', 'Navigator''s tools, vehicles (water)', '', 'A belaying pin (club), 50 feet of silk rope, a lucky charm such as a rabbit foot or a small stone with a hole in the center (or you may roll for a random trinket on the Trinkets table in chapter 5), a set of common clothes, and a belt pouch containing 10 gp', (SELECT id FROM core_book_sources WHERE raw_source = 'Player''s Handbook (2014) p. 139'));
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Pirate'), 'Feature: Bad Reputation', 'If your character has a sailor background, you may select this background feature instead of Ship''s Passage.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Pirate'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Pirate') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'My friends know they can rely on me, no matter what.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Pirate') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I work hard so that I can play hard when the work is done.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Pirate') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I enjoy sailing into new ports and making new friends over a flagon of ale.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Pirate') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I stretch the truth for the sake of a good story.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Pirate') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'To me, a tavern brawl is a nice way to get to know a new city.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Pirate') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I never pass up a friendly wager.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Pirate') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'My language is as foul as an otyugh nest.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Pirate') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I like a job well done, especially if I can convince someone else to do it.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Pirate'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Pirate') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Respect. The thing that keeps a ship together is mutual respect between captain and crew. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Pirate') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Fairness. We all do the work, so we all share in the rewards. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Pirate') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Freedom. The sea is freedom — the freedom to go anywhere and do anything. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Pirate') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Mastery. I''m a predator, and the other ships on the sea are my prey. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Pirate') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'People. I''m committed to my crewmates, not to ideals. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Pirate') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Aspiration. Someday I''ll own my own ship and chart my own destiny. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Pirate'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Pirate') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I''m loyal to my captain first, everything else second.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Pirate') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'The ship is most important — crewmates and captains come and go.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Pirate') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I''ll always remember my first ship.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Pirate') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'In a harbor town, I have a paramour whose eyes nearly stole me from the sea.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Pirate') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I was cheated out of my fair share of the profits, and I want to get my due.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Pirate') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'Ruthless pirates murdered my captain and crewmates, plundered our ship, and left me to die. Vengeance will be mine.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Pirate'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Pirate') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I follow orders, even if I think they''re wrong.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Pirate') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I''ll say anything to avoid having to do extra work.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Pirate') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'Once someone questions my courage, I never back down no matter how dangerous the situation.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Pirate') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'Once I start drinking, it''s hard for me to stop.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Pirate') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I can''t help but pocket loose coins and other trinkets I come across.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Pirate') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'My pride will probably lead to my destruction.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('City Watch', 'You have served the community where you grew up, standing as its first line of defense against crime. You aren''t a soldier, directing your gaze outward at possible enemies. Instead, your service to your hometown was to help police its populace, protecting the citizenry from lawbreakers and malefactors of every stripe.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'City Watch'), 'Feature: Watcher''s Eye', 'Your experience in enforcing the law, and dealing with lawbreakers, gives you a feel for local laws and criminals. You can easily find the local outpost of the watch or a similar organization, and just as easily pick out the dens of criminal activity in a community, although you''re more likely to be welcome in the former locations rather than the latter.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'City Watch'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'City Watch') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I''m always polite and respectful.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'City Watch') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I''m haunted by memories of war. I can''t get the images of violence out of my mind.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'City Watch') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I''ve lost too many friends, and I''m slow to make new ones.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'City Watch') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I''m full of inspiring and cautionary tales from my military experience relevant to almost every combat situation.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'City Watch') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I can stare down a hell hound without flinching.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'City Watch') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I enjoy being strong and like breaking things.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'City Watch') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I have a crude sense of humor.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'City Watch') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I face problems head-on. A simple, direct solution is the best path to success.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'City Watch') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Greater Good. Our lot is to lay down our lives in defense of others. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'City Watch') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Responsibility. I do what I must and obey just authority. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'City Watch') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Independence. When people follow orders blindly, they embrace a kind of tyranny. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'City Watch') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Might. In life as in war, the stronger force wins. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'City Watch') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Live and Let Live. Ideals aren''t worth killing over or going to war for. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'City Watch') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Nation. My city, nation, or people are all that matter. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'City Watch') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I would still lay down my life for the people I served with.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'City Watch') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Someone saved my life on the battlefield. To this day, I will never leave a friend behind.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'City Watch') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'My honor is my life.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'City Watch') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I''ll never forget the crushing defeat my company suffered or the enemies who dealt it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'City Watch') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Those who fight beside me are those worth dying for.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'City Watch') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I fight for those who cannot fight for themselves.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'City Watch'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'City Watch') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'The monstrous enemy we faced in battle still leaves me quivering with fear.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'City Watch') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I have little respect for anyone who is not a proven warrior.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'City Watch') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I made a terrible mistake in battle that cost many lives, and I would do anything to keep that mistake secret.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'City Watch') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'My hatred of my enemies is blind and unreasoning.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'City Watch') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I obey the law, even if the law causes misery.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'City Watch') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I''d rather eat my armor than admit when I''m wrong.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Clan Crafter', 'The Stout Folk are well known for their artisanship and the worth of their handiworks, and you have been trained in that ancient tradition. For years you labored under a dwarf master of the craft, enduring long hours and dismissive, sour-tempered treatment in order to gain the fine skills you possess today.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clan Crafter'), 'Feature: Respect of the Stout Folk', 'As well respected as clan crafters are among outsiders, no one esteems them quite so highly as dwarves do. You always have free room and board in any place where shield dwarves or gold dwarves dwell, and the individuals in such a settlement might vie among themselves to determine who can offer you (and possibly your companions) the finest accommodations and assistance.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clan Crafter'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clan Crafter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I believe that anything worth doing is worth doing right. I can''t help it — I''m a perfectionist.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clan Crafter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I''m a snob who looks down on those who can''t appreciate fine art.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clan Crafter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I always want to know how things work and what makes people tick.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clan Crafter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I''m full of witty aphorisms and have a proverb for every occasion.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clan Crafter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I''m rude to people who lack my commitment to hard work and fair play.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clan Crafter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I like to talk at length about my profession.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clan Crafter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I don''t part with my money easily and will haggle tirelessly to get the best deal possible.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clan Crafter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I''m well known for my work, and I want to make sure everyone appreciates it. I''m always taken aback when people haven''t heard of me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clan Crafter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Community. It is the duty of all civilized people to strengthen the bonds of community and the security of civilization. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clan Crafter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Generosity. My talents were given to me so that I could use them to benefit the world. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clan Crafter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Freedom. Everyone should be free to pursue his or her own livelihood. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clan Crafter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Greed. I''m only in it for the money. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clan Crafter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'People. I''m committed to the people I care about, not to ideals. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clan Crafter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Aspiration. I work hard to be the best there is at my craft. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clan Crafter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'The workshop where I learned my trade is the most important place in the world to me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clan Crafter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I created a great work for someone, and then found them unworthy to receive it. I''m still looking for someone worthy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clan Crafter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I owe my guild a great debt for forging me into the person I am today.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clan Crafter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I pursue wealth to secure someone''s love.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clan Crafter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'One day I will return to my guild and prove that I am the greatest artisan of them all.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clan Crafter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I will get revenge on the evil forces that destroyed my place of business and ruined my livelihood.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clan Crafter'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clan Crafter') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I''ll do anything to get my hands on something rare or priceless.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clan Crafter') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I''m quick to assume that someone is trying to cheat me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clan Crafter') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'No one must ever learn that I once stole money from guild coffers.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clan Crafter') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I''m never satisfied with what I have — I always want more.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clan Crafter') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I would kill to acquire a noble title.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clan Crafter') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I''m horribly jealous of anyone who can outshine my handiwork. Everywhere I go, I''m surrounded by rivals.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Cloistered Scholar', 'As a child, you were inquisitive when your playmates were possessive or raucous. In your formative years, you found your way to one of Faerûn''s great institutes of learning, where you were apprenticed and taught that knowledge is a more valuable treasure than gold or gems. Now you are ready to leave your home — not to abandon it, but to quest for new lore to add to its storehouse of knowledge.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Cloistered Scholar'), 'Feature: Library Access', 'Though others must often endure extensive interviews and significant fees to gain access to even the most common archives in your library, you have free and easy access to the majority of the library, though it might also have repositories of lore that are too valuable, magical, or secret to permit anyone immediate access.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Cloistered Scholar'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cloistered Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I use polysyllabic words that convey the impression of great erudition.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cloistered Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I''ve read every book in the world''s greatest libraries — or I like to boast that I have.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cloistered Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I''m used to helping out those who aren''t as smart as I am, and I patiently explain anything and everything to others.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cloistered Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'There''s nothing I like more than a good mystery.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cloistered Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I''m willing to listen to every side of an argument before I make my own judgment.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cloistered Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I... speak... slowly... when talking... to idiots,... which... almost... everyone... is... compared... to me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cloistered Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I am horribly, horribly awkward in social situations.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cloistered Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I''m convinced that people are always trying to steal my secrets.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cloistered Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Knowledge. The path to power and self-improvement is through knowledge. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cloistered Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Beauty. What is beautiful points us beyond itself toward what is true. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cloistered Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Logic. Emotions must not cloud our logical thinking. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cloistered Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'No Limits. Nothing should fetter the infinite possibility inherent in all existence. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cloistered Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Power. Knowledge is the path to power and domination. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cloistered Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Self-Improvement. The goal of a life of study is the betterment of oneself. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cloistered Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'It is my duty to protect my students.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cloistered Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I have an ancient text that holds terrible secrets that must not fall into the wrong hands.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cloistered Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I work to preserve a library, university, scriptorium, or monastery.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cloistered Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'My life''s work is a series of tomes related to a specific field of lore.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cloistered Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I''ve been searching my whole life for the answer to a certain question.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cloistered Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I sold my soul for knowledge. I hope to do great deeds and win it back.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Cloistered Scholar'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cloistered Scholar') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I am easily distracted by the promise of information.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cloistered Scholar') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'Most people scream and run when they see a demon. I stop and take notes on its anatomy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cloistered Scholar') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'Unlocking an ancient mystery is worth the price of a civilization.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cloistered Scholar') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I overlook obvious solutions in favor of complicated ones.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cloistered Scholar') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I speak without really thinking through my words, invariably insulting others.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cloistered Scholar') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I can''t keep a secret to save my life, or anyone else''s.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Courtier', 'In your earlier days, you were a personage of some significance in a noble court or a bureaucratic organization. You might or might not come from an upper-class family; your talents, rather than the circumstances of your birth, could have secured you this position.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Courtier'), 'Feature: Court Functionary', 'Your knowledge of how bureaucracies function lets you gain access to the records and inner workings of any noble court or government you encounter. You know who the movers and shakers are, whom to go to for the favors you seek, and what the current intrigues of interest in the group are.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Courtier'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I believe that anything worth doing is worth doing right. I can''t help it — I''m a perfectionist.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I''m a snob who looks down on those who can''t appreciate fine art.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I always want to know how things work and what makes people tick.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I''m full of witty aphorisms and have a proverb for every occasion.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I''m rude to people who lack my commitment to hard work and fair play.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I like to talk at length about my profession.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I don''t part with my money easily and will haggle tirelessly to get the best deal possible.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I''m well known for my work, and I want to make sure everyone appreciates it. I''m always taken aback when people haven''t heard of me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Community. It is the duty of all civilized people to strengthen the bonds of community and the security of civilization. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Generosity. My talents were given to me so that I could use them to benefit the world. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Freedom. Everyone should be free to pursue his or her own livelihood. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Greed. I''m only in it for the money. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'People. I''m committed to the people I care about, not to ideals. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Aspiration. I work hard to be the best there is at my craft. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'The workshop where I learned my trade is the most important place in the world to me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I created a great work for someone, and then found them unworthy to receive it. I''m still looking for someone worthy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I owe my guild a great debt for forging me into the person I am today.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I pursue wealth to secure someone''s love.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'One day I will return to my guild and prove that I am the greatest artisan of them all.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I will get revenge on the evil forces that destroyed my place of business and ruined my livelihood.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Courtier'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtier') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I''ll do anything to get my hands on something rare or priceless.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtier') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I''m quick to assume that someone is trying to cheat me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtier') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'No one must ever learn that I once stole money from guild coffers.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtier') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I''m never satisfied with what I have — I always want more.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtier') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I would kill to acquire a noble title.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtier') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I''m horribly jealous of anyone who can outshine my handiwork. Everywhere I go, I''m surrounded by rivals.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Faction Agent', 'Many organizations active in the North and across the face of Faerûn aren''t bound by strictures of geography. These factions pursue their agendas without regard for political boundaries, and their members operate anywhere the organization deems necessary. These groups employ listeners, rumormongers, smugglers, sellswords, cache-holders (people who guard caches of wealth or magic for use by the faction''s operatives), haven keepers, and message drop minders, to name a few. At the core of every faction are those who don''t merely fulfill a small function for that organization, but who serve as its hands, head, and heart.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Faction Agent'), 'Factions of the Sword Coast', 'The lack of large, centralized governments in the North and along the Sword Coast is likely directly responsible for the proliferation of secret societies and conspiracies in those lands. If your background is as an agent for one of the main factions of the North and Sword Coast, here are some possibilities.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Faction Agent'), 'Feature: Safe Haven', 'As a faction agent, you have access to a secret network of supporters and operatives who can provide assistance on your adventures. You know a set of secret signs and passwords you can use to identify such operatives, who can provide you with access to a hidden safe house, free room and board, or assistance in finding information. These agents never risk their lives for you or risk revealing their true identities.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Faction Agent'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faction Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I idolize a particular hero of my faith, and constantly refer to that person''s deeds and example.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faction Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I can find common ground between the fiercest enemies, empathizing with them and always working toward peace.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faction Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I see omens in every event and action. The gods try to speak to us, we just need to listen.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faction Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Nothing can shake my optimistic attitude.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faction Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I quote (or misquote) sacred texts and proverbs in almost every situation.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faction Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I am tolerant (or intolerant) of other faiths and respect (or condemn) the worship of other gods.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faction Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I''ve enjoyed fine food, drink, and high society among my temple''s elite. Rough living grates on me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faction Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I''ve spent so long in the temple that I have little practical experience dealing with people in the outside world.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faction Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Tradition. The ancient traditions of worship and sacrifice must be preserved and upheld. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faction Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Charity. I always try to help those in need, no matter what the personal cost. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faction Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Change. We must help bring about the changes the gods are constantly working in the world. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faction Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Power. I hope to one day rise to the top of my faith''s religious hierarchy. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faction Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Faith. I trust that my deity will guide my actions. I have faith that if I work hard, things will go well. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faction Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Aspiration. I seek to prove myself worthy of my god''s favor by matching my actions against his or her teachings. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faction Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I would die to recover an ancient relic of my faith that was lost long ago.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faction Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I will someday get revenge on the corrupt temple hierarchy who branded me a heretic.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faction Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I owe my life to the priest who took me in when my parents died.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faction Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Everything I do is for the common people.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faction Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I will do anything to protect the temple where I served.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faction Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I seek to preserve a sacred text that my enemies consider heretical and seek to destroy.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Faction Agent'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faction Agent') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I judge others harshly, and myself even more severely.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faction Agent') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I put too much trust in those who wield power within my temple''s hierarchy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faction Agent') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'My piety sometimes leads me to blindly trust those that profess faith in my god.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faction Agent') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I am inflexible in my thinking.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faction Agent') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I am suspicious of strangers and expect the worst of them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faction Agent') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'Once I pick a goal, I become obsessed with it to the detriment of everything else in my life.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Far Traveler', 'Almost all of the common people and other folk that one might encounter along the Sword Coast or in the North have one thing in common: they live out their lives without ever traveling more than a few miles from where they were born.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Far Traveler'), 'Why Are You Here?', 'Reason', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Far Traveler') AND title = 'Reason' ORDER BY id DESC LIMIT 1), '1', 'Emissary');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Far Traveler') AND title = 'Reason' ORDER BY id DESC LIMIT 1), '2', 'Exile');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Far Traveler') AND title = 'Reason' ORDER BY id DESC LIMIT 1), '3', 'Fugitive');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Far Traveler') AND title = 'Reason' ORDER BY id DESC LIMIT 1), '4', 'Pilgrim');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Far Traveler') AND title = 'Reason' ORDER BY id DESC LIMIT 1), '5', 'Sightseer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Far Traveler') AND title = 'Reason' ORDER BY id DESC LIMIT 1), '6', 'Wanderer');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Far Traveler'), 'Where Are You From?', 'The most important decision in creating a far traveler background is determining your homeland. The places discussed here are all sufficiently distant from the North and the Sword Coast to justify the use of this background.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Far Traveler'), 'Feature: All Eyes on You', 'Your accent, mannerisms, figures of speech, and perhaps even your appearance all mark you as foreign. Curious glances are directed your way wherever you go, which can be a nuisance, but you also gain the friendly interest of scholars and others intrigued by far-off lands, to say nothing of everyday folk who are eager to hear stories of your homeland.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Far Traveler'), 'Suggested Characteristics', 'Personality Trait', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Far Traveler') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I have different assumptions from those around me concerning personal space, blithely invading others'' space in innocence, or reacting to ignorant invasion of my own.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Far Traveler') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I have my own ideas about what is and is not food, and I find the eating habits of those around me fascinating, confusing, or revolting.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Far Traveler') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I have a strong code of honor or sense of propriety that others don''t comprehend.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Far Traveler') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I express affection or contempt in ways that are unfamiliar to others.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Far Traveler') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I honor my deities through practices that are foreign to this land.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Far Traveler') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I begin or end my day with small traditional rituals that are unfamiliar to those around me.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Far Traveler'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Far Traveler') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Open. I have much to learn from the kindly folk I meet along my way. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Far Traveler') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Reserved. As someone new to these strange lands, I am cautious and respectful in my dealings. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Far Traveler') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Adventure. I''m far from home, and everything is strange and wonderful! (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Far Traveler') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Cunning. Though I may not know their ways, neither do they know mine, which can be to my advantage. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Far Traveler') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Inquisitive. Everything is new, but I have a thirst to learn. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Far Traveler') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Suspicious. I must be careful, for I have no way of telling friend from foe here. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Far Traveler'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Far Traveler') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'So long as I have this token from my homeland, I can face any adversity in this strange land.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Far Traveler') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'The gods of my people are a comfort to me so far from home.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Far Traveler') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I hold no greater cause than my service to my people.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Far Traveler') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'My freedom is my most precious possession. I''ll never let anyone take it from me again.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Far Traveler') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I''m fascinated by the beauty and wonder of this new land.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Far Traveler') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'Though I had no choice, I lament having to leave my loved one(s) behind. I hope to see them again one day.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Far Traveler'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Far Traveler') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I am secretly (or not so secretly) convinced of the superiority of my own culture over that of this foreign land.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Far Traveler') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I pretend not to understand the local language in order to avoid interactions I would rather not have.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Far Traveler') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I have a weakness for the new intoxicants and other pleasures of this land.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Far Traveler') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I don''t take kindly to some of the actions and motivations of the people of this land, because these folk are different from me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Far Traveler') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I consider the adherents of other gods to be deluded innocents at best, or ignorant fools at worst.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Far Traveler') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I have a weakness for the exotic beauty of the people of these lands.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Inheritor', 'You are the heir to something of great value — not mere coin or wealth, but an object that has been entrusted to you and you alone. Your inheritance might have come directly to you from a member of your family, by right of birth, or it could have been left to you by a friend, a mentor, a teacher, or someone else important in your life. The revelation of your inheritance changed your life, and might have set you on the path to adventure, but it could also come with many dangers, including those who covet your gift and want to take it from you — by force, if need be.', 'Survival, plus one from among Arcana, History, and Religion', 'Your choice of a gaming set or a musical instrument', 'One of your choice', 'Your inheritance, a set of traveler''s clothes, the tool you choose for this background''s tool proficiency, and a pouch containing 15 gp', (SELECT id FROM core_book_sources WHERE raw_source = 'Sword Coast Adventurer''s Guide p. 150'));
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Inheritor'), 'Feature: Inheritance', 'Object', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inheritor') AND title = 'Object' ORDER BY id DESC LIMIT 1), '1', 'A document such as a map, a letter, or a journal');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inheritor') AND title = 'Object' ORDER BY id DESC LIMIT 1), '2-3', 'A trinket (see "Trinkets" in chapter 5 of the Player''s Handbook)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inheritor') AND title = 'Object' ORDER BY id DESC LIMIT 1), '4', 'An article of clothing');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inheritor') AND title = 'Object' ORDER BY id DESC LIMIT 1), '5', 'A piece of jewelry');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inheritor') AND title = 'Object' ORDER BY id DESC LIMIT 1), '6', 'An arcane book or formulary');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inheritor') AND title = 'Object' ORDER BY id DESC LIMIT 1), '7', 'A written story, song, poem, or secret');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inheritor') AND title = 'Object' ORDER BY id DESC LIMIT 1), '8', 'A tattoo or other body marking');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Inheritor'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inheritor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I judge people by their actions, not their words.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inheritor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'If someone is in trouble, I''m always ready to lend help.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inheritor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'When I set my mind to something, I follow through no matter what gets in my way.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inheritor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I have a strong sense of fair play and always try to find the most equitable solution to arguments.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inheritor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I''m confident in my own abilities and do what I can to instill confidence in others.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inheritor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Thinking is for other people. I prefer action.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inheritor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I misuse long words in an attempt to sound smarter.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inheritor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I get bored easily. When am I going to get on with my destiny?');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inheritor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Respect. People deserve to be treated with dignity and respect. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inheritor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Fairness. No one should get preferential treatment before the law, and no one is above the law. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inheritor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Freedom. Tyrants must not be allowed to oppress the people. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inheritor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Might. If I become strong, I can take what I want — what I deserve. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inheritor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Sincerity. There''s no good in pretending to be something I''m not. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inheritor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Destiny. Nothing and no one can steer me away from my higher calling. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inheritor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I have a family, but I have no idea where they are. One day, I hope to see them again.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inheritor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I worked the land, I love the land, and I will protect the land.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inheritor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'A proud noble once gave me a horrible beating, and I will take my revenge on any bully I encounter.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inheritor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'My tools are symbols of my past life, and I carry them so that I will never forget my roots.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inheritor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I protect those who cannot protect themselves.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inheritor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I wish my childhood sweetheart had come with me to pursue my destiny.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Inheritor'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inheritor') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'The tyrant who rules my land will stop at nothing to see me killed.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inheritor') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I''m convinced of the significance of my destiny, and blind to my shortcomings and the risk of failure.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inheritor') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'The people who knew me when I was young know my shameful secret, so I can never go home again.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inheritor') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I have a weakness for the vices of the city, especially hard drink.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inheritor') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'Secretly, I believe that things would be better if I were a tyrant lording over the land.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inheritor') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I have trouble trusting in my allies.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Knight of the Order', 'You belong to an order of knights who have sworn oaths to achieve a certain goal. The nature of this goal depends on the order you serve, but in your eyes it is without question a vital and honorable endeavor. Faerûn has a wide variety of knightly orders, all of which have a similar outlook concerning their actions and responsibilities.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Knight of the Order'), 'Feature: Knightly Regard', 'You receive shelter and succor from members of your knightly order and those who are sympathetic to its aims. If your order is a religious one, you can gain aid from temples and other religious communities of your deity. Knights of civic orders can get help from the community — whether a lone settlement or a great nation — that they serve, and knights of philosophical orders can find help from those they have aided in pursuit of their ideals, and those who share their ideals.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Knight of the Order'), 'Knightly Orders of Faerûn', 'Many who rightfully call themselves "knight" earn that title as part of an order in service to a deity, such as Kelemvor''s Eternal Order or Mystra''s Knights of the Mystic Fire. Other knightly orders serve a government, royal family, or are the elite military of a feudal state, such as the brutal Warlock Knights of Vaasa. Other knighthoods are secular and nongovernmental organizations of warriors who follow a particular philosophy, or consider themselves a kind of extended family, similar to an order of monks. Although there are organizations, such as the Knights of the Shield, that use the trappings of knighthood without necessarily being warriors, most folk of Faerûn who hear the word "knight" think of a mounted warrior in armor beholden to a code. Below are a few knightly organizations.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Knight of the Order'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight of the Order') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I''m always polite and respectful.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight of the Order') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I''m haunted by memories of war. I can''t get the images of violence out of my mind.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight of the Order') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I''ve lost too many friends, and I''m slow to make new ones.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight of the Order') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I''m full of inspiring and cautionary tales from my military experience relevant to almost every combat situation.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight of the Order') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I can stare down a hell hound without flinching.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight of the Order') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I enjoy being strong and like breaking things.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight of the Order') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I have a crude sense of humor.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight of the Order') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I face problems head-on. A simple, direct solution is the best path to success.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight of the Order') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Greater Good. Our lot is to lay down our lives in defense of others. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight of the Order') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Responsibility. I do what I must and obey just authority. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight of the Order') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Independence. When people follow orders blindly, they embrace a kind of tyranny. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight of the Order') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Might. In life as in war, the stronger force wins. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight of the Order') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Live and Let Live. Ideals aren''t worth killing over or going to war for. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight of the Order') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Nation. My city, nation, or people are all that matter. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight of the Order') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I would still lay down my life for the people I served with.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight of the Order') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Someone saved my life on the battlefield. To this day, I will never leave a friend behind.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight of the Order') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'My honor is my life.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight of the Order') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I''ll never forget the crushing defeat my company suffered or the enemies who dealt it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight of the Order') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Those who fight beside me are those worth dying for.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight of the Order') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I fight for those who cannot fight for themselves.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Knight of the Order'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight of the Order') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'The monstrous enemy we faced in battle still leaves me quivering with fear.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight of the Order') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I have little respect for anyone who is not a proven warrior.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight of the Order') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I made a terrible mistake in battle that cost many lives, and I would do anything to keep that mistake secret.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight of the Order') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'My hatred of my enemies is blind and unreasoning.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight of the Order') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I obey the law, even if the law causes misery.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight of the Order') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I''d rather eat my armor than admit when I''m wrong.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Mercenary Veteran', 'As a sell-sword who fought battles for coin, you''re well acquainted with risking life and limb for a chance at a share of treasure. Now, you look forward to fighting foes and reaping even greater rewards as an adventurer. Your experience makes you familiar with the ins and outs of mercenary life, and you likely have harrowing stories of events on the battlefield. You might have served with a large outfit such as the Zhentarim or the soldiers of Mintarn, or a smaller band of sell-swords, maybe even more than one. (See the "Mercenaries of the North" sidebar for a collection of possibilities.)
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Mercenary Veteran'), 'Mercenaries of the North', 'Countless mercenary companies operate up and down the Sword Coast and throughout the North. Most are smallscale operations that employ a dozen to a hundred folk who offer security services, hunt monsters and brigands, or go to war in exchange for gold. Some organizations, such as the Zhentarim, Flaming Fist, and the nation of Mintarn have hundreds or thousands of members and can provide private armies to those with enough funds. A few organizations operating in the North are described below.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Mercenary Veteran'), 'Feature: Mercenary Life', 'You know the mercenary life as only someone who has experienced it can. You are able to identify mercenary companies by their emblems, and you know a little about any such company, including who has hired them recently. You can find the taverns and festhalls where mercenaries abide in any area, as long as you speak the language. You can find mercenary work between adventures sufficient to maintain a comfortable lifestyle (see "Practicing a Profession" under "Downtime Activities" in chapter 8 of the Player''s Handbook).');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Mercenary Veteran'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mercenary Veteran') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I''m always polite and respectful.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mercenary Veteran') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I''m haunted by memories of war. I can''t get the images of violence out of my mind.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mercenary Veteran') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I''ve lost too many friends, and I''m slow to make new ones.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mercenary Veteran') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I''m full of inspiring and cautionary tales from my military experience relevant to almost every combat situation.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mercenary Veteran') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I can stare down a hell hound without flinching.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mercenary Veteran') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I enjoy being strong and like breaking things.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mercenary Veteran') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I have a crude sense of humor.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mercenary Veteran') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I face problems head-on. A simple, direct solution is the best path to success.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mercenary Veteran') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Greater Good. Our lot is to lay down our lives in defense of others. (Good)');
