INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Urban Bounty Hunter', 'Before you became an adventurer, your life was already full of conflict and excitement, because you made a living tracking down people for pay. Unlike some people who collect bounties, though, you aren''t a savage who follows quarry into or through the wilderness. You''re involved in a lucrative trade, in the place where you live, that routinely tests your skills and survival instincts. What''s more, you aren''t alone, as a bounty hunter in the wild would be: you routinely interact with both the criminal subculture and other bounty hunters, maintaining contacts in both areas to help you succeed.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Urban Bounty Hunter'), 'Feature: Ear to the Ground', 'You are in frequent contact with people in the segment of society that your chosen quarries move through. These people might be associated with the criminal underworld, the rough-and-tumble folk of the streets, or members of high society. This connection comes in the form of a contact in any city you visit, a person who provides information about the people and places of the local area.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Urban Bounty Hunter'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urban Bounty Hunter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I always have a plan for what to do when things go wrong.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urban Bounty Hunter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I am always calm, no matter what the situation. I never raise my voice or let my emotions control me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urban Bounty Hunter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'The first thing I do in a new place is note the locations of everything valuable — or where such things could be hidden.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urban Bounty Hunter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I would rather make a new friend than a new enemy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urban Bounty Hunter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I am incredibly slow to trust. Those who seem the fairest often have the most to hide.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urban Bounty Hunter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I don''t pay attention to the risks in a situation. Never tell me the odds.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urban Bounty Hunter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'The best way to get me to do something is to tell me I can''t do it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urban Bounty Hunter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I blow up at the slightest insult.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urban Bounty Hunter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Honor. I don''t steal from others in the trade. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urban Bounty Hunter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Freedom. Chains are meant to be broken, as are those who would forge them. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urban Bounty Hunter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Charity. I steal from the wealthy so that I can help people in need. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urban Bounty Hunter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Greed. I will do whatever it takes to become wealthy. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urban Bounty Hunter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'People. I''m loyal to my friends, not to any ideals, and everyone else can take a trip down the Styx for all I care. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urban Bounty Hunter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Redemption. There''s a spark of good in everyone. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urban Bounty Hunter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I''m trying to pay off an old debt I owe to a generous benefactor.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urban Bounty Hunter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'My ill-gotten gains go to support my family.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urban Bounty Hunter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Something important was taken from me, and I aim to steal it back.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urban Bounty Hunter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I will become the greatest thief that ever lived.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urban Bounty Hunter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I''m guilty of a terrible crime. I hope I can redeem myself for it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urban Bounty Hunter') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Someone I loved died because of 1 mistake I made. That will never happen again.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Urban Bounty Hunter'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urban Bounty Hunter') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'When I see something valuable, I can''t think about anything but how to steal it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urban Bounty Hunter') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'When faced with a choice between money and my friends, I usually choose the money.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urban Bounty Hunter') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'If there''s a plan, I''ll forget it. If I don''t forget it, I''ll ignore it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urban Bounty Hunter') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I have a "tell" that reveals when I''m lying.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urban Bounty Hunter') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I turn tail and run when things look bad.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Urban Bounty Hunter') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'An innocent person is in prison for a crime that I committed. I''m okay with that.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Uthgardt Tribe Member', 'Though you might have only recently arrived in civilized lands, you are no stranger to the values of cooperation and group effort when striving for supremacy. You learned these principles, and much more, as a member of an Uthgardt tribe.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Uthgardt Tribe Member'), 'Barbarian Tribes of Faerûn', 'Though this section details the Uthgardt specifically, either it or the outlander background from the Player''s Handbook can be used for a character whose origin lies with one of the other barbarian tribes in Faerûn.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Uthgardt Tribe Member'), 'Feature: Uthgardt Heritage', 'You have an excellent knowledge of not only your tribe''s territory, but also the terrain and natural resources of the rest of the North. You are familiar enough with any wilderness area that you can find twice as much food and water as you normally would when you forage there.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Uthgardt Tribe Member'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Uthgardt Tribe Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I''m driven by a wanderlust that led me away from home.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Uthgardt Tribe Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I watch over my friends as if they were a litter of newborn pups.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Uthgardt Tribe Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I once ran twenty-five miles without stopping to warn to my clan of an approaching orc horde. I''d do it again if I had to.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Uthgardt Tribe Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I have a lesson for every situation, drawn from observing nature.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Uthgardt Tribe Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I place no stock in wealthy or well-mannered folk. Money and manners won''t save you from a hungry owlbear.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Uthgardt Tribe Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I''m always picking things up, absently fiddling with them, and sometimes accidentally breaking them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Uthgardt Tribe Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I feel far more comfortable around animals than people.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Uthgardt Tribe Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I was, in fact, raised by wolves.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Uthgardt Tribe Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Change. Life is like the seasons, in constant change, and we must change with it. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Uthgardt Tribe Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Greater Good. It is each person''s responsibility to make the most happiness for the whole tribe. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Uthgardt Tribe Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Honor. If I dishonor myself, I dishonor my whole clan. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Uthgardt Tribe Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Might. The strongest are meant to rule. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Uthgardt Tribe Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Nature. The natural world is more important than all the constructs of civilization. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Uthgardt Tribe Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Glory. I must earn glory in battle, for myself and my clan. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Uthgardt Tribe Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'My family, clan, or tribe is the most important thing in my life, even when they are far from me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Uthgardt Tribe Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'An injury to the unspoiled wilderness of my home is an injury to me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Uthgardt Tribe Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I will bring terrible wrath down on the evildoers who destroyed my homeland.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Uthgardt Tribe Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I am the last of my tribe, and it is up to me to ensure their names enter legend.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Uthgardt Tribe Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I suffer awful visions of a coming disaster and will do anything to prevent it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Uthgardt Tribe Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'It is my duty to provide children to sustain my tribe.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Uthgardt Tribe Member'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Uthgardt Tribe Member') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I am too enamored of ale, wine, and other intoxicants.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Uthgardt Tribe Member') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'There''s no room for caution in a life lived to the fullest.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Uthgardt Tribe Member') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I remember every insult I''ve received and nurse a silent resentment toward anyone who''s ever wronged me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Uthgardt Tribe Member') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I am slow to trust members of other races, tribes, and societies.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Uthgardt Tribe Member') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'Violence is my answer to almost any challenge.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Uthgardt Tribe Member') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'Don''t expect me to save those who can''t save themselves. It is nature''s way that the strong thrive and the weak perish.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Investigator', 'Rarer than watch or patrol members are a community''s investigators, who are responsible for solving crimes after the fact. Though such folk are seldom found in rural areas, nearly every settlement of decent size has at least one or two watch members who have the skill to investigate crime scenes and track down criminals.', 'Insight, Investigation', '', 'Two of your choice', 'A uniform in the style of your unit and indicative of your rank, a horn with which to summon help, a set of manacles, and a pouch containing 10 gp', (SELECT id FROM core_book_sources WHERE raw_source = 'Sword Coast Adventurer''s Guide p. 145'));
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Investigator'), 'Feature: Watcher''s Eye', 'Your experience in enforcing the law, and dealing with lawbreakers, gives you a feel for local laws and criminals. You can easily find the local outpost of the watch or a similar organization, and just as easily pick out the dens of criminal activity in a community, although you''re more likely to be welcome in the former locations rather than the latter.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Investigator'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I''m always polite and respectful.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I''m haunted by memories of war. I can''t get the images of violence out of my mind.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I''ve lost too many friends, and I''m slow to make new ones.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I''m full of inspiring and cautionary tales from my military experience relevant to almost every combat situation.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I can stare down a hell hound without flinching.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I enjoy being strong and like breaking things.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I have a crude sense of humor.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I face problems head-on. A simple, direct solution is the best path to success.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Greater Good. Our lot is to lay down our lives in defense of others. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Responsibility. I do what I must and obey just authority. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Independence. When people follow orders blindly, they embrace a kind of tyranny. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Might. In life as in war, the stronger force wins. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Live and Let Live. Ideals aren''t worth killing over or going to war for. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Nation. My city, nation, or people are all that matter. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I would still lay down my life for the people I served with.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Someone saved my life on the battlefield. To this day, I will never leave a friend behind.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'My honor is my life.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I''ll never forget the crushing defeat my company suffered or the enemies who dealt it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Those who fight beside me are those worth dying for.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I fight for those who cannot fight for themselves.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Investigator'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'The monstrous enemy we faced in battle still leaves me quivering with fear.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I have little respect for anyone who is not a proven warrior.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I made a terrible mistake in battle that cost many lives, and I would do anything to keep that mistake secret.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'My hatred of my enemies is blind and unreasoning.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I obey the law, even if the law causes misery.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I''d rather eat my armor than admit when I''m wrong.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Waterdhavian Noble', 'You are a scion of one of the great noble families of Waterdeep. Human families who jealously guard their privilege and place in the City of Splendors, Waterdhavian nobles have a reputation across Faerûn for being eccentric, spoiled, venal, and, above all else, rich.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Waterdhavian Noble'), 'Feature: Kept in Style', 'While you are in Waterdeep or elsewhere in the North, your house sees to your everyday needs. Your name and signet are sufficient to cover most of your expenses; the inns, taverns, and festhalls you frequent are glad to record your debt and send an accounting to your family''s estate in Waterdeep to settle what you owe.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Waterdhavian Noble'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Waterdhavian Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'My eloquent flattery makes everyone I talk to feel like the most wonderful and important person in the world.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Waterdhavian Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'The common folk love me for my kindness and generosity.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Waterdhavian Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'No one could doubt by looking at my regal bearing that I am a cut above the unwashed masses.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Waterdhavian Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I take great pains to always look my best and follow the latest fashions.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Waterdhavian Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I don''t like to get my hands dirty, and I won''t be caught dead in unsuitable accommodations.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Waterdhavian Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Despite my noble birth, I do not place myself above other folk. We all have the same blood.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Waterdhavian Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'My favor, once lost, is lost forever.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Waterdhavian Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'If you do me an injury, I will crush you, ruin your name, and salt your fields.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Waterdhavian Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Respect. Respect is due to me because of my position, but all people regardless of station deserve to be treated with dignity. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Waterdhavian Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Responsibility. It is my duty to respect the authority of those above me, just as those below me must respect mine. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Waterdhavian Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Independence. I must prove that I can handle myself without the coddling of my family. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Waterdhavian Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Power. If I can attain more power, no one will tell me what to do. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Waterdhavian Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Family. Blood runs thicker than water. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Waterdhavian Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Noble Obligation. It is my duty to protect and care for the people beneath me. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Waterdhavian Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I will face any challenge to win the approval of my family.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Waterdhavian Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'My house''s alliance with another noble family must be sustained at all costs.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Waterdhavian Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Nothing is more important than the other members of my family.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Waterdhavian Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I am in love with the heir of a family that my family despises.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Waterdhavian Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'My loyalty to my sovereign is unwavering.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Waterdhavian Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'The common folk must see me as a hero of the people.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Waterdhavian Noble'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Waterdhavian Noble') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I secretly believe that everyone is beneath me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Waterdhavian Noble') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I hide a truly scandalous secret that could ruin my family forever.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Waterdhavian Noble') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I too often hear veiled insults and threats in every word addressed to me, and I''m quick to anger.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Waterdhavian Noble') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I have an insatiable desire for carnal pleasures.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Waterdhavian Noble') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'In fact, the world does revolve around me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Waterdhavian Noble') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'By my words and actions, I often bring shame to my family.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Black Fist Double Agent', 'You are an informant for the Tears of Virulence who now lord over Phlan, but are also a double agent for original town guard of Phlan, the Black Fists. For the Tears you''ve been tasked with ferreting out the secrets of Phlan''s criminal underworld, insurgency, and the common peoples of Phlan. In exchange for reporting on the activities of dissenters, criminals, and other rebel elements, the Tears of Virulence leave you alone to conduct your affairs in peace.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Black Fist Double Agent'), 'Feature: Double Agent', 'You have a reliable and trusty contact within the Tears of Virulence garrison in Phlan to whom you pass information and secrets. In exchange, you can get away with minor criminal offenses within the town of Phlan. In addition, your Black Fists contacts can help you secure an audience with the Lord Regent, the Lord Sage, members of the Black Fists, or deposed nobles and authority figures who are sympathetic to the Phlan refugees and insurgents. [Note: This feature is a variant of the Noble feature.]');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Black Fist Double Agent'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Black Fist Double Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'People are only as trustworthy as you are valuable to them. Always strive to be the most valuable person around.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Black Fist Double Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'My eloquence and sophistication are tools I use to avoid arousing suspicion myself.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Black Fist Double Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I am a thrill-seeker, excited by covert and dangerous missions.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Black Fist Double Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I live by my wits and always check every lock twice, just to be certain.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Black Fist Double Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I never admit to my mistakes lest they be used against me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Black Fist Double Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I take every effort to be unnoticeable and blend into the crowd. Passersby rarely give me a second look.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Black Fist Double Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I am prepared for any eventuality; including the day my usefulness as a spy comes to an end.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Black Fist Double Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I always make certain to know my enemy before acting, lest I bite off more than I can chew.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Black Fist Double Agent'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Black Fist Double Agent') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Suspicious. In my experience, everybody has something to hide, and what they hide can usually hurt me. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Black Fist Double Agent') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Secretive. I trade in secrets, and am not about to let any of mine slip. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Black Fist Double Agent') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Hedonist. Life is short. I live my life to the fullest, as I know any day could be my last. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Black Fist Double Agent') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Selfless. I use my position to help the downtrodden avoid persecution from the authorities. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Black Fist Double Agent') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Patriotic. I am a loyal supporter of Phlan and its leaders, and see my role as a solemn duty and necessary evil to prevent anarchy. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Black Fist Double Agent') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Manipulative. I use my knowledge to blackmail and manipulate others to my own benefit. (Evil)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Black Fist Double Agent'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Black Fist Double Agent') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I was framed for a crime I did not commit, and seek to bring the true culprit to justice.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Black Fist Double Agent') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I am a part of an underground network that smuggles innocent civilians out of the city prior to being raided by the authorities.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Black Fist Double Agent') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I miss the glory days of Phlan, before the coming of the dragon.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Black Fist Double Agent') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I seek to prove myself worthy of joining the Black Fist as a member of their order.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Black Fist Double Agent') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'My sister was killed by a Tear of Virulence, and now I feed them false information whenever possible.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Black Fist Double Agent') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'My family was wrongly imprisoned, and I act as an informant in order to secure their release.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Black Fist Double Agent'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Black Fist Double Agent') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I think too highly of myself, and have an exaggerated sense of self-importance.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Black Fist Double Agent') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I have difficulty trusting strangers. I see spies and informants everywhere.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Black Fist Double Agent') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'Years of getting away with minor crimes has left me believing that I am above the law, and have diplomatic immunity above my station.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Black Fist Double Agent') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'Years of seeing innocent people suffer have left me despondent and pessimistic for the future.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Black Fist Double Agent') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'My desire for vengeance often gets me into trouble.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Black Fist Double Agent') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I am spendthrift, and share my wealth with the patrons of my favorite tavern.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Dragon Casualty', 'When the Maimed Virulence descended upon Phlan, you were one of the unfortunate casualties of war. Captured during the initial assault, you have spent the last year of your life as a plaything of a capricious and malevolent overlord.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Dragon Casualty'), 'Origin', 'Origin', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '1', 'Dockworker / Fisherman | Vehicles (water)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '2', 'Tradesperson / Merchant | Artisan''s Tools');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '3', 'Black Fist Soldier | Gaming set or Vehicles (land)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '4', 'Adventurer / Visitor | Vehicles (land)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '5', 'Entertainer | Musical Instrument');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '6', 'Scholar / Healer | Alchemist''s Supplies or Herbalism Kit');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '7', 'Criminal | Thieves'' Tools, Forgery Kit, or Disguise Kit');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '8', 'Unskilled Laborer | Gaming Set');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Dragon Casualty'), 'Feature: Dragonscarred', 'Over a period of several months you were subject to magical and mundane torture at the claws of Vorgansharax and his minions. These experiments have left you horribly disfigured but mark you as a survivor of the Maimed Virulence.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Dragon Casualty'), 'Disfigurement (Optional)', 'Disfigurement', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Disfigurement' ORDER BY id DESC LIMIT 1), '1', 'Small non-functional wing(s) or alternately - misshapen, wing-like membranes along one or both arms');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Disfigurement' ORDER BY id DESC LIMIT 1), '2', 'Elongated, claw-like hand(s) and/or feet');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Disfigurement' ORDER BY id DESC LIMIT 1), '3', 'Painful green scales randomly embedded in skin');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Disfigurement' ORDER BY id DESC LIMIT 1), '4', 'Bulbous, reptilian eye(s)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Disfigurement' ORDER BY id DESC LIMIT 1), '5', 'Enlarged dorsal spines');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Disfigurement' ORDER BY id DESC LIMIT 1), '6', 'Hair replaced with small irregular spines');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Dragon Casualty'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I am driven to escape my past, and rarely stay in one place long.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I know secrets of the Maimed Virulence, but fear the harm that may befall me should others learn them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Speaking of my ordeal helps sooth the still open wounds in my soul.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'My former life is meaningless, and was ripped to shreds by the claws of Vorgansharax. All that matters now is what I do with the future.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I have faced the worst a dragon can deliver and survived. I am fearless, and my resolve unshakable.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I am haunted my tortured past, and wake at night screaming at half-remembered horrors.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I sleep with my back to a wall or tree, and a weapon within arm''s reach.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I am slow to trust, but incredibly loyal to those who have earned it.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Dragon Casualty'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Survivor. No matter the cost, I will take any action necessary to survive. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Independence. When in trouble, the only person I can rely on is myself. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Compassionate. I have suffered long at the hands of a Dragon, and take pity and compassion on the suffering of others. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Secretive. I am withdrawn, and hide my monstrous appearance for fear of drawing unwanted attention. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Justice. I have been wronged, and will not allow the same fate to befall others. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Sycophant. During my ordeal, I became a willing servant of the Maimed Virulence, and spy on his behalf. (Evil)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Dragon Casualty'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I have sworn vengeance on the Maimed Virulence and those that follow him.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I long to reunite with friends and family who may dwell among the Phlan Refugees, and protect them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'While a prisoner of the Maimed Virulence, I overheard rumors of an item or treasure the Dragon seeks. I will have that treasure for myself!');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I seek to reclaim and rebuild my former life to the best of my ability.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I have been reborn as a child of Vorgansharax. I will claim my birthright as his chosen heir and successor.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I attribute my survival to the work of the divine, and seek to prove myself worthy of the honor.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Dragon Casualty'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I have been touched with dragon-greed, and have a lust for wealth which can never be satisfied.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I secretly believe others are plotting to harm me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I no longer enjoy the simple pleasures in life. Food is but ashes and bile in my throat.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'Anyone who refuses to celebrate my celebrity does not deserve my company.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I am paranoid and overly suspicious of others. Anyone may be an agent of the Maimed Virulence.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Casualty') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'Once I make up my mind, I follow my chosen course of action regardless of the consequences.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Iron Route Bandit', 'The Iron Route, once the primary trade route between Phlan and Zhentil Keep, used to be a site of extensive banditry until the Phlan''s recent occupation. Your time as an erstwhile bandit has given you plenty of experience in the saddle and a knack for acquiring and appraising other people''s mounts, pets, and vehicles among other things. This particular set of skills has become very lucrative for you by working for the underground as a horse thief for a local guild of thieves and other shadowy organizations.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Iron Route Bandit'), 'Feature: Black-Market Breeder', 'You know how to find people who are always looking for stolen animals & vehicles, whether to provide for animal pit fights, or to supply some desperate rogues the means to get away faster on mounts during an illegal job. This contact not only provides you with information of what such animals & vehicles are in high demand in the area, but also offer to give you favors and information (DM choice) if you bring such animals & vehicles to them. [Note: This is a variant of the Criminal Contact feature.]');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Iron Route Bandit'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Iron Route Bandit') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'If people leave their gear unsecured, they must not want it very much.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Iron Route Bandit') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I feel more comfortable sleeping under the open sky.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Iron Route Bandit') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I always pre-plan my escape should things go bad; I always like to have an exit strategy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Iron Route Bandit') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I tend to give animal owners breeding and care advice whether or not they want it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Iron Route Bandit') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I lost a pet as a child and sadly reflect on it to this day.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Iron Route Bandit') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I always form a powerful, emotional');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Iron Route Bandit'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Iron Route Bandit') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '7', 'I recoil at the thought of killing someone else''s pet or mount.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Iron Route Bandit') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '8', 'I prefer to hang to the back of a scuffle or discussion. Better to have my enemies in front of me.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Iron Route Bandit'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Iron Route Bandit') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Loyalty. Never bite the hand that feeds. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Iron Route Bandit') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Unpredictability. Keep your enemy guessing and off-balance like a confused deer. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Iron Route Bandit') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Power. I strive to become leader of the pack at all costs. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Iron Route Bandit') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Freedom. I bow to no one I don''t respect. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Iron Route Bandit') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Resourcefulness. Our wits are our most valuable resource in troubled times. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Iron Route Bandit') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Unity. Lone wolves fail where the pack succeeds. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Iron Route Bandit') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'I cannot leave a harmed animal behind; I must save it or put it out of its misery.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Iron Route Bandit') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'I leave behind my own personal calling cards when I do a job.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Iron Route Bandit') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'I do not trust people who do not have a pet, mount, or furry companion.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Iron Route Bandit') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'The pelt I wear on my back was from an animal that died saving my life, I will always cherish it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Iron Route Bandit') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'If my pet does not like you, I do not like you!');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Iron Route Bandit') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Once you''ve ridden with me and fought by my side, I''ll be there for you odds be damned.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Iron Route Bandit'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Iron Route Bandit') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I talk to animals; I believe they understand me, even if they do not.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Iron Route Bandit') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I growl at and bite anyone who gets too close to my food while I am eating.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Iron Route Bandit') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I strongly dislike enclosed spaces and require intoxication or firm encouragement to enter them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Iron Route Bandit') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I robbed the wrong caravan once. The owner is a powerful merchant who holds a grudge.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Iron Route Bandit') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I''m an inveterate gambler.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Iron Route Bandit') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I judge people based on how well they stand their ground in a fight. I got not time for cowards...');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Phlan Insurgent', 'The taking of Phlan by Vorgansharax is a clear memory in your mind. You were going about your everyday business when the green dragon''s forces spilled out of the sewers and assailed your home. Many of Phlan''s citizens, young and old alike, were captured, killed, or offered as tribute to the Maimed Virulence. You, yourself were one of those captured. But, either with the help of adventurers or through your own wits and sheer determination, you escaped.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent'), 'Origin', 'Origin', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '1', 'Fisher | Stojanow river worker');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '2', 'Hunter | Twilight Marsh worker');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '3', 'Craftsperson | Mantor''s Library scribe');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '4', 'Priest/Priestess | Clergy of Ilmater');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '5', 'Cook | Laughing Goblin server');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '6', 'City Watch | Black Fist guard');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '7', 'Servant | House Sokol retainer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '8', 'Unskilled laborer | Bay of Phlan dockworker');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent'), 'Feature: Guerilla', 'You''ve come to know the surrounding forests, streams, caves, and other natural features in which you can take refuge - or set up ambushes. You can quickly survey your environment for advantageous features. Additionally, you can scavenge around your natural surroundings to cobble together simple supplies (such as improvised torches, rope, patches of fabric, etc.) that are consumed after use. [Note: This feature is a variant of the Outlander feature.]');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'My patience knows no bounds, so long as my goal is in sight.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'In life and in struggle, the ends justify my actions.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'If you aren''t helping me, you''d best at least stay out of my way.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I long for the life that was taken away from me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Friends and family perished, tragically, before my eyes. I hope never to undergo that again.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Making the right choices in life are important to me. The choices I make might save not just my life, but the lives of others as well.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I can never allow my foes to get the drop on me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'Time is a precious resource that I must spend wisely.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Leadership. The oppressed need someone to inspire them to courageous acts. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Unpredictability. Keeping the enemy guessing and off-balance is my tactical strength. (Chaos)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Determination. Threats to my home must be eliminated at all costs. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Freedom. Those who are enslaved and unjustly imprisoned deserve my aid. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Resourcefulness. Our wits are our most valuable resource in troubled times. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Unity. Working together, we can overcome all obstacles, even the most seemingly insurmountable ones. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I''ll never let my fellow insurgents down. They are my only remaining friends.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I was separated from a loved one during my escape from town. I will find them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'One of the Tears of the Virulence was a trusted friend, until the day they betrayed the city. They will pay harshly for their transgressions.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'An item I hold close is my last remaining connection to the family I lost during the fall.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'The dragon who took my past life away from me will feel the full extent of my vengeance.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'The knowledge in Mantor''s Library is an irreplaceable treasure that must be protected.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I have no respect for those who flee. I harbor a deep grudge against the citizens who abandoned Phlan.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'Ale is the only way I can escape the desperation of my circumstances.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'It doesn''t take much to get me into a fight.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'Being an insurgent means doing things that aren''t always ethical. I''m still learning to live with that.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'My desire to liberate Phlan oftentimes clouds my judgment, despite my best efforts.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Insurgent') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I relentlessly despise the Maimed Virulence and his allies. I''d abandon other goals in order to strike out at them.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Stojanow Prisoner', '"We need to leave, now!"
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Stojanow Prisoner'), 'Feature: Ex-Convict', 'The knowledge gained during your incarceration lets you gain insight into local guards and jailors. You know which will accept bribes, or look the other way for you. You can also seek shelter for yourself from authorities with other criminals in the area. [Note: This feature is a variant of the Courtier feature.]');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Stojanow Prisoner'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Stojanow Prisoner') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I am a bully; others will suffer as I have.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Stojanow Prisoner') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I always say yes even when I mean no; it''s just easier.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Stojanow Prisoner') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I aim to misbehave.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Stojanow Prisoner') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I go out of my way to frustrate or anger those in power.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Stojanow Prisoner') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I strive to obey the law. I will never again make the mistake of going against authority.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Stojanow Prisoner') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I always plan everything out. The one time I let others plan things it did not end well for me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Stojanow Prisoner') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I take blame to protect others from pain.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Stojanow Prisoner') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I hoard information, you never know what may come in handy.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Stojanow Prisoner'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Stojanow Prisoner') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Loss. I freely give those who offend me what was so brutally denied me, death. (Chaos)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Stojanow Prisoner') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Dedication. I never betray those who trust me. (Law)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Stojanow Prisoner') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Vengeance. I use any means to get information I need; I have been well taught. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Stojanow Prisoner') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Redemption. Everyone deserves a second chance. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Stojanow Prisoner') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Resilience. I can survive any challenge (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Stojanow Prisoner') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Leadership. The best teams are made up of those that society has discarded.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Stojanow Prisoner'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Stojanow Prisoner') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I take up arms to help establish a free Phlan.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Stojanow Prisoner') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'The horrors of my time in Stojanow haunt my dreams, only after a day of hard work can I find sleep.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Stojanow Prisoner') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I am indebted to those who freed me from prison, I will repay this debt.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Stojanow Prisoner') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'My torturer survived the attack that set me free, I will find him/her.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Stojanow Prisoner') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I will not rest while others suffer fates similar to mine.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Stojanow Prisoner') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I am searching for a way to heal the scars of Stojanow, both physical and emotional.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Stojanow Prisoner'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Stojanow Prisoner') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'During stressful times, I find myself crying for no reason.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Stojanow Prisoner') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'My nerve endings are shot from the interrogations; I am numb to all but the harshest touch.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Stojanow Prisoner') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I am incapable of standing up for myself.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Stojanow Prisoner') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I folded under the torture, and gave information that I promised would be kept secret. My life would be in jeopardy if others found out.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Stojanow Prisoner') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'Survival is worth more than friendship.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Stojanow Prisoner') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'The ghosts from my past hinder my actions.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Ticklebelly Nomad', 'You were born into a nomadic tribe that called the Ticklebelly Hills home. You migrated from location to location, living off the land with your tribe. The tribe would seasonally travel south into the Grass Sea and the Giant''s Cairn, north into the Dragonspine Mountains, and even occasionally east across the Stojanow River to the borders of the Quivering forest.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Ticklebelly Nomad'), 'Feature: At Home in the Wild', 'The wilderness is your home and you are comfortable dwelling in it. You can find a place to hide, rest, or recuperate when out in the wild. This place of rest is secure enough to conceal you from most natural threats. Threats that are supernatural, magical, or are actively seeking you out might do so with difficulty depending on the nature of the threat (as determined by the DM). However, this feature doesn''t shield or conceal you from scrying, mental probing, nor from threats that don''t necessarily require the five senses to find you. [Note: This feature is a variant of the Folk Hero feature.]');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Ticklebelly Nomad'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ticklebelly Nomad') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I eagerly inject myself into the unknown.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ticklebelly Nomad') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Villages, towns, and cities do not suit me. I''d rather be out in the wilderness any day.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ticklebelly Nomad') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I accomplish my tasks efficiently, using as few resources as possible.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ticklebelly Nomad') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'It''s difficult for me to remain in one place for long.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ticklebelly Nomad') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I loudly brag about my tribe every chance I get.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ticklebelly Nomad') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Having walked among giants, I am fearless in the face of bigger creatures.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ticklebelly Nomad') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I am quiet and reserved, but observant. Nothing escapes my attention.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ticklebelly Nomad') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'My word is my');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Ticklebelly Nomad'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ticklebelly Nomad') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Kinship. Family is most important in life. Though I may be far from my own, the bonds of family must be protected in others'' lives as well. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ticklebelly Nomad') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Preservation. Nature must not be despoiled by encroaching civilization. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ticklebelly Nomad') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Wanderlust. One must expand their horizons by seeing the world and exploring. (Chaos)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ticklebelly Nomad') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Isolation. My tribe and its ways must be protected and shielded from outside influence. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ticklebelly Nomad') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Protection. Threats to the land and to the people must be dealt with at any and all costs. (Law)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ticklebelly Nomad') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Belonging. All creatures have a place in the world, so I strive to help others find theirs. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ticklebelly Nomad') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'I ache to return to my tribe and the family I left, but cannot until my obligations are fulfilled.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ticklebelly Nomad') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'The dragon cultists that invaded my homeland stole away one of my tribe''s people. I will not know rest until I''ve found them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ticklebelly Nomad') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'The dragon''s presence in the hills destroyed valuable territory and resulted in deaths within my tribe. The creature must pay for what it has done.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ticklebelly Nomad') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'I carry a trinket that spiritually and emotionally ties me to my people and my home.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ticklebelly Nomad') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'I discovered a strange relic in the hills during my tribe''s wanderings. I must discover what it is.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ticklebelly Nomad') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'One of the stone giant clans from the Giant''s Cairn has graced me with a mark of kinship.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Ticklebelly Nomad'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ticklebelly Nomad') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I throw myself and my friends into situations rarely ever thinking about consequences.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ticklebelly Nomad') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'Unfamiliar people and surroundings put me on edge.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ticklebelly Nomad') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I have absolutely no patience for slowpokes and those who prove indecisive.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ticklebelly Nomad') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'My desire to experience new things causes me to make unsafe choices.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ticklebelly Nomad') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I am overly protective of nature, sometimes to the detriment of my companions and myself.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ticklebelly Nomad') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'My lack of worldliness often proves my undoing in social, commercial, and hostile situations.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Caravan Specialist', 'You are used to life on the road. You pride yourself at having traveled every major trade way in the Moonsea region, including the best backroads and shortcuts. When traveling these roads, you know where the best inns, campsites, and water sources are located, as well as potential locations of danger such as ambush. Having worked the roads as long as you have, you have made many acquaintances and find it easy to pick up information and rumors floating from town to town. You are skilled with beasts of burden and handling and repairing wagons of all kinds.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Caravan Specialist'), 'Feature: Wagonmaster', 'You are used to being in charge of the operation and your reputation for reliability has you on a short list when the job is critical. Experience has taught you to rely on your gut. Others recognize this and look to you for direction when a situation gets serious. You are able to identify the most defensible locations for camping. If you are part of a caravan outfit, you are able to attract two additional workers that are loyal to you based on your reputation. You have an excellent memory for maps and geography and can always determine your cardinal directions while traveling. [Note: This feature is a variant of the Outlander feature.]');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Caravan Specialist'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Caravan Specialist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Any group is only as strong as its weakest link. Everyone has to pull their own weight.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Caravan Specialist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'There''s always someone out there trying to take what I''ve got. Always be vigilant.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Caravan Specialist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Anything can be learned if you have the right teacher. Most folks just need a chance.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Caravan Specialist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Early to bed and early to rise; this much at least is under my control.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Caravan Specialist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'You can listen to me or don''t and wish you had. Everyone ends up on one side of that fence.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Caravan Specialist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Eventually my hard work will be rewarded. Maybe that time has finally come.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Caravan Specialist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'A strong ox or horse is more reliable than most people I''ve met.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Caravan Specialist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I never had time for books, but wish I had. I admire folks who have taken the time to learn.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Caravan Specialist'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Caravan Specialist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Service. Using my talents to help others is the best way of helping myself. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Caravan Specialist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Selfish. What people don''t know WILL hurt them, but why is that my problem? (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Caravan Specialist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Wanderer. I go where the road takes me. Sometimes that''s a good thing... (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Caravan Specialist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Fittest. On the open road, the law of nature wins. Victims are the unprepared. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Caravan Specialist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Focused. I simply have a job to do, and I''m going to do it. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Caravan Specialist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Motivated. There''s a reason I''m good at what I do, I pay attention to the details. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Caravan Specialist'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Caravan Specialist') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'My brother has a farm In Elmwood and I''ve helped him and his neighbors move their goods to Mulmaster and other surrounding towns. Those are good people.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Caravan Specialist') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'A caravan I lead was attacked by bandits and many innocents died. I swear that I will avenge them by killing any bandits I encounter.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Caravan Specialist') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'The Soldiery are mostly good guys who understand the importance of protecting the roads. The City Watch is who you have to look out for. If they are inspecting your goods, get ready to pay a fine.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Caravan Specialist') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'The new commander of Southroad Tower, Capt. Holke, understands the importance of safe roads. He''s hired me for several jobs and I''m grateful.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Caravan Specialist') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'There''s always a road I haven''t traveled before. I''m always looking for new places to explore.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Caravan Specialist') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'Wealth and power mean little without the freedom to go where and when you want.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Caravan Specialist'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Caravan Specialist') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I have trouble trusting people I''ve just met.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Caravan Specialist') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I enjoy the open road. Underground and tight spaces make me very nervous.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Caravan Specialist') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I expect others to heed my orders and have little respect or sympathy if they don''t.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Caravan Specialist') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I am very prideful and have trouble admitting when I''m wrong.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Caravan Specialist') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'Once I decide on a course of action, I do not waiver.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Caravan Specialist') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I like to explore, and my curiosity will sometimes get me into trouble.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Earthspur Miner', 'You are a down-on-your-luck miner from the Earthspur Mountains who is no stranger to hardship. You have spent a great deal of time living among the dwarves, goliaths, and denizens of the Underdark that also work mines in the area. At this point, you''re just as comfortable working underground as above. You know how to read a seam, dicker for supplies with the deep gnomes, party with dwarves, and find your way back to the surface afterwards. Unfortunately, you haven''t struck it rich... yet. Although you''ve come to Mulmaster looking for work, the tall peaks and deep mines of the Earthspurs still call to you.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Earthspur Miner'), 'Feature: Deep Miner', 'You are used to navigating the deep places of the earth. You never get lost in caves or mines if you have either seen an accurate map of them or have been through them before. Furthermore, you are able to scrounge fresh water and food for yourself and as many as five other people each day if you are in a mine or natural caves. [Note: This feature is a variant of the Outlander feature.]');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Earthspur Miner'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Earthspur Miner') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Nothing bothers me for long.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Earthspur Miner') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I hate the horrors of the Underdark with a passion. They took my friends and family and almost got me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Earthspur Miner') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Anything worth doing takes time and patience. I have learned to plan and wait for the things I want and to be patient to achieve my goals.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Earthspur Miner') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I can party with everyone. Whether with dwarves, or goliaths, or deep gnomes, I can find a way to have a good time.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Earthspur Miner') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I''d rather be mining. This is okay; mining is better.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Earthspur Miner') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I think that I will stumble upon great riches if I just keep looking.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Earthspur Miner') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'People who don''t work with their hands and who live in houses are soft and weak.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Earthspur Miner') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I wish I were more educated. I look up to people who are.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Earthspur Miner'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Earthspur Miner') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Generosity. The riches of the earth are to be shared by all. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Earthspur Miner') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Greed. Gems and precious metals, I want them all for myself. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Earthspur Miner') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Mooch. Property, schmoperty. If I need it, I take and use it. If I don''t, I leave it for someone else. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Earthspur Miner') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Boundaries. Everything and everyone has its prescribed place; I respect that and expect others to do the same. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Earthspur Miner') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Let it Be. I don''t meddle in the affairs of others if I can avoid it. They''re none of my business. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Earthspur Miner') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Materialist. I want riches to improve my life. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Earthspur Miner'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Earthspur Miner') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'The people of the Earthspur mines are my family. I will do anything to protect them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Earthspur Miner') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'A deep gnome saved my life when I was injured and alone. I owe his people a great debt.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Earthspur Miner') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I must behold and preserve the natural beauty of places below the earth.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Earthspur Miner') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'Gems hold a special fascination for me, more than gold, land, magic, or power.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Earthspur Miner') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I want to explore new depths and scale new heights.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Earthspur Miner') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'Someday I''m going to find the mother lode, then I''ll spend the rest of my life in luxury.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Earthspur Miner'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Earthspur Miner') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I''m uncomfortable spending time under the open sky. I''d rather be indoors or underground.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Earthspur Miner') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I''m not used to being around other people much and sometimes get grouchy about it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Earthspur Miner') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'Good tools are more reliable than people. In a cave in, I would save a sturdy pick before a stranger.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Earthspur Miner') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I jealously guard my secrets, because I think others will take advantage of me if they learn what I know.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Earthspur Miner') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I am obsessed with getting rich. I always have a scheme brewing for making it big.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Earthspur Miner') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I''m afraid of the dark.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Harborfolk', 'You are one of the hundreds of small-time fishermen and women who haul the bounty of Mulmaster''s freshwater harbor to the city''s markets each morning. You have spent countless days rowing in the waters in and around Mulmaster and know them and the other fisherfolk, dockworkers, and port inhabitants better than anyone. Though you have left that life behind, you still visit once in a while.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Harborfolk'), 'Feature: Harborfolk', 'You grew up on the docks and waters of Mulmaster Harbor. The harborfolk remember you and still treat you as one of them. They welcome you and your companions. While they might charge you for it, they''ll always offer what food and shelter they have; they''ll even hide you if the City Watch is after you (but not if the Hawks are). [Note: This feature is a variant of the Folk Hero feature.]');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Harborfolk'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Harborfolk') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I am curious. I want to know why things are the way they are and why people do the things that they do.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Harborfolk') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I can''t sing, but that never stops me from doing it, loudly. Everyone loves a good sea chanty!');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Harborfolk') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I think the High Blade is doing a terrific job, don''t you?');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Harborfolk') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I''m very excited that the House Built on Gold is being restored. I am a zealous worshipper of Waukeen.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Harborfolk') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I am quite superstitious. I see portents in everyday occurrences.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Harborfolk') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I resent the rich and enjoy thwarting their plans and spoiling their fun in small ways.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Harborfolk') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I have a sea story to fit every occasion.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Harborfolk') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I''m a fisher, but I secretly detest eating fish. I will do anything to avoid it.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Harborfolk'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Harborfolk') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Calm. For all things, there is a tide. I set sail when it is right, and mend my nets when it is not. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Harborfolk') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Windblown. I go where the winds blow. No man or woman tells me where or when to sail. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Harborfolk') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Aspiring. I will gain the favor of a Zor or Zora patron, maybe even one of the Blades! (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Harborfolk') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Salty. I want people to look to me as an expert on plying Mulmaster Harbor. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Harborfolk') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Selfless. We are all children of the sea. I help everyone in peril afloat and ashore. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Harborfolk') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Let them Drown. I refuse to risk my hide to help others. They wouldn''t help me if roles were reversed. (Evil)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Harborfolk'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Harborfolk') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I once lost everything but my rowboat. I''ll do anything to protect it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Harborfolk') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'My brother was in the Soldiery, but he was killed. I really look up to the men and women who serve.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Harborfolk') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'The Cloaks killed my friend for spellcasting. I''ll get them back somehow, someday.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Harborfolk') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'The High House of Hurting helped me when I was hurt and asked nothing in return. I owe them my life.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Harborfolk') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I was robbed in the Zhent ghetto once. It will not happen again.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Harborfolk') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I would do anything to protect the other harborfolk. They are my family.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Harborfolk'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Harborfolk') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I drink too much, which causes me to miss the tide.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Harborfolk') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I killed a drunk member of the City Watch in a brawl. I am terrified that they might find out.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Harborfolk') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I oversell myself and make promises I can''t keep when I want to impress someone.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Harborfolk') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'Book learning is a waste of time. I have no patience for people who don''t speak from experience.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Harborfolk') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I almost always cheat. I can''t help myself.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Harborfolk') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I am a secret informant for the Hawks. I send them reports about everything I see and hear, even what my friends and allies are up to.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Mulmaster Aristocrat', 'From your hilltop home, you have looked down (literally and perhaps figuratively) on the unwashed masses of Mulmaster for your entire life. Your fur-trimmed robes and training in the visual and performing arts mark you as wealthy and perhaps well-born; you are a member of the City of Danger''s aristocracy.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Mulmaster Aristocrat'), 'Feature: Highborn', 'Mulmaster is run by and for its aristocracy. Every other class of citizen in the city defers to you, and even the priesthood, Soldiery, Hawks, and Cloaks treat you with deference. Other aristocrats and nobles accept you in their circles and likely know you or of you. Your connections can get you the ear of a Zor or Zora under the right circumstances. [Note: This feature is a variant of the Noble feature.]');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Mulmaster Aristocrat'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mulmaster Aristocrat') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'My ambitions are boundless. I will be a Zor or Zora one day!');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mulmaster Aristocrat') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I must always look my best.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mulmaster Aristocrat') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Beauty is everywhere. I can find it in even the homeliest person and the most horrible tragedy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mulmaster Aristocrat') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Decorum must be preserved at all costs.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mulmaster Aristocrat') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I will not admit I am wrong if I can avoid it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mulmaster Aristocrat') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I am extremely well-educated and frequently remind others of that fact.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mulmaster Aristocrat') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I take what I can today, because I do not know what tomorrow holds.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mulmaster Aristocrat') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'My life is full of dance, song, drink, and love.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Mulmaster Aristocrat'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mulmaster Aristocrat') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Generous. I have a responsibility to help and protect the less fortunate. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mulmaster Aristocrat') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Loyal. My word, once given, is my');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Mulmaster Aristocrat'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mulmaster Aristocrat') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'Callous. I am unconcerned with any negative effects my actions may have on the lives and fortunes of others. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mulmaster Aristocrat') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'Impulsive. I follow my heart. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mulmaster Aristocrat') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'Ignorant. Explanations bore me. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mulmaster Aristocrat') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'Isolationist. I am concerned with the fortunes of my friends and family. Others must see to themselves. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mulmaster Aristocrat') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I have dedicated my wealth and my talents to the service of one of the city''s many temples.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mulmaster Aristocrat') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'My family and I are loyal supporters of High Blade Jaseen Drakehorn. Our fortunes are inexorably tied to hers. I would do anything to support her.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mulmaster Aristocrat') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'Like many families who were close to High Blade Selfaril Uoumdolphin, mine has suffered greatly since his fall. We honor his memory in secret.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mulmaster Aristocrat') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'My family plotted with Rassendyll Uoumdolphin brother usurped brother as High Blade. Betrayal is the quickest route to power.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mulmaster Aristocrat') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'Wealth and power are nothing. Fulfillment can only be found in artistic expression.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mulmaster Aristocrat') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'It''s not how you feel, who you know, or what you can do — it''s how you look, and I look fabulous.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Mulmaster Aristocrat'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mulmaster Aristocrat') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I have difficulty caring about anyone or anything other than myself.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mulmaster Aristocrat') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'Having grown up with wealth, I am careless with my finances. I overspend and am overly generous.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mulmaster Aristocrat') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'The ends (my advancement) justify any means.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mulmaster Aristocrat') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I must have what I want and will brook no delay.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mulmaster Aristocrat') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'My family has lost everything. I must keep up appearances, lest we become a laughingstock.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mulmaster Aristocrat') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I have no artistic sense. I hide that fact behind extreme opinions and have become a trendsetter.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Phlan Refugee', 'Gone are the happier days of walking into the Laughing Goblin Inn after a hard day''s labor. Everything has changed, and you are lucky to be alive. Back in Phlan you could count yourself among those street-wise folks who knew when to pay a bribe and who to work with to make a living. Your ability to listen to the winds of change have saved you before, and this time they allowed you to be one of the lucky few who escaped Phlan with something more than just the shirt on your back.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Phlan Refugee'), 'Feature: Phlan Survivor', 'Whatever your prior standing was, you are now one of the many refugees that have come to Mulmaster. You are able to find refuge with others from Phlan and those who sympathize with your plight. Within Mulmaster this means that you can find a place to bed down, recover, and hide from the watch with either other refugees from Phlan, or the Zhents within the ghettos. [Note: This feature is a variant of the Folk Hero feature.]');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Phlan Refugee'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Refugee') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I may have lost everything I worked for most of my life, but there''s work to be done, no time to linger on the past.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Refugee') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I worked hard to get where I am and I refuse to let a little hardship stop me from succeeding.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Refugee') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I protect those around me, you never know when one of them will be useful.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Refugee') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I have always gotten ahead by giving, why change now?');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Refugee') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I prepare for everything, it paid off in Phlan and it will pay off again.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Refugee') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I will reclaim my home, though the path may be long, I will never give up hope.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Refugee') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I never cared for personal hygiene, and am amazed that It bothers others.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Refugee') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I am always willing to volunteer my services, just as long as I don''t have to do anything.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Phlan Refugee'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Refugee') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Justice. Corruption brought Phlan down, I will not tolerate that any longer. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Refugee') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Acceptance. Stability is a myth, to think you can control your future is futile. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Refugee') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Hope. I am guided by a higher power and I trust that everything will be right in the end. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Refugee') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Restraint. I hate those who caused my loss. It is all I can do not to lash out at them. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Refugee') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Strength. As shown in Phlan, the strong survive. If you are weak you deserve what you get (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Refugee') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Openness. I am always willing to share my life story with anyone who will listen. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Phlan Refugee'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Refugee') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I have the chance at a new life and this time I am going to do things right.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Refugee') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'The Lord Regent brought this suffering upon his people. I will see him brought to justice.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Refugee') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I await the day I will be able to return to my home in Phlan.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Refugee') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I will never forget the debt owed to Glevith of the Welcomers. I will be ready to repay that debt when called upon.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Refugee') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'There was someone I cared about in Phlan, I will find out what happened to them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Refugee') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'Some say my life wasn''t worth saving, I will prove them wrong.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Phlan Refugee'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Refugee') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I used the lives of children to facilitate my escape from Phlan.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Refugee') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I am a sucker for the underdog, and always bet on the loosing team.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Refugee') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I am incapable of standing up for myself.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Refugee') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I will borrow money from friends with no intention to repay it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Refugee') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I am unable to keep secrets. A secret is just an untold story.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Phlan Refugee') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'When something goes wrong, it''s never my fault.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Cormanthor Refugee', 'You are one of hundreds of refugees that were driven from Hillsfar or that fled the destruction of Myth Drannor and who now shelter in hidden camps under the northern eaves of the Cormanthor Forest. If you up grew in the camps, you have never been to a settlement other than the village of Elventree. As a guest of the elves, you have learned their ways and the ways of the forest. You are also a traumatized, as residual wild magic, energies released by the fall of Thultanar upon Myth Drannor, and the constant fear of raids hunting for non-humans to fight in Hillsfar''s Arena have taken their toll on you, as they have on everyone in the camps.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Cormanthor Refugee'), 'Feature: Shelter of the Elven Clergy', 'The clerics of Elventree have vowed to care for the Cormanthor refugees. They will help you when they can, including providing you and your companions with free healing and care at temples, shrines, and other established presences in Elventree. They will also provide you (but only you) with a poor lifestyle. [Note: This feature is a variant of the Acolyte feature.]');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Cormanthor Refugee'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cormanthor Refugee') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I long for a home that never really existed, whether in the camps, Hillsfar, or Myth Drannor.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cormanthor Refugee') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Though I am not an elf, I am a fervent, radical worshipper of the elven gods.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cormanthor Refugee') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I live in the moment, knowing my life could be turned upside down any day.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cormanthor Refugee') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I appreciate beauty in all of its forms.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cormanthor Refugee') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I hate the dark elves and the Netherese for each driving the elves out of Cormanthyr in the past.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cormanthor Refugee') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I am a forest bumpkin who grew up in a tent in the woods and is wholly ignorant of city life.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cormanthor Refugee') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I was raised alongside children of many other races. I harbor no racial prejudices at all.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cormanthor Refugee') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'The elves have just the right word for so many things that cannot be expressed as well in other languages. I pepper my speech with elven words, phrases, and sayings.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Cormanthor Refugee'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cormanthor Refugee') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Patient. The elves have taught me to think and plan for the long-term. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cormanthor Refugee') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Rebellious. Governments and politicians drove my family to the camps. I subtly defy authority whenever I think I can get away with it. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cormanthor Refugee') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Self-Absorbed. I''ve had to look out for number one so long that it has become second nature. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cormanthor Refugee') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Wanderlust. I want to see as much of the world beyond the camps as I can. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cormanthor Refugee') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Generous. I give everything I can to help those in need, regardless of who they are. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cormanthor Refugee') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'To the Abyss with Them. The people of Hillsfar cast me out. I won''t risk my hide to help them. (Evil)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Cormanthor Refugee'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cormanthor Refugee') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'The elves took me in when I had nowhere else to go. In return, I do what I can to help elves in need.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cormanthor Refugee') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I seek revenge against the people of Hillsfar for driving my family into the forest.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cormanthor Refugee') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'My family lost everything when they were driven from Hillsfar. I strive to rebuild that fortune.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cormanthor Refugee') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'The forest has provided me with food and shelter. In return, I protect forests and those who dwell within.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cormanthor Refugee') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I am deeply, tragically in love with someone whose racial lifespan is far longer or shorter than mine.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cormanthor Refugee') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'Members of my extended family did not make it to the camps or have been kidnapped to fight in the Arena. I search for them tirelessly.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Cormanthor Refugee'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cormanthor Refugee') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I am very uncomfortable indoors and underground.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cormanthor Refugee') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I am haughty. I grew up among the elves and emulate them. Other races are crude in comparison.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cormanthor Refugee') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'Elf this, elf that. I am sick and tired of the elves.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cormanthor Refugee') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I am a miser. Having lost everything once before, I clutch my possessions and wealth very tightly.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cormanthor Refugee') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I am a moocher. I am so used to others providing for me that I have come to expect everyone to do it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cormanthor Refugee') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I believe the gods have cursed me, my family, and all of the Cormanthor refugees. We are all doomed, doomed I tell you!');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Gate Urchin', 'All traffic into and out of the City of Trade passes through the Hillsfar Gate, making it the ideal place for the destitute to gather to panhandle, busk, gossip, and pick pockets. You grew up on the streets in the shadow of that great steel edifice, which houses both Red Plumes and Guild Mages. Though you may have moved on, you still have friends among them, and that life has had a lasting impact on you.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Gate Urchin'), 'Feature: Red Plume and Mage Guild Contacts', 'You made a number of friends among the Red Plumes and the Mage''s Guild when you lived at the Hillsfar Gate. They remember you fondly and help you in little ways when they can. You can invoke their assistance in and around Hillsfar to obtain food, as well as simple equipment for temporary use. You can also invoke it to gain access to the low-security areas of their garrisons, halls, and encampments. [Note: This feature is a variant of the Soldier feature.]');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Gate Urchin'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Urchin') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I appreciate the simple things in life: a song, a warm meal, a sunny day. I don''t need any more.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Urchin') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'My problems are always caused by others. I''m never to blame.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Urchin') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I am afraid I could wind up back on the streets any day.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Urchin') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I get along with everyone.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Urchin') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I see people as marks for a con and have difficulty feeling true empathy for them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Urchin') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I have a real flair for matchmaking. I can find anyone a spouse!');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Urchin') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I think money is the true measure of appreciation and affection. Everything else is talk or an act.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Urchin') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I don''t like having a lot of stuff, just a few simple things I need. I don''t like being tied down and tend to leave things behind when I don''t need them anymore.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Gate Urchin'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Urchin') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Loyal. I never rat out any of my friends, even when the Red Plumes or the Rogues Guild ask. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Urchin') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Adventurous. I don''t like doing the same thing every day. I crave variety. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Urchin') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Strong. Only the strong survive. I respect those who are strong and powerful. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Urchin') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Witty. Brains are better than brawn. I rely on my wits and respect others who do the same. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Urchin') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Honest. Others can do what they want, but I won''t lie or steal, even to feed my family. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Urchin') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Ungrateful. Those who give, only do it to make themselves feel better. I steal from them. (Evil)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Gate Urchin'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Urchin') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'The Joydancers of Lliira gave me my instrument when I really needed food. I hate them for that.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Urchin') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'Busking has taught me to love music above all else.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Urchin') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'The Rogues Guild spared me when I did a job without cutting them in. I owe them a great debt.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Urchin') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I know people hate the Red Plumes, but some of them were really good to me. I help Red Plumes whenever I can, and I respect them. They''re just doing what they have to do to get by in this world.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Urchin') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I will be wealthy some day. My descendants will live in comfort and style.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Urchin') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I know how hard life on the streets is. I do everything I can for those who have less than me.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Gate Urchin'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Urchin') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'Though I no longer live at the Gate, I am still always concerned about where I will get my next meal.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Urchin') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'Years of thieving have become habit. I sometimes steal from strangers without thinking about it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Urchin') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I am ashamed of my origins. I pretend I am higher-born and fear others will find out the truth.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Urchin') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I think people who grew up in houses are soft, spoiled, and ungrateful. I frequently tell them so.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Urchin') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I am still very uncomfortable wearing nice clothes, sleeping in a warm bed, and eating fine food.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Urchin') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I do not trust anyone who has not had a hard life.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Hillsfar Merchant', 'Before becoming an adventurer, you were a successful merchant operating out Hillsfar, the City of Trade. Your family operated warehouses, organized caravans, managed stores, or owned a ship and has trade contacts throughout the Moonsea region, as well as up and down the length of the Sword Coast. Perhaps they import ore, uncut gems, untreated furs, or grain into the City of Trade, or they export fine cloth, faceted gems, fine furs, or Dragon''s Breath, a brandy-like liquor. Regardless, you''ve largely given that life up for some reason and have chosen to seek adventure instead. Nevertheless, the training you received then, and perhaps the contacts you made, serve you well as an adventurer.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Hillsfar Merchant'), 'Feature: Factor', 'Although you''ve left the day-to-day life of a merchant behind, your family has assigned you the services of a loyal retainer from the business, a factor, husbanding agent, seafarer, caravan guard, or clerk. This individual is a commoner who can perform mundane tasks for you such as making purchases, delivering messages, and running errands. He or she will not fight for you and will not follow you into obviously dangerous areas (such as dungeons), and will leave if frequently endangered or abused. If he or she is killed, the family assigns you another within a few days. [Note: This feature is a variant of the Noble Knight''s Retainers feature.]');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Hillsfar Merchant'), 'Variant Feature: Trade Contact', 'You and your family have trade contacts such as caravan masters, shopkeepers, sailors, artisans, and farmers throughout the Moonsea region and all along the Sword Coast. Once per game session, when adventuring in either of those areas, you can use those contacts to get information about the local area or to pass a message to someone in those areas, even across the great distance between the two areas. [Note: This feature is a variant of the Criminal Contact and Researcher features.]');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Hillsfar Merchant'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Merchant') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I fill my evenings with wine or mead and song.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Merchant') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I greatly admire gladiators and enjoy the Arena.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Merchant') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I take my wealth for granted. It seldom occurs to me that others aren''t rich themselves.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Merchant') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I leave broken hearts all around the Moonsea and up and down the Sword Coast.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Merchant') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I work hard and seldom make time for fun.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Merchant') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I am a particularly devout and pray often.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Merchant') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'The Red Plumes caught me once. I hate them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Merchant') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I ask a lot of questions to get information about those with whom I am working and dealing.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Hillsfar Merchant'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Merchant') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Frugal. I spend my money very carefully. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Merchant') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Profligate. I tend to spend extravagantly. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Merchant') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Honest. I deal with others above board. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Merchant') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Sharp. I seek to make the best deal possible. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Merchant') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Charitable. I give generously to others. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Merchant') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Greedy. I do not share my wealth with others. (Evil)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Hillsfar Merchant'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Merchant') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I am fiercely loyal to those with whom I work.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Merchant') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I must uphold the good name of my family.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Merchant') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I will prove myself to my family as an adventurer.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Merchant') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'Deals are sacrosanct. I never go back on my word.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Merchant') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I love making deals and negotiating agreements.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Merchant') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I guard my wealth jealously.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Hillsfar Merchant'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Merchant') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I am a braggart. I promote myself shamelessly.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Merchant') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I am vain. I always wear the latest fashions.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Merchant') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I am a glutton. I eat and drink to excess.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Merchant') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I am a snob. I want only the finest things in life.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Merchant') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I am lazy. I want others to take care of everything.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Merchant') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I am overconfident. I overestimate my abilities.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Hillsfar Smuggler', 'Hillsfar is the City of Trade. However, the Great Law of Trade only protects "legitimate" trade, trade that passes through the city''s sole gate, which the Red Plumes monitor and tax. And the Great Law of Humanity banishes non-humans from the city altogether. The two Great Laws create great demand and great risk for smugglers, who shepherd illicit goods and non-humans into and out of the city by secret routes. The Rogues Guild tightly controls all of this activity, taking its cut from sanctioned jobs and exacting punishment for independent jobs.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Hillsfar Smuggler'), 'Feature: Secret Passage', 'You can call on your contacts within the smuggling community to secure secret passage into or out of Hillsfar for yourself and your adventuring companions, no questions asked, and no Red Plume entanglements. Because you''re calling in a favor, you can''t be certain they will be able to help on your timetable or at all. Your Dungeon Master will determine whether you can be smuggled into or out of the city. In return for your passage, you and your companions may owe the Rouges Guild a favor and/or may have to pay bribes. [Note: This feature is a variant of the Sailor feature.]');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Hillsfar Smuggler'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Smuggler') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'When I''m not smuggling, I gamble.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Smuggler') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I just love Halfling cooking and baking!');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Smuggler') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I party with dwarves whenever I can.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Smuggler') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I''m a terrible singer, but I love to do it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Smuggler') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I was raised to honor Chauntea and still do.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Smuggler') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'The blood sports of the Arena sicken me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Smuggler') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I think non-humans are really interesting.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Smuggler') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I exaggerate the tales of my exploits.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Hillsfar Smuggler'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Smuggler') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Fair. I think everyone deserves to be treated fairly. I don''t play favorites. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Smuggler') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Impulsive. Planning is often a waste of time. No plan survives contact with reality. It''s easier to dive in and deal with the consequences. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Smuggler') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Curious. I want to learn as much as I can about the people and places I encounter. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Smuggler') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Prepared. I think success depends on preparing as much as possible in advance. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Smuggler') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Respectful. I think everyone deserves to be treated with respect and dignity, regardless of their race, creed, color, or origin. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Smuggler') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Corrupt. I will break the law or act dishonestly if the money is right. (Evil)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Hillsfar Smuggler'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Smuggler') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I am loyal to the Rogues Guild and would do anything for them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Smuggler') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I love the city of Hillsfar and my fellow Hillsfarians, despite the recent problems.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Smuggler') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I admire the elves. I help them whenever I can.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Smuggler') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'A gnome helped me once. I pay the favor forward.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Smuggler') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I enjoy tricking the Red Plumes at every opportunity.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Smuggler') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I smuggled agricultural goods for non-human farmers. I try to help them when I can.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Hillsfar Smuggler'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Smuggler') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'My hatred for the Red Plumes burns so brightly that I have difficulty suppressing It around them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Smuggler') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'The Red Plumes caught me once before, and I was branded for my crime. If they catch me again, for any offense, the punishment will be dire.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Smuggler') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I treat all Hillsfarans poorly. I am disgusted with their failure to revolt against the Great Law of Humanity.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Smuggler') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I have difficulty trusting strangers. Anyone could be a spy for the authorities.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Smuggler') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I am greedy. There Isn''t much I won''t do for money.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Hillsfar Smuggler') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I''m an informant for the Red Plumes. They let me continue my activities, so long as I pass them information about illegal activity in Hillsfar.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Secret Identity', 'Even though you are a non-human, despite Hillsfar''s Great Law of Humanity, you continue to live in the City of Trade. You do so by maintaining a secret identity, forging documents, and wearing a disguise. Few, if any, know you aren''t human.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Secret Identity'), 'Feature: Secret Identity', 'You have created a secret identity that you use to conceal your true race and that offers a covering explanation for your presence in Hillsfar. In addition, you can forge documents, including official papers and personal letters, as long as you have seen an example of the kind of document or the handwriting you are trying to copy [Note: This feature is a variant of the Charlatan feature.]');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Secret Identity'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Secret Identity') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Despite its problems, I love Hillsfar, it''s the greatest city in the world. The only one for me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Secret Identity') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I move from place to place, never staying anywhere long and leaving nothing behind.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Secret Identity') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I think flattery is the best way to direct attention away from me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Secret Identity') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I don''t make friends easily. They''re a liability I cannot afford.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Secret Identity') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Risk and danger are exhilarate me. Pulling off schemes and deceptions is a rush.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Secret Identity') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'The First Lord is right, humans are superior. I really admire them, despite the atrocities.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Secret Identity') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I avoid people of my own race, as well as things associated with my race, lest they give me away.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Secret Identity') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I live for the Arena. I admire gladiators and enjoy the thrill of blood on the sands!');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Secret Identity'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Secret Identity') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Quisling. Supporting the rulers of the land and following the laws is the road to salvation. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Secret Identity') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Sco');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Secret Identity'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Secret Identity') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'Optimist. Everyone Is basically good. Though the government is misguided it will all be okay. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Secret Identity') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'Secretive. I am in the habit of not talking about myself. My business is none of yours. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Secret Identity') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'Heroic. I do everything I can to help non-humans, regardless of the personal cost to me. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Secret Identity') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'Depraved. I have lost my moral compass. The ends justify most any means. (Evil)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Secret Identity'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Secret Identity') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'The humans of Hillsfar have inflicted terrible harm on me, my family, and my race. I will have revenge.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Secret Identity') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I am part of an underground network that smuggles non-humans into and out of the city.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Secret Identity') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I am a partisan. I commit minor acts of defiance against the First Lord and Red Plumes when I can.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Secret Identity') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I am a spy. I report on events in and around Hillsfar.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Secret Identity') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'My secret identity is the only thing protecting me from the Arena. I will stop at nothing to maintain it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Secret Identity') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I am madly in love with a human who does not know my true identity, and I fear rejection if I reveal it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Secret Identity') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'After years of denying who I am, I now despise myself and other members of my pathetic race.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Secret Identity') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'Years of hiding have made me somewhat paranoid. I trust no one.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Secret Identity') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I''ve been lying so often and for so long that I can''t help it anymore. I frequently lie for no reason at all.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Secret Identity') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I am ashamed. I failed to protect a member of my family who was seized and thrown into the Area.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Secret Identity') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I am struggling with maintaining my secret identity. I subconsciously want to get caught and therefore sometimes let my secret identity slip.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Secret Identity') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'Years of successfully deceiving others have made me cocky. I think no one can see through my lies.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Shade Fanatic', 'You grew up at a time when the wizards of Netheril were at war with the elves of Cormanthor. You recall sitting cross-legged hearing the stories of the glorious Thultanthar, also called the Shade Enclave and the City of Shade, and aspired to study there and maybe even did, for a time. Your dreams came crashing down a few years ago when Thultanthar fell from the sky upon Myth Drannor.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Shade Fanatic'), 'Feature: Secret Society', 'You have a special way of communicating with others who feel the same way you do about the Shade. When you enter a village or larger city you can identify contact who will give you information on those that would hinder your goals and those would help you simply because of your desire to see the Shade Enclave return in all its glory. [Note: This feature is a variant of the Criminal feature.]');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Shade Fanatic'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shade Fanatic') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I am a bully; I try to hide it though.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shade Fanatic') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I let my actions speak for themselves');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shade Fanatic') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I am important; I will not let anyone forget that.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shade Fanatic') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'You are either with me or against me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shade Fanatic') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I know it is only a time before I am betrayed by those I care for.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shade Fanatic') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I never understand why people get so emotional.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shade Fanatic') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'They are out to get me. It is only my cunning that keeps me ahead of them');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shade Fanatic') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'Everyone has a choice, the one I make is always right though.');
