INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shade Fanatic') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Frugal. I horde my possessions knowing that someday I will be called upon to give everything I have to the cause (Any)');
