INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shade Fanatic') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Compassionate. It is through love that others will join in our cause. (Good)');
