INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shade Fanatic') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'My true love was a killed by the Red Plumes; I plot to make them suffer.');
