INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shade Fanatic') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I cannot bear to let those I care for out of my sight.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shade Fanatic') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I am incapable of standing up for myself.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shade Fanatic') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'The group I am with has committed atrocities; I am always worried their actions will become public.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shade Fanatic') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I always enjoy a good mug of ale... or five.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shade Fanatic') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I know what I do is wrong, but am afraid to speak up about it.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Trade Sheriff', 'You are one of the many people that make sure the trade routes are clear at ALL times. You assure that the Great Law of Trade is followed at all costs. You work by yourself or in groups to quell bandits and brigands who might stop trade routes from going through. You investigate potential ambushes and possible rumors as to someone wanting to rob or stop caravans. You are as much an investigator as you are law enforcement.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Trade Sheriff'), 'Feature: Investigative Services', 'You are part of a small force outside of Hillsfar. You have a special way of communicating with others and they seem to be at ease around you. You can invoke your rank to allow you access to a crime scene or to requisition equipment or horses on a temporary basis. When you enter a town or village around Hillsfar you can identify a contact who will give you information on the local rumors and would help you simply because of your desire to get answers and information for anyone wanting to disrupt trade. [Note: This feature is a variant of the soldier feature.]');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Trade Sheriff'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Trade Sheriff') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I am always polite and respectful');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Trade Sheriff') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I let my actions speak for themselves');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Trade Sheriff') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I am haunted by my past having seen the murder of a close friend or family member and it is the one case I always needed to solve but have not been able to.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Trade Sheriff') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I am quick to judge and slow to vindicate');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Trade Sheriff') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I can be very persuasive and am able to ask questions where others might not be able to.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Trade Sheriff') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I have a quirky personality that seems to take others off their guard.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Trade Sheriff') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'My sense of humor is considered by most to be awkward.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Trade Sheriff') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'Everyone has a choice, and they can always make the right choice, mine!');
