INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Trade Sheriff') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Dedicated. Once I start an investigation, until told to do so, I do not quit, not matter where it leads. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Trade Sheriff') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Nation. My city, nation, or people are all that matter. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Trade Sheriff') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Mercenary. When I do investigations, I expect answers immediately. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Trade Sheriff') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Eloquent. I use my words to sway others to give me answers. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Trade Sheriff') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Might. It is through threats and force that I get my answers. (Lawful)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Trade Sheriff'), 'Suggested Characteristics', 'Bond', '1d6');
