INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Trade Sheriff') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'It is my right to believe what I will, just try and stop me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Trade Sheriff') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I need to prove my worth to my fellow Sheriffs');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Trade Sheriff') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'Someone I cared for died under suspicious circumstances. I will find out what happened to them and bring their killer to justice.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Trade Sheriff') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I speak for those that cannot speak for themselves.');
