INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Trade Sheriff') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I took a bribe to tank an investigation and I would do anything to keep it secret.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Trade Sheriff') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I have little respect for those that are of "low" intelligence/race.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Trade Sheriff') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I always enjoy a good mug of ale... or five to cover up my past.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Trade Sheriff') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I speak for the First Lord of Hillsfar and make sure everyone knows it.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Haunted One', 'You are haunted by something so terrible that you dare not speak of it. You''ve tried to bury it and run away from it, to no avail. Whatever this thing is that haunts you can''t be slain with a sword or banished with a spell. It might come to you as a shadow on the wall, a bloodcurdling nightmare, a memory that refuses to die, or a demonic whisper in the dark. The burden has taken its toll, isolating you from most people and making you question your sanity. You must find a way to overcome it before it destroys you.', 'Choose one of Arcana, Investigation, Religion, or Survival', '', 'Choose one exotic language (Abyssal, Celestial, Deep Speech, Draconic, Infernal, Primordial, Sylvan, or Undercommon)', 'A monster hunter''s pack (containing a chest, a crowbar, a hammer, three wooden stakes, a holy symbol, a flask of holy water, a set of manacles, a steel mirror, a flask of oil, a tinderbox, and 3 torches), one trinket of special significance (choose one or roll on the Gothic Trinkets table below)', (SELECT id FROM core_book_sources WHERE raw_source = 'Curse of Strahd p. 209'));
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Haunted One'), 'Harrowing Event', 'Harrowing Event', '1d10');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One') AND title = 'Harrowing Event' ORDER BY id DESC LIMIT 1), '1', 'A monster that slaughtered dozens of innocent people spared your life, and you don''t know why.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One') AND title = 'Harrowing Event' ORDER BY id DESC LIMIT 1), '2', 'You were born under a dark star. You can feel it watching you, coldly and distantly. Sometimes it beckons you in the dead of night.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One') AND title = 'Harrowing Event' ORDER BY id DESC LIMIT 1), '3', 'An apparition that has haunted your family for generations now haunts you. You don''t know what it wants, and it won''t leave you alone.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One') AND title = 'Harrowing Event' ORDER BY id DESC LIMIT 1), '4', 'Your family has a history of practicing the dark arts. You dabbled once and felt something horrible clutch at your soul, whereupon you fled in terror.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One') AND title = 'Harrowing Event' ORDER BY id DESC LIMIT 1), '5', 'An oni took your sibling one cold, dark night, and you were unable to stop it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One') AND title = 'Harrowing Event' ORDER BY id DESC LIMIT 1), '6', 'You were cursed with lycanthropy and later cured. You are now haunted by the innocents you slaughtered.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One') AND title = 'Harrowing Event' ORDER BY id DESC LIMIT 1), '7', 'A hag kidnapped and raised you. You escaped, but the hag still has a magical hold over you and fills your mind with evil thoughts.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One') AND title = 'Harrowing Event' ORDER BY id DESC LIMIT 1), '8', 'You opened an eldritch tome and saw things unfit for a sane mind. You burned the book, but its words and images are burned into your psyche.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One') AND title = 'Harrowing Event' ORDER BY id DESC LIMIT 1), '9', 'A fiend possessed you as a child. You were locked away but escaped. The fiend is still inside you, but now you try to keep it locked away.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One') AND title = 'Harrowing Event' ORDER BY id DESC LIMIT 1), '10', 'You did terrible things to avenge the murder of someone you loved. You became a monster, and it haunts your waking dreams.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Haunted One'), 'Feature: Heart of Darkness', 'Those who look into your eyes can see that you have faced unimaginable horror and that you are no stranger to darkness. Though they might fear you, commoners will extend you every courtesy and do their utmost to help you. Unless you have shown yourself to be a danger to them, they will even take up arms to fight alongside you, should you find yourself facing an enemy alone.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Haunted One'), 'Gothic Trinket', 'Trinket', '1d100');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '01-02', 'A picture you drew as a child of your imaginary friend');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '03-04', 'A lock that opens when blood is dripped in its keyhole');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '05-06', 'Clothes stolen from a scarecrow');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '07-08', 'A spinning top carved with four faces: happy, sad, wrathful, and dead');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '09-10', 'The necklace of a sibling who died on the day you were born');
