INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '27-28', 'A cameo with the profile''s face scratched away');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '29-30', 'A lantern with a black candle that never runs out and that burns with green flame');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '31-32', 'A teacup from a child''s tea set, stained with blood');
