INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Anthropologist', 'You have always been fascinated by other cultures, from the most ancient and primeval lost lands to the most modern civilizations. By studying other cultures'' customs, philosophies, laws, rituals, religious beliefs, languages, and art, you have learned how tribes, empires, and all forms of society in between craft their own destinies and doom. This knowledge came to you not only through books and scrolls, but also through first-hand observation — by visiting far-flung settlements and exploring local histories and customs.', 'Insight, Religion', '', 'Two of your choice', 'A leather-bound diary, a bottle of ink (1-ounce bottle), an ink pen, a set of traveler''s clothes, one trinket of special significance, and a pouch containing 10 gp', (SELECT id FROM core_book_sources WHERE raw_source = 'Tomb of Annihilation p. 191'));
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Anthropologist'), 'Cultural Chameleon', 'Culture', '1d8');
