INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Anthropologist') AND title = 'Culture' ORDER BY id DESC LIMIT 1), '8', 'Orc');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Anthropologist'), 'Feature: Adept Linguist', 'You can communicate with humanoids who don''t speak any language you know. You must observe the humanoids interacting with one another for at least 1 day, after which you learn a handful of important words, expressions, and gestures — enough to communicate on a rudimentary level.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Anthropologist'), 'Suggested Characteristics', 'Personality Trait', '1d6');
