INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Anthropologist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I''m a stickler when it comes to observing proper etiquette and local customs');
