INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Anthropologist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I would risk life and limb to discover a new');
