INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Anthropologist') AND title = 'Culture' ORDER BY id DESC LIMIT 1), '6', 'When I arrive at a new settlement for the first time, I must learn all its customs.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Anthropologist'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Anthropologist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Discovery. I want to be the first person to discover a lost culture. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Anthropologist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Distance. One must not interfere with the affairs of another culture — even one in need of aid. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Anthropologist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Knowledge. By understanding other races and cultures, we learn to understand ourselves. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Anthropologist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Power. Common people crave strong leadership, and I do my utmost to provide it. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Anthropologist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Protection. I must do everything possible to save a society facing extinction. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Anthropologist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Indifferent. Life is cruel. What''s the point in saving people if they''re going to die anyway? (Chaotic)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Anthropologist'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Anthropologist') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'My mentor gave me a journal filled with lore and wisdom. Losing it would devastate me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Anthropologist') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'Having lived among the people of a primeval tribe or clan, I long to return and see how they are faring.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Anthropologist') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'Years ago, tragedy struck the members of an isolated society I befriended, and I will honor them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Anthropologist') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I want to learn more about a particular humanoid culture that fascinates me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Anthropologist') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I seek to avenge a clan, tribe, kingdom, or empire that was wiped out.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Anthropologist') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I have a trinket that I believe is the key to finding a long-lost society.');
