INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Anthropologist') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'Boats make me seasick.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Anthropologist') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I talk to myself, and I don''t make friends easily.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Anthropologist') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I believe that I''m intellectually superior to people from other cultures and have much to teach them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Anthropologist') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I''ve picked up some unpleasant habits living among goblins, lizardfolk, or orcs.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Anthropologist') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I complain about everything.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Anthropologist') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I wear a tribal mask and never take it off.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Archaeologist', 'An archaeologist learns about the long-lost and fallen cultures of the past by studying their remains — their bones, their ruins, their surviving masterworks, and their tombs. Those who practice archaeology travel to the far corners of the world to root through crumbled cities and lost dungeons, digging in search of artifacts that might tell the stories of monarchs and high priests, wars and cataclysms.', 'History, Survival', 'Cartographer''s tools or navigator''s tools', 'One of your choice', 'A wooden case containing a map to a ruin or dungeon, a bullseye lantern, a miner''s pick, a set of traveler''s clothes, a shovel, a two-person tent, a trinket recovered from a dig site, and a pouch containing 25 gp', (SELECT id FROM core_book_sources WHERE raw_source = 'Tomb of Annihilation p. 192'));
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Archaeologist'), 'Dust Digger', 'Signature Item', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Archaeologist') AND title = 'Signature Item' ORDER BY id DESC LIMIT 1), '1', 'Pole (10-foot)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Archaeologist') AND title = 'Signature Item' ORDER BY id DESC LIMIT 1), '2', 'Crowbar');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Archaeologist') AND title = 'Signature Item' ORDER BY id DESC LIMIT 1), '3', 'Hat');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Archaeologist') AND title = 'Signature Item' ORDER BY id DESC LIMIT 1), '4', 'Hooded lantern');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Archaeologist') AND title = 'Signature Item' ORDER BY id DESC LIMIT 1), '5', 'Medallion');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Archaeologist') AND title = 'Signature Item' ORDER BY id DESC LIMIT 1), '6', 'Shovel');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Archaeologist') AND title = 'Signature Item' ORDER BY id DESC LIMIT 1), '7', 'Sledgehammer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Archaeologist') AND title = 'Signature Item' ORDER BY id DESC LIMIT 1), '8', 'Whip');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Archaeologist'), 'Feature: Historical Knowledge', 'When you enter a ruin or dungeon, you can correctly ascertain its original purpose and determine its builders, whether those were dwarves, elves, humans, yuan-ti, or some other known race. In addition, you can determine the monetary value of art objects more than a century old.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Archaeologist'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Archaeologist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I love a good puzzle or mystery.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Archaeologist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I''m a pack rat who never throws anything away.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Archaeologist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Fame is more important to me than money.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Archaeologist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I have no qualms about stealing from the dead.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Archaeologist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I''m happier in a dusty old tomb than I am in the centers of civilization.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Archaeologist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Traps don''t make me nervous. Idiots who trigger traps make me nervous.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Archaeologist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I might fail, but I will never give up.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Archaeologist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'You might think I''m a scholar, but I love a good brawl. These fists were made for punching.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Archaeologist'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Archaeologist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Preservation. That artifact belongs in a museum. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Archaeologist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Greed. I won''t risk my life for nothing. I expect some kind of payment. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Archaeologist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Death Wish. Nothing is more exhilarating than a narrow escape from the jaws of death. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Archaeologist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Dignity. The dead and their belongings deserve to be treated with respect. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Archaeologist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Immortality. All my exploring is part of a plan to find the secret of everlasting life. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Archaeologist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Danger. With every great discovery comes grave danger. The two walk hand in hand. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Archaeologist'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Archaeologist') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'Ever since I was a child, I''ve heard stories about a lost city. I aim to find it, learn its secrets, and earn my place in the history books.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Archaeologist') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I want to find my mentor, who disappeared on an expedition some time ago.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Archaeologist') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I have a friendly rival. Only one of us can be the best, and I aim to prove it''s me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Archaeologist') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I won''t sell an art object or other treasure that has historical significance or is one of a kind.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Archaeologist') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I''m secretly in love with the wealthy patron who sponsors my archaeological exploits.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Archaeologist') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I hope to bring prestige to a library, a museum, or a university.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Archaeologist'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Archaeologist') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I have a secret fear of some common wild animal — and in my work, I see them everywhere.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Archaeologist') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I can''t leave a room without searching it for secret doors.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Archaeologist') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'When I''m not exploring dungeons or ruins, I get jittery and impatient.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Archaeologist') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I have no time for friends or family. I spend every waking moment thinking about and preparing for my next expedition.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Archaeologist') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'When given the choice of going left or right, I always go left.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Archaeologist') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I can''t sleep except in total darkness.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Azorius Functionary', 'The brutes are at the gate, chaos is looming, and the only thing standing in the way of disaster is the foundation of law and order—as embodied in the Azorius Senate. You''re a proud member of the Azorius guild, which both enacts and enforces the laws that make Ravnican society function smoothly and safely.', 'Insight, Intimidation', '', 'Two of your choice', 'An Azorius insignia, a scroll containing the text of a law important to you, A bottle of black ink, a ink pen, a set of fine clothes, and a belt pouch containing 10 gp (Azorius-minted 1-zino coins)', (SELECT id FROM core_book_sources WHERE raw_source = 'Guildmasters'' Guide to Ravnica p. 33'));
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Azorius Functionary'), 'Feature: Legal Authority', 'You have the authority to enforce the laws of Ravnica, and that status inspires a certain amount of respect and even fear in the populace. People mind their manners in your presence and avoid drawing your attention; they assume you have the right to be wherever you are. Showing your Azorius insignia gets you an audience with anyone you want to talk to (though it might cause more problems than it solves when you''re dealing with incorrigible lawbreakers). If you abuse this privilege, though, you can get in serious trouble with your superiors and even be stripped of your position.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Azorius Functionary'), 'Azorius Guild Spells', 'Prerequisite: Spellcasting or Pact Magic class feature
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Azorius Functionary'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I try never to let my judgment become clouded by emotion.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I have infinite patience with the dolts and boors I''m forced to deal with every day.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'When I give an order, I expect it to be obeyed.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I just want things the way I like them: neat, orderly, and clean.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'No wrongdoing can escape my watchful gaze.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I always say exactly what I mean, no matter how many words it takes to communicate the particular nuance I am attempting to convey.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I''m very literal and don''t appreciate metaphor or sarcasm.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I never change my mind once it''s made up.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Guild. My guild is all that really matters. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Order. The law is meant to ensure that the gears of society turn smoothly and quietly. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Peace. The ultimate object of the law is to remove violence from society. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Compliance. Coercion is a fine way of ensuring that the laws are obeyed. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Legislation. The law embodies excellence in its precision and detail. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Punishment. A public display of consequences is an excellent deterrent for other criminals. (Evil)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Azorius Functionary'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I am beholden to an Azorius arrester who captured the criminal who killed my parents, saving me from the same fate.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I hope one day to write the laws, not just enforce them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I tried and failed to prevent a murder, and I have sworn to find and arrest the perpetrator.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I successfully prevented a murder, and the would-be perpetrator wants me dead.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'One of my parents was prominent in the guild, and I resent constantly being compared to that standard.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I''ve modeled my career after a highly respected lawmage or arrester, but I fear that my role model might be involved in something illegal.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Azorius Functionary'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I''m unable to distinguish between the letter and the spirit of the law.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I seem like a harsh judge to others, but I judge myself most harshly of all.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I have a secret, illegal vice.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I was traumatized by witnessing a crime as a child.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I''m incapable of deception.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I wish I had joined the Boros, but I fear they''d never accept me.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Azorius Functionary'), 'Contacts', 'Non-Azorius Contact', '1d10');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Non-Azorius Contact' ORDER BY id DESC LIMIT 1), '1', 'As a teenager, I was a page for a prominent judge.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Non-Azorius Contact' ORDER BY id DESC LIMIT 1), '2', 'One of my childhood friends is now a precognitive mage in service of the guild.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Non-Azorius Contact' ORDER BY id DESC LIMIT 1), '3', 'I joined the Azorius hoping to impress an arrester whom I admire.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Non-Azorius Contact' ORDER BY id DESC LIMIT 1), '4', 'My former mentor is now a warden at Udzec, the new Azorius prison.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Non-Azorius Contact' ORDER BY id DESC LIMIT 1), '5', 'I was second best at everything in my legal training, and now I work with the person who was always just a little bit better.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Non-Azorius Contact' ORDER BY id DESC LIMIT 1), '6', 'A good friend was promoted into work they can''t tell me about.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Non-Azorius Contact' ORDER BY id DESC LIMIT 1), '7', 'I know a homunculus in the halls of New Prahv who can get things done behind the scenes.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Non-Azorius Contact' ORDER BY id DESC LIMIT 1), '8', 'I was once summoned to the presence of Supreme Judge Isperia, the guildmaster of the Azorius, who complimented me on my work.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Non-Azorius Contact' ORDER BY id DESC LIMIT 1), '1', 'Roll an additional Azorius contact; you can decide if the contact is an ally or a rival.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Non-Azorius Contact' ORDER BY id DESC LIMIT 1), '2', 'The person who recruited me into the Azorius left and joined the Boros.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Non-Azorius Contact' ORDER BY id DESC LIMIT 1), '3', 'I have a friendship with a Dimir agent who sometimes funnels me secrets about Azorius activities.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Non-Azorius Contact' ORDER BY id DESC LIMIT 1), '4', 'A Golgari spore druid would love to see me slip up and break the law.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Non-Azorius Contact' ORDER BY id DESC LIMIT 1), '5', 'A lesser Gruul chieftain seems to think I could be useful.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Non-Azorius Contact' ORDER BY id DESC LIMIT 1), '6', 'The black sheep of my family is putting their maniacal genius to use in the Izzet.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Non-Azorius Contact' ORDER BY id DESC LIMIT 1), '7', 'I''m friends with an Orzhov advokist; we compare notes on different forms of law magic.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Non-Azorius Contact' ORDER BY id DESC LIMIT 1), '8', 'I was ridiculed once in a Rakdos performance; the performer was impressed with my good humor about it and now does me occasional favors.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Non-Azorius Contact' ORDER BY id DESC LIMIT 1), '9', 'I have a fanatical Selesnya cousin who keeps trying to recruit me and everyone else in the family.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Azorius Functionary') AND title = 'Non-Azorius Contact' ORDER BY id DESC LIMIT 1), '10', 'While growing up, I was bullied by a brat who''s now a hybrid in the Simic Combine.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Azorius Functionary'), 'How Do I Fit In?', 'As a member of the Azorius Senate, you are probably engaged in the work of law enforcement (even if your background involved the legislative or judicial aspects of the senate''s activities). Legislative aides and judges'' clerks find little reason to venture beyond the Azorius guildhalls, but soldiers and lawmages patrol the streets daily.
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Boros Legionnaire', 'As a member of the Boros Legion, your life is devoted to the service of angels and consecrated to the work of establishing justice and peace on the streets of Ravnica. You might be a true believer, inspired by the example of the angels, moved by the plight of the downtrodden, and devoted to the cause of justice. Or you could be a cynic in the ranks, perhaps because you reluctantly followed in the footsteps of a Boros parent, succumbed to the persuasion of a charismatic recruiter''s lofty promises, or were drawn in by the prospect of a life of action.', 'Athletics, Intimidation', 'One type of gaming set', 'Choose one of Celestial, Draconic, Goblin, or Minotaur', 'A Boros insignia, a feather from an angel''s wing, a tattered piece of a Boros banner (a souvenir from a famous battle), a set of common clothes, and a belt pouch containing 2 gp (Boros-minted 1-zino coins)', (SELECT id FROM core_book_sources WHERE raw_source = 'Guildmasters'' Guide to Ravnica p. 40'));
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire'), 'Feature: Legion Station', 'You have an established place in the hierarchy of the Boros Legion. You can requisition simple equipment for temporary use, and you can gain access to any Boros garrison in Ravnica, where you can rest in safety and receive the attention of medics. You are also paid a salary of 1 gp (a Boros-minted 1-zino coin) per week, which (combined with free lodging in your garrison) enables you to maintain a poor lifestyle between adventures.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire'), 'Boros Guild Spells', 'Prerequisite: Spellcasting or Pact Magic class feature
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I approach every task with the same high degree of military precision.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I am always the first into the fray.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I bear any injury or indignity with stoic discipline.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'My righteous wrath is easily inflamed by the slightest iniquity.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'My honor is more important to me than my life.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Dangerous work is best accomplished by an orderly group working with common purpose.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I treat my weapons, uniform, and insignia with reverence, for they are gifts of the angels.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I pace when standing and fidget incessantly when forced to sit.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Guild. My guild is all that really matters. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Justice. Achieving justice requires establishing fair, equitable, and compassionate relationships within a community. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Protection. It isn''t right for innocents to suffer because of the arrogance of the powerful. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Solidarity. It is most crucial to act with a single will, marching side by side in perfect accord. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Order. Society functions only if people do their duty and respect the chain of command. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Conviction. Anything worth doing is worth doing with your whole heart. (Lawful)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I would lay down my life for Aurelia and the angels.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I owe my life to the Boros captain who took me in when I was living on the streets.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'My fellow legionnaires are my family.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I wield the same Boros weapon my grandparent did, for the honor of our family.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I ran with the Rakdos in my youth, and I''m striving to atone for my past misdeeds.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I do what I can to help out the spouse of a comrade who died in battle.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I act bravely when I''m in a group, but I''m a coward when I''m alone.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I see everything in clear-cut black and white.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I''m just a little fascinated by the ways of the Gruul.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I trust the chain of command more than anything—more even than my closest friends.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I''m slow to trust members of other guilds.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I''ve been known to turn a blind eye to injustice, with the help of a modest bribe.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire'), 'Contacts', 'Non-Boros Contact', '1d10');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Non-Boros Contact' ORDER BY id DESC LIMIT 1), '1', 'A former comrade in arms was promoted into the prestigious Sunhome Guard.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Non-Boros Contact' ORDER BY id DESC LIMIT 1), '2', 'One of my parents is a ranking Boros officer.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Non-Boros Contact' ORDER BY id DESC LIMIT 1), '3', 'A close friend serves aboard the Parhelion II, a flying fortress.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Non-Boros Contact' ORDER BY id DESC LIMIT 1), '4', 'I had a tangled affair with a Boros garrison captain.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Non-Boros Contact' ORDER BY id DESC LIMIT 1), '5', 'I have maintained a relationship with one of my instructors at Horizon Military Academy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Non-Boros Contact' ORDER BY id DESC LIMIT 1), '6', 'I competed with a fellow student for the attention of a mentor at Horizon Military Academy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Non-Boros Contact' ORDER BY id DESC LIMIT 1), '7', 'The person who recruited me into the legion changed the course of my life.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Non-Boros Contact' ORDER BY id DESC LIMIT 1), '8', 'A Boros angel knows my name.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Non-Boros Contact' ORDER BY id DESC LIMIT 1), '1', 'One of my siblings is an Azorius arrester.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Non-Boros Contact' ORDER BY id DESC LIMIT 1), '2', 'Roll an additional Boros contact; you can decide if the contact is an ally or a rival.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Non-Boros Contact' ORDER BY id DESC LIMIT 1), '3', 'I showed mercy to an injured, now-grateful Dimir spy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Non-Boros Contact' ORDER BY id DESC LIMIT 1), '4', 'I suspect someone I know is a Golgari assassin, but I can''t prove it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Non-Boros Contact' ORDER BY id DESC LIMIT 1), '5', 'An adolescent relative ran off to join the Gruul in an act of rebellion and has not yet returned.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Non-Boros Contact' ORDER BY id DESC LIMIT 1), '6', 'I once befriended an Izzet scientist, and we''re still cordial though the relationship ended messily.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Non-Boros Contact' ORDER BY id DESC LIMIT 1), '7', 'I owe a monetary debt to an Orzhov syndic.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Non-Boros Contact' ORDER BY id DESC LIMIT 1), '8', 'A Rakdos blood witch seems to enjoy harassing me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Non-Boros Contact' ORDER BY id DESC LIMIT 1), '9', 'I tried to recruit a friend who ended up joining the Selesnya.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire') AND title = 'Non-Boros Contact' ORDER BY id DESC LIMIT 1), '10', 'I keep running into a particular Simic biomancer, and I enjoy the arguments that inevitably result.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Boros Legionnaire'), 'How Do I Fit In?', 'As a member of the Boros Legion, you are subject to the orders of your superior officers. You go where the angels tell you to go, and you do what they command you to do, to the best of your ability. If you enjoy some measure of independence, it''s either because you have proven yourself capable of working without close supervision or because you''re a rebellious sort who is willing to disobey orders now and face the consequences later.
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Dimir Operative', 'You''re a spy. Secrets and misinformation are your stock in trade. You skulk in the shadows, infiltrate other guilds, and steal the most precious secrets, whether they''re written in locked journals or hidden away in someone''s mind. Even you might not be aware of all the reasons behind the missions you carry out. Sometimes a mission''s sole purpose is to conceal the motivation behind another strike performed in a different part of the city, or simply to spread fear.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Dimir Operative'), 'Feature: False Identity', 'Reason', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Reason' ORDER BY id DESC LIMIT 1), '1', 'My parents belong to this guild, and I let them think I''m following in their footsteps.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Reason' ORDER BY id DESC LIMIT 1), '2', 'I''ve been assigned to track this guild''s activities.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Reason' ORDER BY id DESC LIMIT 1), '3', 'I''ve been assigned to get close to an individual in this guild and learn their secrets.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Reason' ORDER BY id DESC LIMIT 1), '4', 'I''ve been assigned to recruit a new Dimir spy from the ranks of this guild.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Reason' ORDER BY id DESC LIMIT 1), '5', 'I was a member of this guild before the Dimir recruited me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Reason' ORDER BY id DESC LIMIT 1), '6', 'I don''t like what this guild stands for and want to destroy it from within.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Reason' ORDER BY id DESC LIMIT 1), '7', 'I secretly wish I could leave the Dimir and join this guild, but there is no escaping the Dimir.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Reason' ORDER BY id DESC LIMIT 1), '8', 'I chose this guild at random or on a lark.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Dimir Operative'), 'Dimir Guild Spells', 'Prerequisite: Spellcasting or Pact Magic class feature
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Dimir Operative'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I''m good at hiding my true thoughts and feelings.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'When I''m in doubt about revealing something, I assume it''s a secret, and I don''t share it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I like to sound mysterious, because wisdom hidden grows deeper with time.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I have no patience with people who get in my way.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I love hearing about other people''s nightmares.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Combat is meant to be quick, clean, and one-sided.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I like to stick to the shadows.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I never show my anger. I just plot my revenge.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Guild. My true guild is all that really matters. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Control. I like pulling the strings. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Secrets. I collect secrets and never reveal them. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Knowledge. I want to know as much as I can about this city and how it works. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Independence. I value the freedom to pursue my own goals without interference. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Nihilism. I don''t believe in anything, and anyone who does is a fool. (Neutral)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Dimir Operative'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I discovered a secret I can''t let anyone else uncover—including my guild superiors.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I formed a close friendship or romance with someone in the guild I''m infiltrating.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'The Dimir agent who recruited me was unmasked and killed. My revenge on the killers will be thorough and painful.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I spend as much time as I can in the Ismeri Library because I''m certain an information hub operates behind its facade. I want its secrets!');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I''m utterly loyal to my superior in the guild, more than to the guild or its guildmaster.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'Someone has discovered my true identity.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Dimir Operative'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I like secrets so much that I''m reluctant to share details of a plan even with those who need to know.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I would let my friends die rather than reveal my true identity.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I have trouble trusting anyone but myself.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I have a particular vice that puts all my secrets at risk if I''m not careful.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I''m pretty sure I''ve done something horrible that I can''t remember because of the guild''s mind magic.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I put too much trust in the people who give me orders.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Dimir Operative'), 'Contacts', 'Dimir Contact', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Dimir Contact' ORDER BY id DESC LIMIT 1), '1', 'I know a Dimir courier who relays messages to me from someone higher up the chain of command.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Dimir Contact' ORDER BY id DESC LIMIT 1), '2', 'I get orders from a shapeshifter I recognize only through a series of code phrases we exchange.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Dimir Contact' ORDER BY id DESC LIMIT 1), '3', 'An ostentatiously wealthy vampire is my secret guild superior, summoning me to a luxurious estate by means of coded messages.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Dimir Contact' ORDER BY id DESC LIMIT 1), '4', 'I have never met my guild contact, but I receive telepathic messages, usually in my dreams.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Dimir Contact' ORDER BY id DESC LIMIT 1), '5', 'I''ve never met my guild contact, but I get coded messages from a pattern of street lights and graffiti.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Dimir Contact' ORDER BY id DESC LIMIT 1), '6', 'I didn''t discover the identity of my guild contact until after we had begun a romantic relationship.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Dimir Contact' ORDER BY id DESC LIMIT 1), '7', 'My superior maintains an elaborate identity as a young street urchin... unless it''s all a lie, and I''m being sent on ridiculous missions by a twisted child.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dimir Operative') AND title = 'Dimir Contact' ORDER BY id DESC LIMIT 1), '8', 'My sibling and I both get telepathic orders from a mysterious contact, and I''m starting to question the authenticity of my sibling''s orders.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Dimir Operative'), 'How Do I Fit In?', 'As a Dimir adventurer, you are a member of the guild''s network of spies, thieves, assassins, and mind mages that lurks behind the facade of the public guild. On the surface, House Dimir presents the appearance of a network of couriers, investigators, media reporters, and archivists, dealing in information and spreading news. But you and your peers trade in secrets. You use secret symbols, runes, and signals to surreptitiously communicate with other Dimir agents, often in plain sight.
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Golgari Agent', 'You are a member of a teeming horde—one small part of a sprawling organism. Just as you are part of the swarm, the swarm is part of a larger ecosystem, a never-ending cycle of life, death, rot, and rebirth. You have spent your life in the slow churn of that ecosystem, in the dark places of the city where the messy parts of existence are on display. There is little squeamishness among the Golgari, no fear of death or taboo about the dead, just a fierce affirmation of the cycle.', 'Nature, Survival', 'Poisoner''s kit', 'Choose one of Elvish, Giant, or Kraul', 'A Golgari insignia, a poisoner''s kit, a pet beetle or spider, a set of common clothes, and a belt pouch containing 10 gp worth of mixed coins', (SELECT id FROM core_book_sources WHERE raw_source = 'Guildmasters'' Guide to Ravnica p. 53'));
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Golgari Agent'), 'Feature: Undercity Paths', 'You know hidden, underground pathways that you can use to bypass crowds, obstacles, and observation as you move through the city. When you aren''t in combat, you and companions you lead can travel between any two locations in the city twice as fast as your speed would normally allow. The paths of the undercity are haunted by dangers that rarely brave the light of the surface world, so your journey isn''t guaranteed to be safe.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Golgari Agent'), 'Golgari Guild Spells', 'Prerequisite: Spellcasting or Pact Magic class feature
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Golgari Agent'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Remember, I could kill you in your sleep. Or put centipedes in your bedroll.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I like to remind people of their inevitable demise.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Sometimes I give voice to the whispers of the rot, which I hear but no one else does.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I do my best to discourage anyone from approaching or talking to me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I have accepted my death. Hence, I don''t fear it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Like roots growing through stone, I am relentless and determined in my action.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I put my knowledge of anatomy to use by narrating the injuries my enemies suffer in grisly detail.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'Like a wild animal, I lash out viciously when I''m provoked—and I''m easily provoked.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Golgari Agent'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Guild. My guild is all that really matters. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Stoicism. All of us are part of the cyclical march of nature, which will continue with or without us. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Nature. The natural world is more important than the edifices of the city and civilization. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Interdependence. We are all part of nature''s web. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Ambition. The time of Golgari ascendance is at hand, and I intend to have a prominent place in the new world order. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Live and Let Live. Meddling in the affairs of other guilds is a great way to get squashed like a bug. (Neutral)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Golgari Agent'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I cherish the finger of a family member who was petrified by a medusa.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I have an identical twin who is as different from me as any person could be.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I want to lead one faction of the guild to a new position of dominance.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I love spending time in the moss-covered building where I took part in my first reclamation mission.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I found something in the sewer that must never come to light.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I am forever grateful to the reclaimer who found me floating facedown in the sewer, moments from death.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Golgari Agent'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'Death comes for us all, so you can''t expect me to take care of someone who can''t fight it off.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I assume that anyone outside the Golgari looks down on me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I feel a need for revenge against those who enjoy the privilege of living above ground.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I don''t bother to couch my opinions in flattering words.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I can''t help but pocket any trinket or coin I come across, no matter how worthless.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I''m convinced that I''m better and stronger than members of other guilds, isolated as they are from the realities of life and death.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Golgari Agent'), 'Contacts', 'Non-Golgari Contact', '1d10');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Non-Golgari Contact' ORDER BY id DESC LIMIT 1), '1', 'One of my parents is an elite assassin, a member of the Ochran.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Non-Golgari Contact' ORDER BY id DESC LIMIT 1), '2', 'I learned combat from a kraul.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Non-Golgari Contact' ORDER BY id DESC LIMIT 1), '3', 'I know a medusa who is stationed in the guildhall.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Non-Golgari Contact' ORDER BY id DESC LIMIT 1), '4', 'I had a torrid romance with a spore druid responsible for a large rot farm.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Non-Golgari Contact' ORDER BY id DESC LIMIT 1), '5', 'There''s a troll in a remote area of the undercity who seems to find me interesting—and who knows more than you''d think.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Non-Golgari Contact' ORDER BY id DESC LIMIT 1), '6', 'An elf lich is determined to see me become a lich someday, too.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Non-Golgari Contact' ORDER BY id DESC LIMIT 1), '7', 'A medusa decided it would be more fun to recruit me into the guild than to kill me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Non-Golgari Contact' ORDER BY id DESC LIMIT 1), '8', 'I know a findbroker who can locate just about anything, for the right price.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Non-Golgari Contact' ORDER BY id DESC LIMIT 1), '1', 'An Azorius arrester I literally pulled out of the gutter will do anything for me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Non-Golgari Contact' ORDER BY id DESC LIMIT 1), '2', 'Someone joined the Gruul in a battle against the Boros once, and the sergeant of that Boros squad would love to prove that it was me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Non-Golgari Contact' ORDER BY id DESC LIMIT 1), '3', 'I had a romance with a Dimir agent whom I still feed secrets to.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Non-Golgari Contact' ORDER BY id DESC LIMIT 1), '4', 'Roll an additional Golgari contact; you can decide if the contact is an ally or a rival.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Non-Golgari Contact' ORDER BY id DESC LIMIT 1), '5', 'I joined the Gruul in a battle against the Boros once, and the chief of that small clan thanks me for turning the tide.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Non-Golgari Contact' ORDER BY id DESC LIMIT 1), '6', 'An Izzet scientist resents that I sold a scrapped invention I found in the sewer.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Non-Golgari Contact' ORDER BY id DESC LIMIT 1), '7', 'My undercity explorations led me into an Orzhov vault, and a spirit thinks I stole something valuable.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Non-Golgari Contact' ORDER BY id DESC LIMIT 1), '8', 'I found a baby beast and sold it to a Rakdos wrangler who remains grateful to me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Non-Golgari Contact' ORDER BY id DESC LIMIT 1), '9', 'A Selesnya druid and I share an interest in the same garden, and we have enjoyable arguments there.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Golgari Agent') AND title = 'Non-Golgari Contact' ORDER BY id DESC LIMIT 1), '10', 'I regularly pick up refuse from beneath a Simic laboratory, and sometimes I talk to the researcher who dumps it there.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Golgari Agent'), 'How Do I Fit In?', 'As part of the Golgari Swarm, you are a specialized instrument of the greater body. Your orders, when you have such, come from the guildmaster by way of his chancellors, who carry his messages throughout the guild. The swarm relies on you to advance the greater good by protecting some part, however small, of its teeming existence. That responsibility doesn''t mean you''re indispensable; your eventual death is part of your purpose and function, too, and you''ll be replaced even as your body provides nutrients to further the swarm''s growth.
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Gruul Anarch', 'The entire world of Ravnica is a sprawling metropolis, but you are a native of the wild areas that still exist—the abandoned districts, the rubblebelts, the overgrown parks, and the crumbling ruins. You are part of a savage society that clings desperately to the Old Ways—attuned to nature, full of primal rage, and given short shrift by a world consumed with continuing civilization''s march of progress.', 'Animal Handling, Athletics', 'Herbalism kit', 'Choose one of Draconic, Giant, Goblin, or Sylvan', 'A Gruul insignia, a hunting trap, an herbalism kit, the skull of a boar, a beast-hide cloak, a set of traveler''s clothes, and a belt pouch containing 10 gp (Azorius 1-zino coins)', (SELECT id FROM core_book_sources WHERE raw_source = 'Guildmasters'' Guide to Ravnica p. 60'));
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Gruul Anarch'), 'Feature: Rubblebelt Refuge', 'You are intimately familiar with areas of the city that most people shun: ruined neighborhoods where wurms rampaged, overgrown parks that no hand has tended in decades, and the vast, sprawling rubblebelts of broken terrain that civilized folk have long abandoned. You can find a suitable place for you and your allies to hide or rest in these areas. In addition, you can find food and fresh water in these areas for yourself and up to five other people each day.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Gruul Anarch'), 'Gruul Guild Spells', 'Prerequisite: Spellcasting or Pact Magic class feature
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Gruul Anarch'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Unlike people, the beasts of the wild are friends who won''t stab me in the back.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Go ahead and insult me—I dare you.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I scorn those who can''t survive away from the comforts of the city.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Don''t tell me I''m not allowed to do something.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Laws are for people who are afraid to face their inner beasts.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I smear the blood of my enemies over my skin.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I was, in fact, raised by maaka.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'HarrRRAAGGHH! [I rarely form a coherent sentence and prefer to express myself by breaking things.]');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Gruul Anarch'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Clan. My clan is all that really matters. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Anarchy. No person or law or custom can tell another what to do. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Nature. We weren''t born tame or domesticated, so we shouldn''t have to live that way. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Might. The strongest are meant to dominate the weak. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Rage. AAAAAARRRRggggh! [To live is to feel and express the rage burning in your belly.] (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Tradition. The Old Ways must be preserved and upheld. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Gruul Anarch'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I am determined that one day I will lead my clan—or a new one.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I would give my life for my clan chieftain.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'The chieftain of another clan has a grudge against me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I am devoted to a sacred site in the midst of the rubblebelt.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'My weapon is made from the first raktusk I ever hunted.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'GrrrRRAAAAGGHH! [I will do anything to prove myself greater than my siblings or ancestors.]');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Gruul Anarch'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'If you question my courage, I will never back down.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'HrrrGGGAAAARRuuuh! [My anger in battle led to the death of a loved one.]');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I''m as stubborn as a batterboar.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I''m so convinced of my superiority over soft, civilized people that I''ll take great risks to prove it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I''m easily manipulated by people I find attractive.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I''m not actually all that angry.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Gruul Anarch'), 'Contacts', 'Non-Gruul Contact', '1d10');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Non-Gruul Contact' ORDER BY id DESC LIMIT 1), '1', 'One of my parents is a renowned warrior in my clan.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Non-Gruul Contact' ORDER BY id DESC LIMIT 1), '2', 'My sibling has the ear of the clan chief.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Non-Gruul Contact' ORDER BY id DESC LIMIT 1), '3', 'I have cousins in a different clan.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Non-Gruul Contact' ORDER BY id DESC LIMIT 1), '4', 'When we were younger, I was romantically involved with a prominent warrior in my clan.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Non-Gruul Contact' ORDER BY id DESC LIMIT 1), '5', 'A druid in my clan believes I have a destiny to fulfill.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Non-Gruul Contact' ORDER BY id DESC LIMIT 1), '6', 'The warrior who trained me remembers me for my exceptional potential.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Non-Gruul Contact' ORDER BY id DESC LIMIT 1), '7', 'My clan chief killed one of my parents, who had challenged the chief for leadership of the clan. Some combination of resentment and remorse stirs the clan chief to help me sometimes.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Non-Gruul Contact' ORDER BY id DESC LIMIT 1), '8', 'I made a strong impression on Borborygmos.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Non-Gruul Contact' ORDER BY id DESC LIMIT 1), '1', 'An Azorius arrester thinks I can be reformed.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Non-Gruul Contact' ORDER BY id DESC LIMIT 1), '2', 'A Boros soldier gives me gifts in exchange for information about other clans'' movements.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Non-Gruul Contact' ORDER BY id DESC LIMIT 1), '3', 'I once caught and released a Dimir spy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Non-Gruul Contact' ORDER BY id DESC LIMIT 1), '4', 'I consult with a Golgari shaman for spiritual guidance at times.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Non-Gruul Contact' ORDER BY id DESC LIMIT 1), '5', 'Roll an additional Gruul contact; you can decide if the contact is an ally or a rival.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Non-Gruul Contact' ORDER BY id DESC LIMIT 1), '6', 'An Izzet scientist blames the Gruul for the destruction of his life''s work in a raid, but seems to think that I''m not like other Gruul.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Non-Gruul Contact' ORDER BY id DESC LIMIT 1), '7', 'I foolishly borrowed money from an Orzhov syndic to indulge a shameful vice.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Non-Gruul Contact' ORDER BY id DESC LIMIT 1), '8', 'A close friend left our clan and joined the Cult of Rakdos.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Non-Gruul Contact' ORDER BY id DESC LIMIT 1), '9', 'A distant relative is trying to recruit me into the Selesnya Conclave.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gruul Anarch') AND title = 'Non-Gruul Contact' ORDER BY id DESC LIMIT 1), '10', 'I stopped a Simic biomancer from trapping wild beasts to perform vile experiments on them.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Gruul Anarch'), 'How Do I Fit In?', 'In service of a simple goal, you have a simple part to play: Fight. Unleash your rage. Flatten buildings and defeat those who stand in your way. Be Gruul, in your own way.
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Izzet Engineer', 'Armed with an inventive intellect, a love of magical technology, and an unquenchable energy, you are an enthusiastic participant in the research work of the Izzet League. Though you''re likely to begin your career as a mere attendant, you can aspire to become a skilled mage or alchemist, a laboratory supervisor, or even a flamethrower-wielding scorchbringer tasked with defending Izzet laboratories. The Izzet are obsessive, brilliant, inspired, and an unpredictable force of chaos in Ravnica, and you epitomize all of those qualities.', 'Arcana, Investigation', 'One type of artisan''s tools', 'Choose one of Draconic, Goblin, or Vedalken', 'An Izzet insignia, one set of artisan''s tools, the charred and twisted remains of a failed experiment, a hammer, a block and tackle, a set of common clothes, and a belt pouch containing 5 gp (Azorius 1-zino coins)', (SELECT id FROM core_book_sources WHERE raw_source = 'Guildmasters'' Guide to Ravnica p. 66'));
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Izzet Engineer'), 'Feature: Urban Infrastructure', 'The popular conception of the Izzet League is based on mad inventions, dangerous experiments, and explosive blasts. Much of that perception is accurate, but the league is also involved with mundane tasks of construction and architecture—primarily in crafting the infrastructure that allows Ravnicans to enjoy running water, levitating platforms, and other magical and technological wonders.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Izzet Engineer'), 'Izzet Guild Spells', 'Prerequisite: Spellcasting or Pact Magic class feature
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Izzet Engineer'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I have a hard time staying focused on... oh, and my brain tends to jump from one... did I mention focus?');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I get really excited about my ideas and I can''t wait to talk about them and start putting them into practice and tinkering with them and I want to tell you about how exciting it all is!');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'It''s not magic—or anything, really—if you do it only halfway. Whatever I do, I give it all I''ve got.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I do what my gut tells me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Life''s an experiment, and I can''t wait to see what happens.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I pepper my speech with the incomprehensible jargon of my trade, like mizzium droplets inserted into a weird-field suspension.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'Great ideas are fine, but great results are what counts.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'If you can guess what I''m about to do, that means I''ve run out of imagination.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Izzet Engineer'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Guild. My guild is all that really matters. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Creativity. Half the world''s troubles come from stodgy thinking, stuck in the past. We need innovative solutions. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Discovery. Every experiment has the potential to reveal more secrets of the multiverse. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Science. A rigorous application of logical principles and protocols will lead us toward progress more surely than any belief system. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Fun. I love my job! Despite the dangerous working conditions, there''s nothing I''d rather do. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Power. Someday I''ll find or create the magic that will make me the most powerful being in Ravnica. (Evil)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Izzet Engineer'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I have dedicated my life to finding a solution to a scientific problem.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I''ll never forget the laboratory where I learned my skills, or the other attendants who learned alongside me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I''m convinced it was sabotage that destroyed my first laboratory and killed many of my friends, and I seek revenge against whoever did it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I have the schematics for an invention that I hope to build one day, once I have the necessary resources.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'A fellow student and I are racing to solve the same scientific puzzle.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I would do anything the guildmaster told me to do.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Izzet Engineer'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'If there''s a plan, I''ll probably forget it. If I don''t forget it, I''ll probably ignore it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I get bored easily, and if nothing is happening I''ll make something happen.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'Nothing is ever simple, and if it seems simple, I''ll find a way to make it complicated.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I tend to ignore sleep for days when I''m conducting research, often at the expense of my own health and safety.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I''m convinced there''s not a soul in Ravnica, except maybe the great Niv-Mizzet, who can match my boundless intellect.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I''m incapable of admitting a flaw in my logic.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Izzet Engineer'), 'Contacts', 'Non-Izzet Contact', '1d10');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Non-Izzet Contact' ORDER BY id DESC LIMIT 1), '1', 'An older relative is a member of the guild''s board of directors.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Non-Izzet Contact' ORDER BY id DESC LIMIT 1), '2', 'I know a sprite who carries important messages among the guild''s laboratories.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Non-Izzet Contact' ORDER BY id DESC LIMIT 1), '3', 'A sibling is the head of a laboratory doing exotic research.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Non-Izzet Contact' ORDER BY id DESC LIMIT 1), '4', 'A former colleague is now an attendant in a laboratory in the central guildhall.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Non-Izzet Contact' ORDER BY id DESC LIMIT 1), '5', 'I''m in regular communication with an instructor who set me on the course of my life and research.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Non-Izzet Contact' ORDER BY id DESC LIMIT 1), '6', 'I had a romance with a chemister working in the Blistercoils.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Non-Izzet Contact' ORDER BY id DESC LIMIT 1), '7', 'As an attendant, I had a fierce rivalry with another attendant for our supervisor''s attention.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Non-Izzet Contact' ORDER BY id DESC LIMIT 1), '8', 'The guildmaster, Niv-Mizzet, took note of one of my experiments!');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Non-Izzet Contact' ORDER BY id DESC LIMIT 1), '1', 'An Azorius inspector seems interested in my work.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Non-Izzet Contact' ORDER BY id DESC LIMIT 1), '2', 'I was ready to join the Boros before I decided on Izzet, and I sometimes still hear from the sergeant who tried to recruit me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Non-Izzet Contact' ORDER BY id DESC LIMIT 1), '3', 'One of my former assistants turned out to be a Dimir spy. We''re not on friendly terms anymore, but we have a habit of running into each other.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Non-Izzet Contact' ORDER BY id DESC LIMIT 1), '4', 'A Golgari assassin killed a bitter rival of mine, leaving me with conflicted feelings.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Non-Izzet Contact' ORDER BY id DESC LIMIT 1), '5', 'I helped a minor Gruul chieftain acquire an Izzet weapon.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Non-Izzet Contact' ORDER BY id DESC LIMIT 1), '6', 'Roll an additional Izzet contact; you can decide if the contact is an ally or a rival.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Non-Izzet Contact' ORDER BY id DESC LIMIT 1), '7', 'An Orzhov banker financed my laboratory''s current work and expects great returns.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Non-Izzet Contact' ORDER BY id DESC LIMIT 1), '8', 'I have a cousin in the Cult of Rakdos, and we get along quite well.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Non-Izzet Contact' ORDER BY id DESC LIMIT 1), '9', 'A former attendant from the same laboratory ran off to join the Selesnya, and we get into a big argument every time we run into each other.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Izzet Engineer') AND title = 'Non-Izzet Contact' ORDER BY id DESC LIMIT 1), '10', 'I compare notes and techniques with a Simic scientist over lunch sometimes.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Izzet Engineer'), 'How Do I Fit In?', 'Whatever your role in the Izzet League, you are expected to contribute to its research in some way. That contribution might involve participating in tests, whether as an assistant, a researcher, or a subject. You might be one of the soldiers who protects a laboratory, or a laborer responsible for lifting heavy pieces of equipment into place. Everyone''s contribution matters, even if the Izzet know that some matter more than others.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Orzhov Representative', 'The prospect of immense wealth is the promise of membership in the Orzhov Syndicate. All of the guild''s endeavors channel wealth from Ravnican society into the ranks of the Orzhov—and concentrates the spoils at the top of the hierarchy. As a functionary in that system, your best hope is to claim as much as possible of the money that passes through your hands on its way up, so that you can work your way into a more prominent position.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Orzhov Representative'), 'Feature: Leverage', 'You can exert leverage over one or more individuals below you in the guild''s hierarchy and demand their help as needs warrant. For example, you can have a message carried across a neighborhood, procure a short carriage ride without paying, or have others clean up a bloody mess you left in an alley. The DM decides if your demands are reasonable and if there are subordinates available to fulfill them. As your status in the guild improves, you gain influence over more people, including ones in greater positions of power.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Orzhov Representative'), 'Orzhov Guild Spells', 'Prerequisite: Spellcasting or Pact Magic class feature
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Orzhov Representative'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I am always willing to act in accordance with the financial incentive offered.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Debts are never meant to be forgiven.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I am accustomed to enjoying the finest pleasures money can buy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'No one could doubt that I am a cut above the masses of pitiful peasants that infest the city.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I can''t stand to spend a zib more than necessary to purchase what I need.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I hate it when people try to make light of a serious situation.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I want to make sure everyone is aware of how wealthy, powerful, and important I am.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I can''t think of anything to look forward to.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Orzhov Representative'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Guild. My guild is all that really matters. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Wealth. I will do whatever it takes to become as rich as the oligarchs. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Power. One day, I will be the one giving orders. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Prestige. I want to be admired, respected, feared, or even hated for my position and wealth. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Stability. The economy functions best when chaos is kept under control and everyone knows their place. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Eternity. I want to live forever—in the flesh as long as possible, and as a spirit afterward. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Orzhov Representative'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'The unbearable weight of my debt has driven me to desperation.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I''m duty-bound to obey the dictates of an ancestor on the Ghost Council.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I value my worldly goods more highly than my mortal life.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'An oligarch publicly humiliated me, and I will exact revenge on that whole family.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'My faith in the Obzedat never wavers.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I want to prove myself more worthy than an older sibling and thereby ensure that I inherit a greater share of my parents'' wealth.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Orzhov Representative'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I hold a scandalous secret that could ruin my family forever—but could also earn me the favor of the Ghost Council.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I''m convinced that everyone I know is plotting against me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I''ll brave any risk if the monetary reward is great enough.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I am convinced that I am far more important than anyone else is willing to acknowledge.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I have little respect for anyone who isn''t wealthy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I''ll take any opportunity to steal from wealthier people, even for worthless trinkets.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Orzhov Representative'), 'Contacts', 'Non-Orzhov Contact', '1d10');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Non-Orzhov Contact' ORDER BY id DESC LIMIT 1), '1', 'The spirit of an ancestor has taken an interest in me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Non-Orzhov Contact' ORDER BY id DESC LIMIT 1), '2', 'An older cousin has the ear of a powerful oligarch.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Non-Orzhov Contact' ORDER BY id DESC LIMIT 1), '3', 'I know a knight who is responsible for collecting debts from powerful people.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Non-Orzhov Contact' ORDER BY id DESC LIMIT 1), '4', 'A vampire pontiff tried to use me as a pawn in past schemes.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Non-Orzhov Contact' ORDER BY id DESC LIMIT 1), '5', 'A silent spirit follows me around.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Non-Orzhov Contact' ORDER BY id DESC LIMIT 1), '6', 'A sibling has keys to parts of Vizkopa Bank.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Non-Orzhov Contact' ORDER BY id DESC LIMIT 1), '7', 'A giant thinks I''m adorable.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Non-Orzhov Contact' ORDER BY id DESC LIMIT 1), '8', 'I regularly offer tribute to an angel, and the angel has been kind to me in turn.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Non-Orzhov Contact' ORDER BY id DESC LIMIT 1), '1', 'An Azorius arrester is always snooping into my family''s business transactions.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Non-Orzhov Contact' ORDER BY id DESC LIMIT 1), '2', 'A Boros paladin saved my life, to my everlasting shame.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Non-Orzhov Contact' ORDER BY id DESC LIMIT 1), '3', 'I know a shopkeeper who is secretly a Dimir agent and tries to make sure that I keep that secret hidden.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Non-Orzhov Contact' ORDER BY id DESC LIMIT 1), '4', 'I''m fascinated by the culture of the Golgari kraul, and I have formed a friendship with one of their death priests.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Non-Orzhov Contact' ORDER BY id DESC LIMIT 1), '5', 'A Gruul druid hates me but would never dare to touch me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Non-Orzhov Contact' ORDER BY id DESC LIMIT 1), '6', 'I know an Izzet engineer who is desperate to pay off a debt accrued by a deceased relative.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Non-Orzhov Contact' ORDER BY id DESC LIMIT 1), '7', 'Roll an additional Orzhov contact; you can decide if the contact is an ally or a rival.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Non-Orzhov Contact' ORDER BY id DESC LIMIT 1), '8', 'My childhood friend is now a Rakdos torturer. We still meet for drinks occasionally.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Non-Orzhov Contact' ORDER BY id DESC LIMIT 1), '9', 'I have the key to a vault where a Selesnya druid is hiding an item of secret shame.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Orzhov Representative') AND title = 'Non-Orzhov Contact' ORDER BY id DESC LIMIT 1), '10', 'I was married to a Simic bioengineer.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Orzhov Representative'), 'How Do I Fit In?', 'The structure of the Orzhov Syndicate means that you are always doing the bidding of someone higher up the ladder than you are. Ultimately, your role in the guild is defined by whatever the people (and spirits) above you decide for you.
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Rakdos Cultist', 'You''re an entertainer at heart, a performer with a flair for the dramatic and a love of the spotlight. You''ve spent years honing your craft, mastering a demanding set of skills so you can perform them with panache and make them look easy. The wild applause, the screams of the crowds, the mayhem and terror spreading into the streets—this is what you live for.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist'), 'A Flair for the Dramatic', 'Type of Performer', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Type of Performer' ORDER BY id DESC LIMIT 1), '1', 'Spikewheel acrobat');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Type of Performer' ORDER BY id DESC LIMIT 1), '2', 'Lampooning satirist');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Type of Performer' ORDER BY id DESC LIMIT 1), '3', 'Fire juggler');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Type of Performer' ORDER BY id DESC LIMIT 1), '4', 'Marionette puppeteer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Type of Performer' ORDER BY id DESC LIMIT 1), '5', 'Pain artist');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Type of Performer' ORDER BY id DESC LIMIT 1), '6', 'Noise musician');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Type of Performer' ORDER BY id DESC LIMIT 1), '7', 'Nightmare clown');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Type of Performer' ORDER BY id DESC LIMIT 1), '8', 'Master of ceremonies');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist'), 'Feature: Fearsome Reputation', 'People recognize you as a member of the Cult of Rakdos, and they''re careful not to draw your anger or ridicule. You can get away with minor criminal offenses, such as refusing to pay for food at a restaurant or breaking down a door at a local shop, if no legal authorities witness the crime. Most people are too daunted by you to report your wrongdoing to the Azorius.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist'), 'Rakdos Guild Spells', 'Prerequisite: Spellcasting or Pact Magic class feature
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I revel in mayhem, the more destructive the better.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'When violence breaks out, I lose myself in rage, and it''s sometimes hard to stop.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Everything is funny to me, and the most hilarious and bloodiest things leave me cackling with sadistic glee.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I derive genuine pleasure from the pain of others.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I enjoy testing other people''s patience.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I can''t stand it when things are predictable, so I like to add a little chaos to every situation.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I throw my weight around to make sure I get my way.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I enjoy breaking delicate works of art. And fingers, which are sort of the same.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Guild. My guild is all that really matters. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Hedonism. Death comes for everyone, so take as much pleasure as you can from every moment of life. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Creativity. I strive to find more ways to express my art through pain—my own as well as others''. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Freedom. No one tells me what to do. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Equality. I want to see Ravnica remade, with no guilds and no hierarchies. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Spectacle. People are inspired by the greatness they see in art. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I have belonged to the same performance troupe for years, and these people mean everything to me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'A blood witch told me I have a special destiny to fulfill, and I''m trying to figure out what it is.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I''m secretly hoping that I can change the cult from the inside, using my influence to help rein in the wanton violence.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I own something that Rakdos once touched (it''s seared black at the spot), and I cherish it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I want to be better at my chosen form of performance than any other member of my troupe.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I am devoted to Rakdos and live to impress him.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'My family is prominent in another guild. I enjoy my wild life, but I don''t want to embarrass them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I couldn''t hide my emotions and opinions even if I wanted to.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I throw caution to the wind.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I resent the rich and powerful.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'When I''m angry, I lash out in violence.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'There''s no such thing as too much pleasure.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist'), 'Contacts', 'Non-Rakdos Contact', '1d10');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Non-Rakdos Contact' ORDER BY id DESC LIMIT 1), '1', 'I was part of a two-person act until my former partner moved to a different troupe.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Non-Rakdos Contact' ORDER BY id DESC LIMIT 1), '2', 'My sibling and I ran away from home and joined the Cult of Rakdos together. We''re very close.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Non-Rakdos Contact' ORDER BY id DESC LIMIT 1), '3', 'A childhood friend of mine is an attendant in Rix Maadi, the Rakdos guildhall.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Non-Rakdos Contact' ORDER BY id DESC LIMIT 1), '4', 'My parents brought me into the guild and taught me my trade.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Non-Rakdos Contact' ORDER BY id DESC LIMIT 1), '5', 'There''s a lesser demon in the cult who thinks he owes me a favor, and who am I to argue?');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Non-Rakdos Contact' ORDER BY id DESC LIMIT 1), '6', 'The master of ceremonies in my troupe is well connected with other troupes.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Non-Rakdos Contact' ORDER BY id DESC LIMIT 1), '7', 'I had a romance with a pain artist in another troupe.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Non-Rakdos Contact' ORDER BY id DESC LIMIT 1), '8', 'Rakdos himself has witnessed me perform.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Non-Rakdos Contact' ORDER BY id DESC LIMIT 1), '1', 'I know an Azorius elocutor who has a very amusing dark side.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Non-Rakdos Contact' ORDER BY id DESC LIMIT 1), '2', 'A Boros captain really wants to "redeem" me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Non-Rakdos Contact' ORDER BY id DESC LIMIT 1), '3', 'I think a member of my troupe is a Dimir agent.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Non-Rakdos Contact' ORDER BY id DESC LIMIT 1), '4', 'I once convinced a Golgari medusa to participate in a show. We''ve been on good terms ever since.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Non-Rakdos Contact' ORDER BY id DESC LIMIT 1), '5', 'I came from the Gruul and still have relatives there.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Non-Rakdos Contact' ORDER BY id DESC LIMIT 1), '6', 'An Izzet technician provides pyrotechnics for my performances.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Non-Rakdos Contact' ORDER BY id DESC LIMIT 1), '7', 'An Orzhov oligarch has taken an interest in my career, like a patron of the arts.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Non-Rakdos Contact' ORDER BY id DESC LIMIT 1), '8', 'Roll an additional Rakdos contact; you can decide if the contact is an ally or a rival.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Non-Rakdos Contact' ORDER BY id DESC LIMIT 1), '9', 'A Selesnya healer attends my performances regularly.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist') AND title = 'Non-Rakdos Contact' ORDER BY id DESC LIMIT 1), '10', 'A Simic biomancer provides mutant monsters to add a taste of the bizarre to our shows.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Rakdos Cultist'), 'How Do I Fit In?', 'The Cult of Rakdos encourages independent action on the part of its members. Its goal is fomenting chaos, and it firmly believes in putting its own house in disorder before carrying that mission into the larger city. So your role is to execute your vision of grand satire and disruptive performance art, as you aspire to outdo your guild mates and attract the attention of Rakdos himself.
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Selesnya Initiate', 'You are a member of a blessed community, built on the ideals of harmony. Here, nature and civilization coexist peacefully, living and growing in accordance with the will of the Worldsoul, Mat''Selesnya. As a member of the Selesnya Conclave, you are surrounded by people and other creatures who share your worldview and your longing for deeper spiritual communion with the world, and you have a fervent desire to share the joy you have experienced with Ravnica. Along with the rest of the conclave, you are committed to resisting the ambitions of the other guilds—with military force if necessary.', 'Nature, Persuasion', 'One type of artisan''s tools or one musical instrument', 'Choose one of Elvish, Loxodon, or Sylvan', 'A Selesnya insignia, a healer''s kit, robes, a set of common clothes, and a belt pouch containing 5 gp (Azorius 1-zino coins)', (SELECT id FROM core_book_sources WHERE raw_source = 'Guildmasters'' Guide to Ravnica p. 86'));
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate'), 'Feature: Conclave''s Shelter', 'As a member of the Selesnya Conclave, you can count on your guild mates to provide shelter and aid. You and your companions can find a place to hide or rest in any Selesnya enclave in the city, unless you have proven to be a danger to them. The members of the enclave will shield you from the law or anyone else searching for you, though they will not risk their lives in this effort.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate'), 'Selesnya Guild Spells', 'Prerequisite: Spellcasting or Pact Magic class feature
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I never raise my voice or lose my temper.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I feel the pains and joys of everyone around me, friend or foe.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I would rather make a friend than thwart an enemy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I''m always straining to peer into another reality that seems to be just beyond my senses.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I''m uneasy if I can''t see plants growing or feel soil beneath my feet.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Seeing illness or injury in any creature saddens me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I have much to be proud of, but I am still just one strand in the grand, interwoven tapestry of life.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'Nature offers rich and abundant metaphors for understanding the complexities of life.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Guild. My guild is all that really matters. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Harmony. Nothing is more beautiful than voices and actions aligned in common purpose. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Order. Like a well-pruned tree, society thrives when everything is kept in good order. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Life. Preserving life and nature is always a worthwhile endeavor. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Humility. Ambition and pride are the roots of strife. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Evangelism. When all have joined the Selesnya Conclave, Ravnica will finally know peace. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'I would give my life in the defense of the small enclave where I first encountered Mat''Selesnya.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'I love beasts and plants of all kinds, and am loath to harm them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'A healer nursed me to recovery from a mortal illness.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'I''ll sing the invitation of Mat''Selesnya with my dying breath.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'I cherish a leaf from Vitu-Ghazi that changes color with the seasons, even though it is no longer attached to the tree.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Every member of the conclave is my kin, and I would fight for any one of them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'I''m terrified of getting into a fight where my side is outnumbered.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'I assume that people mean well until they prove otherwise.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'I enjoy comfort and quiet, and prefer to avoid extra effort.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'I have a fierce temper that doesn''t reflect the inner calm I seek.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'I''m convinced that everyone else in the conclave has a deeper connection to the Worldsoul than I do.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'I''m trying to atone for the life of crime I led before I joined the Selesnya, but I find it hard to give up my bad habits.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate'), 'Contacts', 'Non-Selesnya Contact', '1d10');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Non-Selesnya Contact' ORDER BY id DESC LIMIT 1), '1', 'A wise centaur trainer believed in me even though I was a terrible student.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Non-Selesnya Contact' ORDER BY id DESC LIMIT 1), '2', 'A good friend has risen to become a Ledev guardian.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Non-Selesnya Contact' ORDER BY id DESC LIMIT 1), '3', 'I left my former guild and joined the Selesnya along with a close friend.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Non-Selesnya Contact' ORDER BY id DESC LIMIT 1), '4', 'The dryad at the head of my enclave has taken an interest in my activities.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Non-Selesnya Contact' ORDER BY id DESC LIMIT 1), '5', 'A sibling is an instructor at the guild''s training grounds.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Non-Selesnya Contact' ORDER BY id DESC LIMIT 1), '6', 'One of my parents is a votary, tasked with protecting the temple gardens at the Vitu-Ghazi guildhall.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Non-Selesnya Contact' ORDER BY id DESC LIMIT 1), '7', 'I had a romance with a well-known Selesnya healer.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Non-Selesnya Contact' ORDER BY id DESC LIMIT 1), '8', 'Trostani, the head of the guild and the voice of Mat''Selesnya, once welcomed me into her presence.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Non-Selesnya Contact' ORDER BY id DESC LIMIT 1), '1', 'I left the Azorius, and a former colleague still resents me for that act.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Non-Selesnya Contact' ORDER BY id DESC LIMIT 1), '2', 'A good friend, eager for action, left the Selesnya and joined the Boros.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Non-Selesnya Contact' ORDER BY id DESC LIMIT 1), '3', 'I had a relationship with a guild mate who turned out to be a Dimir agent.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Non-Selesnya Contact' ORDER BY id DESC LIMIT 1), '4', 'I know a disgruntled Golgari assassin who is ripe for recruitment.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Non-Selesnya Contact' ORDER BY id DESC LIMIT 1), '5', 'I''m friendly with a Gruul centaur who almost joined us a few years back.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Non-Selesnya Contact' ORDER BY id DESC LIMIT 1), '6', 'I once had a heated public argument with an Izzet chemister, and neither of us is allowed back into that restaurant.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Non-Selesnya Contact' ORDER BY id DESC LIMIT 1), '7', 'I paid off my debt to the Orzhov Syndicate, but my good friend was not so lucky and remains indebted to that guild.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Non-Selesnya Contact' ORDER BY id DESC LIMIT 1), '8', 'At a time of terrible grief in my life, a Rakdos performer made a mockery of my pain, leaving me with mixed feelings of sadness and humor.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Non-Selesnya Contact' ORDER BY id DESC LIMIT 1), '9', 'Roll an additional Selesnya contact; you can decide if the contact is an ally or a rival.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate') AND title = 'Non-Selesnya Contact' ORDER BY id DESC LIMIT 1), '10', 'I have a sibling in the Simic Combine, and we argue every time we see each other.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Selesnya Initiate'), 'How Do I Fit In?', 'Although the guild teaches the importance of subjugating the individual will to the good of the conclave, it also celebrates the diversity of individuals, in the same sense that a field that produces different crops is healthier than one that yields a single crop. That principle applies to your skills as an adventurer. As long as your efforts are directed toward advancing the goals of the guild rather than your private agenda, you''re allowed to put your talents to work in your unique way.
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Simic Scientist', 'Imagine a perfect world: one in which nature and civilization exist in harmony, adapted to each other; one in which life is shaped to match its environment and the environment is shaped to match life. That''s the world you have been trying to grow in the laboratories of the Simic Combine. Nature is all about adaptation, evolution, and balance—but for it to keep up with the pace of advancing civilization, nature needs some help from biomancers and terraformers. If, along the way, you happen to create super-soldiers and mutant monsters that can bolster the combine''s defenses against the schemes and ambitions of the other guilds, so much the better.', 'Arcana, Medicine', '', 'Two of your choice', 'A Simic insignia, a set of common clothes, a book of research notes, an ink pen, a bottle of squid ink, a flask of oil (made from blubber), a vial of acid (derived from digestive juices), a vial of fish scales, a vial of seaweed, a vial of jellyfish stingers, a glass bottle of unidentified slime, and a belt pouch containing 10 gp (Azorius 1-zino coins)', (SELECT id FROM core_book_sources WHERE raw_source = 'Guildmasters'' Guide to Ravnica p. 93'));
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Simic Scientist'), 'Clades and Projects', 'Clade/Project', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Clade/Project' ORDER BY id DESC LIMIT 1), '1', 'Hull Clade, focused on protection and durability');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Clade/Project' ORDER BY id DESC LIMIT 1), '2', 'Fin Clade, focused on movement');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Clade/Project' ORDER BY id DESC LIMIT 1), '3', 'Gyre Clade, focused on cyclical patterns and metamagic');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Clade/Project' ORDER BY id DESC LIMIT 1), '4', 'Guardian Project, focused on creating guard monsters and super soldiers');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Clade/Project' ORDER BY id DESC LIMIT 1), '5', 'Crypsis Project, focused on intelligence and counterintelligence');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Clade/Project' ORDER BY id DESC LIMIT 1), '6', 'Independent research in a new area');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Simic Scientist'), 'Feature: Researcher', 'When you attempt to learn or recall a magical or scientific fact, if you don''t know that information, you know where and from whom you can obtain it. Usually, this information comes from a Simic laboratory, or sometimes from an Izzet facility, a library, a university, or an independent scholar or other learned person or creature. Knowing where the information can be found doesn''t automatically enable you to learn it; you might need to offer bribes, favors, or other incentives to induce people to reveal their secrets.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Simic Scientist'), 'Simic Guild Spells', 'Prerequisite: Spellcasting or Pact Magic class feature
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Simic Scientist'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I can''t wait to see what I become next!');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I am convinced that everything inclines toward constant improvement.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I''m eager to explain every detail of my most intricate experiments and theories to anyone who shows the least bit of interest.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I assume that everyone needs even the most basic concepts explained to them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I describe everything that happens as if it were going into my research notes (and it often is).');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I am insatiably curious about the seemingly infinite forms and adaptations of life.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I can''t resist prying into anything forbidden, since it must be terribly interesting.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I employ a highly technical vocabulary to avoid imprecision and ambiguity in my communication.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Simic Scientist'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Guild. My guild is all that really matters. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Change. All life is meant to progress toward perfection, and our work is to hurry it along—no matter what must be upended along the way. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Knowledge. Understanding the world is more important than what you do with your knowledge. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Greater Good. I want to reshape the world into higher forms of life so that all can enjoy evolution. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Logic. It''s foolish to let emotions and principles interfere with the conclusions of logic. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Superiority. My vast intellect and strength are directed toward increasing my sway over others. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'I helped create a krasis that I love like a pet and would carry with me everywhere... except it''s the size of a building, and it might eat me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'In my laboratory, I discovered something that I think could eliminate half the life on Ravnica.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'The other researchers in my clade are my family—a big, eccentric family including members and parts of many species.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'The laboratory where I did my research contains everything that is precious to me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'I will get revenge on the shortsighted fool who killed my precious krasis creation.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Everything I do is an attempt to impress someone I love.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'I have a rather embarrassing mutation that I do everything I can to keep hidden.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'I''m more interested in taking notes on monstrous anatomy than in fighting monsters.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Every social situation I''m in seems to lead to my asking rude personal questions.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'I''m supremely confident in my ability to adapt to any situation and handle any danger.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'I''ll take any risk to earn recognition for my scientific brilliance.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'I have a tendency to take shortcuts in my research and any other tasks I have to complete.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Simic Scientist'), 'Contacts', 'Non-Simic Contact', '1d10');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Non-Simic Contact' ORDER BY id DESC LIMIT 1), '1', 'My research builds on my parents'' work and takes it in interesting new directions.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Non-Simic Contact' ORDER BY id DESC LIMIT 1), '2', 'If a serious problem confounds me, I can count on my mentor to provide clarity of thought.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Non-Simic Contact' ORDER BY id DESC LIMIT 1), '3', 'A former laboratory colleague went on to work on the Guardian Project.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Non-Simic Contact' ORDER BY id DESC LIMIT 1), '4', 'A former colleague has ventured into fields of research that are possibly immoral and almost certainly illegal.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Non-Simic Contact' ORDER BY id DESC LIMIT 1), '5', 'A former lover is now the supervisor of a prominent clade.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Non-Simic Contact' ORDER BY id DESC LIMIT 1), '6', 'My sibling has become an almost unrecognizable mutant.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Non-Simic Contact' ORDER BY id DESC LIMIT 1), '7', 'An old friend has retreated into a secluded life as an ascetic deepsage, devoted to contemplating philosophical principles.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Non-Simic Contact' ORDER BY id DESC LIMIT 1), '8', 'My former clade supervisor is now engaged in field research studying some of the largest beasts and monsters on Ravnica.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Non-Simic Contact' ORDER BY id DESC LIMIT 1), '1', 'My older sibling is upset that I didn''t follow them into the Azorius.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Non-Simic Contact' ORDER BY id DESC LIMIT 1), '2', 'A Boros sergeant is always asking questions about my work, but I suspect they''re genuinely curious.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Non-Simic Contact' ORDER BY id DESC LIMIT 1), '3', 'A friend in my clade thinks I don''t know they''re a Dimir agent.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Non-Simic Contact' ORDER BY id DESC LIMIT 1), '4', 'I helped a Golgari spore druid with the fertilization and growth of their fungus field.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Non-Simic Contact' ORDER BY id DESC LIMIT 1), '5', 'I can''t fathom what could have made my childhood friend run off and join the Gruul.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Non-Simic Contact' ORDER BY id DESC LIMIT 1), '6', 'I love comparing notes with my friend in the Izzet, though our fields of research are very different.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Non-Simic Contact' ORDER BY id DESC LIMIT 1), '7', 'I borrowed a lot of money from an Orzhov syndic to help finance my research.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Non-Simic Contact' ORDER BY id DESC LIMIT 1), '8', 'A Rakdos ringmaster has taken an interest in my research which, come to think of it, might make a nice sideshow act.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Non-Simic Contact' ORDER BY id DESC LIMIT 1), '9', 'I left the Selesnya—and a lover—behind when I joined the Simic.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Simic Scientist') AND title = 'Non-Simic Contact' ORDER BY id DESC LIMIT 1), '10', 'Roll an additional Simic contact; you can decide if the contact is an ally or a rival.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Simic Scientist'), 'How Do I Fit In?', 'As a Simic adventurer, your mission likely aligns with the Adaptationist philosophy; the disagreements and tensions among the guilds will soon erupt into open conflict, and your guild needs your help to ensure that the Simic survive. That help might come in the form of defending against Golgari incursions into Simic zonots or shielding Simic research from Azorius intrusion. It could also involve more subtle, diplomatic work to maintain balance among the guilds, or subterfuge aimed at undermining another guild''s grab for power.
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Fisher', 'You have spent your life aboard fishing vessels or combing the shallows for the bounty of the ocean. Perhaps you were born into a family of fisher folk, working with your kin to feed your village. Maybe the job was a means to an end — a way out of an undesirable circumstance that forced you to take up life aboard a ship. Regardless of how you began, you soon fell in love with the sea, the art of fishing, and the promise of the eternal horizon.', 'History, Survival', '', 'One of your choice', 'Fishing tackle, a net, a favorite fishing lure or oiled leather wading boots, a set of traveler''s clothes, and a belt pouch containing 10 gp', (SELECT id FROM core_book_sources WHERE raw_source = 'Ghosts of Saltmarsh p. 29'));
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Fisher'), 'Feature: Harvest the Water', 'You gain advantage on ability checks made using fishing tackle. If you have access to a body of water that sustains marine life, you can maintain a moderate lifestyle while working as a fisher, and you can catch enough food to feed yourself and up to ten other people each day.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Fisher'), 'Fishing Tale', 'Tale', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Fisher') AND title = 'Tale' ORDER BY id DESC LIMIT 1), '1', 'Lobster Wrestling. You fought in hand-to-hand combat with an immense lobster.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Fisher') AND title = 'Tale' ORDER BY id DESC LIMIT 1), '2', 'It Dragged the Boat. You nearly caught a fish of monstrous size that pulled your boat for miles.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Fisher') AND title = 'Tale' ORDER BY id DESC LIMIT 1), '3', 'Fins of Pure Gold. You caught a sea animal whose fins were made of pure gold, but another fisher stole it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Fisher') AND title = 'Tale' ORDER BY id DESC LIMIT 1), '4', 'Ghost Fish. You are haunted by a ghostly fish that only you can see.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Fisher') AND title = 'Tale' ORDER BY id DESC LIMIT 1), '5', 'Nemesis Clam. A large clam containing a pearl the size of your head claimed one of your fingers before jetting away; one day, you''ll find that clam.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Fisher') AND title = 'Tale' ORDER BY id DESC LIMIT 1), '6', 'It Swallowed the Sun. You once saw a fish leap from the water and turn day into night.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Fisher') AND title = 'Tale' ORDER BY id DESC LIMIT 1), '7', 'Dive into the Abyss. You found yourself in an underwater cave leading to the Abyss, and your luck has been sour ever since.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Fisher') AND title = 'Tale' ORDER BY id DESC LIMIT 1), '8', 'Love Story. You fell in love with a creature of pure water, but your brief romance ended tragically.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Fisher'), 'Saltmarsh Ties', 'Eda Oweland and her family are the informal leaders of the fishing industry in Saltmarsh. You can always count on the Owelands for minor aid and can gain access to Eda if the need is obvious and imminent.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Fisher'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Fisher') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I am unmoved by the wrath of nature.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Fisher') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'My friends are my crew; we sink or float together.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Fisher') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I need long stretches of quiet to clear my head.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Fisher') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Rich folk don''t know the satisfaction of hard work.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Fisher') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I laugh heartily, feel deeply, and fear nothing.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Fisher') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I work hard; nature offers no handouts.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Fisher') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I dislike bargaining; state your price and mean it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Fisher') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'Luck favors me, and I take risks others might not.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Fisher'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Fisher') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Camaraderie. Good people make even the longest voyage bearable. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Fisher') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Luck. Our luck depends on respecting its rules — now throw this salt over your shoulder. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Fisher') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Daring. The richest bounty goes to those who risk everything. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Fisher') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Plunder. Take all that you can and leave nothing for the scavengers. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Fisher') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Balance. Do not fish the same spot twice in a row; suppress your greed, and nature will reward you. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Fisher') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Hard Work. No wave can move a soul hard at work. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Fisher'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Fisher') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I lost something important in the deep sea, and I intend to find it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Fisher') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'Someone else''s greed destroyed my livelihood, and I will be compensated.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Fisher') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I will fish the many famous waters of this land.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Fisher') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'The gods saved me during a terrible storm, and I will honor their gift.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Fisher') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'My destiny awaits me at the bottom of a particular pond in the Feywild.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Fisher') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I must repay my village''s debt.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Fisher'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Fisher') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I am judgmental, especially of those I deem homebodies or otherwise lazy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Fisher') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I become depressed and anxious if I''m away from the sea too long.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Fisher') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I have lived a hard life and find it difficult to empathize with others.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Fisher') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I am inclined to tell long-winded stories at inopportune times.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Fisher') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I work hard, but I play harder.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Fisher') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I am obsessed with catching an elusive aquatic beast, often to the detriment of other pursuits.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Marine', 'You were trained for battle on sandy beaches and rocky shores. You have launched midnight raids from swift ships whose names evoke terror in the hearts of your adversaries. The water is your second home, the rain your shelter, and the crashing waves your battle cry.', 'Athletics, Survival', 'Vehicles (land) and vehicles (water)', '', 'A dagger that belonged to a fallen comrade, a folded flag emblazoned with the symbol of your ship or company, a set of traveler''s clothes, and a belt pouch containing 10 gp', (SELECT id FROM core_book_sources WHERE raw_source = 'Ghosts of Saltmarsh p. 31'));
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Marine'), 'Feature: Steady', 'You can move twice the normal amount of time (up to 16 hours) each day before being subject to the effect of a forced march (see "Travel Pace" in chapter 8 of the Player''s Handbook). Additionally, you can automatically find a safe route to land a boat on shore, provided such a route exists.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Marine'), 'Hardship Endured', 'Hardship', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marine') AND title = 'Hardship' ORDER BY id DESC LIMIT 1), '1', 'Nearly Drowned. You hid underwater to avoid detection by enemies and held your breath for an extremely long time. Just before you would have died, you had a revelation about your existence.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marine') AND title = 'Hardship' ORDER BY id DESC LIMIT 1), '2', 'Captured. You spent months enduring thirst, starvation, and torture at the hands of your enemy, but you never broke.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marine') AND title = 'Hardship' ORDER BY id DESC LIMIT 1), '3', 'Sacrifice. You enabled the escape of your fellow soldiers, but at great cost to yourself. Some of your past comrades may think you''re dead.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marine') AND title = 'Hardship' ORDER BY id DESC LIMIT 1), '4', 'Juggernaut. No reasonable explanation can explain how you survived a particular battle. Every arrow and bolt missed you. You slew scores of enemies single-handedly and led your comrades to victory.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marine') AND title = 'Hardship' ORDER BY id DESC LIMIT 1), '5', 'Stowaway. For days, you hid in the bilge of an enemy ship, surviving on brackish water and foolhardy rats. At the right moment, you crept up to the deck and took over the ship on your own.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marine') AND title = 'Hardship' ORDER BY id DESC LIMIT 1), '6', 'Leave None Behind. You carried an injured marine for miles to avoid capture and death.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Marine'), 'Saltmarsh Ties', 'As a former member of Saltmarsh''s elite marine contingent. you are friends of Tom and Will Stoutly and can turn to them for help and support. Eliander Fireborn, captain of the guard. also makes time to listen to your concerns.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Marine'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marine') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I speak rarely but mean every word I say.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marine') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I laugh loudly and see the humor in stressful situations.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marine') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I prefer to solve problems without violence, but I finish fights decisively.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marine') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I enjoy being out in nature; poor weather never sours my mood.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marine') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I am dependable.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marine') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I am always working on some project or other.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marine') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I become cantankerous and quiet in the rain.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marine') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'When the sea is within my sight, my mood is jovial and optimistic.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Marine'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marine') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Teamwork. Success depends on cooperation and communication. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marine') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Code. The marines'' code provides a solution for every problem, and following it is imperative. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marine') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Embracing. Life is messy. Throwing yourself into the worst of it is necessary to get the job done. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marine') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Might. The strong train so that they might rule those who are weak. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marine') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Bravery. To act when others quake in fear — this is the essence of the warrior. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marine') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Perseverance. No injury or obstacle can turn me from my goal. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Marine'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marine') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I face danger and evil to offset an unredeemable act in my past.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marine') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I. Will. Finish. The. Job.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marine') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I must set an example of hope for those who have given up.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marine') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I''m searching for a fellow marine captured by an elusive enemy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marine') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'Fear leads to tyranny, and both must be eradicated.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marine') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'My commander betrayed my unit, and I will have revenge.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Marine'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marine') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I grow combative and unpredictable when I drink.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marine') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I find civilian life difficult and struggle to say the right thing in social situations.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marine') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'My intensity can drive others away.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marine') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I hold grudges and have difficulty forgiving others.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marine') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I become irrational when innocent people are hurt.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marine') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I sometimes stay up all night listening to the ghosts of my fallen enemies.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Shipwright', 'You have sailed into war on the decks of great ships, patching their hulls with soup bowls and prayers. You once helped build a fishing vessel that single-handedly saved a town from starvation. You have seen a majestic prow in your dreams that you have not been able to replicate in wood. Since childhood, you have loved the water and have been captivated by the many vessels that travel on it.', 'History, Perception', 'Carpenter''s tools, vehicles (water)', '', 'A set of well-loved carpenter''s tools, a blank book, ink (1-ounce bottle), an ink pen, a set of traveler''s clothes, and a leather pouch with 10 gp', (SELECT id FROM core_book_sources WHERE raw_source = 'Ghosts of Saltmarsh p. 33'));
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Shipwright'), 'Feature: I''ll Patch It!', 'Provided you have carpenter''s tools and wood, you can perform repairs on a water vehicle. When you use this ability, you restore a number of hit points to the hull of a water vehicle equal to 5 × your proficiency modifier. A vehicle cannot be patched by you in this way again until after it has been pulled ashore and fully repaired.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Shipwright'), 'Life at Sea', 'Sea', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Sea' ORDER BY id DESC LIMIT 1), '1', 'Grand Designs. You are working on plans and schematics for a new, very fast ship. You must examine as many different kinds of vessels as possible to help ensure the success of your design.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Sea' ORDER BY id DESC LIMIT 1), '2', 'Solid and Sound. You patched up a war galley and prevented it from sinking. The local navy regards you as a friend.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Sea' ORDER BY id DESC LIMIT 1), '3', 'Favored. You insisted on thicker planking for a merchant vessel''s hull, which saved it from sinking when it smashed against a reef. You have a standing invitation to visit the merchant''s distant mansion.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Sea' ORDER BY id DESC LIMIT 1), '4', 'Master of Armaments. You specialized in designing and mounting defenses for the navy. You easily recognize and determine the quality of such items.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Sea' ORDER BY id DESC LIMIT 1), '5', 'Low Places. You have');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Shipwright'), 'Life at Sea', 'Contact', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Contact' ORDER BY id DESC LIMIT 1), '6', 'Mysteries of the Deep. You experienced an encounter with a possibly divine being while sailing alone. Work with your DM to determine the secret about the deep waters of the sea that this entity revealed to you.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Shipwright'), 'Saltmarsh Ties', 'Contact', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Contact' ORDER BY id DESC LIMIT 1), '1-2', 'Eda Oweland');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Contact' ORDER BY id DESC LIMIT 1), '3-4', 'Gellan Primewater');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Contact' ORDER BY id DESC LIMIT 1), '5-6', 'Anders Solmor');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Shipwright'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I love talking and being heard more than I like to listen.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I''m extremely fond of puzzles.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I thrive under pressure.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I love sketching and designing objects, especially boats.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I''m not afraid of hard work — in fact, I prefer it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'A pipe, an ale, and the smell of the');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Shipwright'), 'Suggested Characteristics', 'Sea', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Sea' ORDER BY id DESC LIMIT 1), '7', 'I have an endless supply of cautionary tales related to the sea.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Sea' ORDER BY id DESC LIMIT 1), '8', 'I don''t mind getting my hands dirty.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Shipwright'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Crew. If everyone on deck pitches in, we''ll never sink. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Careful Lines. A ship must be balanced according to the laws of the universe. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Invention. Make what you need out of whatever is at hand. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Perfection. To measure a being and find it lacking is the greatest disappointment. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Reflection. Muddied water always clears in time. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Hope. The horizon at sea holds the greatest promise. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Shipwright'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I must visit all the oceans of the world and behold the ships that sail there.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'Much of the treasure I claim will be used to enrich my community.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I must find a kind of wood rumored to possess magical qualities.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I repair broken things to redeem what''s broken in myself.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I will craft a boat capable of sailing through the most dangerous of storms.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'A kraken destroyed my masterpiece; its teeth shall adorn my hearth.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Shipwright'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I don''t know when to throw something away. You never know when it might be useful again.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I get frustrated to the point of distraction by shoddy craftsmanship.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'Though I am an excellent crafter, my work tends to look as though it belongs on a ship.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I am so obsessed with sketching my ideas for elaborate inventions that I sometimes forget little things like eating and sleeping.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I''m judgmental of those who are not skilled with tools of some kind.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Shipwright') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I sometimes take things that don''t belong to me, especially if they are very well made.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Smuggler', 'On a rickety barge, you carried a hundred longswords in fish barrels right past the dock master''s oblivious lackeys. You have paddled a riverboat filled with stolen elven wine under the gaze of the moon and sold it for twice its value in the morning. In your more charitable times, you have transported innocents out of war zones or helped guide herd animals to safety on the banks of a burning river.', 'Athletics, Deception', 'Vehicles (water)', '', 'A fancy leather vest or a pair of leather boots, a set of common clothes, and a leather pouch with 15 gp', (SELECT id FROM core_book_sources WHERE raw_source = 'Ghosts of Saltmarsh p. 34'));
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Smuggler'), 'Feature: Down Low', 'You are acquainted with a network of smugglers who are willing to help you out of tight situations. While in a particular town, city, or other similarly sized community (DM''s discretion), you and your companions can stay for free in safe houses. Safe houses provide a poor lifestyle. While staying at a safe house, you can choose to keep your presence (and that of your companions) a secret.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Smuggler'), 'Claim to Fame', 'Accomplishment', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Smuggler') AND title = 'Accomplishment' ORDER BY id DESC LIMIT 1), '1', 'Spirit of the Whale. You smuggled stolen dwarven spirits in the body of a dead whale being pulled behind a fishing boat. When you delivered the goods, the corpse suddenly exploded, sending whale meat and whiskey bottles for half a mile.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Smuggler') AND title = 'Accomplishment' ORDER BY id DESC LIMIT 1), '2', 'Cart and Sword. You drove a cart filled with stolen art through the middle of a battlefield while singing sea shanties to confuse the combatants.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Smuggler') AND title = 'Accomplishment' ORDER BY id DESC LIMIT 1), '3', 'The Recruit. You enlisted in another nation''s navy for the purpose of smuggling stolen jewels to a distant port. You attained a minor rank before disappearing from the navy and making your way here.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Smuggler') AND title = 'Accomplishment' ORDER BY id DESC LIMIT 1), '4', 'River of Shadows. Your riverboat accidentally slipped through the veil into the Shadowfell for several hours. While you were there, you sold some stolen dragonborn artifacts before returning to this plane and paddling home.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Smuggler') AND title = 'Accomplishment' ORDER BY id DESC LIMIT 1), '5', 'Gold-Hearted. You agreed to transport a family escaping a war. The baby began to cry at a checkpoint, and you gave the guards all your gold to let you pass. The family never found out about this gesture.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Smuggler') AND title = 'Accomplishment' ORDER BY id DESC LIMIT 1), '6', 'Playing Both Sides. You once smuggled crates of crossbow bolts and bundles of arrows, each destined for an opposing side in a regional war, at the same time. The buyers arrived within moments of each other but did not discover your trickery.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Smuggler'), 'Saltmarsh Ties', 'It''s an open secret, at least among the old-timers in town, that Gellan Primewater runs the biggest smuggling operation in this section of the coast. You have contacts with him and his organization, enabling you to request an audience with him as necessary.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Smuggler'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Smuggler') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I love being on the water but hate fishing.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Smuggler') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I think of everything in terms of monetary value.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Smuggler') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I never stop smiling.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Smuggler') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Nothing rattles me; I have a lie for every occasion.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Smuggler') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I love gold but won''t cheat a friend.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Smuggler') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I enjoy doing things others believe to be impossible.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Smuggler') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I become wistful when I see the sun rise over the ocean.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Smuggler') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I am no common criminal; I am a mastermind.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Smuggler'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Smuggler') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Wealth. Heaps of coins in a secure vault is all I dream of. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Smuggler') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Smuggler''s Code. I uphold the unwritten rules of the smugglers, who do not cheat one another or directly harm innocents. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Smuggler') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'All for a Coin. I''ll do nearly anything if it means I turn a profit. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Smuggler') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Peace and Prosperity. I smuggle only to achieve a greater goal that benefits my community. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Smuggler') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'People. For all my many lies, I place a high value on friendship. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Smuggler') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Daring. I am most happy when risking everything. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Smuggler'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Smuggler') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'My vessel was stolen from me, and I burn with the desire to recover it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Smuggler') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I intend to become the leader of the network of smugglers that I belong to.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Smuggler') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I owe a debt that cannot be repaid in gold.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Smuggler') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'After one last job, I will retire from the business.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Smuggler') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I was tricked by a fellow smuggler who stole something precious from me. I will find that thief.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Smuggler') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I give most of my profits to a charitable cause, and I don''t like to brag about it.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Smuggler'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Smuggler') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'Lying is reflexive, and I sometimes engage in it without realizing.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Smuggler') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I tend to assess my relationships in terms of profit and loss.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Smuggler') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I believe everyone has a price and am cynical toward those who present themselves as virtuous.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Smuggler') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I struggle to trust the words of others.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Smuggler') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'Few people know the real me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Smuggler') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'Though I act charming, I feel nothing for others and don''t know what friendship is.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Celebrity Adventurer''s Scion', 'Your family name strikes fear and admiration in the hearts of the common folk—but that''s got nothing to do with you. Songs and stories celebrating the adventuring exploits of your famous parent are widely known. Kids across the land grew up wishing they were you. But being the child of a famous adventurer wasn''t all hugs and kisses.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Celebrity Adventurer''s Scion'), 'Feature: Name Dropping', 'You know and have met any number of powerful people across the land—and some of them might even remember you. You might be able to wrangle minor assistance from a major figure in the campaign, at the DM''s discretion. Additionally, the common folk treat you with deference, and your heritage and the stories you tell might be good for a free meal or a place to sleep.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Celebrity Adventurer''s Scion'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Celebrity Adventurer''s Scion') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I will never get out of my famous parent''s shadow, and no one else will ever understand this burden.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Celebrity Adventurer''s Scion') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I''ve seen enough of the adventuring life to have realistic expectations and empathy for my peers.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Celebrity Adventurer''s Scion') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Living up to my legacy will be difficult, but I''m going to do it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Celebrity Adventurer''s Scion') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I''m used to the very best in life, and that''s a hard habit to break.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Celebrity Adventurer''s Scion') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'My parent taught me a sense of duty. I strive to uphold it, even when the odds are against me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Celebrity Adventurer''s Scion') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'No one can fake a smile, a handshake, or an interested nod like I can.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Celebrity Adventurer''s Scion') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I''ve been part of the adventuring life since I was old enough to walk. Let me explain a few things to you.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Celebrity Adventurer''s Scion') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'No risk is too great for the rewards of defeating my enemies... and taking their stuff.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Celebrity Adventurer''s Scion'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Celebrity Adventurer''s Scion') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Power. The only way to get ahead in this world is to attain power and hold onto it with all your might. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Celebrity Adventurer''s Scion') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Peace. Those who can find or make peace in the chaotic world around them have everything. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Celebrity Adventurer''s Scion') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Fame. I''ve seen what fame can bring. And I''ll do anything to get all that for myself. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Celebrity Adventurer''s Scion') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Training. Hard work, sacrifice, and training lead to success—and eventually to perfection. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Celebrity Adventurer''s Scion') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Anonymity. I want to be successful. And alone. With lots of guards and wards between me and everyone else in the world. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Celebrity Adventurer''s Scion') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Wisdom. Material wealth is an illusion. Wisdom is the real treasure. (Good)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Celebrity Adventurer''s Scion'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Celebrity Adventurer''s Scion') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'While my parent was out adventuring, a servant raised me, and I care about that person more than anyone.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Celebrity Adventurer''s Scion') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I consider every member of my parent''s former adventuring party to be family.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Celebrity Adventurer''s Scion') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'Despite their absences, my famous parent was kind and generous. I love them and want to make them proud.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Celebrity Adventurer''s Scion') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'My parent once brought a cursed magic item home. It is my obsession.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Celebrity Adventurer''s Scion') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'My childhood home holds all my best memories, and its upkeep is my primary concern.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Celebrity Adventurer''s Scion') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'Growing up, I had an imaginary friend I could always count on. That friend is still with me.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Celebrity Adventurer''s Scion'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Celebrity Adventurer''s Scion') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'You don''t know what I''m going through. You never can.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Celebrity Adventurer''s Scion') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'You. Fetch my cloak. And maybe rub my feet for a while.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Celebrity Adventurer''s Scion') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'My comrades are brave, but I must defeat this threat alone to prove my worth.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Celebrity Adventurer''s Scion') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'Oh, yeah, that spell? Named after my parent''s best friend. Let me tell you about them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Celebrity Adventurer''s Scion') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'My best days are behind me. Ahead lies only toil, pain, and death.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Celebrity Adventurer''s Scion') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'You have to look out for yourself. No one else will.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Failed Merchant', 'Maybe you come from a long line of merchants. Perhaps you were an entrepreneur. Regardless, your ventures ended poorly. Whether it was because of outside influences, bad luck, or simply because your business acumen was weak, you lost everything.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Failed Merchant'), 'Feature: Supply Chain', 'From your time as a merchant, you retain connections with wholesalers, suppliers, and other merchants and entrepreneurs. You can call upon these connections when looking for items or information.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Failed Merchant'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Failed Merchant') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I didn''t have the cutthroat attitude necessary to succeed. I won''t make that mistake again.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Failed Merchant') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Even my competitors said I was affable and talented. Those traits should serve me well.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Failed Merchant') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'To prosper, you have to be in control.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Failed Merchant') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'The customer is always right.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Failed Merchant') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I was cutting corners and breaking deals to maximize profit. That''s why I failed.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Failed Merchant') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'When I get an idea, I am single-minded in its execution—even if it''s a terrible idea.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Failed Merchant') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'If I can be everyone''s friend, I''ll always have support.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Failed Merchant') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'My heart wasn''t in being a merchant, so I failed. I''m not all that keen on adventuring either, but I need the money.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Failed Merchant'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Failed Merchant') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Survival. Where there''s life, there''s hope. If I remain alive and flexible, I can succeed. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Failed Merchant') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Generosity. People helped me when I was down. Now that I''m back on my feet, I''ll pay it forward. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Failed Merchant') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Excitement. Caution got me nowhere in my previous business. I''m not going to let it hold me back now. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Failed Merchant') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Wealth. With enough coin, I can buy comfort, power, knowledge, and even eternal life. Nothing will stand between me and money. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Failed Merchant') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Stability. The mercantile trade was too chaotic for me. I need a nice stable profession, like adventuring. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Failed Merchant') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Redemption. Too many people consider me a failure. So I need to prove them wrong. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Failed Merchant'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Failed Merchant') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'My family means everything to me. I failed them before, and I must not do so again.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Failed Merchant') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'My church provides a connection to my god, so I must ensure that it is protected and funded.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Failed Merchant') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'My former business partner fell ill, and then our business failed. Part of my new venture involves earning enough to take care of their family.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Failed Merchant') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'If I take care of my possessions, they''ll take care of me. People come and go, but a weapon or a wand is something you can always rely on.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Failed Merchant') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'Although my business failed, the people of my community were kind to me. I''ll do everything in my power to protect them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Failed Merchant') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I owe a dangerous person a lot of money. As long as they''re happy, they let my debt rest unpaid.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Failed Merchant'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Failed Merchant') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'Why spend gold here when you can buy the same thing for copper in the next town?');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Failed Merchant') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I must have the best of everything. Like, right now.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Failed Merchant') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'You haven''t heard of me? I''m sure that''s because of your ignorance and low breeding.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Failed Merchant') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I failed, but I''m awesome. So when anyone else is successful, it must be because of nepotism, dishonesty, or dumb luck.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Failed Merchant') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I find that most people are trustworthy. Hey, where''s my belt pouch?');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Failed Merchant') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'Nothing gets between me and danger except my fellow adventurers. So I''ll be sure to put them there.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Gambler', 'All you need to make a lot of gold is a little gold. Except at those times when all you need to have no gold at all is a little gold. Whether you''re a good gambler or a bad one rarely matters, because no one can divine the whims of Lady Luck. Sometimes you''re up, sometimes you''re down. But the thing about gambling is that someone is always willing to take a bet.', 'Deception, Insight', 'One gaming set', 'One of your choice', 'One gaming set, a lucky charm, a set of fine clothes, and a belt pouch containing 15 gp', (SELECT id FROM core_book_sources WHERE raw_source = 'Acquisitions Incorporated p. 49'));
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Gambler'), 'Feature: Never Tell Me the Odds', 'Odds and probability are your bread and butter. During downtime activities that involve games of chance or figuring odds on the best plan, you can get a solid sense of which choice is likely the best one and which opportunities seem too good to be true, at the DM''s determination.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Gambler'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gambler') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I plan for every contingency. Leave nothing to chance!');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gambler') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Every copper wants to be a silver. Each bet is an opportunity.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gambler') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I''m one of Lady Luck''s favored. Anything I try is destined to succeed.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gambler') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I''ve lost so much to gambling that I refuse to spend money on anything anymore.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gambler') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Nothing is certain. Planning is a coward''s act.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gambler') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I can''t be sure who I''ve swindled, cheated, or defeated, so I keep a low profile in public.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gambler') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'The perfect bet is out there somewhere. I just have to keep my eyes open.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gambler') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I have beaten my addiction, but all it takes is one weak moment and I''ll be back at the card table.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Gambler'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gambler') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Knowledge. Knowledge is power, and knowing which horse to back is the key to success. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gambler') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Fate. Whatever happens is fated, regardless of any planning or striving. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gambler') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Bravery. If you want to succeed, you have to take risks. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gambler') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Survival. You can''t win if you''re dead. Live to fight another day—when the odds might be more in your favor. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gambler') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Reliability. When I was in need, I was able to rely on others. Now I want to be the one others rely on. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gambler') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Victory. Winning is the real measure of a person. In the end, the only thing that matters is the scoreboard. (Evil)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Gambler'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gambler') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'One person in particular owes me a lot of money, and I need to keep them alive if I want to be repaid.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gambler') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I''m loyal to the friend or family member who taught me how to gamble.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gambler') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'The person who saved me from my gambling addiction is the only reason I''m alive today.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gambler') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'A patron once fronted me money in exchange for a percentage of my winnings. I owe them a debt of gratitude. And a lot of cash.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gambler') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'A criminal syndicate I once played for isn''t happy I left the game, and its enforcers are looking for me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gambler') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'Urchins once helped me find marks for my games. Now I''m driven to help them escape the streets.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Gambler'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gambler') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I don''t know when to quit. Especially when everyone else is telling me to.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gambler') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I save my sympathy for my friends, and I have no friends.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gambler') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'You think we''re in trouble now? Let me tell you how bad things are likely to get!');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gambler') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'You can loan me a little, right? I''ve got a sure thing. I''ll double your money, guaranteed.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gambler') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I was once a terribly flawed person, like you. Let me tell you how you can save yourself.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gambler') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I''m a great gambler. I''m just bad at math and logic.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Plaintiff', 'Sure, accidents happen. But they seem to happen an awful lot when Acquisitions Incorporated operatives are on the scene. Naturally, nothing ever happens when there are no witnesses left behind. But sometimes one casualty is left a little less than dead, just waiting to discover what a lucky break that accident actually was.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Plaintiff'), 'Feature: Legalese', 'Your experience with your local legal system has given you a firm knowledge of the ins and outs of that system. Even when the law is not on your side, you can use complex terms like ex injuria jus non oritur and cogitationis poenam nemo patitur to frighten people into thinking you know what you''re talking about. With common folks who don''t know any better, you might be able to intimidate or deceive to get favors or special treatment.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Plaintiff'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Plaintiff') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I can''t believe I have a chance to join Acquisitions Incorporated! The fun I''m going to have!');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Plaintiff') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I''ve been wronged my entire life, and the world owes me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Plaintiff') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I have always tried to make the best of a bad situation.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Plaintiff') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'The law doesn''t protect the honest and the hard working. I''m going to do whatever needs to be done to make things right.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Plaintiff') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I''m always in the wrong place at the wrong time.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Plaintiff') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'My superiors are smarter and wiser than I am. I do what I''m told.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Plaintiff') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'Never pass up the opportunity to make an easy bit of coin. That''s my motto.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Plaintiff') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I''m beginning to feel like the gods are not on my side.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Plaintiff'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Plaintiff') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Justice. Those who break the law need to answer for their crimes. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Plaintiff') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Freedom. People must have the freedom to do what they want and pursue their dreams. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Plaintiff') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Greed. Everyone I see is getting theirs, so I''m surely going to get mine. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Plaintiff') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Chaos. You''re out of order! And you''re out of order! This whole realm is out of order! (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Plaintiff') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Humility. I''m just a small part of a larger whole. So is everyone else. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Plaintiff') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Responsibility. We all have our roles to play. I''ll hold up my end of the bargain. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Plaintiff'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Plaintiff') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'Others hurt in the same accident that hurt me are my new family. I''ll make sure they''re taken care of.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Plaintiff') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'The rulers of this place were kind to me, and they have my lifelong devotion.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Plaintiff') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'My parents worry about me, but I''ll make them proud.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Plaintiff') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'The only bond that matters is the one holding my money pouch to my belt.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Plaintiff') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'The other new hires at Acquisitions Incorporated are my allies. We have each other''s backs.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Plaintiff') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'My legal counsel is my best friend. I owe all my forthcoming opportunities to their hard work.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Plaintiff'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Plaintiff') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'The person who gains the most reward for the least effort wins.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Plaintiff') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'Three magic beans for just one cow? What a deal!');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Plaintiff') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I have only one vice, but it controls my life.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Plaintiff') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'Sleep is for the weak. We need to keep training more if we''re going to be ready for the challenges ahead.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Plaintiff') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'Until my songs are sung in every tavern in this realm, I won''t be satisfied.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Plaintiff') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'If people find me unpleasant, that''s their problem.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Rival Intern', 'You were an intern at a rival of Acquisitions Incorporated, and you gained a healthy respect for not just the job and the franchising opportunities, but for the ruthless and efficient way Acquisitions Incorporated goes about its business. Why deal with the rest, when you can work for the best?
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Rival Intern'), 'Feature: Inside Informant', 'You have connections to your previous employer or other groups you dealt with during your previous employment. You can communicate with your contacts, gaining information at the DM''s discretion.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Rival Intern'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rival Intern') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'My previous employer didn''t respect me, and now I''ll do whatever I can to gain respect.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rival Intern') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'The job is important, but the relationships I forge with my coworkers are even more so.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rival Intern') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'The job is everything to me. Who needs relaxation, hobbies, and a social life?');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rival Intern') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I know I''m not the best and brightest, but if I put my best self forward, I can overcome anything.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rival Intern') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'My former boss was an idiot. So was my boss before that. And before that. I''m sure those were all coincidences.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rival Intern') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'This company is so much better than my previous one. It will always be the best until they stop paying me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rival Intern') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I know this dagger belongs to the company, but I''m sure they won''t miss it. Or this flask. Or this armor.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rival Intern') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'It''s only a matter of time before I''ll be upper management. I just have to kiss up to my superiors and kick down those beneath me.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Rival Intern'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rival Intern') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Advancement. Money and power can be gained more easily within an organization. I plan to gain as much as possible. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rival Intern') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Structure. Life goes much more smoothly when you follow the rules and work within a system. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rival Intern') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Uncertainty. The more chaos that swirls around me, the more opportunities I can find to profit. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rival Intern') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Justice. I can''t stand people being treated unjustly. I do whatever it takes to stop injustice and those who flout the law. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rival Intern') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Pleasure. What''s the use of working hard and making money if you can''t enjoy the finer things in life? (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rival Intern') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Power. Money is fine, but real power means never having to say you''re sorry. (Evil)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Rival Intern'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rival Intern') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I have a family member in need. I consider them in everything I do.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rival Intern') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'My peers keep me grounded.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rival Intern') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'My past mistakes cost someone else dearly. I have to rectify that.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rival Intern') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'A childhood mentor put me on my current path. If I succeed, I want to repay that mentor in some way.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rival Intern') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I value an oath of loyalty I took to a group of friends over everything else in my life.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rival Intern') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'Although I don''t get along well with people, my pet means the world to me.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Rival Intern'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rival Intern') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I know what''s best. Trust me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rival Intern') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'Flaw? I have no flaws. I''m perfect.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rival Intern') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'My loyalties are... fluid.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rival Intern') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'If anything goes wrong, it must be someone else''s fault. Let me explain that in detail.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rival Intern') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'There''s right and there''s wrong, and there''s no gray area in between.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rival Intern') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'Our superiors might not like what you''re doing. I''m going to have to put that in my report.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Baldur''s Gate Acolyte', 'It''s said that every faith in the world has a believer in Baldur''s Gate. Not only are most established faiths tolerated — even if some of them, including most of the openly evil faiths, are relegated to the Outer City''s Twin Songs neighborhood — but new ones arrive constantly, carried by travelers and proselytizers from far-flung lands. A character with this background might aspire to greater things, not for themselves, but for their faith.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Acolyte'), 'Feature: Shelter of the Faithful', 'As an acolyte, you command the respect of those who share your faith, and you can perform the religious ceremonies of your deity. You and your adventuring companions can expect to receive free healing and care at a temple, shrine, or other established presence of your faith, though you must provide any material components needed for spells. Those who share your religion will support you (but only you) at a modest lifestyle.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Acolyte'), 'Baldur''s Gate Feature: Religious Community', 'The effects of a Baldur''s Gate feature can be used only while the character is in Baldur''s Gate—though, at the DM''s discretion, they might have applicable effects in situations similar to those in Baldur''s Gate.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Acolyte'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I idolize a particular hero of my faith, and constantly refer to that person''s deeds and example.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I can find common ground between the fiercest enemies, empathizing with them and always working toward peace.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I see omens in every event and action. The gods try to speak to us, we just need to listen.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Nothing can shake my optimistic attitude.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I quote (or misquote) sacred texts and proverbs in almost every situation.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I am tolerant (or intolerant) of other faiths and respect (or condemn) the worship of other gods.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I''ve enjoyed fine food, drink, and high society among my temple''s elite. Rough living grates on me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I''ve spent so long in the temple that I have little practical experience dealing with people in the outside world.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Tradition. The ancient traditions of worship and sacrifice must be preserved and upheld. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Charity. I always try to help those in need, no matter what the personal cost. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Change. We must help bring about the changes the gods are constantly working in the world. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Power. I hope to one day rise to the top of my faith''s religious hierarchy. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Faith. I trust that my deity will guide my actions. I have faith that if I work hard, things will go well. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Aspiration. I seek to prove myself worthy of my god''s favor by matching my actions against his or her teachings. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I would die to recover an ancient relic of my faith that was lost long ago.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I will someday get revenge on the corrupt temple hierarchy who branded me a heretic.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I owe my life to the priest who took me in when my parents died.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Everything I do is for the common people.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I will do anything to protect the temple where I served.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I seek to preserve a sacred text that my enemies consider heretical and seek to destroy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I judge others harshly, and myself even more severely.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I put too much trust in those who wield power within my temple''s hierarchy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'My piety sometimes leads me to blindly trust those that profess faith in my god.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I am inflexible in my thinking.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I am suspicious of strangers and expect the worst of them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Acolyte') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Once I pick a goal, I become obsessed with it to the detriment of everything else in my life.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Baldur''s Gate Charlatan', 'Everybody''s always trying to get a leg up on somebody in Baldur''s Gate. One group''s con artist might be another''s revolutionary. Or maybe you''re just in it for yourself.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan'), 'Feature: False Identity', 'You have created a second identity that includes documentation, established acquaintances, and disguises that allow you to assume that persona. Additionally, you can forge documents including official papers and personal letters, as long as you have seen an example of the kind of document or the handwriting you are trying to copy.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan'), 'Baldur''s Gate Feature: Long-Lost Heir', 'The effects of a Baldur''s Gate feature can be used only while the character is in Baldur''s Gate—though, at the DM''s discretion, they might have applicable effects in situations similar to those in Baldur''s Gate.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan'), 'Favorite Schemes', 'Scam', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan') AND title = 'Scam' ORDER BY id DESC LIMIT 1), '1', 'I cheat at games of chance.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan') AND title = 'Scam' ORDER BY id DESC LIMIT 1), '2', 'I shave coins or forge documents.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan') AND title = 'Scam' ORDER BY id DESC LIMIT 1), '3', 'I insinuate myself into people''s lives to prey on their weakness and secure their fortunes.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan') AND title = 'Scam' ORDER BY id DESC LIMIT 1), '4', 'I put on new identities like clothes.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan') AND title = 'Scam' ORDER BY id DESC LIMIT 1), '5', 'I run sleight-of-hand cons on street corners.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan') AND title = 'Scam' ORDER BY id DESC LIMIT 1), '6', 'I convince people that worthless junk is worth their hard-earned money.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I fall in and out of love easily, and am always pursuing someone.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I have a joke for every occasion, especially occasions where humor is inappropriate.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Flattery is my preferred trick for getting what I want.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I''m a born gambler who can''t resist taking a risk for a potential payoff.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I lie about almost everything, even when there''s no reason to.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Sarcasm and insults are my weapons of choice.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I keep multiple holy symbols on me and invoke whatever deity might come in useful at any given moment.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I pocket anything I see that might have some value.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Independence. I am a free spirit—no one tells me what to do. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Fairness. I never target people who can''t afford to lose a few coins. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Charity. I distribute the money I acquire to the people who really need it. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Creativity. I never run the same con twice. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Friendship. Material goods come and go.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'Aspiration. I''m determined to make something of myself. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I fleeced the wrong person and must work to ensure that this individual never crosses paths with me or those I care about.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I owe everything to my mentor—a horrible person who''s probably rotting in jail somewhere.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'Somewhere out there, I have a child who doesn''t know me. I''m making the world better for him or her.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I come from a noble family, and one day I''ll reclaim my lands and title from those who stole them from me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'A powerful person killed someone I love. Some day soon, I''ll have my revenge.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I swindled and ruined a person who didn''t deserve it. I seek to atone for my misdeeds but might never be able to forgive myself.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I can''t resist a pretty face.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I''m always in debt. I spend my ill-gotten gains on decadent luxuries faster than I bring them in.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I''m convinced that no one could ever fool me the way I fool others.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I''m too greedy for my own good. I can''t resist taking a risk if there''s money involved.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I can''t resist swindling people who are more powerful than me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Charlatan') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I hate to admit it and will hate myself for it, but I''ll run and preserve my own hide if the going gets tough.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Baldur''s Gate Criminal', 'No career criminal in Baldur''s Gate operates without being aware of the Guild. Some studiously keep a low profile, carrying just the occasional smuggled load in with legitimate merchandise, or only breaking knees when it can plausibly be claimed as an act of personal revenge. Others join up with crews for protection, or with the Guild itself. A few former Guild members have been cast out of the organization due to incompetence or after offending a more powerful member, and now shuffle for scraps to survive.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal'), 'Feature: Criminal Contact', 'You have a reliable and trustworthy contact who acts as your liaison to a network of other criminals. You know how to get messages to and from your contact, even over great distances; specifically, you know the local messengers, corrupt caravan masters, and seedy sailors who can deliver messages for you.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal'), 'Baldur''s Gate Feature: Criminal Connections', 'The effects of a Baldur''s Gate feature can be used only while the character is in Baldur''s Gate—though, at the DM''s discretion, they might have applicable effects in situations similar to those in Baldur''s Gate.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal'), 'Criminal Origins', 'Origin', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '1', 'You crippled a Guild kingpin''s cousin without realizing the connection. You got the Guild to back off demands for your death by offering to make amends by working for the criminals, but even so the kingpin still plots a personal revenge.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '2', 'The Guild took over your family business, ran it into the ground, and burned the building for insurance money. You were driven into crime yourself, but you''ll never work for the Guild. You take special joy in hitting its targets first, tipping off its con victims and otherwise frustrating its schemes.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '3', 'It''s always been about money. You''re not paid what you''re worth, working for someone who has more than they deserve. But the Guild offered you a way to fix that. You keep doing what you''ve always done—guard work, dock labor, business accounting—but what you learn you pass on to the Guild.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '4', 'The inequality of Baldur''s Gate has driven you to take matters into your own hands. You steal from patriars and rich Lower City residents, funneling the money through charitable fronts to help the needy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '5', 'You got into crime as a bored patriar looking for excitement. Your family has no idea of your activities, and neither does the Guild. If either of them ever finds out, your life as you know it will be over.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '6', 'A close friend or relative joined the Guild and vanished mysteriously. You''ve worked your way into the lower ranks hoping to find out what happened to them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '7', 'You''ve always wanted to be a member of the Guild. As a child, you looked up to the Guild members'' swagger, their flashy dress, and their competence, which all marked them as different from the other adults you knew. As soon as you could, you joined the organization.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '8', 'To you, this is just a way to earn a living. You go to work, do what needs to be done, and get paid. Anyone who pursues crime for thrill-seeking, to strike back at unjust authorities, or anything else are amateurs, and they''re liable to get you arrested or killed with their idiocy.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal'), 'Specialty', 'Specialty', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '1', 'Blackmailer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '2', 'Burglar');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '3', 'Enforcer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '4', 'Fence');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '5', 'Highway robber');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '6', 'Hired killer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '7', 'Pickpocket');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '8', 'Smuggler');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I always have a plan for what to do when things go wrong.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I am always calm, no matter what the situation. I never raise my voice or let my emotions control me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'The first thing I do in a new place is note the locations of everything valuable—or where such things could be hidden.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I would rather make a new friend than a new enemy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I am incredibly slow to trust. Those who seem the fairest often have the most to hide.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I don''t pay attention to the risks in a situation. Never tell me the odds.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'The best way to get me to do something is to tell me I can''t do it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I blow up at the slightest insult.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Honor. I don''t steal from others in the trade. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Freedom. Chains are meant to be broken, as are those who would forge them. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Charity. I steal from the wealthy so that I can help people in need. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Greed. I will do whatever it takes to become wealthy. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'People. I''m loyal to my friends, not to any ideals, and everyone else can take a trip down the Styx for all I care. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Redemption. There''s a spark of good in everyone. (Good)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I''m trying to pay off an old debt I owe to a generous benefactor.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'My ill-gotten gains go to support my family.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'Something important was taken from me, and I aim to steal it back.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I will become the greatest thief that ever lived.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I''m guilty of a terrible crime. I hope I can redeem myself for it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'Someone I loved died because of a mistake I made. That will never happen again.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'When I see something valuable, I can''t think about anything but how to steal it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'When faced with a choice between money and my friends, I usually choose the money.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'If there''s a plan, I''ll forget it. If I don''t forget it, I''ll ignore it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I have a "tell" that reveals when I''m lying.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I turn tail and run when things look bad.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Criminal') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'An innocent person is in prison for a crime that I committed. I''m okay with that.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Baldur''s Gate Entertainer', 'From the Oasis Theater''s spectacular singers and acrobats to the pantomimes and shadow puppeteers of the Wide, Baldur''s Gate hosts a colorful array of performers. Good acts can always find ready audiences, and the constant flow of travelers means that both new spectators and new spectacles are always passing through.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer'), 'Feature: By Popular Demand', 'You can always find a place to perform, usually in an inn or tavern but possibly with a circus, at a theater, or even in a noble''s court. At such a place, you receive free lodging and food of a modest or comfortable standard (depending on the quality of the establishment), as long as you perform each night. In addition, your performance makes you something of a local figure. When strangers recognize you in a town where you have performed, they typically take a liking to you.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer'), 'Baldur''s Gate Feature: Backstage Pass', 'The effects of a Baldur''s Gate feature can be used only while the character is in Baldur''s Gate—though, at the DM''s discretion, they might have applicable effects in situations similar to those in Baldur''s Gate.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer'), 'Entertainer Routines', 'Routines', '1d10');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Routines' ORDER BY id DESC LIMIT 1), '1', 'Actor');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Routines' ORDER BY id DESC LIMIT 1), '2', 'Dancer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Routines' ORDER BY id DESC LIMIT 1), '3', 'Fire-eater');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Routines' ORDER BY id DESC LIMIT 1), '4', 'Jester');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Routines' ORDER BY id DESC LIMIT 1), '5', 'Juggler');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Routines' ORDER BY id DESC LIMIT 1), '6', 'Instrumentalist');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Routines' ORDER BY id DESC LIMIT 1), '7', 'Poet');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Routines' ORDER BY id DESC LIMIT 1), '8', 'Singer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Routines' ORDER BY id DESC LIMIT 1), '9', 'Storyteller');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Routines' ORDER BY id DESC LIMIT 1), '10', 'Tumbler');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I know a story relevant to almost every situation.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Whenever I come to a new place, I collect local rumors and spread gossip.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I''m a hopeless romantic, always searching for that "special someone".');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Nobody stays angry at me or around me for long, since I can defuse any amount of tension.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I love a good insult, even one directed at me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I get bitter if I''m not the center of attention.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I''ll settle for nothing less than perfection.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I change my mood or my mind as quickly as I change key in a song.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Beauty. When I perform, I make the world better than it was. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Tradition. The stories, legends, and songs of the past must never be forgotten, for they teach us who we are. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Creativity. The world is in need of new ideas and bold action. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Greed. I''m only in it for the money and fame. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'People. I like seeing the smiles on people''s faces when I perform. That''s all that matters. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Honesty. Art should reflect the soul; it should come from within and reveal who we really are. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'My instrument is my most treasured possession, and it reminds me of someone I love.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'Someone stole my precious instrument, and someday I''ll get it back.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I want to be famous, whatever it takes.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I idolize a hero of the old tales and measure my deeds against that person''s.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I will do anything to prove myself superior to my hated rival.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I would do anything for the other members of my old troupe.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I''ll do anything to win fame and renown.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I''m a sucker for a pretty face.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'A scandal prevents me from ever going home again. That kind of trouble seems to follow me around.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I once satirized a noble who still wants my head. It was a mistake that I will likely repeat.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I have trouble keeping my true feelings hidden. My sharp tongue lands me in trouble.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Entertainer') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'Despite my best efforts, I am unreliable to my friends.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Baldur''s Gate Folk Hero', 'Baldur''s Gate is a city badly in need of heroes. and every so often, one rises from among its own. Ordinary people who rise to greatness are beloved in local history, but the popular imagination can turn on such champions almost as quickly as it anoints them.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero'), 'Feature: Rustic Hospitality', 'Since you come from the ranks of the common folk, you fit in among them with ease. You can find a place to hide, rest, or recuperate among other commoners, unless you have shown yourself to be a danger to them. They will shield you from the law or anyone else searching for you, though they will not risk their lives for you.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero'), 'Baldur''s Gate Feature: Social Vengeance', 'The effects of a Baldur''s Gate feature can be used only while the character is in Baldur''s Gate—though, at the DM''s discretion, they might have applicable effects in situations similar to those in Baldur''s Gate.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero'), 'Folk Hero Origins', 'Origin', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '1', 'You helped get healing for a sick child. Now the sick come to you, knowing you''ll help them find a way to salvation.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '2', 'You helped break a Guild protection racket afflicting a community of immigrants in the Outer City. Now, you can''t travel through that part of the city without your dozen adopted grandparents inviting you in for a meal.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '3', 'Seeing a lost patriar after dark in the Outer City, you guided the wayward noble through back streets to safety. The patriar repaid your help by paying for improved roofs and lamps in your neighborhood, causing the entire community to celebrate your deed.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '4', 'Fueled by alcohol, you faced down a carrion crawler that slunk out of the sewer, knocking it out with a single punch. Since then, however, the ale you once credited with your heroism has drowned it, and even your most patient admirers are losing hope. You''re hoping for one last chance to win back the goodwill you''ve drunk away.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '5', 'You once defeated a raging bugbear with a hand mirror, a mounted deer''s head, and two kicks to the groin. Later, you and the bugbear became friends.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '6', 'Last winter, you dove into the frigid river to haul out a foundering fishing boat with your bare hands, saving all aboard. Now, everyone on the docks knows your name.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero'), 'Specialty', 'Defining', '1d10');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Defining' ORDER BY id DESC LIMIT 1), '1', 'I stood up to a tyrant''s agents.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Defining' ORDER BY id DESC LIMIT 1), '2', 'I saved people during a natural disaster.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Defining' ORDER BY id DESC LIMIT 1), '3', 'I stood alone against a terrible monster.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Defining' ORDER BY id DESC LIMIT 1), '4', 'I stole from a corrupt merchant to help the poor.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Defining' ORDER BY id DESC LIMIT 1), '5', 'I led a militia to fight off an invading army.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Defining' ORDER BY id DESC LIMIT 1), '6', 'I broke into a tyrant''s castle and stole weapons to arm the people.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Defining' ORDER BY id DESC LIMIT 1), '7', 'I trained the peasantry to use farming implements as weapons against a tyrant''s soldiers.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Defining' ORDER BY id DESC LIMIT 1), '8', 'A lord rescinded an unpopular decree after I led a symbolic act of protest against it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Defining' ORDER BY id DESC LIMIT 1), '9', 'A celestial, fey, or similar creature gave me a blessing or revealed my secret');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero'), 'Specialty', 'Origin', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '10', 'Recruited into a lord''s army, I rose to leadership and was commended for my heroism.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I judge people by their actions, not their words.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'If someone is in trouble, I''m always ready to lend help.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'When I set my mind to something, I follow through no matter what gets in my way.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I have a strong sense of fair play and always try to find the most equitable solution to arguments.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I''m confident in my own abilities and do what I can to instill confidence in others.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Thinking is for other people. I prefer action.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I misuse long words in an attempt to sound smarter.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I get bored easily. When am I going to get on with my destiny?');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Respect. People deserve to be treated with dignity and respect. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Fairness. No one should get preferential treatment before the law, and no one is above the law. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Freedom. Tyrants must not be allowed to oppress the people. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Might. If I become strong, I can take what I want—what I deserve. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Sincerity. There''s no good in pretending to be something I''m not. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Destiny. Nothing and no one can steer me away from my higher calling. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I have a family, but I have no idea where they are. One day, I hope to see them again.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I worked the land, I love the land, and I will protect the land.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'A proud noble once gave me a horrible beating, and I will take my revenge on any bully I encounter.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'My tools are symbols of my past life, and I carry them so that I will never forget my roots.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I protect those who cannot protect themselves.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I wish my childhood sweetheart had come with me to pursue my destiny.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'The tyrant who rules my land will stop at nothing to see me killed.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I''m convinced of the significance of my destiny, and blind to my shortcomings and the risk of failure.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'The people who knew me when I was young know my shameful secret, so I can never go home again.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I have a weakness for the vices of the city, especially hard drink.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'Secretly, I believe that things would be better if I were a tyrant lording over the land.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Folk Hero') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I have trouble trusting in my allies.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Baldur''s Gate Guild Artisan', 'Numerous guilds and professional associations exist in Baldur''s Gate, covering every imaginable trade and discipline from gravediggers to moneylenders.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan'), 'Feature: Guild Membership', 'As an established and respected member of a guild, you can rely on certain benefits that membership provides. Your fellow guild members will provide you with lodging and food if necessary, and pay for your funeral if needed. In some cities and towns, a guildhall offers a central place to meet other members of your profession, which can be a good place to meet potential patrons, allies, or hirelings.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan'), 'Baldur''s Gate Feature: Professional Courtesy', 'The effects of a Baldur''s Gate feature can be used only while the character is in Baldur''s Gate—though, at the DM''s discretion, they might have applicable effects in situations similar to those in Baldur''s Gate.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan'), 'Guild Business', 'Guild Business', '1d20');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '1', 'Alchemists and apothecaries');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '2', 'Armorers, locksmiths, and finesmiths');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '3', 'Brewers, distillers, and vintners');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '4', 'Calligraphers, scribes, and scriveners');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '5', 'Carpenters, roofers, and plasterers');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '6', 'Cartographers, surveyors, and chart-makers');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '7', 'Cobblers and shoemakers');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '8', 'Cooks and bakers');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '9', 'Glassblowers and glaziers');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '10', 'Jewelers and gemcutters');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '11', 'Leatherworkers, skinners, and tanners');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '12', 'Masons and stonecutters');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '13', 'Painters, limners, and sign-makers');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '14', 'Potters and tile-makers');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '15', 'Shipwrights and sail-makers');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '16', 'Smiths and metal-forgers');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '17', 'Tinkers, pewterers, and casters');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '18', 'Wagon-makers and wheelwrights');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '19', 'Weavers and dyers');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Guild Business' ORDER BY id DESC LIMIT 1), '20', 'Woodcarvers, coopers, and bowyers');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I believe that anything worth doing is worth doing right. I can''t help it — I''m a perfectionist.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I''m a snob who looks down on those who can''t appreciate fine art.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I always want to know how things work and what makes people tick.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I''m full of witty aphorisms and have a proverb for every occasion.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I''m rude to people who lack my commitment to hard work and fair play.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I like to talk at length about my profession.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I don''t part with my money easily and will haggle tirelessly to get the best deal possible.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I''m well known for my work, and I want to make sure everyone appreciates it. I''m always taken aback when people haven''t heard of me.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Community. It is the duty of all civilized people to strengthen the');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'Generosity. My talents were given to me so that I could use them to benefit the world. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'Freedom. Everyone should be free to pursue his or her own livelihood. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'Greed. I''m only in it for the money. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'People. I''m committed to the people I care about, not to ideals. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'Aspiration. I work hard to be the best there is at my craft. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'The workshop where I learned my trade is the most important place in the world to me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I created a great work for someone, and then found them unworthy to receive it. I''m still looking for someone worthy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I owe my guild a great debt for forging me into the person I am today.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I pursue wealth to secure someone''s love.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'One day I will return to my guild and prove that I am the greatest artisan of them all.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I will get revenge on the evil forces that destroyed my place of business and ruined my livelihood.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I''ll do anything to get my hands on something rare or priceless.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I''m quick to assume that someone is trying to cheat me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'No one must ever learn that I once stole money from guild coffers.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I''m never satisfied with what I have — I always want more.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I would kill to acquire a noble title.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Guild Artisan') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I''m horribly jealous of anyone who can outshine my handiwork. Everywhere I go, I''m surrounded by rivals.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Baldur''s Gate Hermit', 'While some might think it strange to find hermits in a bustling city, others know that sometimes the most profound solitude exists in the midst of a crowd. Baldur''s Gate holds a handful of souls who manage to find isolation amid its tumult.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit'), 'Feature: Discovery', 'The quiet seclusion of your extended hermitage gave you access to a unique and powerful discovery. The exact nature of this revelation depends on the nature of your seclusion. It might be a great truth about the cosmos, the deities, the powerful beings of the outer planes, or the forces of nature. It could be a site that no one else has ever seen. You might have uncovered a fact that has long been forgotten, or unearthed some relic of the past that could rewrite history. It might be information that would be damaging to the people who or consigned you to exile, and hence the reason for your return to society.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit'), 'Baldur''s Gate Feature: The Real City', 'The effects of a Baldur''s Gate feature can be used only while the character is in Baldur''s Gate—though, at the DM''s discretion, they might have applicable effects in situations similar to those in Baldur''s Gate.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit'), 'Hermit Origins', 'Origin', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '1', 'You led an ill-fated expedition into the Riverveins. Your friends were swept away by flooding, and you''ve never been able to shake the guilt of causing their deaths over a frivolous lark. You''ve maintained a solitary vigil outside the cavern entrance ever since.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '2', 'You crossed the Guild in a bad way. Fortunately, its members think you''re dead. Less fortunately, maintaining that deception might require you to stay in hiding until you actually are.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '3', 'You study the puzzles of mortal natures. You''ve seen followers of evil deities perform miracles for the helpless at Twin Songs, and you''ve seen patriars who worship good deities turn their backs on the poor daily. Bearing witness to such things, and meditating on their contradictions, fills your hours.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '4', 'You tend to some part of the city''s forgotten history: the unmarked graves in Cliffside Cemetery, the crumbling remains of dead patriar families'' manors, or a collection of religious texts stuffed into an attic and forgotten when a believer''s patron deity died. In this solitary work, you''ve learned secrets no one else knows.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '5', 'You killed a patriar''s scion in an illegal duel. The family swore revenge, and you fled to the slums rather than risk bringing their wrath down on your kin.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '6', 'You aren''t originally from Baldur''s Gate. You came here seeking something else, only to learn that the quest that drove you had become impossible to fulfill—its object was destroyed or its purpose was negated by some superseding event. Suddenly directionless and unable to return to your homeland, you have lingered, adrift, in this wretched city.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit'), 'Specialty', 'Life', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Life' ORDER BY id DESC LIMIT 1), '1', 'I was searching for spiritual enlightenment.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Life' ORDER BY id DESC LIMIT 1), '2', 'I was partaking of communal living in accordance with the dictates of a religious order.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Life' ORDER BY id DESC LIMIT 1), '3', 'I was exiled for a crime I didn''t commit.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Life' ORDER BY id DESC LIMIT 1), '4', 'I retreated from society after a life-altering event.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Life' ORDER BY id DESC LIMIT 1), '5', 'I needed a quiet place to work on my art, literature, music, or manifesto.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Life' ORDER BY id DESC LIMIT 1), '6', 'I needed to commune with nature, far from civilization.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Life' ORDER BY id DESC LIMIT 1), '7', 'I was the caretaker of an ancient ruin or relic.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Life' ORDER BY id DESC LIMIT 1), '8', 'I was a pilgrim in search of a person, place, or relic of spiritual significance.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I''ve been isolated for so long that I rarely speak, preferring gestures and the occasional grunt.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I am utterly serene, even in the face of disaster.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'The leader of my community had something wise to say on every topic, and I am eager to share that wisdom.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I feel tremendous empathy for all who suffer.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I''m oblivious to etiquette and social expectations.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I connect everything that happens to me to a grand, cosmic plan.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I often get lost in my own thoughts and contemplation, becoming oblivious to my surroundings.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I am working on a grand philosophical theory and love sharing my ideas.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Greater Good. My gifts are meant to be shared with all, not used for my own benefit. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Logic. Emotions must not cloud our sense of what is right and true, or our logical thinking. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Free Thinking. Inquiry and curiosity are the pillars of progress. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Power. Solitude and contemplation are paths toward mystical or magical power. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Live and Let Live. Meddling in the affairs of others only causes trouble. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Self-Knowledge. If you know yourself, there''s nothing left to know. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'Nothing is more important than the other members of my hermitage, order, or association.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I entered seclusion to hide from the ones who might still be hunting me. I must someday confront them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I''m still seeking the enlightenment I pursued in my seclusion, and it still eludes me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I entered seclusion because I loved someone I could not have.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'Should my discovery come to light, it could bring ruin to the world.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'My isolation gave me great insight into a great evil that only I can destroy.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'Now that I''ve returned to the world, I enjoy its delights a little too much.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I harbor dark, bloodthirsty thoughts that my isolation and meditation failed to quell.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I am dogmatic in my thoughts and philosophy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I let my need to win arguments overshadow friendships and harmony.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I''d risk too much to uncover a lost bit of knowledge.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Hermit') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I like keeping secrets and won''t share them with anyone.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Baldur''s Gate Noble', 'The patriars of Baldur''s Gate live in the Upper City. where they host grand galas and flaunt cosmopolitan fashions, but are walled off from the poverty and squalor of the less fortunate districts and their neighborhoods. Although they might visit prosperous Bloomridge to try a fashionable restaurant or boutique, or watch a spectacle at the Oasis Theater, the patriars have little reason to venture into the dirtier, more dangerous parts of the city. If they do, it is generally as thrill-seeking tourists or enclosed by a retinue of armed guards, not as friends or neighbors. As a result, many patriars are at once acutely attuned to the nuances of royal courts half a continent away and shockingly ignorant of what life is like for the poor outside their own doorsteps.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Noble'), 'Feature: Position of Privilege', 'Thanks to your noble birth, people are inclined to think the best of you. You are welcome in high society, and people assume you have the right to be wherever you are. The common folk make every effort to accommodate you and avoid your displeasure, and other people of high birth treat you as a member of the same social sphere. You can secure an audience with a local noble if you need to.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Noble'), 'Baldur''s Gate Feature: Patriar', 'The effects of a Baldur''s Gate feature can be used only while the character is in Baldur''s Gate—though, at the DM''s discretion, they might have applicable effects in situations similar to those in Baldur''s Gate.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Noble'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'My eloquent flattery makes everyone I talk to feel like the most wonderful and important person in the world.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'The common folk love me for my kindness and generosity.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'No one could doubt by looking at my regal bearing that I am a cut above the unwashed masses.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I take great pains to always look my best and follow the latest fashions.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I don''t like to get my hands dirty, and I won''t be caught dead in unsuitable accommodations.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Despite my noble birth, I do not place myself above other folk. We all have the same blood.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'My favor, once lost, is lost forever.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Noble') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'If you do me an injury, I will crush you, ruin your name, and salt your fields.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Noble'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Noble') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Respect. Respect is due to me because of my position, but all people regardless of station deserve to be treated with dignity. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Noble') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Responsibility. It is my duty to respect the authority of those above me, just as those below me must respect mine. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Noble') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Independence. I must prove that I can handle myself without the coddling of my family. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Noble') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Power. If I can attain more power, no one will tell me what to do. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Noble') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Family. Blood runs thicker than water. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Noble') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Noble Obligation. It is my duty to protect and care for the people beneath me. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Noble') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'I will face any challenge to win the approval of my family.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Noble') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'My house''s alliance with another noble family must be sustained at all costs.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Noble') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Nothing is more important than the other members of my family.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Noble') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'I am in love with the heir of a family that my family despises.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Noble') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'My loyalty to my sovereign is unwavering.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Noble') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'The common folk must see me as a hero of the people.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Noble'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Noble') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I secretly believe that everyone is beneath me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Noble') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I hide a truly scandalous secret that could ruin my family forever.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Noble') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I too often hear veiled insults and threats in every word addressed to me, and I''m quick to anger.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Noble') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I have an insatiable desire for carnal pleasures.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Noble') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'In fact, the world does revolve around me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Noble') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'By my words and actions, I often bring shame to my family.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Baldur''s Gate Outlander', 'Coming to Baldur''s Gate might seem like a good idea for a spectrum of reasons. Profit, excitement, and cosmopolitan opportunities all present tempting prospects, but rarely does one start on the path to Baldur''s Gate fully understanding the complex social morass that awaits. You enter the city an outsider, and it''s likely that — no matter how long you spend in the city — you''ll leave an outsider, if you leave at all.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander'), 'Feature: Wanderer', 'You have an excellent memory for maps and geography, and you can always recall the general layout of terrain, settlements, and other features around you. In addition, you can find food and fresh water for yourself and up to five other people each day, provided that the land offers berries, small game, water, and so forth.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander'), 'Baldur''s Gate Feature: Immigrant Experience', 'The effects of a Baldur''s Gate feature can be used only while the character is in Baldur''s Gate—though, at the DM''s discretion, they might have applicable effects in situations similar to those in Baldur''s Gate.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander'), 'Outlander Origins', 'Origin', '1d10');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '1', 'Someone stole something precious from your people. You tracked the thief to the city gates, but finding clues in an urban environment is very different from tracking someone across the wilderness. You don''t know where to go from here, but your people need you to succeed.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '2', 'You''ve always been fascinated by the glitter and glamor of city life, so different from the slow pace of life in your homeland. Now you''re here, ready to make your mark in the world, but unsure how to begin.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '3', 'War, plague, famine, or a marauding monster ravaged your home, forcing you to flee for your life. You don''t even know how many of your people survived or where to find them. Alone or accompanied by a handful of equally bereft survivors, you must navigate a new life that you never asked for.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '4', 'You were captured by kidnappers and taken far from your home. The Knights of the Unicorn freed you and brought you here, but now you''re on your own.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '5', 'You were exiled for breaking a trivial-seeming taboo. For this seemingly minor transgression, you lost your friends, family, and homeland in one fell swoop, and were given little choice but to strike out on your own.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '6', 'A peddler once brought something astonishing to your homeland—a Gondan clockwork, shimmering cloth of gold, a trained speaking bird, or some other small wonder—and told you that it came from Baldur''s Gate. You''ve come to see the source of such wonders, and perhaps learn to create them.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander'), 'Specialty', 'Origin', '1d10');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '1', 'Forester');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '2', 'Trapper');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '3', 'Homesteader');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '4', 'Guide');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '5', 'Exile or outcast');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '6', 'Bounty hunter');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '7', 'Pilgrim');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '8', 'Tribal nomad');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '9', 'Hunter-gatherer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '10', 'Tribal marauder');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I''m driven by a wanderlust that led me away from home.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I watch over my friends as if they were a litter of newborn pups.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I once ran twenty-five miles without stopping to warn to my clan of an approaching orc horde. I''d do it again if I had to.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I have a lesson for every situation, drawn from observing nature.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I place no stock in wealthy or well-mannered folk. Money and manners won''t save you from a hungry owlbear.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I''m always picking things up, absently fiddling with them, and sometimes accidentally breaking them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I feel far more comfortable around animals than people.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I was, in fact, raised by wolves.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Change. Life is like the seasons, in constant change, and we must change with it. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Greater Good. It is each person''s responsibility to make the most happiness for the whole tribe. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Honor. If I dishonor myself, I dishonor my whole clan. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Might. The strongest are meant to rule. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Nature. The natural world is more important than all the constructs of civilization. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Glory. I must earn glory in battle, for myself and my clan. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'My family, clan, or tribe is the most important thing in my life, even when they are far from me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'An injury to the unspoiled wilderness of my home is an injury to me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'I will bring terrible wrath down on the evildoers who destroyed my homeland.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'I am the last of my tribe, and it is up to me to ensure their names enter legend.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'I suffer awful visions of a coming disaster and will do anything to prevent it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'It is my duty to provide children to sustain my tribe.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I am too enamored of ale, wine, and other intoxicants.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'There''s no room for caution in a life lived to the fullest.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I remember every insult I''ve received and nurse a silent resentment toward anyone who''s ever wronged me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I am slow to trust members of other races, tribes, and societies.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'Violence is my answer to almost any challenge.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Outlander') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'Don''t expect me to save those who can''t save themselves. It is nature''s way that the strong thrive and the weak perish.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Baldur''s Gate Sage', 'Baldur''s Gate has a modest academic community centered around the libraries of the High Hall and the various temples dedicated to gods of learning and innovation. Lecturers, researchers, and historians all participate with passing scholars from Candlekeep in a lively exchange of ideas, debating and collaborating in bookfilled halls across the Upper and Lower City. The city is also rife with opportunities for arcane study, although its masters are dispersed across individual wizards'' abodes and lack concentrated communities.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage'), 'Feature: Researcher', 'When you attempt to learn or recall a piece of lore, if you do not know that information, you often know where and from whom you can obtain it. Usually, this information comes from a library, scriptorium, university, or a sage or other learned person or creature, Your DM might rule that the knowledge you seek is secreted away in an almost inaccessible place, or that it simply cannot be found. Unearthing the deepest secrets of the multiverse can require an adventure or even a whole campaign.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage'), 'Baldur''s Gate Feature: Rumor Monger', 'The effects of a Baldur''s Gate feature can be used only while the character is in Baldur''s Gate—though, at the DM''s discretion, they might have applicable effects in situations similar to those in Baldur''s Gate.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage'), 'Specialty', 'Field of Study', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage') AND title = 'Field of Study' ORDER BY id DESC LIMIT 1), '1', 'Alchemist');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage') AND title = 'Field of Study' ORDER BY id DESC LIMIT 1), '2', 'Astronomer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage') AND title = 'Field of Study' ORDER BY id DESC LIMIT 1), '3', 'Discredited academic');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage') AND title = 'Field of Study' ORDER BY id DESC LIMIT 1), '4', 'Librarian');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage') AND title = 'Field of Study' ORDER BY id DESC LIMIT 1), '5', 'Professor');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage') AND title = 'Field of Study' ORDER BY id DESC LIMIT 1), '6', 'Researcher');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage') AND title = 'Field of Study' ORDER BY id DESC LIMIT 1), '7', 'Wizard''s apprentice');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage') AND title = 'Field of Study' ORDER BY id DESC LIMIT 1), '8', 'Scribe');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I use polysyllabic words that convey the impression of great erudition.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I''ve read every book in the world''s greatest libraries—or I like to boast that I have.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I''m used to helping out those who aren''t as smart as I am, and I patiently explain anything and everything to others.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'There''s nothing I like more than a good mystery.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I''m willing to listen to every side of an argument before I make my own judgment.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I... speak... slowly... when talking... to idiots,... which... almost... everyone... is... compared... to me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I am horribly, horribly awkward in social situations.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I''m convinced that people are always trying to steal my secrets.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Knowledge. The path to power and self-improvement is through knowledge. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Beauty. What is beautiful points us beyond itself toward what is true. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Logic. Emotions must not cloud our logical thinking. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'No Limits. Nothing should fetter the infinite possibility inherent in all existence. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Power. Knowledge is the path to power and domination. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Self-Improvement. The goal of a life of study is the betterment of oneself. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'It is my duty to protect my students.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I have an ancient text that holds terrible secrets that must not fall into the wrong hands.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I work to preserve a library, university, scriptorium, or monastery.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'My life''s work is a series of tomes related to a specific field of lore.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I''ve been searching my whole life for the answer to a certain question.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I sold my soul for knowledge. I hope to do great deeds and win it back.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I am easily distracted by the promise of information.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'Most people scream and run when they see a demon. I stop and take notes on its anatomy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'Unlocking an ancient mystery is worth the price of a civilization.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I overlook obvious solutions in favor of complicated ones.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I speak without really thinking through my words, invariably insulting others.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sage') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I can''t keep a secret to save my life, or anyone else''s.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Baldur''s Gate Sailor', 'Baldur''s Gate was founded by sailors, and its harbor is still the city''s beating heart. Several patriars are descended from captains of yore, the commerce of the Lower City is built on the port, and even the Outer City''s rhythms are dominated by the ebb and flow of river trade. Because sailors are as fundamental and ubiquitous to Baldur''s Gate as the cobbles on its streets, characters with this background are common.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sailor'), 'Feature: Ship''s Passage', 'When you need to, you can secure free passage on a sailing ship for yourself and your adventuring companions. You might sail on the ship you served on, or another ship you have good relations with (perhaps one captained by a former crewmate). Because you''re calling in a favor, you can''t be certain of a schedule or route that will meet your every need. Your Dungeon Master will determine how long it takes to get where you need to go. In return for your free passage, you and your companions are expected to assist the crew during the voyage.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sailor'), 'Baldur''s Gate Feature: Smuggler''s Sense', 'The effects of a Baldur''s Gate feature can be used only while the character is in Baldur''s Gate—though, at the DM''s discretion, they might have applicable effects in situations similar to those in Baldur''s Gate.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sailor'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sailor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'My friends know they can rely on me, no matter what.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sailor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I work hard so that I can play hard when the work is done.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sailor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I enjoy sailing into new ports and making new friends over a flagon of ale.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sailor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I stretch the truth for the sake of a good story.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sailor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'To me, a tavern brawl is a nice way to get to know a new city.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sailor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I never pass up a friendly wager.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sailor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'My language is as foul as an otyugh nest.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sailor') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I like a job well done, especially if I can convince someone else to do it.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sailor'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sailor') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Respect. The thing that keeps a ship together is mutual respect between captain and crew. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sailor') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Fairness. We all do the work, so we all share in the rewards. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sailor') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Freedom. The sea is freedom—the freedom to go anywhere and do anything. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sailor') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Mastery. I''m a predator, and the other ships on the sea are my prey. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sailor') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'People. I''m committed to my crewmates, not to ideals. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sailor') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Aspiration. Someday, I''ll own my own ship and chart my own destiny. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sailor'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sailor') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I''m loyal to my captain first, everything else second.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sailor') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'The ship is most important—crewmates and captains come and go.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sailor') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I''ll always remember my first ship.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sailor') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'In a harbor town, I have a paramour whose eyes nearly stole me from the sea.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sailor') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I was cheated out of my fair share of the profits, and I want to get my due.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sailor') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'Ruthless pirates murdered my captain and crewmates, plundered our ship, and left me to die. Vengeance will be mine.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sailor'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sailor') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I follow orders, even if I think they''re wrong.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sailor') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I''ll say anything to avoid having to do extra work.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sailor') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'Once someone questions my courage, I never back down no matter how dangerous the situation.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sailor') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'Once I start drinking, it''s hard for me to stop.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sailor') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I can''t help but pocket loose coins and other trinkets I come across.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Sailor') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'My pride will probably lead to my destruction.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Baldur''s Gate Soldier', 'Mercenaries, private guards, Watch soldiers, and members of the Flaming Fist number among just a few of the many soldiers on the streets of Baldur''s Gate.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier'), 'Feature: Military Rank', 'You have a military rank from your career as a soldier. Soldiers loyal to your former military organization still recognize your authority and influence, and they defer to you if they are of a lower rank. You can invoke your rank to exert influence over other soldiers and requisition simple equipment or horses for temporary use. You can also usually gain access to friendly military encampments and fortresses where your rank is recognized.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier'), 'Baldur''s Gate Feature: City Guard', 'The effects of a Baldur''s Gate feature can be used only while the character is in Baldur''s Gate—though, at the DM''s discretion, they might have applicable effects in situations similar to those in Baldur''s Gate.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier'), 'Baldur''s Gate Feature: Loyalty Test', 'The effects of a Baldur''s Gate feature can be used only while the character is in Baldur''s Gate—though, at the DM''s discretion, they might have applicable effects in situations similar to those in Baldur''s Gate.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier'), 'Specialty', 'Role', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier') AND title = 'Role' ORDER BY id DESC LIMIT 1), '1', 'Officer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier') AND title = 'Role' ORDER BY id DESC LIMIT 1), '2', 'Scout');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier') AND title = 'Role' ORDER BY id DESC LIMIT 1), '3', 'Infantry');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier') AND title = 'Role' ORDER BY id DESC LIMIT 1), '4', 'Cavalry');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier') AND title = 'Role' ORDER BY id DESC LIMIT 1), '5', 'Healer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier') AND title = 'Role' ORDER BY id DESC LIMIT 1), '6', 'Quartermaster');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier') AND title = 'Role' ORDER BY id DESC LIMIT 1), '7', 'Standard-bearer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier') AND title = 'Role' ORDER BY id DESC LIMIT 1), '8', 'Support staff (cook, blacksmith, or the like)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I''m always polite and respectful.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I''m haunted by memories of war. I can''t get the images of violence out of my mind.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I''ve lost too many friends, and I''m slow to make new ones.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I''m full of inspiring and cautionary tales from my military experience relevant to almost every combat situation.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I can stare down a hell hound without flinching.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I enjoy being strong and like breaking things.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I have a crude sense of humor.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I face problems head-on. A simple, direct solution is the best path to success.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Greater Good. Our lot is to lay down our lives in defense of others. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Responsibility. I do what I must and obey just authority. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Independence. When people follow orders blindly, they embrace a kind of tyranny. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Might. In life as in war, the stronger force wins. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Live and Let Live. Ideals aren''t worth killing over or going to war for. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Nation. My city, nation, or people are all that matter. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I would still lay down my life for the people I served with.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Someone saved my life on the battlefield. To this day, I will never leave a friend behind.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'My honor is my life.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I''ll never forget the crushing defeat my company suffered or the enemies who dealt it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Those who fight beside me are those worth dying for.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I fight for those who cannot fight for themselves.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'The monstrous enemy we faced in battle still leaves me quivering with fear.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I have little respect for anyone who is not a proven warrior.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I made a terrible mistake in battle that cost many lives—and I would do anything to keep that mistake secret.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'My hatred of my enemies is blind and unreasoning.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I obey the law, even if the law causes misery.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Soldier') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I''d rather eat my armor than admit when I''m wrong.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Baldur''s Gate Urchin', 'Bands of orphans and runaways band together in the Outer City, running after passersby in ragged throngs to plead for scraps. In the Lower City, urchins are often recruited into the lowest echelons of the Guild or pressed into dirty and dangerous work by unscrupulous masters.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Urchin'), 'Feature: City Secrets', 'You know the secret patterns and flow to cities and can find passages through the urban sprawl that others would miss. When you are not in combat, you (and companions you lead) can travel between any two locations in the city twice as fast as your speed would normally allow.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Urchin'), 'Baldur''s Gate Feature: Gateguide Connection', 'The effects of a Baldur''s Gate feature can be used only while the character is in Baldur''s Gate—though, at the DM''s discretion, they might have applicable effects in situations similar to those in Baldur''s Gate.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Urchin'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Urchin') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I hide scraps of food and trinkets away in my pockets.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Urchin') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I ask a lot of questions.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Urchin') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I like to squeeze into small places where no one else can get to me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Urchin') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I sleep with my back to a wall or tree, with everything I own wrapped in a bundle in my arms.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Urchin') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I eat like a pig and have bad manners.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Urchin') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I think anyone who''s nice to me is hiding evil intent.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Urchin') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I don''t like to bathe.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Urchin') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I bluntly say what others are hinting at or hiding.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Urchin'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Urchin') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Respect. All people, rich or poor, deserve respect. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Urchin') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Community. We have to take care of each other, because no one else is going to do it. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Urchin') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Change. The low are lifted up, and the high and mighty are brought down. Change is the nature of things. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Urchin') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Retribution. The rich need to be shown what life and death are like in the gutters. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Urchin') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'People. I help the people who help me—that''s what keeps us alive. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Urchin') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Aspiration. I''m going to prove that I''m worthy of a better life. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Urchin'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Urchin') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'My town or city is my home, and I''ll fight to defend it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Urchin') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I sponsor an orphanage to keep others from enduring what I was forced to endure.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Urchin') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I owe my survival to another urchin who taught me to live on the streets.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Urchin') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I owe a debt I can never repay to the person who took pity on me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Urchin') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I escaped my life of poverty by robbing an important person, and I''m wanted for it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Urchin') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'No one else should have to endure the hardships I''ve been through.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Urchin'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Urchin') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'If I''m outnumbered, I will run away from a fight.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Urchin') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'Gold seems like a lot of money to me, and I''ll do just about anything for more of it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Urchin') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I will never fully trust anyone other than myself.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Urchin') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I''d rather kill someone in their sleep than fight fair.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Urchin') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'It''s not stealing if I need it more than someone else.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Baldur''s Gate Urchin') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'People who can''t take care of themselves get what they deserve.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Faceless', 'Being who you are, you could never be a hero. Whether due to your class, your people, your family, or your sins, something about you prevents you from effectively pursuing the path you''ve chosen. Even so, that doesn''t stop you. You''ve left your old face behind, taking on a new persona, becoming something more.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Faceless'), 'Faceless Persona', 'Faceless Persona', '1d10');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Faceless Persona' ORDER BY id DESC LIMIT 1), '1', 'A flamboyant spy or brigand');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Faceless Persona' ORDER BY id DESC LIMIT 1), '2', 'The incarnation of a nation or people');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Faceless Persona' ORDER BY id DESC LIMIT 1), '3', 'A scoundrel with a masked guise');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Faceless Persona' ORDER BY id DESC LIMIT 1), '4', 'A vengeful spirit');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Faceless Persona' ORDER BY id DESC LIMIT 1), '5', 'The manifestation of a deity or your faith');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Faceless Persona' ORDER BY id DESC LIMIT 1), '6', 'One whose beauty is greatly accented using makeup');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Faceless Persona' ORDER BY id DESC LIMIT 1), '7', 'An impersonation of another hero');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Faceless Persona' ORDER BY id DESC LIMIT 1), '8', 'The embodiment of a school of magic');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Faceless Persona' ORDER BY id DESC LIMIT 1), '9', 'A warrior with distinctive armor');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Faceless Persona' ORDER BY id DESC LIMIT 1), '10', 'A disguise with animalistic or monstrous characteristics, meant to inspire fear');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Faceless'), 'Feature: Dual Personalities', 'Most of your fellow adventurers and the world know you as your persona. Those who seek to learn more about you—your weaknesses, your origins, your purpose—find themselves stymied by your disguise. Upon donning a disguise and behaving as your persona, you are unidentifiable as your true self. By removing your disguise and revealing your true face, you are no longer identifiable as your persona. This allows you to change appearances between your two personalities as often as you wish, using one to hide the other or serve as convenient camouflage. However, should someone realize the connection between your persona and your true self, your deception might lose its effectiveness.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Faceless'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I''m earnest and uncommonly direct.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I strive to have no personality—it''s easier to forget what''s hardly there.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I treasure a memento of the person or instance that set me upon my path.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I sleep just as much as I need to and on an unusual schedule.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I think far ahead, a detachedness often mistaken for daydreaming.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I cultivate a single obscure hobby or study and eagerly discuss it at length.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I am ever learning how to be among others—when to stay quiet, when to laugh.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I behave like an extreme opposite of my persona.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Faceless'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Justice. Place in society shouldn''t determine one''s access to what is right. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Security. Doing what must be done can''t bring the innocent to harm. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Confusion. Deception is a weapon. Strike from where your foes won''t expect. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Infamy. My name will be a malediction, a curse that fulfills my will. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Incorruptibility. Be a symbol, and leave your');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Faceless'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'Anonymity. It''s my deeds that should be remembered, not their instrument. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Faceless'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I do everything for my family. My first thought is keeping them safe.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'What I do, I do for the world. The people don''t realize how much they need me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I''ve seen too many in need. I must not fail them as everyone else has.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I stand in opposition, lest the wicked go unopposed.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I am exceptional. I do this because no one else can, and no one can stop me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I do everything for those who were taken from me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I am callous about death. It comes to us all eventually.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I never make eye contact or hold it unflinchingly.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I have no sense of humor. Laughing is uncomfortable and embarrassing.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I overexert myself, sometimes needing to recuperate for a day or more.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I think far ahead, a detachedness often mistaken for daydreaming.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Faceless') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I see morality entirely in black and white.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('House Agent', 'You have sworn fealty to a dragonmarked house. If you have a dragonmark, you''re likely a member of one of the house''s influential families; otherwise you''re an outsider who hopes to make your fortune through the house. Your main task is to serve as the eyes of your house, but you could be called on at any time to act as its hand. Such missions can be perilous but lucrative.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'House Agent'), 'Role', 'Role', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'House Agent') AND title = 'Role' ORDER BY id DESC LIMIT 1), '1', 'Acquisition');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'House Agent') AND title = 'Role' ORDER BY id DESC LIMIT 1), '2', 'Investigation');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'House Agent') AND title = 'Role' ORDER BY id DESC LIMIT 1), '3', 'Research & Development');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'House Agent') AND title = 'Role' ORDER BY id DESC LIMIT 1), '4', 'Security');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'House Agent') AND title = 'Role' ORDER BY id DESC LIMIT 1), '5', 'Intimidation');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'House Agent') AND title = 'Role' ORDER BY id DESC LIMIT 1), '6', 'Exploration');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'House Agent') AND title = 'Role' ORDER BY id DESC LIMIT 1), '7', 'Negotiation');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'House Agent') AND title = 'Role' ORDER BY id DESC LIMIT 1), '8', 'Covert Operations');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'House Agent'), 'Feature: House Connections', 'As an agent of your house, you can always get food and lodging for yourself and your friends at a house enclave. When the house assigns you a mission, it will usually provide you with the necessary supplies and transportation. Beyond this, you have many old friends, mentors, and rivals in your house, and you may encounter one of them when you interact with a house business. The degree to which such acquaintances are willing to help you depends on your current standing in your house.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'House Agent'), 'Suggested Characteristics', 'Personality Trait', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'House Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I''m always looking to improve efficiency.');
