INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Athlete') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I feel most at peace during physical exertion, be it exercise or battle.');
