INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Athlete') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'When I see others struggling, I offer to help.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Athlete') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I love to trade banter and gibes.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Athlete') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'Anything worth doing is worth doing best.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Athlete') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I get irritated if people praise someone else and not me.');
