INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Athlete') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Growth. Lessons hide in victory and defeat. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Athlete') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'My teammates are my family.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Athlete') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'I will overcome a rival and prove myself their better.');
