INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One (Van Richten''s)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '10', 'Revelation. I need to know what lies beyond the mysteries of death, the world, or the Mists.');
