INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I''ve seen great darkness, and I''m committed to being a light against it—the light of all lights.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'Someone I love has become a monster, murderer, or other threat. It''s up to me to redeem them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'The world has been convinced of a terrible lie. It''s up to me to reveal the truth.');
