INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '8', 'A great evil dwells within me. I will fight against it and the world''s other evils for as long as I can.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '9', 'I''m desperately seeking a cure to an affliction or a curse, either for someone close to me for myself.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '10', 'Spirits are drawn to me. I do all I can to help them find peace.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '11', 'I use my cunning mind to solve mysteries and find justice for those who''ve been wronged.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '12', 'I lost someone I care about, but I still see them in guilty visions, recurring dreams, or as a spirit.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I believe doom follows me and that anyone who gets close to me will face a tragic end.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I''m convinced something is after me, appearing in mirrors, dreams, and places where no one could.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I''m especially superstitious and live life seeking to avoid bad luck, wicked spirits, or the Mists.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I''ve done unspeakable evil and will do anything to prevent others from finding out.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I am exceptionally credulous and believe any story or legend immediately.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I''m a skeptic and don''t believe in the power of rituals, religion, superstition, or spirits.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '7', 'I know my future is written and that anything I do will lead to a prophesied end.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '8', 'I need to find the best in everyone and everything, even when that means denying obvious malice.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '9', 'I''ve seen the evil of a type of place—like forests, cities, or graveyards—and resist going there.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '10', 'I''m exceptionally cautious, planning laboriously and devising countless contingencies.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '11', 'I have a reputation for defeating a great evil, but that''s a lie and the wicked force knows.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Haunted One (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '12', 'I know the ends always justify the means and am quick to make sacrifices to attain my goals.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Investigator (Van Richten''s)', 'You relentlessly seek the truth. Perhaps you''re motivated by belief in the law and a sense of universal justice, or maybe that very law has failed you and you seek to make things right. You could have witnessed something remarkable or terrible, and now you must know more about this hidden truth. Or maybe you''re a detective for hire, uncovering secrets for well-paying clients. Whether the mysteries you''re embroiled in are local crimes or realm-spanning conspiracies, you''re driven by a personal need to hunt down even the most elusive clues and reveal what others would keep hidden in the shadows.', 'Choose two from among Insight, Investigation, or Perception', 'Disguise kit, thieves'' tools', '', 'A magnifying glass, evidence from a past case (choose one or roll for a trinket from the Horror Trinkets table later in this chapter), a set of common clothes, and 10 gp', (SELECT id FROM core_book_sources WHERE raw_source = 'Van Richten''s Guide to Ravenloft p. 35'));
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)'), 'Path to Mystery', 'Case', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)') AND title = 'Case' ORDER BY id DESC LIMIT 1), '1', 'A friend was wrongfully accused of murder. You tracked down the actual killer, proving your friend''s innocence and starting your career as a detective.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)') AND title = 'Case' ORDER BY id DESC LIMIT 1), '2', 'You''re told you went missing for weeks. When you were found, you had no memory of being gone. Now you search to discover what happened to you.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)') AND title = 'Case' ORDER BY id DESC LIMIT 1), '3', 'You helped a spirit find peace by finding its missing corpse. Ever since, other spectral clients have sought you out to help them find rest.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)') AND title = 'Case' ORDER BY id DESC LIMIT 1), '4', 'You revealed that the monsters terrorizing your home were illusions created by a cruel mage. The magic-user escaped, but you''ve continued to uncover magical hoaxes.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)') AND title = 'Case' ORDER BY id DESC LIMIT 1), '5', 'You were wrongfully accused and convicted of a crime. You managed to escape and seek to help others avoid the experience you suffered, even while still being pursued by the law.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)') AND title = 'Case' ORDER BY id DESC LIMIT 1), '6', 'You survived the destructive use of a magic device that wiped out your home. Members of a secret organization found you. You now work with them, tracking down dangerous supernatural phenomena and preventing them from doing harm.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)') AND title = 'Case' ORDER BY id DESC LIMIT 1), '7', 'You found evidence of a conspiracy underpinning society. You tried to expose this mysterious cabal, but no one believed you. You''re still trying to prove what you know is true.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)') AND title = 'Case' ORDER BY id DESC LIMIT 1), '8', 'You got a job with an agency that investigates crimes that local law enforcement can''t solve. You often wonder which you value more, the truth or your pay.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)'), 'Feature: Official Inquiry', 'You''re experienced at gaining access to people and places to get the information you need. Through a combination of fast-talking, determination, and official-looking documentation, you can gain access to a place or an individual related to a crime you''re investigating. Those who aren''t involved in your investigation avoid impeding you or pass along your requests. Additionally, local law enforcement has firm opinions about you, viewing you as either a nuisance or one of their own.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)'), 'Horror Trinkets', 'Trinket', '1d100');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '01', 'A picture you drew as a child of your imaginary friend');
