INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '16', 'A teacup from a child''s tea set, stained with blood');
