INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '12', 'Truth. I care about the truth above all else, even if it doesn''t benefit anyone.');
