INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'Everything I do is in the service of a powerful master, one I must keep a secret from everyone.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I owe much to my vanished mentor. I seek to continue their work even as I search to find them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I''ve seen great darkness, and I''m committed to being a light against it—the light of all lights.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'Someone I love has become a monster, murderer, or other threat. It''s up to me to redeem them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'The world has been convinced of a terrible lie. It''s up to me to reveal the truth.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '7', 'I deeply miss someone and am quick to adopt people who remind me of them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '8', 'A great evil dwells within me. I will fight against it and the world''s other evils for as long as I can.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '9', 'I''m desperately seeking a cure to an affliction or a curse, either for someone close to me for myself.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '10', 'Spirits are drawn to me. I do all I can to help them find peace.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '11', 'I use my cunning mind to solve mysteries and find justice for those who''ve been wronged.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '12', 'I lost someone I care about, but I still see them in guilty visions, recurring dreams, or as a spirit.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I believe doom follows me and that anyone who gets close to me will face a tragic end.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I''m convinced something is after me, appearing in mirrors, dreams, and places where no one could.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I''m especially superstitious and live life seeking to avoid bad luck, wicked spirits, or the Mists.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I''ve done unspeakable evil and will do anything to prevent others from finding out.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I am exceptionally credulous and believe any story or legend immediately.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I''m a skeptic and don''t believe in the power of rituals, religion, superstition, or spirits.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '7', 'I know my future is written and that anything I do will lead to a prophesied end.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '8', 'I need to find the best in everyone and everything, even when that means denying obvious malice.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '9', 'I''ve seen the evil of a type of place—like forests, cities, or graveyards—and resist going there.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '10', 'I''m exceptionally cautious, planning laboriously and devising countless contingencies.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '11', 'I have a reputation for defeating a great evil, but that''s a lie and the wicked force knows.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (Van Richten''s)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '12', 'I know the ends always justify the means and am quick to make sacrifices to attain my goals.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Feylost', 'You grew up in the Feywild after disappearing from your home plane as a child. Perhaps you were spirited away by kindly Fey who thought you were destined for great things. Perhaps you stumbled through a fey crossing by chance during a twilight stroll in the woods. Perhaps you were kidnapped by evil Fey but escaped from their clutches. Whatever the manner of your disappearance, you gradually fell under the Feywild''s spell and learned a little about the nature of the mercurial tricksters that dwell there.
