INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Fey Mark' ORDER BY id DESC LIMIT 1), '2', 'You have a sweet scent, like that of nectar or honey.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Fey Mark' ORDER BY id DESC LIMIT 1), '3', 'You have long whiskers like those of a cat.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Fey Mark' ORDER BY id DESC LIMIT 1), '4', 'Your ears are covered with soft tufts of fur.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Fey Mark' ORDER BY id DESC LIMIT 1), '5', 'Your skin sparkles in moonlight.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Fey Mark' ORDER BY id DESC LIMIT 1), '6', 'Flowers either bloom or wilt (your choice) in your presence.');
