INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Fey Mark' ORDER BY id DESC LIMIT 1), '8', 'You have a tail like that of a dog or another animal.');
