INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Feylost'), 'Feature: Feywild Connection', 'Your mannerisms and knowledge of fey customs are recognized by natives of the Feywild, who see you as one of their own. Because of this, friendly Fey creatures are inclined to come to your aid if you are lost or need help in the Feywild.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Feylost'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I''m haunted by fey laughter that only I can hear, though I know it''s just my mind playing tricks on me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Like a nomad, I can''t settle down in one place for very long.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Good music makes me weep like a baby.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Wherever I go, I try to bring a little of the warmth and tranquility of home with me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I have never lost my childlike sense of wonder.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'When I have a new idea, I get wildly excited about it until I come up with another, better idea.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I live by my own set of weird and wonderful rules.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I can''t bring myself to trust most adults.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Friendship. I never leave a friend behind. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Empathy. No creature should be made to suffer. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Wanderlust. I prefer to take the less traveled path. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Changeability. Change is good, which is why I live by an ever-changing set of rules. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Honor. A deal is a deal, and I would never break one. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Rule of Three. Everything in the multiverse happens in threes. I see the "rule of three" everywhere. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'Obsession. I won''t let go of a grudge. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'Greed. I will do whatever it takes to get what I want, regardless of the harm it might cause. (Evil)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Feylost'), 'Suggested Characteristics', 'Bond', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I would never break my word.');
