INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I do what I can to protect the natural world.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'A trusted friend is the most important thing in the multiverse to me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I can''t bring myself to harm a Fey creature, either because I consider myself one or because I fear the repercussions.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'The Witchlight Carnival feels like home to me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '7', 'I''m drawn to the Feywild and long to return there, if only for a short while.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '8', 'I feel indebted to Mister Witch and Mister Light for giving me a home and a purpose.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Feylost'), 'Suggested Characteristics', 'Flaw', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I easily lose track of time. My poor sense of time means I''m always late.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I think the whole multiverse is out to get me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I''m always operating under a tight timeline, and I''m obsessed with keeping everything on schedule.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I''m a kleptomaniac who covets shiny, sparkling treasure.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I''m forgetful. Sometimes I can''t remember even the simplest things.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I never give away anything for free and always expect something in return.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '7', 'I have many vices and tend to indulge them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feylost') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '8', 'I''m always changing my mind—well, almost always.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Witchlight Hand', 'You crept into the Witchlight Carnival as a child or youth and never looked back, earning a place among those who work behind the scenes to keep the carnival in business. As a hand, you work hard and party hard. The carnival has borne you to many fantastic worlds, circling back around to your home world once every eight years, but you know almost nothing about these worlds because you spend all your time in the carnival. You know the other hands well, but the carnival''s owners—Mister Witch and Mister Light—remain mysterious to you even after all these years.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Witchlight Hand'), 'Carnival Companion', 'Carnival Companion', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Carnival Companion' ORDER BY id DESC LIMIT 1), '1', 'Old, cantankerous Witchlight hand');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Carnival Companion' ORDER BY id DESC LIMIT 1), '2', 'Young, impressionable Witchlight hand');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Carnival Companion' ORDER BY id DESC LIMIT 1), '3', 'Performer (such as an acrobat, a clown, or a musician)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Carnival Companion' ORDER BY id DESC LIMIT 1), '4', 'Retired performer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Carnival Companion' ORDER BY id DESC LIMIT 1), '5', 'Seasoned animal trainer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Carnival Companion' ORDER BY id DESC LIMIT 1), '6', 'Old blink dog');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Carnival Companion' ORDER BY id DESC LIMIT 1), '7', 'Cheery sprite');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Carnival Companion' ORDER BY id DESC LIMIT 1), '8', 'Harmless, magical wisp of light (no stat block required) that has a flying speed of 30 feet, can hover, and sheds bright light in a 5-foot radius and dim light for an additional 5 feet');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Witchlight Hand'), 'Feature: Carnival Fixture', 'The Witchlight Carnival provides you with free, modest lodging and food. In addition, you may wander about the carnival and partake of its many wonders at no cost to you, provided you don''t disrupt its shows or cause any other trouble.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Witchlight Hand'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I''m haunted by fey laughter that only I can hear, though I know it''s just my mind playing tricks on me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Like a nomad, I can''t settle down in one place for very long.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Good music makes me weep like a baby.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Wherever I go, I try to bring a little of the warmth and tranquility of home with me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I have never lost my childlike sense of wonder.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'When I have a new idea, I get wildly excited about it until I come up with another, better idea.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I live by my own set of weird and wonderful rules.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I can''t bring myself to trust most adults.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Friendship. I never leave a friend behind. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Empathy. No creature should be made to suffer. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Wanderlust. I prefer to take the less traveled path. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Changeability. Change is good, which is why I live by an ever-changing set of rules. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Honor. A deal is a deal, and I would never break one. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Rule of Three. Everything in the multiverse happens in threes. I see the "rule of three" everywhere. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'Obsession. I won''t let go of a grudge. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'Greed. I will do whatever it takes to get what I want, regardless of the harm it might cause. (Evil)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Witchlight Hand'), 'Suggested Characteristics', 'Bond', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I would never break my word.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I find magic in all its forms to be compelling. The more magical a place, the more I am drawn to it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I do what I can to protect the natural world.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'A trusted friend is the most important thing in the multiverse to me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I can''t bring myself to harm a Fey creature, either because I consider myself one or because I fear the repercussions.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'The Witchlight Carnival feels like home to me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '7', 'I''m drawn to the Feywild and long to return there, if only for a short while.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '8', 'I feel indebted to Mister Witch and Mister Light for giving me a home and a purpose.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Witchlight Hand'), 'Suggested Characteristics', 'Flaw', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I easily lose track of time. My poor sense of time means I''m always late.');
