INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I''m always operating under a tight timeline, and I''m obsessed with keeping everything on schedule.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I''m a kleptomaniac who covets shiny, sparkling treasure.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I''m forgetful. Sometimes I can''t remember even the simplest things.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I never give away anything for free and always expect something in return.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '7', 'I have many vices and tend to indulge them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Witchlight Hand') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '8', 'I''m always changing my mind—well, almost always.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Strixhaven Lorehold Student', 'You have spent your youth preparing to be a student of Lorehold College, reading every book on the college''s recommended reading list for prospective students. Your academic passions are in the broad field of history. You might dream of using magic to contact great historical figures or your own ancestors or of recreating the mighty magic of a distant age.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Strixhaven Lorehold Student'), 'Feature: Lorehold Initiate', 'You gain the Strixhaven Initiate feat and must choose Lorehold within it.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Strixhaven Lorehold Student'), 'Building a Lorehold Character', 'Any class or subclass that deals with knowledge of the past can be a good fit in Lorehold. Bards thrive in Lorehold, and wizards (particularly those of the School of Divination) are numerous among its students. Clerics (often with the Knowledge or Light domains) are also quite common.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Strixhaven Lorehold Student'), 'Lorehold Trinkets', 'Trinket', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Lorehold Student') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '1', 'A map made of concentric circles that can be rotated around the page');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Lorehold Student') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '2', 'A puzzle box bedecked with amber');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Lorehold Student') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '3', 'A dented brass compass with a red needle');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Lorehold Student') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '4', 'The head of a broken statue that houses the consciousness of a snarky sage');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Lorehold Student') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '5', 'A sheet of parchment embossed with tactile lettering, glowing red');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Lorehold Student') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '6', 'A broken dagger with a wavy blade and a serpentine hilt');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Strixhaven Lorehold Student'), 'Suggested Characteristics', 'Personality Trait', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Lorehold Student') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I thrive on esoteric lore. The more obscure the historical references I can include in everyday conversation, the better.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Lorehold Student') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'By searching for these lost artifacts, I hope to find who I really am along the way.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Lorehold Student') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I can barely go a minute without talking about my research. I have so much knowledge in my head, and it needs to be let out somewhere!');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Lorehold Student') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'The spirits of the dead are so much more interesting to talk with than living classmates.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Lorehold Student') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I can speak eloquently about the historical ramifications of an ancient war. But ask me to add two-digit numbers together, and I''m a mess.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Lorehold Student') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'In the end, it''s all just entropy. Everything falls apart someday.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Strixhaven Prismari Student', 'You are an artist, and you have been preparing to hone your craft according to the traditions of Prismari College. Whatever your medium—sculpture, dance, paint, music, drama, architecture, or any other field—you have grand visions of bringing your art to life in a fusion of magic and creativity.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Strixhaven Prismari Student'), 'Feature: Prismari Initiate', 'You gain the Strixhaven Initiate feat and must choose Prismari within it.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Strixhaven Prismari Student'), 'Building a Prismari Character', 'Any class or subclass that wields elemental forces of cold, fire, lightning, and wind can be a good fit in Prismari. Druids and sorcerers are common in Prismari, and wizards who study the Schools of Evocation or Transmutation are also well represented. Clerics aren''t very common in this school, but some with the Tempest domain end up here.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Strixhaven Prismari Student'), 'Prismari Trinkets', 'Trinket', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Prismari Student') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '1', 'A pair of rose-tinted glasses with glittery frames');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Prismari Student') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '2', 'A stoppered glass bottle that, when opened, plays a brassy orchestral piece');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Prismari Student') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '3', 'A quartet of hovering water motes in a vial');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Prismari Student') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '4', 'A bandolier of watercolor paints');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Prismari Student') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '5', 'A tiara capped with a crystal that crackles with harmless lightning');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Prismari Student') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '6', 'An iridescent quill');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Strixhaven Prismari Student'), 'Suggested Characteristics', 'Personality Trait', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Prismari Student') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I''m the life of the party, and I expect everyone''s attention when I walk into a room.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Prismari Student') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Two weeks ago, I was enthralled with my latest project. Now, I think it''s garbage and deserves to be destroyed.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Prismari Student') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I believe everyone has the ability to express their truest selves through art, and I''m happy to quietly push them in the right direction.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Prismari Student') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Everyone is a critic, and I work to win them all over.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Prismari Student') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I''m beset with such an overwhelming sense of ennui regarding my art. Nothing quite captures my attention anymore.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Prismari Student') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Instead of confronting my negative emotions, I channel them into explosive artistic displays.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Strixhaven Quandrix Student', 'In preparation to become a student of Quandrix College, you have spent years working through the college''s math problems for prospective students. Your interests concentrate on the mathematical principles and patterns found in the natural world. You might lean more toward mathematics or physics, or you delve into more abstract studies of metaphysics, arcana, or logic.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Strixhaven Quandrix Student'), 'Feature: Quandrix Initiate', 'You gain the Strixhaven Initiate feat and must choose Quandrix within it.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Strixhaven Quandrix Student'), 'Building a Quandrix Character', 'Any spellcasting class or subclass can work well for a Quandrix character. Both the scholarly focus of wizards (especially those who study the Schools of Abjuration, Illusion, or Transmutation) and the metamagic manipulations of sorcerers are welcome in Quandrix, and many druids explore the patterns of nature in Quandrix as well. A few clerics, particularly those with the Knowledge or Nature domains, study in Quandrix as well.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Strixhaven Quandrix Student'), 'Quandrix Trinkets', 'Trinket', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Quandrix Student') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '1', 'A small succulent in a dodecahedral clay pot');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Quandrix Student') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '2', 'A blue knit hat that looks a bit like a bottle folding in on itself');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Quandrix Student') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '3', 'A model hypercube carved from green crystal, showcasing the fourth dimension');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Quandrix Student') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '4', 'A crumpled test on the theory of gravity manipulation, with a failing grade on the front and the name of a famous Quandrix professor');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Quandrix Student') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '5', 'A blue tetrahedron that, when tapped twice, projects a recording of an old mathematics lecture');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Quandrix Student') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '6', 'A round bread roll cut so that someone could spread butter on both halves without ever lifting the knife');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Strixhaven Quandrix Student'), 'Suggested Characteristics', 'Personality Trait', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Quandrix Student') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'When I find a subject I''m interested in, I won''t stop studying until I know everything about it. It keeps me up at night.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Quandrix Student') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I hope this all makes sense to me one day. Until then, I''m going to keep faking it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Quandrix Student') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Equations and patterns come naturally to my mind. I wish friendship came just as easily.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Quandrix Student') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I believe I''m always the smartest person in the room. And I''ll prove it, even if no one asks me to.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Quandrix Student') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'If these classes have taught me anything, it''s that reality is a lie, and nothing matters. So why bother?');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Quandrix Student') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Before I graduate, I want to achieve something mathematically impossible. I must leave a legacy!');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Strixhaven Silverquill Student', 'You have spent years practicing your writing and oration in the hope of becoming a student of Silverquill College. Your studies focus on language, literature, and the power of words, both magical and otherwise. The metaphorical power of words to illuminate or conceal is joined with the literal ability of magic to do the same in your academic pursuits.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Strixhaven Silverquill Student'), 'Feature: Silverquill Initiate', 'You gain the Strixhaven Initiate feat and must choose Silverquill within it.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Strixhaven Silverquill Student'), 'Building a Silverquill Character', 'Many bards find a home in Silverquill, putting the power of their voices to use with Silverquill magic. Wizards (especially those who study the Schools of Illusion and Enchantment) are common in Silverquill, as are warlocks. Clerics with the Divine Domains of Light and Trickery also fit in well among the mages of Silverquill.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Strixhaven Silverquill Student'), 'Silverquill Trinkets', 'Trinket', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Silverquill Student') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '1', 'A black leather notebook filled with half-finished poems');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Silverquill Student') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '2', 'A set of flashcards detailing different colloquialisms and their meanings');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Silverquill Student') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '3', 'A canteen that makes any liquid drunk from it taste sweet');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Silverquill Student') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '4', 'A forged permission slip granting access to the special archives of Strixhaven''s libraries');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Silverquill Student') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '5', 'A stylish silver pin that references a famous series of novels about warlocks');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Silverquill Student') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '6', 'A stack of small pieces of parchment, each enchanted to stick to whatever surface it''s pressed against and peel off easily');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Strixhaven Silverquill Student'), 'Suggested Characteristics', 'Personality Trait', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Silverquill Student') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I''ll say whatever I need to in order to maintain my high social status.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Silverquill Student') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I prefer saying the blunt truth over a pretty lie, and I don''t particularly care whose feelings I hurt.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Silverquill Student') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I believe that uplifting my peers is the best way to succeed.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Silverquill Student') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I''ve mastered the art of using humor as a defense, and I always have a charming joke ready.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Silverquill Student') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I always wait before speaking, analyzing the situation for whichever angle is most advantageous to my goals.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Silverquill Student') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'No one knows about the all-nighters I''ve pulled to keep my magic looking effortless, and I''m going to keep it that way.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Strixhaven Witherbloom Student', 'You filled your childhood with rudimentary alchemical experiments, all in the hope of becoming a student of Witherbloom College. The life sciences are the focus of your reading, which include biology and necromancy. Brewing strange concoctions with medicinal properties, exploring the anatomies of monsters, and cataloging swamp flora are the kinds of studies you might pursue at Witherbloom College.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Strixhaven Witherbloom Student'), 'Feature: Witherbloom Initiate', 'You gain the Strixhaven Initiate feat and must choose Witherbloom within it.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Strixhaven Witherbloom Student'), 'Building a Witherbloom Character', 'Druids and warlocks make up most of Witherbloom''s student body. A few wizards (mostly those who study the School of Necromancy) and clerics (including those with the Divine Domains of Life or Nature) also find their home in Witherbloom.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Strixhaven Witherbloom Student'), 'Witherbloom Trinkets', 'Trinket', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Witherbloom Student') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '1', 'A black bird-shaped mask, trimmed with glowing green thread');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Witherbloom Student') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '2', 'A set of rabbit bones');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Witherbloom Student') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '3', 'A pair of thick knee-high waders, stained with muck and moss');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Witherbloom Student') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '4', 'A slimy green tentacle, which occasionally wriggles');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Witherbloom Student') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '5', 'A notebook containing waterproof paper');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Witherbloom Student') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '6', 'A necklace of five small vials, each filled with luminescent white liquid');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Strixhaven Witherbloom Student'), 'Suggested Characteristics', 'Personality Trait', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Witherbloom Student') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I love brewing up a new recipe, even if some might be repulsed by my choice of ingredients. Or the final product. Or both.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Witherbloom Student') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'My fashion sense is like my garden: withered, black, and weird.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Witherbloom Student') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I''m going to befriend every single monster in this swamp if it''s the last thing I do.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Witherbloom Student') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Everything in this world dies eventually. The question is, what will you do with the time you have left?');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Witherbloom Student') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I know we just met, but when you die, may I have your bones? For research.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Strixhaven Witherbloom Student') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Don''t interrupt me; I''m brooding.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Astral Drifter', 'For longer than you can remember, you have traversed the Astral Sea. There, you experienced firsthand the wonders of the Silver Void: you stopped aging and no longer felt hunger or thirst. Driven by wanderlust, you drifted from one part of the Astral Sea to another, like a mote of dust on the wind. You have lost count of the decades that have passed since you arrived here.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Astral Drifter'), 'Longevity', 'You are 20d6 years older than you look, because you have spent that much time in the Astral Sea without aging.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Astral Drifter'), 'Feature: Divine Contact', 'Wandering Deity', '1d10');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Astral Drifter') AND title = 'Wandering Deity' ORDER BY id DESC LIMIT 1), '1', 'Corellon, god of art and magic (chaotic good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Astral Drifter') AND title = 'Wandering Deity' ORDER BY id DESC LIMIT 1), '2', 'Tymora, god of good fortune (chaotic good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Astral Drifter') AND title = 'Wandering Deity' ORDER BY id DESC LIMIT 1), '3', 'Fharlanghn, god of horizons and travel (neutral good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Astral Drifter') AND title = 'Wandering Deity' ORDER BY id DESC LIMIT 1), '4', 'Istus, god of fate and destiny (neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Astral Drifter') AND title = 'Wandering Deity' ORDER BY id DESC LIMIT 1), '5', 'Nuada, god of war and warriors (neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Astral Drifter') AND title = 'Wandering Deity' ORDER BY id DESC LIMIT 1), '6', 'Zivilyn, god of wisdom (neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Astral Drifter') AND title = 'Wandering Deity' ORDER BY id DESC LIMIT 1), '7', 'Arawn, god of life and death (neutral evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Astral Drifter') AND title = 'Wandering Deity' ORDER BY id DESC LIMIT 1), '8', 'Hecate, god of magic and moons (chaotic evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Astral Drifter') AND title = 'Wandering Deity' ORDER BY id DESC LIMIT 1), '9', 'Celestian, god of stars and wanderers (neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Astral Drifter') AND title = 'Wandering Deity' ORDER BY id DESC LIMIT 1), '10', 'Ptah, god of knowledge and secrets (lawful neutral)');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Wildspacer', 'You were raised in the void of Wildspace—home to asteroid miners, moon farmers, and other hardy folk. Perhaps you grew up in a far-flung settlement such as the Rock of Bral, or you spent your early years on the crew of a spelljamming ship, performing helpful chores such as swabbing the deck, loading and offloading cargo, and scraping barnacles off the hull.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Wildspacer'), 'Close Encounter', 'Creature', '1d10');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Wildspacer') AND title = 'Creature' ORDER BY id DESC LIMIT 1), '1', 'Beholder');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Wildspacer') AND title = 'Creature' ORDER BY id DESC LIMIT 1), '2', 'Cosmic horror*');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Wildspacer') AND title = 'Creature' ORDER BY id DESC LIMIT 1), '3', 'Feyr*');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Wildspacer') AND title = 'Creature' ORDER BY id DESC LIMIT 1), '4', 'Lunar dragon*');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Wildspacer') AND title = 'Creature' ORDER BY id DESC LIMIT 1), '5', 'Mind flayer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Wildspacer') AND title = 'Creature' ORDER BY id DESC LIMIT 1), '6', 'Neh-thalggu*');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Wildspacer') AND title = 'Creature' ORDER BY id DESC LIMIT 1), '7', 'Neogi*');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Wildspacer') AND title = 'Creature' ORDER BY id DESC LIMIT 1), '8', 'Space clown*');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Wildspacer') AND title = 'Creature' ORDER BY id DESC LIMIT 1), '9', 'Vampirate*');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Wildspacer') AND title = 'Creature' ORDER BY id DESC LIMIT 1), '10', 'Void scavver*');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Wildspacer'), 'Feature: Wildspace Adaptation', 'You gain the Tough feat from the Player''s Handbook. In addition, you learned how to adapt to zero gravity. Being weightless doesn''t give you disadvantage on any of your melee attack rolls (see "Weightlessness" in chapter 2).');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Knight Of Solamnia', 'You have trained to be a valorous warrior known as a Knight of Solamnia. Strict rules guide your every action, and you work to uphold them as you strive to defend the weak and oppose evil. Your honor is as important to you as your life.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Knight Of Solamnia'), 'Feature: Squire Of Solamnia', 'You gain the Squire of Solamnia feat.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Knight Of Solamnia'), 'Building a Knight of Solamnia Character', 'Any class that has martial prowess can be a good fit in the Knights of Solamnia. Fighters and paladins make up the bulk of the knighthood''s forces. Clerics (often with the War domain) can also be found among the knights'' ranks.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Knight Of Solamnia'), 'Knight of Solamnia Trinkets', 'Trinket', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight Of Solamnia') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '1', 'A flat silver disk you record your heroics upon');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight Of Solamnia') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '2', 'A piece of a fallen knight''s armor');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight Of Solamnia') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '3', 'A pendant featuring a crown, a rose, or a sword');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight Of Solamnia') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '4', 'The pommel of your mentor''s sword');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight Of Solamnia') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '5', 'A meaningful favor from someone you defended—perhaps a handkerchief or glove');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Knight Of Solamnia') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '6', 'A locket with a sketch of a silver dragon inside');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Mage Of High Sorcery', 'Your talent for magic came to the attention of the Mages of High Sorcery, an organization of spellcasters that study magic and prevent its misuse. You''ve trained among the Mages, but whether or not you''ll face the dangerous tests required to become a true member of the group remains to be determined. Your passion for studying magic has likely already predisposed you toward one of the organization''s three orders: the benevolent Order of the White Robes, the balance-pursuing Order of the Red Robes, or the ruthless Order of the Black Robes.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Mage Of High Sorcery'), 'Feature: Initiate Of High Sorcery', 'You gain the Initiate of High Sorcery feat.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Mage Of High Sorcery'), 'Building a Mage of High Sorcery Character', 'Mages of High Sorcery are typically sorcerers, warlocks, or wizards and might have any subclass. Spellcasters who gain their magic through devotion are less likely to be welcomed among the traditionalist mages. Nevertheless, the Mages of High Sorcery are shrewd, and they rarely let unique opportunities or individuals pass them by. Even members of martial classes who train in magic might find a rare place among the group''s three orders.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Mage Of High Sorcery'), 'Mage of High Sorcery Trinkets', 'Trinket', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mage Of High Sorcery') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '1', 'An unopened letter from your first teacher');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mage Of High Sorcery') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '2', 'A broken wand made of black, red, or white wood');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mage Of High Sorcery') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '3', 'A scroll bearing an incomprehensible formula');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mage Of High Sorcery') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '4', 'A purposeless device covered in colored stones that can fold into various enigmatic shapes');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mage Of High Sorcery') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '5', 'A pouch or spell book emblazoned with the triple moon symbol of the Mages of High Sorcery');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Mage Of High Sorcery') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '6', 'A lens through which you can see Krynn''s invisible black moon, Nuitari');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Giant Foundling', 'Though you aren''t a Giant, you grew up among giants. Maybe you were an orphan taken in by a sympathetic family of stone giants who raised you as one of their own. Or perhaps you lived in a lost prehistoric pocket of the world, surrounded by giants and fearsome behemoths or hulking dinosaurs.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Giant Foundling'), 'Origin Stories', 'Origin', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Giant Foundling') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '1', 'You were found as a baby by a family of nomadic giants who raised you as one of their own.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Giant Foundling') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '2', 'A family of stone giants rescued you when you fell into a mountain chasm, and you have lived with them underground ever since.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Giant Foundling') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '3', 'You were lost or abandoned as a child in a jungle that teemed with ravenous dinosaurs. There, you found an equally lost frost giant; together, you survived.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Giant Foundling') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '4', 'Your farm was crushed and your family killed in a battle between warring groups of giants. Racked with guilt over the destruction, a sympathetic giant soldier promised to care for you.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Giant Foundling') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '5', 'After you had a series of strange dreams as a child, your superstitious parents sent you to study with a powerful but aloof storm giant oracle.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Giant Foundling') AND title = 'Origin' ORDER BY id DESC LIMIT 1), '6', 'While playing hide-and-seek with your friends, you stumbled into the castle of a cloud giant, who immediately adopted you.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Giant Foundling'), 'Feature: Strike of the Giants', 'You gain the Strike of the Giants feat.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Giant Foundling'), 'Suggested Characteristics', 'Personality Trait', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Giant Foundling') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'What I lack in stature compared to giants, I make up for with sheer spite.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Giant Foundling') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I insist on being taken seriously as a full-grown adult. Nobody talks down to me!');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Giant Foundling') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Crowded spaces make me uncomfortable. I''d much rather be in an open field than a bustling tavern.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Giant Foundling') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I embrace my shorter stature. It helps me stay unnoticed—and underestimated.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Giant Foundling') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Every avalanche begins as a single pebble.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Giant Foundling') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'The world always feels too big, and I''m afraid I''ll never find my place in it.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Rune Carver', 'You''ve dedicated your life to studying runecraft. Whether you were taught by a master rune carver or learned by poring over ancient engravings, you can tap into the supernatural power held within runes.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Rune Carver'), 'Rune Styles', 'Style', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rune Carver') AND title = 'Style' ORDER BY id DESC LIMIT 1), '1', 'You inscribe runes in wax or clay with a fine metal needle.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rune Carver') AND title = 'Style' ORDER BY id DESC LIMIT 1), '2', 'You whittle pieces of wood into small figurines you mark with runes.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rune Carver') AND title = 'Style' ORDER BY id DESC LIMIT 1), '3', 'You engrave runes onto glass beads and thread them onto necklaces and bracelets.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rune Carver') AND title = 'Style' ORDER BY id DESC LIMIT 1), '4', 'You stitch runes into the hems of clothing.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rune Carver') AND title = 'Style' ORDER BY id DESC LIMIT 1), '5', 'You carve runes on a set of animal bones you can throw in different formations.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rune Carver') AND title = 'Style' ORDER BY id DESC LIMIT 1), '6', 'You draw runes into candles, melting the wax to smooth over the engravings.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Rune Carver'), 'Feature: Rune Shaper', 'You gain the Rune Shaper feat.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Rune Carver'), 'Suggested Characteristics', 'Personality Trait', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rune Carver') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Is it practical to learn an ancient language that is rarely spoken? No. But is it fun? Very.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rune Carver') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I learned one of my ancestors was a lauded rune carver whose story was lost to time. I seek to rekindle that legacy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rune Carver') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'The old, traditional markings of runecraft look so boring. Why not give my runes some flair?');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rune Carver') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'In my studies of runes, I strive to understand how great civilizations of the past fell, so I can prevent it from happening to societies of the present.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rune Carver') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Life may be a whirlwind of chaos, but whenever I create my runes, I feel at peace.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rune Carver') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'My brain struggles to process words written in ink, but the feeling of carved runes makes my mind sing.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Gate Warden', 'Those who dwell for an extended time near a permanent portal to another plane absorb the essence radiating from the realm beyond. Those influenced by the same plane share similarities in behavior and even physical appearance.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Gate Warden'), 'Feature: Planar Infusion', 'Living in a gate-town or a similar location steeped you in planar energy. You gain the Scion of the Outer Planes feat. In addition, you know where to find free, modest lodging and food in the community you grew up in.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Gate Warden'), 'Gate Warden Trinkets', 'Trinket', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Warden') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '1', 'A tiny vial pendant, filled with a drip of honey that glows faintly.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Warden') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '2', 'A small lead ingot with a strange thumbprint pressed into it that whispers when held tightly.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Warden') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '3', 'Two lodestone spheres that chime when they attract each other.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Warden') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '4', 'A smoldering pebble of coal that, while always hot, doesn''t burn skin, fur, scales, or clothing.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Warden') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '5', 'A feather that sheds dim light in a 5-foot radius.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Warden') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '6', 'A ring made from a chain link that, once donned, won''t come off without pulling painfully hard.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Gate Warden'), 'Suggested Characteristics', 'Personality Trait', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Warden') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Strange events and otherworldly creatures don''t faze me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Warden') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I think in terms of exchange: something for something, nothing for nothing.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Warden') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I speak with an unusual cadence.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Warden') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I pepper my speech with words or curses borrowed from planar languages.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Warden') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I''ve seen enough to know that you can''t take anyone at face value, so I scrutinize everyone.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gate Warden') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I have a superstitious habit I picked up from my gate-town, such as touching iron when I''m nervous or arranging objects in a specific order.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Planar Philosopher', 'You subscribe to a philosophy that seeks to understand the nature of the planes or some hidden truth of the multiverse. You draw strength from your conviction and, perhaps, a network of like-minded thinkers, such as the factions of Sigil (summarized below). In your travels, you explore the depths of your understanding and spread your philosophy wherever you go.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Planar Philosopher'), 'Factions of Sigil', 'Twelve factions have risen to prominence in the City of Doors, though many more exist. Your character might belong to one of these groups or another ideological faction, perhaps one of your own creation.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Planar Philosopher'), 'Feature: Conviction', 'You gain the Scion of the Outer Planes feat. In addition, members of your organization provide you free, modest lodging and food at any of their holdings or the homes of other faction members.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Planar Philosopher'), 'Planar Philosopher Trinkets', 'Trinket', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Planar Philosopher') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '1', 'Locket with a picture of my mentor and an inscription I can''t read');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Planar Philosopher') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '2', 'Bleached cranium rat skull with colored glass beads in its eye sockets');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Planar Philosopher') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '3', 'Torn parchment with half a rebus painted on it');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Planar Philosopher') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '4', 'Bracelet of twisted razorvine stems');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Planar Philosopher') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '5', 'Fragment of a bronze blade covered in verdigris');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Planar Philosopher') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '6', 'Broken holy symbol of a forgotten god');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Planar Philosopher'), 'Suggested Characteristics', 'Personality Trait', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Planar Philosopher') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I don''t venerate any gods. With time, we can be as powerful as them or greater.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Planar Philosopher') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Experience is everything; live in the moment.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Planar Philosopher') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'When things crumble, I find meaning in the dust.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Planar Philosopher') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Life thrives through order, and I seek to maintain that order.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Planar Philosopher') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'When others make plans, the multiverse laughs and so do I.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Planar Philosopher') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I know what''s right, and none will stand in my way.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Rewarded', 'You were living a difficult life before your destiny suddenly changed through a miraculous turn of good fortune. Perhaps a benevolent deity gave you precisely what you most desired. You might have stumbled across a Deck of Many Things and drawn a card with a potent, positive effect. Or maybe you inherited a modest fortune from a distant relative you didn''t know you had.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Rewarded'), 'Feature: Fortune''s Favor', 'Your unexpected good fortune is reflected by a minor boon. You gain the Lucky, Magic Initiate, or Skilled feat (your choice). Your choice of feat reflects the transformation that changed your life. An encounter with a genie who gave you three wishes might have resulted in magical powers represented by Magic Initiate. If you paid off all your family debts with a fortuitous round of three-dragon ante, you might be Lucky instead. Alternatively, you could use the Skilled feat to reflect whatever trial you endured to secure your new destiny and to model the knowledge and abilities imparted to you by whatever force transformed your life.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Rewarded'), 'Building a Rewarded Character', 'Rewarded characters have varied histories, but they have one thing in common: they were down on their luck before their lives abruptly turned around. They might have given up all hope of a happier life, only to suddenly get a second chance. A rewarded character has often spent many years struggling to escape painful circumstances. Now, extricated from the (sometimes literal) prison that was their former life, they throw their cares to the wind and become adventurers.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Rewarded'), 'Suggested Characteristics', 'Personality Trait', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rewarded') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'A safe home is a foundation on which anything else can be built. (Key, Throne)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rewarded') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I was elevated to heights I could never otherwise attain, and I won''t waste my fortune. (Star, Sun)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rewarded') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I try to be a source of inspiration and joy to others. Life is never as bad as you think! (Euryale, Jester)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rewarded') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Courage and boldness can carry the day when all else fails. (Comet, Knight)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rewarded') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'My good fortune means I can lift others up as well. (Gem, Moon)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rewarded') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Having the right answers is the first step to solving any problem, no matter how dire. (Fates, Sage)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Rewarded'), 'Rewarded Trinkets', 'Trinket', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rewarded') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '1', 'A perfumed silk scarf from an admirer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rewarded') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '2', 'A crystal bead that glows like a candle in the dark');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rewarded') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '3', 'A letter of introduction and invitation from an influential person in a far-off city');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rewarded') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '4', 'A motto or symbol whose meaning eludes you, etched on a piece of your equipment');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rewarded') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '5', 'A crisp playing card that never seems to tear or soil, depicting the card that affected you');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Rewarded') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '6', 'Half a medallion designed to be rejoined to its other half');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Rewarded'), 'Heroes of Destiny', 'The Fated Destinies table provides narrative ideas inspired by the Deck of Many Things that you can use as prompts while developing your character. The entries are geared toward characters who rely on faith, conviction, or fate—particularly clerics, druids, and paladins—but any character''s story can benefit from them. When using this table, you can choose the entries that speak to you or randomly pull cards from The Deck of Many Things card set.
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Ruined', 'Everything was going so well! You had a life of luxury, love, and comfort when you suddenly lost it all. Perhaps you were framed for crimes you didn''t commit and lost your reputation, fortune, and career. Maybe a rampaging dragon or another monster wiped out everything you had in a single calamitous afternoon. Or you might have sought out a Deck of Many Things, hoping to make your successful life even more glorious—only to draw a destructive card that changed your destiny forever.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Ruined'), 'Feature: Still Standing', 'You have weathered ruinous misfortune, and you possess hidden reserves others don''t expect. You gain the Alert, Skilled, or Tough feat (your choice). Your choice of feat reflects how you''ve dealt with the terrible loss that changed your life forever. If you''ve kept your senses sharp for every opportunity and climbed your way out of misery by seizing the tiniest scrap of hope, choose Alert. If you''ve redoubled your efforts to reclaim what was once yours, choose Skilled. If you''ve stoically persevered through your misfortune, select Tough.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Ruined'), 'Building a Ruined Character', 'Ruined characters were on top of the world before misfortune struck. Many were wealthy. Others come from modest backgrounds, but they were surrounded by friends, family, and loved ones. They might have been famous, or simply never encountered serious hardship before. Some were born to privilege or rose to prominence through trickery or a false reputation. Now a Deck of Many Things—or another calamity—has knocked them down like a house of cards.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Ruined'), 'Suggested Characteristics', 'Personality Trait', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ruined') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I''ve changed from my past, and I work to live up to my new path. (Balance, Throne)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ruined') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Every moment is a gift I refuse to squander. (Euryale, Skull)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ruined') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Now that I''ve overcome having nothing, I can survive anything. (Fool, Ruin, Talons)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ruined') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I know enemies are set against me, and I always prepare for the worst. (Flames, Rogue)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ruined') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I interpret every event as part of a larger pattern I just haven''t worked out yet. (Puzzle, Star)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ruined') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I must make up for so much time I''ve already lost. (Donjon, Void)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Ruined'), 'Ruined Trinkets', 'Trinket', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ruined') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '1', 'A rusted scrap of a once-beloved family heirloom');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ruined') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '2', 'A land deed, but all the names and markings that once tied it to you have faded into obscurity');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ruined') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '3', 'A bauble once imbued with powerful magic');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ruined') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '4', 'A battered playing card with a hole pierced in it, its face depicting the card that affected you');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ruined') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '5', 'A yellowed Humanoid tooth that whispers eerily when placed under a pillow');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ruined') AND title = 'Trinket' ORDER BY id DESC LIMIT 1), '6', 'A keepsake from someone you were once close to but who is now your enemy');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Ruined'), 'Heroes of Destiny', 'The Fated Destinies table provides narrative ideas inspired by the Deck of Many Things that you can use as prompts while developing your character. The entries are geared toward characters who rely on faith, conviction, or fate—particularly clerics, druids, and paladins—but any character''s story can benefit from them. When using this table, you can choose the entries that speak to you or randomly pull cards from The Deck of Many Things card set.
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Augen Trust (Spy)', 'Little is known about the Augen Trust, even within the court of the Dwendalian Empire. Only one person knows all the members of this elite network of imperial spies: its leader, Oliver Schreiber, a noble gnome with the ear of King Dwendal.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)'), 'Feature: Spy Contact', 'You have a reliable and trustworthy contact who acts as your liaison to a network of other spies. You know how to get messages to and from your contact, even over great distances; specifically, you know the local messengers, corrupt caravan masters, and seedy sailors who can deliver messages for you.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)'), 'Spy Specialty', 'Specialty', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '1', 'Blackmailer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '2', 'Burglar');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '3', 'Enforcer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '4', 'Fence');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '5', 'Highway robber');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '6', 'Hired killer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '7', 'Pickpocket');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '8', 'Smuggler');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I always have a plan for what to do when things go wrong.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I am always calm, no matter what the situation. I never raise my voice or let my emotions control me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'The first thing I do in a new place is note the locations of everything valuable-or where such things could be hidden.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I would rather make a new friend than a new enemy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I am incredibly slow to trust. Those who seem the fairest often have the most to hide.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I don''t pay attention to the risks in a situation. Never tell me the odds.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'The best way to get me to do something is to tell me I can''t do it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I blow up at the slightest insult.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Honor. I don''t steal from others in the trade. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Freedom. Chains are meant to be broken, as are those who would forge them. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Charity. I steal from the wealthy so that I can help people in need. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Greed. I will do whatever it takes to become wealthy. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'People. I''m loyal to my friends, not to any ideals, and everyone else can take a trip down the Styx for all I care. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Redemption. There''s a spark of good in everyone. (Good)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I''m trying to pay off an old debt I owe to a generous benefactor.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'My ill-gotten gains go to support my family.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'Something important was taken from me, and I aim to steal it back.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I will become the greatest thief that ever lived.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I''m guilty of a terrible crime. I hope I can redeem myself for it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'Someone I loved died because of 1 mistake I made. That will never happen again.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'When I see something valuable, I can''t think about anything but how to steal it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'When faced with a choice between money and my friends, I usually choose the money.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'If there''s a plan, I''ll forget it. If I don''t forget it, I''ll ignore it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I have a "tell" that reveals when I''m lying.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I turn tail and run when things look bad.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Augen Trust (Spy)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'An innocent person is in prison for a crime that I committed. I''m okay with that.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Cobalt Scholar', 'The Cobalt Soul is an organization of monastic scholars dedicated to preserving knowledge and recorded history, not just across Wildemount but all of Exandria. Its operations are primarily based out of the cities of Zadash and Rexxentrum in the Dwendalian Empire, and you likely studied at one of those sites.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar'), 'Specialty', 'Field of Study', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar') AND title = 'Field of Study' ORDER BY id DESC LIMIT 1), '1', 'Alchemist');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar') AND title = 'Field of Study' ORDER BY id DESC LIMIT 1), '2', 'Astronomer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar') AND title = 'Field of Study' ORDER BY id DESC LIMIT 1), '3', 'Discredited academic');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar') AND title = 'Field of Study' ORDER BY id DESC LIMIT 1), '4', 'Librarian');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar') AND title = 'Field of Study' ORDER BY id DESC LIMIT 1), '5', 'Professor');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar') AND title = 'Field of Study' ORDER BY id DESC LIMIT 1), '6', 'Researcher');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar') AND title = 'Field of Study' ORDER BY id DESC LIMIT 1), '7', 'Wizard''s apprentice');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar') AND title = 'Field of Study' ORDER BY id DESC LIMIT 1), '8', 'Scribe');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar'), 'Feature: Researcher', 'When you attempt to learn or recall a piece of lore, if you do not know that information, you often know where and from whom you can obtain it. Usually, this information comes from a library, scriptorium, university, or a sage or other learned person or creature, Your DM might rule that the knowledge you seek is secreted away in an almost inaccessible place, or that it simply cannot be found. Unearthing the deepest secrets of the multiverse can require an adventure or even a whole campaign.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I use polysyllabic words that convey the impression of great erudition.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I''ve read every book in the world''s greatest libraries—or I like to boast that I have.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I''m used to helping out those who aren''t as smart as I am, and I patiently explain anything and everything to others.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'There''s nothing I like more than a good mystery.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I''m willing to listen to every side of an argument before I make my own judgment.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I... speak... slowly... when talking... to idiots,... which... almost... everyone... is... compared... to me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I am horribly, horribly awkward in social situations.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I''m convinced that people are always trying to steal my secrets.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Knowledge. The path to power and self-improvement is through knowledge. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Beauty. What is beautiful points us beyond itself toward what is true. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Logic. Emotions must not cloud our logical thinking. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'No Limits. Nothing should fetter the infinite possibility inherent in all existence. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Power. Knowledge is the path to power and domination. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Self-Improvement. The goal of a life of study is the betterment of oneself. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'It is my duty to protect my students.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I have an ancient text that holds terrible secrets that must not fall into the wrong hands.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I work to preserve a library, university, scriptorium, or monastery.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'My life''s work is a series of tomes related to a specific field of lore.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I''ve been searching my whole life for the answer to a certain question.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I sold my soul for knowledge. I hope to do great deeds and win it back.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I am easily distracted by the promise of information.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'Most people scream and run when they see a demon. I stop and take notes on its anatomy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'Unlocking an ancient mystery is worth the price of a civilization.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I overlook obvious solutions in favor of complicated ones.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I speak without really thinking through my words, invariably insulting others.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cobalt Scholar') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I can''t keep a secret to save my life, or anyone else''s.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Grinner', 'The Golden Grin began in the far-off land of Tal''Dorei, trading secret messages through a network of bards and minstrels to undermine an iron-fisted king''s authoritarian rule. Today, Tal''Dorei is a relatively peaceful republic, and some Grinners have grown restless while waiting for tyranny to raise its ugly head again. A contingent of such Grinners traveled to the Menagerie Coast and set up a network of secret bases in the cities of the Clovis Concord, from which they are presently working their way into the highest echelons of the Dwendalian Empire.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Grinner'), 'Favorite Code-Song', 'All members of the Golden Grin have learned a handful of folk songs in their travels, and use those songs to send secret codes and alert fellow Grinners to danger.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Grinner'), 'Feature: Ballad of the Grinning Fool', 'Like every Grinner, you know how to find a hideout. In any city of 10,000 people or more on the Menagerie Coast or in the lands of the Dwendalian Empire, you can play the "Ballad of the Grinning Fool" in a major tavern or inn. A member of the Golden Grin will find you and give shelter to you and any companions you vouch for. This shelter might be discontinued if it becomes too dangerous to hide you, at the DM''s discretion.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Grinner'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Grinner') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I love the spotlight. Everyone, look at me!');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Grinner') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Give me a drink and I''m your friend.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Grinner') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Talk to me about yourself. I''m a hell of a listener.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Grinner') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I hate to start fights, but I love to finish them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Grinner') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I can''t sit still.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Grinner') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I''m always humming an old tune from my past.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Grinner') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'When I don''t have a reason to smile, I''m miserable.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Grinner') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I''m lucky like you wouldn''t believe.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Grinner'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Grinner') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Revolution. Tyrants must fall, no matter the cost. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Grinner') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Compassion. The only way to make a better world is to perform small kindnesses. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Grinner') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Justice. A nation built upon just foundations will uphold freedom for all. (Law)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Grinner') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Expression. Music, joy, and laughter are the keys to freedom. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Grinner') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Self-Determination. People should be free to do as they please. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Grinner') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Vigilance. A free people must be carefully taught, lest they be misled. (Neutral)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Grinner'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Grinner') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I lost someone important to an agent of the Dwendalian Empire. That regime will fall.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Grinner') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'The first people to be hurt by this war will be the common folk. I need to protect them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Grinner') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'Music helped me through a dark time in my life. Now, I''ll use music to change the world.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Grinner') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I will be known as the greatest spy who ever lived.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Grinner') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'All life is precious to me. I know I can change the world without taking a humanoid life.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Grinner') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'The elite in their ivory towers don''t understand how we suffer. I intend to show them.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Grinner'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Grinner') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I''ve never lied once in my life. What? No, I''m not crossing my fingers!');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Grinner') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I do everything big! Subtlety? I don''t know the meaning of subtlety! Oh, that''s a problem?');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Grinner') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'Being a spy in wartime is painful. I''ve seen so much suffering, I think I''m losing my mind.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Grinner') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I can''t focus on my mission. I just want to carouse and sing and play!');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Grinner') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'Yeah, that''s my name. Yeah, I''m a Grinner spy. Who cares about staying undercover?');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Grinner') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I can''t afford to trust anyone. Not. Anyone.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Luxonborn', 'Every acolyte is a servant of some god. But you serve the Luxon, a being that you believe transcends godhood and created the cosmos. No mere priest, you are a student of potential, for the Luxon is potentiality incarnate. Your Shelter of the Faithful feature applies to all who venerate the Luxon, and to all who practice the art of dunamancy that the Luxon bestowed upon the world.', 'Insight, Religion', '', 'Two of your choice', 'A holy symbol (a gift to you when you entered the priesthood), a prayer book or prayer wheel, 5 sticks of incense, vestments, a set of common clothes, and a belt pouch containing 15 gp', (SELECT id FROM core_book_sources WHERE raw_source = 'Explorer''s Guide to Wildemount p. 203'));
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Luxonborn'), 'Feature: Shelter of the Faithful', 'As an acolyte, you command the respect of those who share your faith, and you can perform the religious ceremonies of your deity. You and your adventuring companions can expect to receive free healing and care at a temple, shrine, or other established presence of your faith, though you must provide any material components needed for spells. Those who share your religion will support you (but only you) at a modest lifestyle.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Luxonborn'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Luxonborn') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I idolize a particular hero of my faith, and constantly refer to that person''s deeds and example.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Luxonborn') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I can find common ground between the fiercest enemies, empathizing with them and always working toward peace.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Luxonborn') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I see omens in every event and action. The gods try to speak to us, we just need to listen.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Luxonborn') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Nothing can shake my optimistic attitude.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Luxonborn') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I quote (or misquote) sacred texts and proverbs in almost every situation.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Luxonborn') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I am tolerant (or intolerant) of other faiths and respect (or condemn) the worship of other gods.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Luxonborn') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I''ve enjoyed fine food, drink, and high society among my temple''s elite. Rough living grates on me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Luxonborn') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I''ve spent so long in the temple that I have little practical experience dealing with people in the outside world.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Luxonborn') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Tradition. The ancient traditions of worship and sacrifice must be preserved and upheld. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Luxonborn') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Charity. I always try to help those in need, no matter what the personal cost. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Luxonborn') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Change. We must help bring about the changes the gods are constantly working in the world. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Luxonborn') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Power. I hope to one day rise to the top of my faith''s religious hierarchy. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Luxonborn') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Faith. I trust that my deity will guide my actions. I have faith that if I work hard, things will go well. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Luxonborn') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Aspiration. I seek to prove myself worthy of my god''s favor by matching my actions against his or her teachings. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Luxonborn') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I would die to recover an ancient relic of my faith that was lost long ago.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Luxonborn') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I will someday get revenge on the corrupt temple hierarchy who branded me a heretic.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Luxonborn') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I owe my life to the priest who took me in when my parents died.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Luxonborn') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Everything I do is for the common people.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Luxonborn') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I will do anything to protect the temple where I served.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Luxonborn') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I seek to preserve a sacred text that my enemies consider heretical and seek to destroy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Luxonborn') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I judge others harshly, and myself even more severely.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Luxonborn') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I put too much trust in those who wield power within my temple''s hierarchy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Luxonborn') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'My piety sometimes leads me to blindly trust those that profess faith in my god.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Luxonborn') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I am inflexible in my thinking.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Luxonborn') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I am suspicious of strangers and expect the worst of them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Luxonborn') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Once I pick a goal, I become obsessed with it to the detriment of everything else in my life.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Myriad Operative', 'Most criminals in Wildemount are petty thieves and cutpurses who operate out of desperation. You, however, are a criminal of a higher caliber. You are a member of the Myriad, the greatest crime syndicate to ever grace the face of Exandria. You have a network of criminal contacts and allies to help you in your crimes - but you also have rivals and superiors that would gladly throw you to the wolves to save their own skins.', 'Deception, Stealth', 'One type of gaming set, thieves'' tools', '', 'A crowbar, a set of dark common clothes including a hood, and a belt pouch containing 15 gp', (SELECT id FROM core_book_sources WHERE raw_source = 'Explorer''s Guide to Wildemount p. 203'));
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Myriad Operative'), 'Criminal Specialty', 'Specialty', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Myriad Operative') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '1', 'Blackmailer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Myriad Operative') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '2', 'Burglar');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Myriad Operative') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '3', 'Enforcer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Myriad Operative') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '4', 'Fence');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Myriad Operative') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '5', 'Highway robber');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Myriad Operative') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '6', 'Hired killer');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Myriad Operative') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '7', 'Pickpocket');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Myriad Operative') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '8', 'Smuggler');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Myriad Operative'), 'Feature: Criminal Contact', 'You have a reliable and trustworthy contact who acts as your liaison to a network of other criminals. You know how to get messages to and from your contact, even over great distances; specifically, you know the local messengers, corrupt caravan masters, and seedy sailors who can deliver messages for you.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Myriad Operative'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Myriad Operative') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I always have a plan for what to do when things go wrong.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Myriad Operative') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I am always calm, no matter what the situation. I never raise my voice or let my emotions control me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Myriad Operative') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'The first thing I do in a new place is note the locations of everything valuable—or where such things could be hidden.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Myriad Operative') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I would rather make a new friend than a new enemy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Myriad Operative') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I am incredibly slow to trust. Those who seem the fairest often have the most to hide.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Myriad Operative') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I don''t pay attention to the risks in a situation. Never tell me the odds.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Myriad Operative') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'The best way to get me to do something is to tell me I can''t do it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Myriad Operative') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I blow up at the slightest insult.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Myriad Operative'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Myriad Operative') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Honor. I don''t steal from others in the trade. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Myriad Operative') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Freedom. Chains are meant to be broken, as are those who would forge them. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Myriad Operative') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Charity. I steal from the wealthy so that I can help people in need. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Myriad Operative') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Greed. I will do whatever it takes to become wealthy. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Myriad Operative') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'People. I''m loyal to my friends, not to any ideals, and everyone else can take a trip down the Styx for all I care. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Myriad Operative') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Redemption. There''s a spark of good in everyone. (Good)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Myriad Operative'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Myriad Operative') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I''m trying to pay off an old debt I owe to a generous benefactor.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Myriad Operative') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'My ill-gotten gains go to support my family.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Myriad Operative') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'Something important was taken from me, and I aim to steal it back.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Myriad Operative') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I will become the greatest thief that ever lived.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Myriad Operative') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I''m guilty of a terrible crime. I hope I can redeem myself for it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Myriad Operative') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'Someone I loved died because of a mistake I made. That will never happen again.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Myriad Operative'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Myriad Operative') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'When I see something valuable, I can''t think about anything but how to steal it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Myriad Operative') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'When faced with a choice between money and my friends, I usually choose the money.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Myriad Operative') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'If there''s a plan, I''ll forget it. If I don''t forget it, I''ll ignore it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Myriad Operative') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I have a "tell" that reveals when I''m lying.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Myriad Operative') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I turn tail and run when things look bad.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Myriad Operative') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'An innocent person is in prison for a crime that I committed. I''m okay with that.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Revelry Pirate', 'It seems that just about everyone is a sailor on the Menagerie Coast. Plenty of pirates ply the waters of the coast as well, but none are as infamous or as organized as the mighty Revelry. If you''re a pirate of the Revelry, you serve the Plank King and return a cut of all your plunder to him on the island of Darktow.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Revelry Pirate'), 'Feature: Ship''s Passage', 'When you need to, you can secure free passage on a sailing ship for yourself and your adventuring companions. You might sail on the ship you served on, or another ship you have good relations with (perhaps one captained by a former crewmate). Because you''re calling in a favor, you can''t be certain of a schedule or route that will meet your every need. Your Dungeon Master will determine how long it takes to get where you need to go. In return for your free passage, you and your companions are expected to assist the crew during the voyage.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Revelry Pirate'), 'Variant Feature: Bad Reputation', 'If your character has a sailor background, you may select this background feature instead of Ship''s Passage.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Revelry Pirate'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Revelry Pirate') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'My friends know they can rely on me, no matter what.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Revelry Pirate') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I work hard so that I can play hard when the work is done.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Revelry Pirate') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I enjoy sailing into new ports and making new friends over a flagon of ale.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Revelry Pirate') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I stretch the truth for the sake of a good story.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Revelry Pirate') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'To me, a tavern brawl is a nice way to get to know a new city.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Revelry Pirate') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I never pass up a friendly wager.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Revelry Pirate') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'My language is as foul as an otyugh nest.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Revelry Pirate') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I like a job well done, especially if I can convince someone else to do it.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Revelry Pirate'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Revelry Pirate') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Respect. The thing that keeps a ship together is mutual respect between captain and crew. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Revelry Pirate') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Fairness. We all do the work, so we all share in the rewards. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Revelry Pirate') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Freedom. The sea is freedom—the freedom to go anywhere and do anything. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Revelry Pirate') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Mastery. I''m a predator, and the other ships on the sea are my prey. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Revelry Pirate') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'People. I''m committed to my crewmates, not to ideals. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Revelry Pirate') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Aspiration. Someday, I''ll own my own ship and chart my own destiny. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Revelry Pirate'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Revelry Pirate') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I''m loyal to my captain first, everything else second.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Revelry Pirate') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'The ship is most important—crewmates and captains come and go.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Revelry Pirate') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I''ll always remember my first ship.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Revelry Pirate') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'In a harbor town, I have a paramour whose eyes nearly stole me from the sea.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Revelry Pirate') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I was cheated out of my fair share of the profits, and I want to get my due.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Revelry Pirate') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'Ruthless pirates murdered my captain and crewmates, plundered our ship, and left me to die. Vengeance will be mine.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Revelry Pirate'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Revelry Pirate') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I follow orders, even if I think they''re wrong.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Revelry Pirate') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I''ll say anything to avoid having to do extra work.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Revelry Pirate') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'Once someone questions my courage, I never back down no matter how dangerous the situation.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Revelry Pirate') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'Once I start drinking, it''s hard for me to stop.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Revelry Pirate') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I can''t help but pocket loose coins and other trinkets I come across.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Revelry Pirate') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'My pride will probably lead to my destruction.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Volstrucker Agent', 'The Volstrucker are a clandestine organization of arcane assassins and enforcers in the service of the Cerberus Assembly. Their operatives bear no official title, but are referred to in hushed tones as "scourgers" by residents of large Dwendalian cities such as Zadash and Rexxentrum.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Volstrucker Agent'), 'Tragedy', 'Tragedy', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Volstrucker Agent') AND title = 'Tragedy' ORDER BY id DESC LIMIT 1), '1', 'Familicide. Through deceit or manipulation, the Volstrucker convinced you to slaughter your own family.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Volstrucker Agent') AND title = 'Tragedy' ORDER BY id DESC LIMIT 1), '2', 'Amnesia. You were forced to study magic so potent that it strained your mind beyond mortal limits, stealing away the memories of your past.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Volstrucker Agent') AND title = 'Tragedy' ORDER BY id DESC LIMIT 1), '3', 'Capture. You were captured and tortured by agents of the Kryn Dynasty, and barely escaped.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Volstrucker Agent') AND title = 'Tragedy' ORDER BY id DESC LIMIT 1), '4', 'Starvation. A terrible blight afflicted your rural village, and many of your friends and family members starved to death. You survived, but only barely.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Volstrucker Agent') AND title = 'Tragedy' ORDER BY id DESC LIMIT 1), '5', 'Disfigurement. One of your arcane experiments went wrong and scarred or dismembered you so gravely that others now shun you. Only the Volstrucker showed you kindness after that day.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Volstrucker Agent') AND title = 'Tragedy' ORDER BY id DESC LIMIT 1), '6', 'Vicissitude. You were once the scion of a wealthy family who lost their entire fortune in the blink of an eye.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Volstrucker Agent'), 'Feature: Shadow Network', 'You have access to the Volstrucker shadow network, which allows you to communicate with other members of the order over long distances. If you write a letter in a special arcane ink, address it to a member of the Volstrucker, and cast it into a fire, the letter will burn to cinders and materialize whole again on the person of the agent you addressed it to.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Volstrucker Agent'), 'Suggested Characteristics', 'Personality Trait', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Volstrucker Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I prefer to keep my thoughts to myself.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Volstrucker Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I indulge vice in excess to quiet my conscience.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Volstrucker Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I''ve left emotion behind me. I''m now perfectly placid.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Volstrucker Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Some event from the past keeps worming its way into my mind, making me restless.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Volstrucker Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I always keep my word—except when I''m commanded to break it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Volstrucker Agent') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I laugh off insults and never take them personally.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Volstrucker Agent'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Volstrucker Agent') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Order. The will of the crown is absolute. (Law)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Volstrucker Agent') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'True Loyalty. The Cerberus Assembly is greater than any power, even the crown. (Law)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Volstrucker Agent') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Death. The penalty for disloyalty is death. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Volstrucker Agent') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Determination. I cannot fail. Not ever. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Volstrucker Agent') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Fear. People should not respect power. They should fear it. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Volstrucker Agent') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Escape. The Volstrucker are pure evil! I can''t atone for what I''ve done for them, but I can escape with my life. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Volstrucker Agent'), 'Suggested Characteristics', 'Bond', '1d4');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Volstrucker Agent') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'The job is all that matters. I will see it through.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Volstrucker Agent') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'My orders are important, but my comrades are worth more than anything. I would die for them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Volstrucker Agent') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'Everything I''ve done, I''ve done to protect someone close to me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Volstrucker Agent') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'If the empire falls, all of civilization falls with it. I will hold back chaos and barbarism at any cost.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Volstrucker Agent'), 'Suggested Characteristics', 'Flaw', '1d4');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Volstrucker Agent') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I drink to dull the pain in the back of my head.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Volstrucker Agent') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I go a bit mad when I see blood.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Volstrucker Agent') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I can hear the voices of everyone I''ve killed. I see their faces. I can''t be free of these ghosts.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Volstrucker Agent') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'Fear is a powerful motivator. I will do whatever it takes to prevent those who know what I am from seeing me fail, and from those I care about from knowing what I am.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Ashari', 'Isolated from the political struggles of urban Tal''Dorei, you are a member of the Ashari people—a society bound to an ancient covenant protecting the Material Plane from the chaos of the primordial elements. You''ve been raised to revere and protect the natural world, and to shepherd the tumultuous elemental forces that give it life. When the elements threaten to overwhelm the delicate balance of nature, it is the role of the Ashari to restore harmony.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Ashari'), 'Feature: Elemental Harmony', 'Growing up surrounded by wild elemental magics has attuned your senses to those chaotic forces, enabling you to subtly bend them to your will. As an action, you channel minor magic involving the element of your chosen Ashari order, giving you one of the following abilities:
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Ashari'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ashari') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I like to keep my hands busy, no matter where I am.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ashari') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I love to explore new places and meet new people.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ashari') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I meditate at dawn each day—and I can''t stand it when my routine is interrupted.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ashari') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I like noticing patterns in the world around me, whether or not they mean anything.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ashari') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I don''t let anything—or anyone—stand in the way of my mission.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ashari') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I''m a plain talker, even with people who outrank me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ashari') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I''ve always got some of my native element with me in some form. (This might be modeling clay, pure water, special burning incense, or a bottled cloud.)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ashari') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I talk with everyone like I''ve known them all my life. Because most people I know, I have known all my life!');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Ashari'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ashari') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Destiny. I believe that everyone has a role to play. Now I just have to find mine. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ashari') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Community. It''s important to surround yourself with people you can count on, and who will support you. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ashari') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Knowledge. I want to learn everything I can about the Elemental Planes—and maybe even visit them myself. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ashari') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Freedom. I don''t care what anyone says. Even if it causes problems, the elements must be free. And so should I. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ashari') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Structure. The elements are in harmony when they are free to act as they will, within the safe boundaries set by the Ashari. People are much the same. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ashari') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Virtuous Cycle. If I see someone who needs help, I feel compelled to assist them. Surely they''ll return the favor someday! (Good)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Ashari'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ashari') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I have a cousin in another Ashari tribe whom I''ve never met, but someday I want to visit my extended family.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ashari') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'The leader of my tribe thinks I could be their successor, but I worry that I don''t have enough experience to lead my people.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ashari') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'A mysterious person killed a member of my family. I''ve left home to discover who the killer was—and to seek vengeance.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ashari') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'My older sibling set out on their Aramante a year ago, and I haven''t seen them since.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ashari') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'When I was a baby, a giant eagle brought me to Zephrah. I love my family, but I often wonder who my birth parents are.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ashari') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I trust my animal friends more than any humanoid ally.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Ashari'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ashari') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'Big cities are overwhelming. I get nervous when surrounded by people I don''t know.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ashari') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I know all too well that elemental power is dangerous—but I like playing around with it anyway.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ashari') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I get surly if I go too long without being in contact with my native element.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ashari') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I think the mission of my people is a fool''s errand. They should abandon isolation, let the elements be, and enjoy the pleasures of the world!');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ashari') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I can''t stand it when people say one thing and mean another! Just say what you mean!');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Ashari') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'Ugh, I know it''s not right, but I can''t help but look down on people who can''t manipulate the elements. It''s not like it''s hard!');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Clasp Member', 'Whether you grew up in the mean streets of Kymal bamboozling foolish gamblers, or spent your youth pilfering loose coin from the pockets of Emon''s many tourists, your lifestyle of deceiving-to-survive eventually drew the attention of the Clasp: a storied crime syndicate that has hideouts in every city in Tal''Dorei. In exchange for protection, a modicum of kinship, and a number of useful resources to further develop your craft as a criminal, you agreed to receive the brand of the Clasp and join their ranks.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clasp Member'), 'Feature: A Favor in Turn', 'You have gained enough clout in the Clasp that you can call in a favor from your contacts whenever you''re close enough to a center of syndicate activity. A request for a favor can be no longer than 20 words, and is passed up the chain to an undisclosed Spireling for approval. This favor can take on any form subject to the approval of the GM, who decides how it is fulfilled. If muscle is requested, an NPC member of the Clasp (using statistics found in chapter 6) can temporarily aid your party. If money is needed, a small loan can be provided. If you''ve been imprisoned, Clasp operatives can look into breaking you out or paying off the jailer.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clasp Member'), 'Variant Feature: Myriad Operative', 'Your skill set might be similar to that of many members of the Clasp, but you work for a criminal organization that is far more sophisticated—and even less scrupulous. As a Myriad operative in Tal''Dorei, you might have been given a specific task that furthers that syndicate''s hunger to expand beyond Wildemount, or which gives them an edge in their rivalry with the Clasp. Moreover, you understand the wisdom of keeping your activities secret from fellow criminals as well as law enforcement, since the agents of the Clasp will show you no mercy if your true identity is ever revealed.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clasp Member'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clasp Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'What''s life without risk? I''m always willing to take a risk if the reward seems worth it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clasp Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I only show my emotions around people I really trust.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clasp Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I don''t need friends; I need allies. When I do make "friends," I only consider what they can do for me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clasp Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I look for simple solutions. The world''s full of tough problems, but a well-placed knife is a one-size-fits-all answer.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clasp Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Money talks. I don''t. We''ve got an efficient relationship.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clasp Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I used to have one rule—don''t get involved in other people''s problems. Why are things so complicated now?');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clasp Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'Crime is a game, and I play to win. I have no sympathy for players who don''t get that.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clasp Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'This organization has a lot of folks who cling to ugly, brutal practices. I''m not like that. I''m a professional, and professionals have standards.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clasp Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'By Any Means. I complete jobs. Collateral damage isn''t my problem. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clasp Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Ambition. I will climb to the top of the ladder. Everything I do is a stepping-stone to a Spireling''s position. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clasp Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Decisiveness. It''s important to make up your mind so you can act swiftly and without delay. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clasp Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Honor. There''s room in the Clasp for both good and evil. Every day, I awake and choose to do what''s right. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clasp Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Family. The Clasp is family. Anything that''s good for the family is good for me. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clasp Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Self-Interest. There are too many bleeding hearts in the Clasp these days. Doing the right thing means doing the thing that makes my life better. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clasp Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I''d do anything—anything—to protect my comrades.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clasp Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I''ll always be grateful to the Spireling who took me in when I was an orphaned kid.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clasp Member') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I was inspired to join the Clasp by the stories my parents told of being saved from the Chroma Conclave''s attack on Emon. I can look past the organization''s');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clasp Member'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clasp Member') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I was nearly killed by the Myriad. If the Clasp is the enemy of those villains, then the Clasp is my friend.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clasp Member') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I''ve got family back in the old town who are counting on me for money. They don''t know how I get it, but they don''t need to know.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clasp Member') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I joined the Clasp to become rich, powerful, and beloved. That''s all there is to it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clasp Member') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I''m hopeless at organizing my belongings, and I''m always losing things.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clasp Member') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I get bored whenever a plan is going too smoothly. A win is always more fun when it''s by the skin of my teeth!');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clasp Member') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I''ve seen Spirelings walk out among cheering crowds of thousands. Gods, I wish that were me. I need that to be me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clasp Member') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I''m rubbish with money, and never seem to leave town with a full purse. Keeps me coming back to the life, I suppose.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clasp Member') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I can''t work with shoddy, makeshift thieves'' tools. I need everything involving my work to be perfect.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clasp Member') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'Any slight against me, no matter how small, is cause for revenge.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Lyceum Scholar', 'You most likely came up through money or hailed from a family with social prestige if you were able to afford tuition at the Alabaster Lyceum in Emon. But whatever your origin, your interests and pursuits brought you to the hallowed halls of Tal''Dorei''s highest place of learning, where you soaked in every lesson you could, hoping to better make your mark on the world. (Or at least you pretended to. Even at the Lyceum, every class has its slackers.) Eventually, though, the call to adventure pulled you from your studies, and you now face the challenge of keeping up with your education even as the tides of destiny threaten to sweep you away.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Lyceum Scholar'), 'Feature: Academic Requisition', 'You''ve cleared enough lessons—and have gained an ally or two among the staff—to enable access to certain private areas within the Lyceum and other allied universities. Whenever you''re on Lyceum grounds or at another major academic institution, you can requisition any set of tools found in the fifth edition rules. Each set of tools is magically marked to sound an alarm if they are removed from the university''s grounds.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Lyceum Scholar'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Lyceum Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I can''t believe I''m here! At the Alabaster Lyceum. Oh, gods, I''ve dreamed of this my whole life, and now I''m here!');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Lyceum Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I can''t believe I squandered all the opportunities I had at school. I was supposed to be learning good stuff, but I wasted it all daydreaming about fighting monsters.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Lyceum Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Every night at school, I''d knock back a couple of meads and read with my pals! Just a bunch of nerds having fun, and I loved it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Lyceum Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Everyone at school was such a stick in the mud. Dressing the same, listening to the same bards…ugh, it''s sad. Just be yourself.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Lyceum Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I''m happiest when I''ve got my little party with me. At school, it was like we were a squad of heroes, slaying projects like monsters.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Lyceum Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I''d really rather you didn''t bother me. Can''t you see I''m studying here?');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Lyceum Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I don''t care. I just don''t care about it all. The dates I had to memorize, the formulae I learned…I just want to run away and live!');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Lyceum Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Preparedness. I can''t go out into the world unless I know what I''m up against. Study first, act later. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Lyceum Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Stardom. Having a team is good and all, but you can''t win a game of ball without the star charger, and you know that''s me. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Lyceum Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Individuality. The world keeps us down by trying to put us all into little boxes. I''m tired of living in my box, and I don''t care what you think about it. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Lyceum Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Purpose. I study because there are things I need to know. I''ll find my place in the world, and I''ll make the world better. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Lyceum Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Code of Conduct. The student code is there to benefit all students, you know. It''s the same for laws! (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Lyceum Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Recreation. All this studying crap wasn''t worth anything if you weren''t partying when you were done. Meet me down at the tavern, okay? (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Lyceum Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I came to the Lyceum with no one, but I fell in love with the city of Emon. I''ve finally found a place that feels like home!');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Lyceum Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Most of my professors drove me to frustration, but there''s one who was kind and wise. I know they''ll always have my back.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Lyceum Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'My family saved every copper piece to give me the opportunities I have now. I can''t let them down.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Lyceum Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I came to the Lyceum with a childhood friend, but we''ve long been drifting apart.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Lyceum Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Discovery is the only thing that matters to me. The topic doesn''t matter. Books keep me company on my loneliest days.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Lyceum Scholar') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'The Lyceum is my life. I''d give up anything—everything—to protect it from harm.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Lyceum Scholar'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Lyceum Scholar') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'The Lyceum taught me to never want to leave my room. The campus was so huge, and the crowds were so horrible.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Lyceum Scholar') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'You think you''re so great just because you''ve got muscles, and endurance, and…shut up! Read a book sometime!');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Lyceum Scholar') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'Huh? What? Sorry, I was thinking about a test I need to retake when I get back to school...');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Lyceum Scholar') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I spent too much time studying. Now I don''t have any friends.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Lyceum Scholar') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'If you don''t match my aesthetic, I''m not interested in you. We can work together, but we won''t be friends. Got it?');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Lyceum Scholar') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I''m always striving for perfection. I got top of my class, sure, but only with a 98 average. And that''s. Not. Perfect.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Reformed Cultist', 'Two pantheons have battled over the fate of Exandria since the world''s founding—the Prime Deities and the Betrayer Gods. During the Calamity, the Prime Deities allied themselves with the mortal races of Exandria, their original creations, to protect their flawed world and its multifarious forms of life. The Betrayers sought to wipe the slate clean.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Reformed Cultist'), 'Feature: Fell Teachings', 'You were inundated with knowledge about one of the Betrayer Gods, and know by heart everything from their basic commandments to some of their most esoteric secrets. Choose one of the Betrayer Gods described in chapter 2. You have advantage on Intelligence (Religion) checks to know information about their faith, including obscure secrets unknown to most worshipers.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Reformed Cultist'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Reformed Cultist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I need a dagger close at hand at all times. Just in case they find me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Reformed Cultist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I can''t believe I''m out here fighting monsters. After everything I''ve been through, why can''t I find a normal life?');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Reformed Cultist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I need a stiff drink before I do anything stressful these days. I know it''s a problem. Just…let me have this.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Reformed Cultist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Murder is okay when it''s for a good cause! I didn''t tear my past out by the roots so I could let evil people cause more harm.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Reformed Cultist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'My past is filled with stories like you wouldn''t believe. Ones that''ll really make your skin crawl. Do you want to hear…?');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Reformed Cultist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Yeah, I''m crying. I do that. Get over yourself.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Reformed Cultist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I know you''ve told me your name twice already, but that''s not good enough. How can I be sure you are who you say you are?');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Reformed Cultist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'My mind is always racing. I can''t…I just need to…you have to give me a second—or else I can''t…organize my thoughts.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Reformed Cultist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Life. I''ve spent too long shackled to an evil master. No matter what happened before, I deserve my freedom now. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Reformed Cultist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Redemption. People can change, but redemption must be something they choose for themselves. If they do, it is my duty to help them along that path. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Reformed Cultist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Power. When I abandoned the cult, it wasn''t out of some misguided sense of righteousness. That pathetic organization was merely a shackle on my potential. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Reformed Cultist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Vengeance. The cult has poisoned my life. I will see all its followers suffer. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Reformed Cultist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Hierarchy. The cult was vile, but its strength was in stability and organization. As long as good folk lack unity, evil will always triumph. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Reformed Cultist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Reparations. As a cultist, I harmed people whose names I''ll never know. I feel obligated to repay my debt by aiding others. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Reformed Cultist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'My cousin escaped the cult with me. I lost track of them when we fled, but I know they''re alive. I can feel it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Reformed Cultist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I was saved from the cult by a priest of one of the Prime Deities. If not for that sign of faith, I would surely be lost.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Reformed Cultist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I was told by the person who saved me that a sage once said: "Life needs things to live." I don''t know what that means, but I''ve dedicated my existence to finding out.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Reformed Cultist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'One of my cultist parents had a change of heart when I was a teenager, and we fled together in the dark of night. I didn''t want to leave, but I understand now that their courage saved my life.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Reformed Cultist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I was bested by a warrior when I fumbled a cult-ordered assassination. I don''t know why that person took pity on me, but they gave me purpose when I was lost.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Reformed Cultist') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Now that I''ve saved myself, the only person important to me is my former cult leader—because I''ve sworn that they''ll die by my hand.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Reformed Cultist'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Reformed Cultist') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I''m haunted by what I saw in those ritual chambers. Every time I see blood, I…oh, gods, I can''t bear to even think about it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Reformed Cultist') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I ran from the cult long ago. But deep down, there''s a part of me that still thinks they were right about certain things.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Reformed Cultist') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I can''t help but feel a rush whenever I see a life snuffed out before me. Just one more kill… just one more.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Reformed Cultist') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'Organized religion terrifies me. Betrayer Gods or Prime Deities…it doesn''t matter. The sight of the faithful freezes my blood cold.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Reformed Cultist') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'Oh, I always tell the truth. Always. I''ve never had to keep a secret from anyone, so of course I''ll be open with you.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Reformed Cultist') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I don''t trust easily. If you grew up being lied to about every little thing? The fundamental nature of the world? You wouldn''t, either.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Whitestone Rifle Corps', 'You trained with the elite forces charged with the defense and protection of the isolated city-state of Whitestone, with much of that training focused on how to care for, protect, and safeguard the firearms that are the weapons of the Whitestone Rifle Corps. You are also entrusted with a solemn duty—to never let the secrets of your liege-lord''s deadly creations fall into the wrong hands.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps'), 'Feature: Legacy of Secrecy', 'You have been entrusted with the use and care of a weapon both powerful and terrifying. The rifle you wield might transform the face of warfare and life in Tal''Dorei. It is a weapon that haunts the mind of its creator, Percival de Rolo, manifesting as a pain that lives always behind his kind eyes.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps'), 'Feature: Rifle Corps Relationship', 'Current', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps') AND title = 'Current' ORDER BY id DESC LIMIT 1), '1', 'I retired honorably from the Rifle Corps—and now it''s time for me to pursue my own adventures.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps') AND title = 'Current' ORDER BY id DESC LIMIT 1), '2', 'I''m on an important mission to protect Whitestone or guard one of our allies.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps') AND title = 'Current' ORDER BY id DESC LIMIT 1), '3', 'Whitestone is in trouble, and I was sent away to seek help.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps') AND title = 'Current' ORDER BY id DESC LIMIT 1), '4', 'I don''t think firearms technology should be kept secret, so I escaped from the Rifle Corps with my weapon and am on the run.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps') AND title = 'Current' ORDER BY id DESC LIMIT 1), '5', 'I was on a mission with my company when I got separated from them. Now I need to find my way back home.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps') AND title = 'Current' ORDER BY id DESC LIMIT 1), '6', 'My weapon was stolen. I built a new one, but I can''t return home until I''ve tracked down the thief and recovered the original. (Whitestone Hunter variant only)');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps'), 'Variant Feature: Whitestone Hunter', 'As elite as they are, the members of the Whitestone Rifle Corps do not represent the apex of firearms skill in Whitestone. A clandestine group of elite soldiers and survivalists is drawn secretly from the Rifle Corps ranks. Called the Whitestone Hunters, these soldiers are special operatives of the de Rolo family, and loyally serve as spies, bodyguards, and even assassins when the job requires it. Though few officially know of them, rumors of the Whitestone Hunters'' existence swirl constantly throughout the city-state, often in response to their activities after having been loaned out to protect Whitestone''s allies.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I want to make a good impression at all times. That means keeping my clothes and gear clean and in top condition.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I don''t like being the center of attention. I''d rather let someone else do the talking while I watch their back.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I feel safe only if I''m carrying my trusty rifle. And my dagger. And my concealed pistol. Oh, and of course my….');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I don''t trust people with my secrets easily, so it feels like a big deal when someone else shares a secret with me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I like coming up with solutions to problems using my esoteric knowledge of natural philosophy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Everyone around me takes things so seriously. Sometimes I just want to let loose and have fun!');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'Knowing things that other people don''t know makes me feel special and important.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I''m most at home in woods and mountains, where everything feels at once familiar, always growing and changing.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Responsibility. I have a duty to protect the people of Whitestone and to uphold the trust placed in me by the de Rolos. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Militarization. Everyone should have access to the most powerful weapons available, so they can defend themselves effectively. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Cooperation. Any problem can be solved as long as people are willing to work together. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Camaraderie. It''s important to have people you can trust to help out in a fight—and to uncork a bottle together afterward. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Context. There are no universal rights or wrongs. Every choice depends on the details of the situation. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Secrecy. Information is valuable, but it can also be dangerous. I''ll keep my mouth shut and gather as much intel as I can. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I never knew what to do with myself until I joined the Rifle Corps. Now I have a purpose and comrades to give me direction.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'One of my fellow Rifle Corps soldiers saved my life—and then I saved theirs. That kind of bond lasts forever.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Whitestone is the best city in all of Tal''Dorei. Nowhere else has been blessed by the Dawnfather and has a clock that tracks the movement of the stars!');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'My quick thinking saved a noble from assassination, and she showed me great kindness in return. I daren''t say it, but I''m more loyal to her than I am to the de Rolos.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'My weapon is my life. I clean it, repair it, and care for it—and it serves me loyally in return.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'The people of Whitestone cared for my family when we had nothing. I promise to repay their compassion with my service.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'Who cares about keeping this gun safe? "Don''t let it fall into the wrong hands!" Ha! It''s only a matter of time before someone slips up and these weapons are everywhere.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I think being part of the Rifle Corps is so cool. I love telling people about my position so I can impress them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'My weapon was stolen. I built a new one, but I can''t return home until I''ve tracked down the thief and recovered the original.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I''m tired of protecting spoiled people who don''t know how to protect themselves.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I shoot first and ask questions later.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Whitestone Rifle Corps') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'The first and only time I killed someone, it changed my life. I still dream about it, and I''ll never be the carefree person I was before.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Academic: Antiquarian (Advanced)', 'Academics use their education and knowledge from extensive studies to make a living and achieve their life''s ambitions. Academics are generally held in higher regard than most other backgrounds, as their skills are sought after by the wealthy and powerful. Academics make great researchers, investigators, and versatile problem-solvers.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Academic: Antiquarian (Advanced)'), 'Academic', 'Academics use their education and knowledge from extensive studies to make a living and achieve their life''s ambitions. Academics are generally held in higher regard than most other backgrounds, as their skills are sought after by the wealthy and powerful. Academics make great researchers, investigators, and versatile problem-solvers.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Academic: Antiquarian (Advanced)'), 'Antiquarian', 'Antiquarians are fascinated with history and identifying ancient artifacts and texts. They gather rare and lost items to add to their collections. Antiquarians'' skill with identifying the properties of artifacts makes them adept problem solvers, especially when ancient artifacts and languages are involved.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Academic: Antiquarian (Advanced)'), 'Insightful Discovery', 'Upon spending an hour observing or examining a subject, and succeeding on a medium-DC Intelligence (History) check, you can uncover the purpose and uses of any object, language, cultural practice, or similar subject. The features of magic items you investigate in this manner are revealed to you without you needing to attune to them.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Academic: Antiquarian (Advanced)'), 'Rank 1 - Museum Scholar', 'You work in a museum and have spent your time learning about the intricacies of every cultural artifact there. Under the guidance of the head scholar at the museum, you grew into a respectable scholar in your own right.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Academic: Antiquarian (Advanced)'), 'Rank 2 - Adept Collector', 'After spending a considerable amount of time and money, you have started a reasonable collection of antiquities that you wish to one day put on display.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Academic: Antiquarian (Advanced)'), 'Rank 3 - Senior Professor', 'You now hold a position of esteem within the world of academia. You have dedicated a large portion of your time and efforts to extracting secrets from each item and sharing these discoveries with others. Students flock from around the world to hear you speak on the subjects of your specialisation.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Academic: Antiquarian (Advanced)'), 'Rank 4 - Curator', 'You have become one of the most renowned living antiquarians. Having been referenced in many scholars'' books about your studies, and having authored a few yourself, you are now a consulted expert on several subjects relating to your vast collections. You are the curator of your own museum, with scholars and collectors travelling across the world to visit it.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Academic: Antiquarian (Advanced)'), 'Profession Dice', 'Profession Die', '1d10');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Academic: Antiquarian (Advanced)') AND title = 'Profession Die' ORDER BY id DESC LIMIT 1), '1', '1d4');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Academic: Antiquarian (Advanced)') AND title = 'Profession Die' ORDER BY id DESC LIMIT 1), '2', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Academic: Antiquarian (Advanced)') AND title = 'Profession Die' ORDER BY id DESC LIMIT 1), '3', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Academic: Antiquarian (Advanced)') AND title = 'Profession Die' ORDER BY id DESC LIMIT 1), '4', '1d10');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Academic: Antiquarian (Advanced)'), 'Talents', 'Talents are unique dispositions, experiences, or characteristics your character has picked up due to their time working in their profession. This is represented by allowing characters to add their Profession Die in unique situations that highlight the skills gained through their profession. A character may select a Talent from their corresponding Talent list when they select their profession, and select an additional Talent whenever they rank up within a profession.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Academic: Antiquarian (Advanced)'), 'Talent: Biologist', 'Whenever you make an Intelligence (Nature) check to determine the properties of animals, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Academic: Antiquarian (Advanced)'), 'Talent: Botanist', 'Whenever you make an Intelligence (Nature) check to determine the properties of plants, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Academic: Antiquarian (Advanced)'), 'Talent: Copycat', 'Whenever you make an Intelligence or Intelligence (Forgery Kit) check to forge or create a document, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Academic: Antiquarian (Advanced)'), 'Talent: Diligent Researcher', 'Whenever you make an Intelligence check to research a subject, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Academic: Antiquarian (Advanced)'), 'Talent: Disciplinarian', 'Whenever you make a Charisma (Intimidation) check while issuing a command to an individual of lower rank within your background, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Academic: Antiquarian (Advanced)'), 'Talent: Drunkard', 'Whenever you make a Constitution check to determine if you will throw up, pass out, or how severe a hangover is, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Academic: Antiquarian (Advanced)'), 'Talent: Forecaster', 'Whenever you make a Wisdom (Survival) check to determine the weather for the next 48 hours, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Academic: Antiquarian (Advanced)'), 'Talent: Gambler', 'Whenever you make a Dexterity (Sleight of Hand) or Wisdom (Perception) check to cheat at a game or catch someone else cheating, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Academic: Antiquarian (Advanced)'), 'Talent: Local Historian', 'Whenever you make an Intelligence (History) check to determine the history of an item, location, or cultural phenomenon from the same region as you, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Academic: Antiquarian (Advanced)'), 'Talent: Mystical Scholar', 'Whenever you make an Intelligence (Arcana) check to recall information on Aberrations, Constructs, or Elementals, you can add your Profession Die to the result.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Academic: Antiquarian (Advanced)'), 'Talent: Passionate Orator', 'Whenever you make a Charisma (Performance) check to influence an Academic, Military, or Clergy member, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Academic: Antiquarian (Advanced)'), 'Talent: Pathologist', 'Whenever you make a Wisdom (Medicine) check to diagnose or treat a disease, add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Academic: Antiquarian (Advanced)'), 'Talent: Problem Solver', 'Whenever you make an Intelligence (Investigation) check to decipher a coded message or puzzle, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Academic: Antiquarian (Advanced)'), 'Talent: Runekeeper', 'Whenever you make an Intelligence (Arcana) check to uncover the properties and uses of magical runes, glyphs, or other symbols, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Academic: Antiquarian (Advanced)'), 'Talent: Sawbones', 'Whenever you make a Wisdom (Medicine) check dealing with Grievous Wounds, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Academic: Antiquarian (Advanced)'), 'Talent: Translator', 'Whenever you make an Intelligence check to communicate with a creature who doesn''t share a language with you, you can add your Profession Die to the result.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Academic: Antiquarian (Advanced)'), 'Talent: Pitiable', 'When you make a Charisma (Persuasion) check to convince an Aristocrat, Criminal, Militarist, or Clergy that you are not a threat or beneath their notice, you may add your profession die to the roll.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Academic: Archivist (Advanced)', 'Academics use their education and knowledge from extensive studies to make a living and achieve their life''s ambitions. Academics are generally held in higher regard than most other backgrounds, as their skills are sought after by the wealthy and powerful. Academics make great researchers, investigators, and versatile problem-solvers.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Academic: Archivist (Advanced)'), 'Academic', 'Academics use their education and knowledge from extensive studies to make a living and achieve their life''s ambitions. Academics are generally held in higher regard than most other backgrounds, as their skills are sought after by the wealthy and powerful. Academics make great researchers, investigators, and versatile problem-solvers.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Academic: Archivist (Advanced)'), 'Archivist', 'Archivists are dedicated to the recording and preservation of knowledge. Archivists are often tasked with scouring tomes for errors and falsehoods, because to an archivist there is no greater sin than the recording of lies. Great Archivists often become well respected scholars, holding great power over the future of a kingdom. Regardless, Archivists are often sought out by other Academics, who collaborate with them to complete their own work in related fields.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Academic: Archivist (Advanced)'), 'Academic References', 'Upon spending an hour, and succeeding on a medium-DC Intelligence (Investigation) check, you can find a contact within a library, academy, or place of knowledge. This contact begins as friendly to you and will answer questions you have regarding research.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Academic: Archivist (Advanced)'), 'Rank 1 - Scribe''s Assistant', 'You have become a valued member of a library after learning about the art of collecting and storing written materials. Your years of completing bureaucratic tasks have made you an expert in locating information on specific and obscure subjects.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Academic: Archivist (Advanced)'), 'Rank 2 - Scribe', 'You have been appointed the honored task of transcribing and recreating texts. You are a builder of libraries, a recorder of history, and an entrusted relayer of knowledge for generations to come. As a Scribe, it is your job to record the knowledge of the world. Scribes often venture out into the world to find and document information first-hand.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Academic: Archivist (Advanced)'), 'Rank 3 - Chief Librarian', 'Your dedication to the acquisition and preservation of knowledge has brought you to great heights within the world of academia. You are responsible for the library you serve, from acquiring and copying new texts, to preserving and reviewing existing ones. A Chief Librarian is expected to go to great lengths to fact-check the contents of their library, preferably in person.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Academic: Archivist (Advanced)'), 'Rank 4 - Grand Loremaster', 'As Grand Loremaster, you are the undisputed regional authority in all matters of scholarly concern. For this reason, you are highly revered by figures of prominence for your knowledge and advice. A system of academics looks to you for guidance on obtaining knowledge and spreading enlightenment.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Academic: Archivist (Advanced)'), 'Profession Dice', 'Profession Die', '1d10');
