INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clergy: Inquisitor (Advanced)'), 'Talents', 'Talents are unique dispositions, experiences, or characteristics your character has picked up due to their time working in their profession. This is represented by allowing characters to add their Profession Die in unique situations that highlight the skills gained through their profession. A character may select a Talent from their corresponding Talent list when they select their profession, and select an additional Talent whenever they rank up within a profession.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clergy: Inquisitor (Advanced)'), 'Talent: Astute Intuition', 'Whenever you make a Wisdom (Insight) check while trying to detect a lie from Aristocrats, Criminals, or Common Folk, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clergy: Inquisitor (Advanced)'), 'Talent: Cabal Lorekeeper', 'Whenever you make an Intelligence (Religion) check to recall information about Celestials, Fiends, Fey, or Undead, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clergy: Inquisitor (Advanced)'), 'Talent: Confessor', 'Whenever you make a Charisma (Persuasion) check to reveal a secret or ask a favor of anyone who follows the same religion or deity as you, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clergy: Inquisitor (Advanced)'), 'Talent: Copycat', 'Whenever you make an Intelligence or Intelligence (Forgery Kit) check to forge or create a document, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clergy: Inquisitor (Advanced)'), 'Talent: Diligent Researcher', 'Whenever you make an Intelligence check to research a subject, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clergy: Inquisitor (Advanced)'), 'Talent: Disciplinarian', 'Whenever you make a Charisma (Intimidation) check while issuing a command to an individual of lower rank within your background, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clergy: Inquisitor (Advanced)'), 'Talent: Drunkard', 'Whenever you make a Constitution check to determine if you will throw up, pass out, or how severe a hangover is, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clergy: Inquisitor (Advanced)'), 'Talent: Figure of Authority', 'Whenever you make a Charisma (Persuasion) check while interacting with Common Folk, you may add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clergy: Inquisitor (Advanced)'), 'Talent: Idolist', 'Whenever you make an Intelligence (Religion) check to recall information on religious or cult-related symbols, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clergy: Inquisitor (Advanced)'), 'Talent: Interrogator', 'Whenever you make a Charisma (Intimidation) check to extract information from an enemy who is restrained, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clergy: Inquisitor (Advanced)'), 'Talent: Local Historian', 'Whenever you make an Intelligence (History) check to determine the history of an item, location, or cultural phenomenon from the same region as you, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clergy: Inquisitor (Advanced)'), 'Talent: Menacing Presence', 'Whenever you make a Charisma (Intimidation) check, and are interacting with Academics, Common Folk, or Seafarers, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clergy: Inquisitor (Advanced)'), 'Talent: Mystical Scholar', 'Whenever you make an Intelligence (Arcana) check to recall information on Aberrations, Constructs, or Elementals, you can add your Profession Die to the result.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clergy: Inquisitor (Advanced)'), 'Talent: Passionate Orator', 'Whenever you make a Charisma (Performance) check to influence an Academic, Military, or Clergy member, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clergy: Inquisitor (Advanced)'), 'Talent: Pathologist', 'Whenever you make a Wisdom (Medicine) check to diagnose or treat a disease, add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clergy: Inquisitor (Advanced)'), 'Talent: Sawbones', 'Whenever you make a Wisdom (Medicine) check dealing with Grievous Wounds, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clergy: Inquisitor (Advanced)'), 'Talent: Shrewd Deduction', 'Whenever you make an Intelligence (Investigation) check to investigate a crime scene, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clergy: Inquisitor (Advanced)'), 'Talent: Solidarity', 'When you make a Charisma (Persuasion) check and you are interacting with members of your background, you may add your profession die to the roll.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Clergy: Preacher (Advanced)', 'The Clergy represents those who devote their lives to working within religious institutions. The Clergy are respected members within the social hierarchy in regions where their religion is prominent. Clergymen make effective orators, acolytes, and religious crusaders.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clergy: Preacher (Advanced)'), 'Clergy', 'The Clergy represents those who devote their lives to working within religious institutions. The Clergy are respected members within the social hierarchy in regions where their religion is prominent. Clergymen make effective orators, acolytes, and religious crusaders.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clergy: Preacher (Advanced)'), 'Preacher', 'Religions need devout orators to bring new members into the fold, or reinforce the faith of the faltering. Preachers can inspire the righteous, protect the weak, or incite dread in the unfaithful by speaking clearly and passionately about the faith they serve. These rhetoricians of the divine are not dissuaded by material wealth or hidden lore; instead, they seek good repute and new converts to their deity. Preachers recruit adherents to their religion through personal devotion and overt exhibitions of piety, charity, and charisma.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clergy: Preacher (Advanced)'), 'Propagate Agenda', 'Upon spending an hour speaking publicly, and succeeding on a medium-DC Charisma (Persuasion) check, you can influence the thoughts and opinions of locals. This could be used to heighten the common people''s fear of magic, or bring comfort and peace of mind to those who share the same faith.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clergy: Preacher (Advanced)'), 'Rank 1 - Crier', 'You have always had a natural aptitude for the spoken word, compelling others to listen and partake in your views by speaking loudly and clearly about the deity you serve. You are often seen on busy street corners and in town squares, preaching to passers-by.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clergy: Preacher (Advanced)'), 'Rank 2 - Truth Speaker', 'You have cultivated a reputation in your local town or city as a persuasive and charismatic orator, able to inspire the righteous and decry the evil. Common folk and nobility alike often stop to hear you speak, moved by your unique conviction to consider joining your religion.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clergy: Preacher (Advanced)'), 'Rank 3 - Enlightened Evangelist', 'You have gained considerable reputation in the province where you preach, regularly recruiting new converts to your faith. Your overt exhibitions of devotion and charity combined with a natural charisma make you a well-liked figure among the common people, though the more cynical uninitiated may regard you as a heretic or a charlatan.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clergy: Preacher (Advanced)'), 'Rank 4 - Exalted Savior', 'You have established your reputation as a captivating rhetorician, and people are naturally drawn to you, sometimes seemingly against their will. You may even begin to perceive yourself as a messianic figure, showing the will of your deity through conspicuous demonstrations of personal devotion and charity toward the poor. For those with spellcasting abilities, magic that is woven into such demonstrations is particularly compelling.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clergy: Preacher (Advanced)'), 'Profession Dice', 'Profession Die', '1d10');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clergy: Preacher (Advanced)') AND title = 'Profession Die' ORDER BY id DESC LIMIT 1), '1', '1d4');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clergy: Preacher (Advanced)') AND title = 'Profession Die' ORDER BY id DESC LIMIT 1), '2', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clergy: Preacher (Advanced)') AND title = 'Profession Die' ORDER BY id DESC LIMIT 1), '3', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Clergy: Preacher (Advanced)') AND title = 'Profession Die' ORDER BY id DESC LIMIT 1), '4', '1d10');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clergy: Preacher (Advanced)'), 'Talents', 'Talents are unique dispositions, experiences, or characteristics your character has picked up due to their time working in their profession. This is represented by allowing characters to add their Profession Die in unique situations that highlight the skills gained through their profession. A character may select a Talent from their corresponding Talent list when they select their profession, and select an additional Talent whenever they rank up within a profession.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clergy: Preacher (Advanced)'), 'Talent: Astute Intuition', 'Whenever you make a Wisdom (Insight) check while trying to detect a lie from Aristocrats, Criminals, or Common Folk, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clergy: Preacher (Advanced)'), 'Talent: Cabal Lorekeeper', 'Whenever you make an Intelligence (Religion) check to recall information about Celestials, Fiends, Fey, or Undead, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clergy: Preacher (Advanced)'), 'Talent: Confessor', 'Whenever you make a Charisma (Persuasion) check to reveal a secret or ask a favor of anyone who follows the same religion or deity as you, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clergy: Preacher (Advanced)'), 'Talent: Copycat', 'Whenever you make an Intelligence or Intelligence (Forgery Kit) check to forge or create a document, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Clergy: Preacher (Advanced)'), 'Talent: Diligent Researcher', 'Whenever you make an Intelligence check to research a subject, you can add your Profession Die to the roll.');
