INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Common Folk: Villager (Advanced)'), 'Talents', 'Talents are unique dispositions, experiences, or characteristics your character has picked up due to their time working in their profession. This is represented by allowing characters to add their Profession Die in unique situations that highlight the skills gained through their profession. A character may select a Talent from their corresponding Talent list when they select their profession, and select an additional Talent whenever they rank up within a profession.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Common Folk: Villager (Advanced)'), 'Talent: Beast Whisperer', 'Whenever you make a Wisdom (Animal Handling) check to train or direct orders to a beast, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Common Folk: Villager (Advanced)'), 'Talent: Biologist', 'Whenever you make an Intelligence (Nature) check to determine the properties of animals, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Common Folk: Villager (Advanced)'), 'Talent: Botanist', 'Whenever you make an Intelligence (Nature) check to determine the properties of plants, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Common Folk: Villager (Advanced)'), 'Talent: Calloused Hands', 'Whenever you make a Strength (Athletics) check to lift, drag, or shove a heavy object, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Common Folk: Villager (Advanced)'), 'Talent: Disciplinarian', 'Whenever you make a Charisma (Intimidation) check while issuing a command to an individual of lower rank within your background, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Common Folk: Villager (Advanced)'), 'Talent: Drunkard', 'Whenever you make a Constitution check to determine if you will throw up, pass out, or how severe a hangover is, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Common Folk: Villager (Advanced)'), 'Talent: Elusive', 'Whenever you make a Dexterity (Stealth) check to hide or blend into a crowd of humanoids that share your background, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Common Folk: Villager (Advanced)'), 'Talent: Flamboyant Presentation', 'Whenever you make a Charisma (Performance) check to entertain Aristocrats, Common Folk, or Seafarers, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Common Folk: Villager (Advanced)'), 'Talent: Forecaster', 'Whenever you make a Wisdom (Survival) check to determine the weather for the next 48 hours, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Common Folk: Villager (Advanced)'), 'Talent: Gambler', 'Whenever you make a Dexterity (Sleight of Hand) or Wisdom (Perception) check to cheat at a game or catch someone else cheating, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Common Folk: Villager (Advanced)'), 'Talent: Gut Feeling', 'Whenever you make a Wisdom (Insight) check to detect if an Aristocrat or Criminal is lying, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Common Folk: Villager (Advanced)'), 'Talent: Hard-Working', 'Whenever you make a Constitution check for a repetitive task such as marching or laboring for hours without rest, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Common Folk: Villager (Advanced)'), 'Talent: Navigator', 'Whenever you make a vehicle check, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Common Folk: Villager (Advanced)'), 'Talent: Wayfarer', 'Whenever you make a Wisdom (Survival) check to find a path or avoid getting lost, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Common Folk: Villager (Advanced)'), 'Talent: Applied Leverage', 'When you make a Strength (Athletics) check to force open a door or container, you may add your profession die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Common Folk: Villager (Advanced)'), 'Talent: Camper', 'When you make a Wisdom (Survival) check to construct a temporary shelter, you may add your profession die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Common Folk: Villager (Advanced)'), 'Talent: Charity Case', 'When you make a Charisma (Performance) check to fake an illness, injury, or other condition, you may add your profession die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Common Folk: Villager (Advanced)'), 'Talent: Pitiable', 'When you make a Charisma (Persuasion) check to convince an Aristocrat, Criminal, Militarist, or Clergy that you are not a threat or beneath their notice, you may add your profession die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Common Folk: Villager (Advanced)'), 'Talent: Solidarity', 'When you make a Charisma (Persuasion) check and you are interacting with members of your background, you may add your profession die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Common Folk: Villager (Advanced)'), 'Talent: Vacant Expression', 'When you make a Charisma (Deception) check to feign ignorance and you are interacting with an Academic, Aristocrat, Clergy, or Militarist, you may add your profession die to the roll.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Common Folk: Entertainer (Advanced)', 'Common Folk are, unsurprisingly, the most common station in life. These are the masses of people who are not distinguished by noble blood, inherited wealth, or a special calling that sets them apart.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Common Folk: Entertainer (Advanced)'), 'Common Folk', 'Common Folk are, unsurprisingly, the most common station in life. These are the masses of people who are not distinguished by noble blood, inherited wealth, or a special calling that sets them apart.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Common Folk: Entertainer (Advanced)'), 'Entertainer', 'Entertainers thrive in the spotlight, relishing applause and delighting in the fanciful. Entertainers are expert performers able to enthrall, inspire, and delight their audiences with a range of artistic displays. Their poetry captivates, speaking to the depth of human emotion in portraying sadness, love, or joy. Their music likewise enlivens or stirs sorrow. Their dance steps are nimble and cheering, and their humor is often tailored to their audience, whether low-born or noble.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Common Folk: Entertainer (Advanced)'), 'Find Fandom', 'Upon spending an hour performing and speaking to locals, and completing a medium-DC Charisma (Performance) check, you can find a contact among the local community leaders, such as village elders, tavern owners, and other well-respected common folk. This contact begins as friendly to you and will answer questions you have regarding life within the town, local news, and public opinion.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Common Folk: Entertainer (Advanced)'), 'Rank 1 - Stage Hand', 'Whether you have grown up surrounded by musicians or ran off to join a band of traveling performers, you have become invaluable to a more experienced performer. You pitch in wherever needed, whether as a page turner for musicians, mopping the cages of exotic animals, or helping set up camp for the night. Occasionally, they even allow you to join the performance.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Common Folk: Entertainer (Advanced)'), 'Rank 2 - Performer', 'You have made a name for yourself as a traveling performer, whether by yourself or as part of a troupe of other entertainers. You are easily identifiable by your colorful attire and striking appearance. Your performances stir the hearts of those who hear you.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Common Folk: Entertainer (Advanced)'), 'Rank 3 - Troupe Leader', 'You are the leader of a troupe of traveling performers, and are responsible for securing performances in towns, cities, and noble estates. You have mastered the various components of artistic performance: poetry, dance, music, and comedy. Proprietors of establishments that you perform in compensate you and your troupe with free lodgings and food.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Common Folk: Entertainer (Advanced)'), 'Rank 4 - Court Troubadour', 'You have secured a permanent position as an entertainer in a prestigious estate or court, performing for the delight of high nobility. You are considered a virtuoso in your chosen art.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Common Folk: Entertainer (Advanced)'), 'Profession Dice', 'Profession Die', '1d10');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Common Folk: Entertainer (Advanced)') AND title = 'Profession Die' ORDER BY id DESC LIMIT 1), '1', '1d4');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Common Folk: Entertainer (Advanced)') AND title = 'Profession Die' ORDER BY id DESC LIMIT 1), '2', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Common Folk: Entertainer (Advanced)') AND title = 'Profession Die' ORDER BY id DESC LIMIT 1), '3', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Common Folk: Entertainer (Advanced)') AND title = 'Profession Die' ORDER BY id DESC LIMIT 1), '4', '1d10');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Common Folk: Entertainer (Advanced)'), 'Talents', 'Talents are unique dispositions, experiences, or characteristics your character has picked up due to their time working in their profession. This is represented by allowing characters to add their Profession Die in unique situations that highlight the skills gained through their profession. A character may select a Talent from their corresponding Talent list when they select their profession, and select an additional Talent whenever they rank up within a profession.');
