INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Militarist: Free Sword (Advanced)'), 'Talent: Navigator', 'Whenever you make a vehicle check, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Militarist: Free Sword (Advanced)'), 'Talent: Passionate Orator', 'Whenever you make a Charisma (Performance) check to influence an Academic, Military, or Clergy member, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Militarist: Free Sword (Advanced)'), 'Talent: Recruiter', 'Whenever you make a Charisma (Persuasion) check to recruit people into a role, you can add your Profession Die to the roll.');
