INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Militarist: Free Sword (Advanced)'), 'Talent: Shrewd Deduction', 'Whenever you make an Intelligence (Investigation) check to investigate a crime scene, you can add your Profession Die to the roll.');
