INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Outlander: Beast Hunter (Advanced)'), 'Talent: Local Historian', 'Whenever you make an Intelligence (History) check to determine the history of an item, location, or cultural phenomenon from the same region as you, you can add your Profession Die to the roll.');
