INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Outlander: Explorer (Advanced)'), 'Talent: Temperature Tolerance', 'When you make a Constitution saving throw to avoid exhaustion due to extreme temperatures, you may add your profession die to the roll.');
