INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Seafarer: Sailor (Advanced)'), 'Talents', 'Talents are unique dispositions, experiences, or characteristics your character has picked up due to their time working in their profession. This is represented by allowing characters to add their Profession Die in unique situations that highlight the skills gained through their profession. A character may select a Talent from their corresponding Talent list when they select their profession, and select an additional Talent whenever they rank up within a profession.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Seafarer: Sailor (Advanced)'), 'Talent: Calloused Hands', 'Whenever you make a Strength (Athletics) check to lift, drag, or shove a heavy object, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Seafarer: Sailor (Advanced)'), 'Talent: Copycat', 'Whenever you make an Intelligence or Intelligence (Forgery Kit) check to forge or create a document, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Seafarer: Sailor (Advanced)'), 'Talent: Disciplinarian', 'Whenever you make a Charisma (Intimidation) check while issuing a command to an individual of lower rank within your background, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Seafarer: Sailor (Advanced)'), 'Talent: Drunkard', 'Whenever you make a Constitution check to determine if you will throw up, pass out, or how severe a hangover is, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Seafarer: Sailor (Advanced)'), 'Talent: Forecaster', 'Whenever you make a Wisdom (Survival) check to determine the weather for the next 48 hours, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Seafarer: Sailor (Advanced)'), 'Talent: Gambler', 'Whenever you make a Dexterity (Sleight of Hand) or Wisdom (Perception) check to cheat at a game or catch someone else cheating, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Seafarer: Sailor (Advanced)'), 'Talent: Gut Feeling', 'Whenever you make a Wisdom (Insight) check to detect if an Aristocrat or Criminal is lying, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Seafarer: Sailor (Advanced)'), 'Talent: Hard-Working', 'Whenever you make a Constitution check for a repetitive task such as marching or laboring for hours without rest, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Seafarer: Sailor (Advanced)'), 'Talent: Menacing Presence', 'Whenever you make a Charisma (Intimidation) check, and are interacting with Academics, Common Folk, or Seafarers, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Seafarer: Sailor (Advanced)'), 'Talent: Navigator', 'Whenever you make a vehicle check, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Seafarer: Sailor (Advanced)'), 'Talent: Recruiter', 'Whenever you make a Charisma (Persuasion) check to recruit people into a role, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Seafarer: Sailor (Advanced)'), 'Talent: Renowned', 'Whenever you make a Charisma (Persuasion) check, and are interacting with Military, Outlanders, or Criminals, you may add your Profession Die.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Seafarer: Sailor (Advanced)'), 'Talent: Ropesman', 'Whenever you make a Strength (Athletics) or Dexterity (Acrobatics) check to climb, move along, or jump onto ropes, you can add your Profession Die.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Seafarer: Sailor (Advanced)'), 'Talent: Sea Dog', 'Whenever you make a Strength (Athletics) check to swim in water, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Seafarer: Sailor (Advanced)'), 'Talent: Sentry', 'Whenever you make a Wisdom (Perception) check while on watch duty or defending a fortification, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Seafarer: Sailor (Advanced)'), 'Talent: Wayfarer', 'Whenever you make a Wisdom (Survival) check to find a path or avoid getting lost, you can add your Profession Die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Seafarer: Sailor (Advanced)'), 'Talent: Applied Leverage', 'When you make a Strength (Athletics) check to force open a door or container, you may add your profession die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Seafarer: Sailor (Advanced)'), 'Talent: Charity Case', 'When you make a Charisma (Performance) check to fake an illness, injury, or other condition, you may add your profession die to the roll.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Seafarer: Sailor (Advanced)'), 'Talent: Temperature Tolerance', 'When you make a Constitution saving throw to avoid exhaustion due to extreme temperatures, you may add your profession die to the roll.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Seafarer: Dock Worker (Advanced)', 'Seafarers make their living by sailing or gathering from the sea. Seafarers often start as poor and disrespected members of society. However, at higher ranks, they gain considerable holdings. The sea has much to give, but it can quickly take everything away as well.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Seafarer: Dock Worker (Advanced)'), 'Seafarer', 'Seafarers make their living by sailing or gathering from the sea. Seafarers often start as poor and disrespected members of society. However, at higher ranks, they gain considerable holdings. The sea has much to give, but it can quickly take everything away as well.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Seafarer: Dock Worker (Advanced)'), 'Dock Worker', 'Docks represent the hubs that drive commerce, travel, and exchange of culture. In every one are teams of hardy workers who load goods and supplies onto and off of the ships. From lowly Clerks, who check off goods to and from warehouses, to the mighty Sealord bureaucrats, they keep the machinery of the docks moving.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Seafarer: Dock Worker (Advanced)'), 'Liberate Ledgers', 'Upon spending an hour asking around, and succeeding on a medium-DC Intelligence (Investigation) check, you know the comings and goings of ships, wagons and merchandise at the trade hub you are exploring. You can similarly discern where different types of goods are stored in any warehouse, including the contraband.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Seafarer: Dock Worker (Advanced)'), 'Rank 1 - Stock Clerk', 'Long days of tedious work await you as you check off goods as they are loaded and unloaded from ships to and from warehouses. Many Stock Clerks don''t last long as they are often blamed for broken or missing inventory. In your time you have learned a lot about the comings and goings throughout a dock, what types of ships and barrels typically come from where, and what their purpose is. You have learned who to ask the right questions, who holds the real power, and where to find a good time.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Seafarer: Dock Worker (Advanced)'), 'Rank 2 - Customs Inspector', 'Your days of hard labor are behind you, as you are now charged with inspecting cargo for contraband, collecting tariffs, and filling ledgers of incoming and outgoing vessels. Corruption is rife at any port, and you are exposed to the shady deals, illegal goods, and criminal activities that take place there. It''s up to you whether you use your authority to enforce the law or just make sure you get a cut of the action.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Seafarer: Dock Worker (Advanced)'), 'Rank 3 - Harbour Master', 'Your keen eye and efficient work in customs has earned you the rank of Harbor Master. You now preside over the dock of a harbor town or city. You set the local laws and regulations, governing the flow of trade and passage within your jurisdiction. All who wish to conduct business without hassle make sure they are on good terms with you, as nothing moves without your approval.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Seafarer: Dock Worker (Advanced)'), 'Rank 4 - Sealord', 'Residing in the capital city of your region, you dictate the laws, taxes, and flow of sea commerce across the region. As a Sealord, you are the undisputed authority in your region when it comes to all subjects related to the harbor.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Seafarer: Dock Worker (Advanced)'), 'Profession Dice', 'Profession Die', '1d10');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Seafarer: Dock Worker (Advanced)') AND title = 'Profession Die' ORDER BY id DESC LIMIT 1), '1', '1d4');
