INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Seafarer: Dock Worker (Advanced)') AND title = 'Profession Die' ORDER BY id DESC LIMIT 1), '3', '1d8');
