INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Initiate') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'My weapon was a gift from a beloved trainer who died in an accident.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Initiate') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I carry a memento of my time as an acolyte, and I treasure it above all other things.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Initiate'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Initiate') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I''m easily distracted by an attractive person, which could be the death of me in the trials.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Initiate') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I really wanted to be a vizier, and I''m angry at the god who didn''t choose me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Initiate') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'Training for a lifetime to die in the end seems like a big waste of energy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Initiate') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I''m not at all sure I''ll be able to grant a glorified death to any of my crop-mates.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Initiate') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I have a lasting grudge against one of my crop-mates, and each of us wants to see the other fail.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Initiate') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I think I''ve figured out that this world is not what it seems. Something dark is going on here.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Vizier', 'You are a vizier, a servant of your god. You perform tasks that are essential to facilitating the initiates'' journey, so the gods reward you with entry into the afterlife with the God-Pharaoh''s blessing. You hope to achieve the most honored status in the afterlife by being the best possible servant to your god.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Vizier'), 'Feature: Voice of Authority', 'Your voice is the voice of your god, at least in theory. Your job might include training and instructing initiates, and they are required to obey you. In any circumstance, an initiate is expected to defer to your voice and obey your commands. If you abuse this authority, though, your god might personally punish you.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Vizier'), 'Suggested Characteristics', 'Personality Trait', '1d10');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Vizier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Everything I do, I do gracefully and deliberately, and with complete confidence. (Oketra)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Vizier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Nothing can shake my rock-hard focus. (Oketra)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Vizier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'When I am at peace, I am an oasis of perfect calm in the world. When I am roused to anger, I am an embodiment of terror. (Kefnet)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Vizier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I enjoy teasing acolytes and initiates with juicy tidbits of knowledge wrapped up in fiendishly difficult puzzles. (Kefnet)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Vizier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I have the utmost faith in myself and my abilities. (Rhonas)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Vizier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I get restless when life in the city feels too tame, too safe. (Rhonas)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Vizier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I enjoy solitude as an opportunity to plan my victory. (Bontu)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Vizier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I use satire as a way to undermine the teachings of the other gods. (Bontu)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Vizier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '9', 'I love, fight, and feast with equal zeal. (Hazoret)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Vizier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '10', 'I think of those in my care as my family, in a way that most people have trouble understanding. (Hazoret)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Vizier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Solidarity. The worthy must respect the worthy. In the afterlife, all will be united in goal and action. (Oketra)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Vizier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Knowledge. The worthy shall cultivate a nimble mind, so as to perceive the wonders beyond imagination that wait in the afterlife. (Kefnet)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Vizier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Strength. The worthy shall hone a strong body that can withstand the boundless energies of the afterlife. (Rhonas)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Vizier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Ambition. The worthy shall strive for greatness, for supremacy in life leads to supremacy in the afterlife. (Bontu)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Vizier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Zeal. The worthy shall rush to the God-Pharaoh''s side with relentless passion, rising to overcome every obstacle in their way. (Hazoret)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Vizier') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Naktamun. The life of the city is ordered according to the plan of the God-Pharaoh, and that order must be preserved at all costs.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Vizier'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Vizier') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'My loyalty to my companions embodies the ideal of loyalty to my god. (Oketra)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Vizier') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'The teachings of my god are more precious to me than any possession. (Kefnet)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Vizier') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I would do anything to defend the temple of my god from any harm or desecration. (Rhonas)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Vizier') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I am committed to the service of my god—because it''s my sure ticket into the afterlife. (Bontu)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Vizier') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I love my god and never want my service to end. (Hazoret)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Vizier') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I have a close friend or lover who is also a vizier.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Vizier'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Vizier') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I am in love with an initiate, and I want to shield this person from death in the trials.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Vizier') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I secretly wish I had not been chosen as a vizier, so I could participate in the trials as an initiate.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Vizier') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I secretly question whether the gods care at all about us or what we do.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Vizier') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'A vizier of another god seeks my death in retribution for a past insult.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Vizier') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I am terrified of what lies beyond the Gate to the Afterlife.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Vizier') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I secretly believe the God-Pharaoh''s return will not bring blessing to this world.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Almsgiver (HB)', 'Almsgivers, as one would expect, come in many different flavors; yet they all find themselves dedicating their lives to philanthropic ventures or making charitable donations to the needy through their talents, money or time - wherever they are found. Some almsgivers study to eventually become healers or priests, others join or use their families wealth to fund organizations which are able to cross borders. There is also a rare sub-class of these dedicated humanitarians who take up arms against those who ashamedly take advantage of the poor. It is a natural step up from charity worker to adventurer, as the poor, the needy and the hungry are never far from sight.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Almsgiver (HB)'), 'Feature: Feed the Many', 'When you cook a meal, you may use herbs, meat off-cuts and other daily gathered ingredients to increase the number of portions of food you can prepare to double. Thus you are able to provide sustenance for twice the number of people as you have cooked food portions for.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Almsgiver (HB)'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Almsgiver (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I feel tremendous empathy for all who suffer.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Almsgiver (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'My mind is always ticking over, with grand plans and new ways to help others.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Almsgiver (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I seek no rewards for what I do and would rather remain an unknown benefactor.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Almsgiver (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I treat people the way I''d like to be treated myself.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Almsgiver (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I feel more comfortable amongst common-folk and speak their language well.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Almsgiver (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I get a sense of power/accomplishment by giving to those people who are needier than I.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Almsgiver (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I feel queasy around too much blood and gore.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Almsgiver (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I have a particular affinity for animals who need my charity.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Almsgiver (HB)'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Almsgiver (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Faith. I believe that a power greater than all of us works through my actions. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Almsgiver (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'People. I like seeing the look of appreciation in the eyes of the people who I help. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Almsgiver (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Compassion. There are many who are less fortunate than us, and I wish to bring happiness to them all. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Almsgiver (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Community. Strengthening the');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Almsgiver (HB)'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Almsgiver (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'Restitution. Sometimes we must turn to slightly less savory methods as recompense for others injury or loss. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Almsgiver (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'Fairness. Everybody deserves a little charity every now and again. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Almsgiver (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I am strong enough physically, emotionally and spiritually to provide aid for others. It is my duty and responsibility to help those in need.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Almsgiver (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'My war-torn home village/town taught me to stand up for what I believe in and treat those in pain with kindness and respect.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Almsgiver (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I am forever searching for salvation/happiness/ spiritual enlightenment after a great sadness tore my life asunder.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Almsgiver (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'If it wasn''t for the orphanage/temple I grew up in, I would have never been able to help so many people. I owe them everything.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Almsgiver (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I lost my family to famine. None shall go hungry if I can ever help it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Almsgiver (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I must use my connections and talents to make a name for myself, so I can help others more.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Almsgiver (HB)'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Almsgiver (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I feel guilty that I came from a wealthy community.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Almsgiver (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'Due to constantly working with the poor, I have developed a dependency on a particular drug.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Almsgiver (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I find it very uncomfortable to be around children or young adults.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Almsgiver (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I have a homeless brother/sister who is in need of constant support.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Almsgiver (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I had an affair with another almsgiver, who now hates me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Almsgiver (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I am told a great many secrets… but never seem to keep them for long.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Healer (HB)', 'You work to heal people of all types of ailments, from plagues to broken bones, to sprains and stab wounds. You studied the arts of healing from books or from watching others, and you have done much practical work. Many are better off for the remedies you have administered.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Healer (HB)'), 'Specialty', 'Specialty', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Healer (HB)') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '1', 'Animal Attacks');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Healer (HB)') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '2', 'Battle Wounds');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Healer (HB)') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '3', 'Broken Bones');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Healer (HB)') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '4', 'Common Illnesses');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Healer (HB)') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '5', 'General Aches');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Healer (HB)') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '6', 'Infected Wounds');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Healer (HB)') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '7', 'Infectious Diseases');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Healer (HB)') AND title = 'Specialty' ORDER BY id DESC LIMIT 1), '8', 'Midwifery');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Healer (HB)'), 'Medical Experience', 'You can identify wounds, illnesses, infections and other ailments with inspection and know how to fix them. In addition, you know the properties of many herbs and where you can acquire them.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Healer (HB)'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Healer (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I will always help those in need.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Healer (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I treat everyone with kindness.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Healer (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I always refuse coin for my services, preferring a meal, board or common supplies.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Healer (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I ask a lot of questions to everyone I encounter to ensure they are healthy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Healer (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I refuse to touch anyone or go into dirty places.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Healer (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I swear by a particular remedy and encourage its use in every situation.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Healer (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I am incredibly sympathetic to those who have lost loved ones.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Healer (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I always wear something that identifies myself as a healer, allowing me to be sought out by those who need help.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Healer (HB)'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Healer (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Charity. We all have a role to play in the welfare of others. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Healer (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Vitality. I will do whatever it takes to gain eternal life. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Healer (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Discovery. We have the power to uncover new truths about our physical bodies. We just have to explore. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Healer (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Power. If I understand what keeps us alive, I will gain the truest mastery over death and pain. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Healer (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Procedure. There is a way in which things must be done, and we all must heed it. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Healer (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Decision. Some people deserve saving. Others don''t. We all deserve the ability to choose. (Chaotic)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Healer (HB)'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Healer (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I will always help the sick and injured.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Healer (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I feel guilty over someone''s death. Their family is my responsibility.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Healer (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I am working on a series of tomes detailing commons illnesses and how to heal them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Healer (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'A loved one is afflicted with a rare illness. I will find a cure.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Healer (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I will never forget a loved one who died of an illness, and I frequently visit their grave.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Healer (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'No one living in my place of birth will die of a disease I can cure.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Healer (HB)'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Healer (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I feel terribly guilty whenever someone else is in pain.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Healer (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'If I help others, I might fail. I suppose I can''t help them, then.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Healer (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'have a weakness for the feeble, and try to help them even when it is undeserved.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Healer (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I am very hard on myself and become desolate when I fail.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Healer (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I love to cause injury as much as I love healing it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Healer (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I am terribly frightened of death.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Dead (HB)', 'You died and came back. This experience changed you and defines who you are today. The specifics of your death are up to you, but the following questions and suggestions will help you determine your odd personal history.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Dead (HB)'), 'Feature: Spirit Talk', 'You can connect with the souls of the dead thanks to the long time you spent dead. You may spend a day of downtime communicating with the souls to learn a piece of information by asking a question. The quality and specifics of the information you get are up to the DM.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Dead (HB)'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dead (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'When faced with a new experience the word "no" isn''t in my vocabulary.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dead (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I always have the loudest laugh in the room.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dead (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'When I want something, I''m direct and simple in my request.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dead (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'The story of my demise, like all my stories, is greatly exaggerated.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dead (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Those who ask me about my death quickly learn to never ask again.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dead (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I treat most people like they''re idiots. They don''t know what I know.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dead (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'If I don''t want to do something, I simply don''t do it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dead (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I don''t excite easily.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Dead (HB)'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dead (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Freedom. Life is short so live how you want. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dead (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Despair. Nothing in life is worth celebrating and death is the destination we all deserve. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dead (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Vengeance. I will find and punish what killed me. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dead (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Charity. I want to prevent others from dying before their time. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dead (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Mercy. I was given a second chance and others should get the same opportunity. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dead (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Labor. Life is short, and so I must keep working to make my mark. (Lawful)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Dead (HB)'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dead (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I have a great fear of that which killed me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dead (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'The scars of my death wound are still on my body.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dead (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'My vengeance is more important than any other task.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dead (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I never wish to return to my gravesite.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dead (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I wish to bring back another who died by my side.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dead (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'Any mention of my hometown reminds me of my first life.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Dead (HB)'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dead (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'Death isn''t a big deal to me. I''ve died and come back. People should stop whining.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dead (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I don''t listen to protests before I do something reckless.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dead (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I''d rather be mad than solve a problem making me angry.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dead (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I would rather cheat, lie, and steal than be honest because none of this matters.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dead (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'Everything was better when I was dead.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dead (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I''m believe that I am better than everyone who hasn''t died and come back from the dead.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Heretic (HB)', 'You believe in a doctrine outlawed by your faith, taught to you in secret by other heretics. Perhaps your superiors excommunicated you from your temple, or forbidden dogma provided answers to questions you never sought. Whatever your origin, you''re now shunned by the faithful, or worse, hunted.', 'Deception, Religion', '', 'Two of your choice', 'A holy symbol (subtly modified to represent your own heretical belief), a prayer book or prayer wheel, vestments, a bottle of invisible ink, a quill, a set of common clothes, and a pouch containing 10 gp', (SELECT id FROM core_book_sources WHERE raw_source = 'Xanathar''s Lost Notes to Everything Else p. 42 (Homebrew)'));
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Heretic (HB)'), 'Heretical Belief', 'Hundreds of outlawed sects exist throughout the Forgotten Realms and other settings. Choose a belief that defines you as a heretic, or roll on the table below.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Heretic (HB)'), 'Feature: Heretical Contacts', 'You know where to find heretics in settlements where your faith is represented. If no heretics are present, you can indoctrinate weak-willed souls if you spend a day preaching. Where heretics are present, you can always find a place to hide, rest, and recuperate. If needed, they can help you escape the settlement on a cart or through a secret tunnel.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Heretic (HB)'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Heretic (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I think I''m right even when I''m wrong.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Heretic (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I pity those who deny the heresy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Heretic (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I always scope out the closest exits and position myself near them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Heretic (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Those who refute my beliefs are damned and aren''t worth saving.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Heretic (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I''m sure my god has chosen me for a greater destiny.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Heretic (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I''m a natural orator, but once I start talking it''s difficult for me to stop.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Heretic (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I''m always looking over my shoulder and the slightest things startle me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Heretic (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I believe the end of the world is nigh.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Heretic (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Guidance. I must teach others my forbidden doctrine. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Heretic (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Caution. It''s always better to be safe than sorry. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Heretic (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Knowledge. If this conspiracy is true, what else are we being lied to about? (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Heretic (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Insurrection. My old faith is corrupt and must be brought down by the new order. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Heretic (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Honesty. I''ve been lied to enough, so now I''m sworn to always tell the truth. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Heretic (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'Self-Knowledge. Those who seek the truth become wise beyond their years. (Good)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Heretic (HB)'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Heretic (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'The heretics who educated me were burned at the stake. I won''t let that happen to me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Heretic (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'Belief in my old faith still nags at me. Have I chosen the right path?');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Heretic (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I used to be friends with the cleric who''s now hunting me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Heretic (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I carry a heretical treatise on my person. It mustn''t be found!');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Heretic (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I protect the faithless so they can know enlightenment.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Heretic (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'One of my underworld contacts reports my movements to the superiors of my old temple.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Heretic (HB)'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Heretic (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I''m suspicious of strangers, as they may be bounty hunters sent to arrest me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Heretic (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I sneer at those whom I deem unworthy of my time.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Heretic (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I''m deeply superstitious, and have many rituals to avoid bad luck.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Heretic (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I tend to misjudge the devotion of others.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Heretic (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I''m obsessed with my studies, often to the detriment of other concerns.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Heretic (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I''m deeply mistrustful of temple hierarchies.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Legendary Lineage (HB)', 'You have heroes in your family background and the story of your ancestors is known far and wide. Perhaps your parents were a famous adventuring duo, or a grandparent hunted and killed great evils. Maybe your heroic bloodline has many heroes within it going back as far as any can remember. Whatever the case, the deeds of those who came before you are great and the public''s expectation of your accomplishments is even greater. The weight of your family name affects all you do.', 'Athletics, History', 'Vehicles (land)', 'One of your choice', 'A set of traveler''s clothes, a wood figure made in the likeness of an ancestor, a signet ring, a gaming set in which you are proficient, and a belt pouch containing 15 gp', (SELECT id FROM core_book_sources WHERE raw_source = 'Xanathar''s Lost Notes to Everything Else p. 43 (Homebrew)'));
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Legendary Lineage (HB)'), 'Feature: Good Reputation', 'Since your family name is known, you can reap many benefits by flying the flag of your heritage. People in positions of power and privilege are willing to take a meeting with you and grant you favors. The DM decides the extent and effect of these favors, but they shouldn''t involve lavish gifts or great personal risk to the granter.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Legendary Lineage (HB)'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Legendary Lineage (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I am polite and humble to all who fawn over me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Legendary Lineage (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I''m sure you''ve heard of the great heroes of my family, but let me tell you anyway.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Legendary Lineage (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I am quiet in public and don''t enjoy being noticed.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Legendary Lineage (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I boast about how I will put my ancestors'' deeds to shame.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Legendary Lineage (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I am attracted to people who don''t fawn over me right away.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Legendary Lineage (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I often find reasons to excuse myself from large groups of people who love me for my name.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Legendary Lineage (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I am able to fake smile for anyone, even if I hate that person.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Legendary Lineage (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I always ask for people to give me free stuff because I can.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Legendary Lineage (HB)'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Legendary Lineage (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Family. I intend to uphold my family name. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Legendary Lineage (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Superiority. I was born better than everyone and so I deserve better than everyone. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Legendary Lineage (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Individuality. I am not my family''s name and will make my own legend. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Legendary Lineage (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Leadership. I feel a call beyond my heritage to protect the people who look to me. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Legendary Lineage (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Aspiration. I want to make the people who believe in me proud. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Legendary Lineage (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Generosity. I was lucky to be born into this family and I will give back to those less fortunate. (Good)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Legendary Lineage (HB)'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Legendary Lineage (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I wield the same weapon my ancestor used.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Legendary Lineage (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'No one knows the real me like my childhood best friend.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Legendary Lineage (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'A parent won''t respect me until I make good on the family name.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Legendary Lineage (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I would do anything to protect the town where I grew up.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Legendary Lineage (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I have my eye on a quiet cottage to which I plan to retire.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Legendary Lineage (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I feel like the only person who really gets me is my dog.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Legendary Lineage (HB)'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Legendary Lineage (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I can only handle so much fawning before I explode in anger.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Legendary Lineage (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'If not praised constantly, I doubt myself.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Legendary Lineage (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'If you speak ill of my ancestors, I will punch you in the face.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Legendary Lineage (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I prefer to have someone else fix my personal problems.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Legendary Lineage (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I put down others to boost my own confidence.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Legendary Lineage (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'If something is fashionable, I avoid it at all costs.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Polymorphed (HB)', 'You were transformed into an animal or monster and lived that way for years. You most likely entered this form unwillingly but how you came down with this affliction is up to you. A caster with an axe to grind may have cast true polymorph for some petty offense committed by you or a loved one. Maybe you unwittingly donned a cursed magic item. Perhaps you activated some arcane trap when you accidentally stumbled into an ancient ruin. You could have made the choice willingly to infiltrate some group of monsters or hide as a beast from someone hunting you. You also should decide who or what changed you back to your true form.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Polymorphed (HB)'), 'Feature: Kindred Spirit', 'Nonhostile creatures of the same type as your polymorphed form are inclined to like you. These creatures might help guide you to a place if they know its location, provide you with shelter, or shield you from danger, provided they aren''t put directly into harm''s way.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Polymorphed (HB)'), 'Variant Feature: Body Horror', 'This feature is meant for games using the variant madness rules in chapter 8 of the Dungeon Master''s Guide. Before choosing this feature you must ask your DM. You have spent so much time in the form of another creature that you aren''t easily shocked by bodily manipulation and mutilation. Whenever such a sight would cause you to save against a madness effect you automatically succeed.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Polymorphed (HB)'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Polymorphed (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I am always fidgeting because I am uncomfortable.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Polymorphed (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I retained a rude physical behavior of my polymorphed form.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Polymorphed (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I obsess over my appearance and wear eccentric outfits.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Polymorphed (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I always judge the way others look.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Polymorphed (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I am unsure of what to say when people ask about me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Polymorphed (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I often speak in my head by accident instead of aloud.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Polymorphed (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I find it''s easier to lie than give real personal details.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Polymorphed (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I must keep touching my body to reassure myself I''m in it.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Polymorphed (HB)'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Polymorphed (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Freedom. People should be free to express who they are. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Polymorphed (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Power. If it gets me what I want, I''ll manipulate anyone. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Polymorphed (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Security. All people deserve a place where they feel at peace. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Polymorphed (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Beauty. All people should appreciate who they are. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Polymorphed (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Authority. If you respect authority, you will be protected. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Polymorphed (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Dog Eat Dog. To survive you must be the smartest or strongest. (Neutral)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Polymorphed (HB)'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Polymorphed (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I have altered my appearance to look like my polymorphed form.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Polymorphed (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'Some friends I made in my polymorphed form don''t know I''ve been turned back.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Polymorphed (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I carry a piece of my old body.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Polymorphed (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'The place where I spent most of my time while polymorphed is home to me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Polymorphed (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I still haven''t seen my family since before I was polymorphed.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Polymorphed (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I still enjoy eating what I ate in my polymorphed form.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Polymorphed (HB)'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Polymorphed (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I want to be back in my polymorphed form.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Polymorphed (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I sometimes believe I still possess abilities of my polymorphed form that I no longer have.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Polymorphed (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'All transmutation magic is terrible and must be stopped.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Polymorphed (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I physically hurt myself for assurance I am in my true form.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Polymorphed (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'It''s easier to back down from an argument than try to convince someone I''m right.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Polymorphed (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'The opinions of my friends are more important than my own.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Retired Adventurer (HB)', 'You lived a life of adventure already and retired to something quieter. You thought you were done and to that end may have given away all your old gear and spent all your treasure. Little did you know something would pull you back into your old life. If you choose this background, you have some exciting questions to answer.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Retired Adventurer (HB)'), 'Feature: C-List Celebrity', 'Whenever you are in a civilized area that knows of your past adventures, you can live an aristocratic lifestyle for free as people wish to honor your past deeds. You can also use your influence to gain audiences with important political figures and get random gifts from admiring fans. However if you choose to use your influence in this way, it also alerts anyone in the area looking for you of your location and draws attention to your party.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Retired Adventurer (HB)'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Retired Adventurer (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I have no idea how to tell a short story.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Retired Adventurer (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I''m sure of my first instincts and always act on them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Retired Adventurer (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Anyone younger than I am needs my advice.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Retired Adventurer (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I don''t enjoy physical contact with others unless it is my fist in their faces.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Retired Adventurer (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I show my numerous scars to help drive points home.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Retired Adventurer (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'When I laugh, the volume of my voice fills up the entire room.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Retired Adventurer (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'Nothing pleases me more than a good meal, good company, and job well done.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Retired Adventurer (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I often correct others when their lack of manners offends me.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Retired Adventurer (HB)'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Retired Adventurer (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Generosity. Everything I have I share with those around me and in need. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Retired Adventurer (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Good Times. I take advantage of every opportunity to have fun. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Retired Adventurer (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Power. Those who don''t tremble at my name will have reason enough to do so soon. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Retired Adventurer (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Logic. Emotions shouldn''t make decisions for us. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Retired Adventurer (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Greater Good. I would gladly give my life to defend those who can''t defend themselves. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Retired Adventurer (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Live and Let Live. Just because we disagree doesn''t mean we have to kill each other. (Neutral)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Retired Adventurer (HB)'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Retired Adventurer (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I never found that magic item I wanted in my early career.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Retired Adventurer (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'There''s more than a few broken hearts in my past.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Retired Adventurer (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'There is an inn I frequent where no one knows about my past life and I''d like to keep it that way.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Retired Adventurer (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I can''t go back to the town I accidentally partially leveled.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Retired Adventurer (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'My old adventuring companions get together now and then to relive the good old days.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Retired Adventurer (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'A famous song is actually about one of my greatest battles.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Retired Adventurer (HB)'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Retired Adventurer (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I don''t take the advice of others because I know best always.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Retired Adventurer (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'Violence solves most problems.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Retired Adventurer (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'Ale is my very, very best friend.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Retired Adventurer (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I really don''t know when to shut up.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Retired Adventurer (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I tend to fall asleep at times when I should be alert.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Retired Adventurer (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I am sarcastic at all times, especially when least appropriate.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Amnesiac (HB)', 'You woke up one day in the city of Luyarnha, with no memory of who you were or where you came from. You''re near-certain that this place is not your home, but all the memories of your past life are a haze of indistinct shapes and sounds. Faint images, like that of a silver lake or a towering mountain, flash through your mind but they provide no concrete details of your origin. You only know that you must find the truth, at any cost.', 'Insight, Investigation', 'One type of artisan''s tools', 'Common and Deep Speech', 'A set of common clothes, a small keepsake from your forgotten past, a set of artisan''s tools with which you are proficient, and a pouch containing 10 gp', (SELECT id FROM core_book_sources WHERE raw_source = 'Steinhardt''s Guide to the Eldritch Hunt p. 174 (Homebrew)'));
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Amnesiac (HB)'), 'Feature: Lingering Echoes', 'The echoes of your lost memories sometimes surface in surprising ways. When you enter a new location or meet a new person, there''s a chance something about them will trigger a faint recollection from your past. The GM might allow you to recall a small detail about the world, give you a hint about the situation at hand, or allow you to use your proficiency with a skill or tool you aren''t normally proficient with for a single roll.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Amnesiac (HB)'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Amnesiac (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I feel a deep sense of loss that I can''t explain.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Amnesiac (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I get frustrated when others know more about me than I do.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Amnesiac (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I feel an inexplicable connection to certain places or things.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Amnesiac (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I have an unquenchable thirst for knowledge, to fill the void left by my forgotten past.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Amnesiac (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I have a wary nature due to the uncertainty of my identity.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Amnesiac (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'My dreams are full of cryptic symbols and images that I try to decipher.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Amnesiac (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I have a heightened empathy for the suffering of others.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Amnesiac (HB)'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Amnesiac (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Order. My life is in chaos. I will establish rules to make sense of the world around me. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Amnesiac (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Resilience. I will endure all challenges until I find the answers 1 seek. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Amnesiac (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Freedom. I have lost myself once; now I will seize control of my fate and carve my own path, regardless of the rules. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Amnesiac (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Isolation. The less I involve myself with others, the less harm my past can cause them. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Amnesiac (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Sanity. I must ensure that no one else loses themselves, be it to amnesia, madness or scourge. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Amnesiac (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Ruthlessness. I will ensure that those responsible for my memory loss pay. (Evil)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Amnesiac (HB)'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Amnesiac (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I am drawn to a particular landmark; I must see what''s inside it with my own eyes.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Amnesiac (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'An item I carry is the key to my past; I will protect it at all costs.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Amnesiac (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I dream of a person who seems important; I must find them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Amnesiac (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'An unsettling symbol haunts my dreams; I must uncover its meaning.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Amnesiac (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I feel a deep bond with a new acquaintance, a connection that transcends both our memories.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Amnesiac (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'The fear in the eyes of Luyarnha''s citizens resonates with me; I must alleviate it.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Amnesiac (HB)'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Amnesiac (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I have a fear of losing my newfound identity.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Amnesiac (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I often hesitate, fearing my past actions might catch up with me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Amnesiac (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I distrust people, thinking they know more about me than they let on.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Amnesiac (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I avoid forming attachments, afraid they will forget about me too.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Amnesiac (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I obsess over small details, convinced they hold the key to my past.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Amnesiac (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I am overly defensive, afraid that others will take advantage of my amnesia.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Beat Hunter (HB)', 'Growing up, the menacing presence of the Scourge loomed over your life in the city. The beasts terrified yo destroyed your homes, and took away people you loved Driven by vengeance, fear, or a desire for justice, you chose to become a hunter. The Church, a mentor, or the Radiant Order taught you the ways of the hunt, giving you the knowledge and skills needed to defend your cit and yourself. Whether you were once a simple citizen turned protector or a seasoned veteran of the First War hardened by countless battles, you have become an essential line of defense against the darkness.', 'Choose one between Acrobatic or Athletics; and one between Medicine or Survival', 'Herbalism kit', '', 'A set of common clothes, a trophy from a beast you''ve slain, a hunter''s kit (includes 20 lead bullets, a silvered dagger, a flare, and a silver flask), a healer''s kit, and a belt pouch containing 10 gp.', (SELECT id FROM core_book_sources WHERE raw_source = 'Steinhardt''s Guide to the Eldritch Hunt p. 175 (Homebrew)'));
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Beat Hunter (HB)'), 'Feature: Born of the Hunt', 'Your continuous battles against the beasts have fortified your body and mind against the horrors of the Scourge. When an aberration or beast forces you to make a saving throw against the frightened condition, you gain a bonus on saving throws equal to your proficiency bonus. Additionally, you share a strong bond with fellow hunters, allowing you to request favors from them.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Beat Hunter (HB)'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Beat Hunter (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I keep my emotions under control, for I fear the day I lose control over them and my humanity.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Beat Hunter (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I am stoic, rarely showing any signs of distress. The beasts smell fear.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Beat Hunter (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I have a crude sense of humor. Laughter keeps the fear at bay.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Beat Hunter (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I am haunted by the beasts I have slain.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Beat Hunter (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I am deeply spiritual. I believe the Church is our only hope for salvation.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Beat Hunter (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I value strength above all else. The weak will not survive.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Beat Hunter (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I am always alert. Danger can appear at any moment.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Beat Hunter (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I am fiercely protective of my fellow citizens.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Beat Hunter (HB)'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Beat Hunter (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Protection. It is my duty to protect the innocent from the Scourge. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Beat Hunter (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Anger. The beasts must pay for the lives they''ve taken. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Beat Hunter (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Faith. I trust in the Church. Their wisdom guides my blade. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Beat Hunter (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Survival. The most important thing is to survive another night. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Beat Hunter (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Cunning. Understanding my prey is key to trapping, torturing and otherwise defeating them. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Beat Hunter (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Courage. I must stand tall in the face of fear. Only then can I inspire others to do the same. (Good)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Beat Hunter (HB)'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Beat Hunter (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'The Church trained me and I owe them my life.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Beat Hunter (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I hunt to avenge my loved ones taken by the scourge.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Beat Hunter (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I fight to protect the people of my neighborhood.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Beat Hunter (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I am hunting a specific beast that took a part of me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Beat Hunter (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'My weapon is a symbol of my duty and a keepsake from a mentor.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Beat Hunter (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'The war veterans taught me everything I know about fighting. I owe them a great debt.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Beat Hunter (HB)'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Beat Hunter (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I have a reckless streak that often gets me into trouble.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Beat Hunter (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I struggle with the brutal nature of the hunt, and fear it''s changing me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Beat Hunter (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I am overly suspicious of everyone and everything.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Beat Hunter (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I carry a deep guilt for those I couldn''t save.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Beat Hunter (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I am obsessively hunting a particular beast, disregarding all else.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Beat Hunter (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I harbor a deep-seated fear of losing my humanity in my bestial fury.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Cultist (HB)', 'Whether through your own choice or a twist of fate, you found yourself part of a clandestine and often misunderstood group of individuals—a cult. This may have been a circle focused on the symbiotic relationship of flesh and nature, a scholastic order obsessed with the cosmos, or followers of a prophecy predicting the apocalypse. However, your faith or circumstances have since changed, leading you to leave the confines of the cult. You might still be an active member, or you''ve escaped its influence. Either way, your experiences within the cult and the knowledge you''ve gained have left a deep impact on your life.', 'Choose one between Arcana or Religion; and one between Persuasion or Survival', 'One type of artisan''s tools or musical instrument', 'Choose one between Deep Speech, Infernal and Sylvan, reflecting the cult to which you belonged', 'A set of common clothes, a brimmed cloak, a trinket or symbol representing your cult, a piece of jewelry worth 10 gp, a hooded lantern, five flasks of oil, and a pouch containing 5 gp.', (SELECT id FROM core_book_sources WHERE raw_source = 'Steinhardt''s Guide to the Eldritch Hunt p. 176 (Homebrew)'));
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Cultist (HB)'), 'Feature: Cult Secrets', 'You know the secret signs, phrases, rituals, and traditions of your cult, and can recognize them anywhere. In addition you can recognize similar signals from other cults. Furthermore, you have advantage on checks made to pass yourself off as a member of similar cults, potentially gaining trust or access to resources.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Cultist (HB)'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I always keep my thoughts and emotions hidden, a habit from my cult days.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I am curious about other belief systems and constantly compare them to my own.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I feel most comfortable following rituals and routines.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I am always questioning everything and seeking deeper meanings.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I feel lost and disconnected after leaving my cult.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I can be charismatic and persuasive when I need to be.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I distrust authority figures and institutions.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I have a habit of slipping into old cult phrases or mannerisms.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Cultist (HB)'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I left someone I care about behind in the cult and am determined to rescue them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I am still part of the cult, and I will protect my brothers and sisters at all costs.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'Someone I loved was sacrificed for the cult''s cause. Their memory haunts me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I harbor a dangerous secret about the cult that others would kill to uncover');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'The leader of the cult still holds a dark sway over me, and I struggle against their influence.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I am seeking revenge against those who forced me out of the cult.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Cultist (HB)'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Freedom: I have left the cult and cherish my newfound liberty. I''ll never let anyone control my life again. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Unity: As a current cult member, I believe that our unified purpose is more significant than individual desires. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Knowledge: The cult''s teachings are hidden truths that the world deserves to know. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Purity: As a former cultist, I realize the cult''s teachings were wrong. I seek to cleanse myself and the world of its influence. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Domination: I believe the cult''s teachings should be enforced, creating a new world order. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Redemption: I have done terrible things in the name of the cult. I must atone for my sins. (Good)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Cultist (HB)'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I am paranoid and distrustful, always expecting a cultist to appear and enact revenge.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I secretly miss the structure and unity of the cult and sometimes consider returning.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'The cult''s influence has left me with warped moral views that others find shocking or abhorrent.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I will go to any length to destroy the cult, even if it harms innocent bystanders.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I struggle with guilt over the actions I took as part of the cult.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Disciple (HB)', 'The Radiant Church, a beacon of hope and strength, raised you, training you in the teachings of the Radiant One. As a disciple, you were immersed in the Church''s lore, its ideals of benevolence born of resilience and fortitude, and its promise of a prosperous future. You were taught to shine your light into the darkness, to lead the way for others, and to be a pillar of strength in times of adversity. Whether you''re an ardent believer, a skeptic questioning your faith, or someone seeking to wield the Church''s influence, your time in the Church has undoubtedly shaped your worldview and your identity.', 'Religion, Persuasion', 'Calligrapher''s supplies', 'One of your choices between Elven and Orcish, representative of the main forces within the Church.', 'A holy symbol of the Radiant One, a prayer book, 5 sticks of incense, vestments, a set of common clothes, and a pouch containing 15 gp', (SELECT id FROM core_book_sources WHERE raw_source = 'Steinhardt''s Guide to the Eldritch Hunt p. 177 (Homebrew)'));
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Disciple (HB)'), 'Features', 'Your skill set varies depending on the role you serve within the Church, pick from the following features the one that is most appropriate for your character, with your GM''s approval.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Disciple (HB)'), 'Feature: Divine Guidance', 'You have spent countless hours praying, studying scriptures, and performing rituals. Your devotion is so great that it draws the attention of the Radiant One, or so you believe. You can call upon this divine favor to guide your path. Once per day, you may ask your deity for guidance. How this guidance manifests can vary—a sudden insight, a prophetic dream, or perhaps a subtle sign in the environment around you.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Disciple (HB)'), 'Feature: Forceful Miracle', 'Through your dedicated service, you have harnessed the powers of the Radiant One. In the most dire of situations, you may extract this power for a miracle. However, these miracles are not to be taken lightly and should not be used frivolously. The miracle cannot be stronger in power than a spell of level equal to half your character level (rounded down, minimum 1st level). After a miracle has been granted, your hit points maximum are lowered by an amount equal to your character level for 1d12 days, and you must perform a significant service for the Radiant One to regain this miracle.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Disciple (HB)'), 'Feature: Scribe of Radiance', 'You''ve spent years studying the ancient scripts and texts of the Radiant Church, and with time, you''ve become a skilled scribe. Not only can you flawlessly replicate any scripture, but you can also identity and understand most religious texts related to your faith. This ability can prove invaluable when deciphering forgotten scriptures or understanding cryptic prophecies.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Disciple (HB)'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Disciple (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I am deeply philosophical and enjoy pondering the mysteries of the universe.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Disciple (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I''m optimistic and always looking for a silver lining.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Disciple (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I strive to be a beacon of hope and strength for others.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Disciple (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I am assertive and never back down from what I believe in.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Disciple (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I often recount stories and parables from the Radiant Church.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Disciple (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I always try to offer guidance to those who seem lost.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Disciple (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I have a peaceful demeanor and a comforting presence.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Disciple (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I am strict and expect others to abide by the teachings of the Church.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Disciple (HB)'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Disciple (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I owe my life to the Church, and I will serve it until my last breath.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Disciple (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I believe that the Radiant One has a grand destiny in store for me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Disciple (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I will bring the teachings of the Radiant Church to those who live in darkness.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Disciple (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I aim to preserve the unity of the Church amidst its division into factions.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Disciple (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I feel responsible for those who stray from the path of the Radiant One.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Disciple (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I will redeem the Church from its insidious corruption and restore its noble');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Disciple (HB)'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Disciple (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Strength: I must be strong so that I can guide others through adversity. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Disciple (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Hope: I am a beacon of light in the darkness, showing the way to a brighter future. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Disciple (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Faith: My faith in the Radiant One gives me strength and purpose. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Disciple (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Knowledge: The true path to enlightenment is the pursuit of knowledge. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Disciple (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Charity: It''s my duty to help those in need, and to provide for the less fortunate. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Disciple (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Independence: I must question and challenge my faith to truly understand it. (Chaotic)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Disciple (HB)'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Disciple (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I am dogmatic and find it hard to accept beliefs that contradict the teachings of the Church.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Disciple (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I am naive and often overoptimistic, which sometimes leads me to underestimate dangers.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Disciple (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I am plagued by an overwhelming fear of divine judgment, constantly doubting whether my actions are in accordance with the teachings of my faith.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Disciple (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I struggle with doubt and question my faith, particularly when facing the unfairness of life.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Disciple (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I have high expectations for myself and others, which often leads to disappointment.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Disciple (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I find it hard to forgive myself for past mistakes and often dwell on them.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Inquisitor (HB)', 'You are a steadfast disciple of the Radiant Church, filled with an unwavering faith and a holy zeal that drives you to root out any threats to the church and its teachings. Your duty might involve questioning potential heretics, investigating signs of corruption within the church, or combating the enemies of the faith. You walk a fine line, enforcing the laws of the Church and ensuring the purity of its followers.', 'Religion, Intimidation', 'Torture tools (SGttEH Page 156)', 'Common, one of your choice between Deep Speech and Primordial, often used in your investigations or dealing with outsiders.', 'A holy symbol of the Radiant Church, a set of common clothes, a set of manacles, torture tools, a tome containing the teachings of the Radiant Church, and a pouch containing 10 gp.', (SELECT id FROM core_book_sources WHERE raw_source = 'Steinhardt''s Guide to the Eldritch Hunt p. 179 (Homebrew)'));
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Inquisitor (HB)'), 'Feature: Zealous Authority', 'As an Inquisitor of the Radiant Church, you command a certain level of respect and fear among church members and outsiders who recognize the authority of the Church. You can leverage your authority to gain access to resources, information, or aid from those who fear the Church''s wrath, revere its power, or follow its teachings.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Inquisitor (HB)'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inquisitor (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I am fervently dedicated to my faith and the teachings of the Radiant Church.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inquisitor (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I am often suspicious of others, especially those who do not share my beliefs.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inquisitor (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I am stern and serious, with little time for humor or frivolity.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inquisitor (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I am not easily swayed by emotional appeals, preferring to deal in facts and evidence.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inquisitor (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I am meticulous and thorough in my investigations, leaving no stone unturned.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inquisitor (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I believe that the ends justify the means when it comes to preserving the Church and its teachings.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inquisitor (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I tend to view things in black and white, with little room for moral ambiguity.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inquisitor (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'Despite my stern exterior, I am deeply compassionate and driven by a desire to protect the innocent.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Inquisitor (HB)'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inquisitor (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Faith: My faith in the Radiant Church is unwavering, and I will do whatever it takes to protect it. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inquisitor (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Order: The laws and teachings of the Church must be upheld at all times. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inquisitor (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Purity: Corruption and heresy will be purged (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inquisitor (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Justice: Those who harm or deceive the Church must be brought to justice. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inquisitor (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Sacrifice: I am willing to sacrifice anything, even my life, for the Church. (Good)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Inquisitor (HB)'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inquisitor (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I am loyal to a mentor or superior within the Church who has guided me on my path.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inquisitor (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I once failed to stop a heretic, and that failure haunts me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inquisitor (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I have a family member or loved one who doesn''t understand my dedication to the Church.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inquisitor (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I am seeking to bring a particular heretic or enemy of the Church to justice.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inquisitor (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'The Church is my family, and I will protect it at all costs.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inquisitor (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I seek to convert or save those who have strayed from the path.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inquisitor (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '7', 'I have killed innocents in my righteous crusade, and their screams still haunt me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inquisitor (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '8', 'I have killed innocents in my righteous crusade, and will do so again if it is necessary.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Inquisitor (HB)'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inquisitor (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'My zealous devotion to the Church can make me blind to its flaws.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inquisitor (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I am unforgiving and tend to hold grudges, especially against perceived heretics.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inquisitor (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I can be overly suspicious, seeing threats to the Church where there are none.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inquisitor (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I am rigid and inflexible, struggling to adapt when things don''t go according to plan.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inquisitor (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'My stern demeanor often drives people away.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Inquisitor (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I sometimes resort to extreme measures in the name of preserving the Church, which can lead to unnecessary conflict.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Investigator (HB)', 'As an Investigator, your life is a ceaseless pursuit of the elusive truth. You are driven by a profound desire to understand the perplexing enigma that is the Scourge, the inscrutable disappearances happening across the city, the veiled cult activities, the covert dealings within the Church, or the aristocrats'' clandestine affairs. Regardless of the specifics, you are a seeker of knowledge and truth, using your keen insight and intellect to unravel the mysteries that plague your world. You might be a former scholar, a detective, or just an average citizen who was personally affected by these events, and you won''t stop until you reveal the truth.', 'Investigation, Insight', 'One type of artisan''s tools related to your investigative methods (e.g., alchemist''s supplies for analyzing samples, or a forgery kit for examining documents)', 'You learn to read one language of your choice, which represents the case that you obsess the most over.', 'A notebook and charcoal pencil, a set of common clothes, a set of artisan''s tools you are proficient with, a mysterious artifact or document related to your investigation, and a pouch containing 10 gp.', (SELECT id FROM core_book_sources WHERE raw_source = 'Steinhardt''s Guide to the Eldritch Hunt p. 181 (Homebrew)'));
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Investigator (HB)'), 'Feature: Inquisitive Mind', 'Your constant searching for answers has trained your mind to find connections and insights where others see none. You have an uncanny knack for seeing through lies and half-truths, and people tend to reveal more information to you, either intentionally or not. You have a knack for getting people to open up to you, whether it''s through kindness, intimidation, or trickery.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Investigator (HB)'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I am relentlessly curious and always eager to learn something new.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Investigator (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'My determination to uncover the truth sometimes leads me to take unnecessary risks.');
