INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Marked for Death (HB)', 'You once were dead, but the mysterious Blood Moon of Rebirth returned you to life, leaving an indelible Eldritch Carving on your body - the Sacrificial Brand. The mark on your flesh is feared by many, as it is believed to attract the terrible creatures that plague the world, to you, it symbolizes the weight of your past life with you, and the promise that this life will end again, violently. Embracing your new life as a symbol of rebirth and sacrifice, you have resolved to use this opportunity, even if it leads you once again to a brutal end.', 'Arcana, Survival', '', 'Deep Speech', 'A flare (used to indicate your position during hunts), a set of common clothes, a small knife for carving runes, a journal detailing your experiences since rebirth, and a pouch containing 10 gold pieces. The Sacrificial Brand Eldritch Carving is scarified on your body.', (SELECT id FROM core_book_sources WHERE raw_source = 'Steinhardt''s Guide to the Eldritch Hunt p. 182 (Homebrew)'));
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Marked for Death (HB)'), 'Feature: 6th Sense', 'Not only were you brought back from the dead by the Blood Moon, but you also survived the horde of beasts that were brought back with you, despite their ability to track you. Your senses are honed beyond mortal comprehension. You can sense the presence and general direction of powerful eldritch energies and artifacts within a 1-mile radius, particularly if they are connected to the Blood Moon of Rebirth or the Mother of a Thousand Young.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Marked for Death (HB)'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marked for Death (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I am extremely cautious, having learned the fragility of life firsthand.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marked for Death (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I am fascinated by the mysteries of life, death, and the forces that bind them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Marked for Death (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I feel a deep connection to the moon, often seeking solace in its light.');
