INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Scion (HB)', 'You are a member of the Scions, a group of brilliant, boundary-pushing scientists, working at the fringe of societal norms and scientific understanding. You have seen life being breathed into the lifeless, creating living constructs through electrifying science, a practice never seen before. As a member of the Scions, the inventions and creations of your group have earned favor among the city''s higher-ups, and given delusions of grandeur to those of you who wrestle with the godlike power of creation at your fingertips.', 'Arcana, Investigation', 'Tinker''s tools, one type of artisan''s tools of your choice', 'One of your choice', 'A set of tinker''s tools, a set of artisan''s tools, a notebook full of your scientific sketches and notes, a bottle of black ink, a quill, a small experimental creation of your design, and a pouch containing 15 gp', (SELECT id FROM core_book_sources WHERE raw_source = 'Steinhardt''s Guide to the Eldritch Hunt p. 183 (Homebrew)'));
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Scion (HB)'), 'Feature: Scion''s Creation', 'Your time as a Scion has made you an expert in the creation and maintenance of complex machinery and constructs. You can spend an hour to perform maintenance on a construct, restoring a number of hit points to it equal to your level; a creature can only benefit from this feature once per day. Additionally, you can identify, inspect, and assess the function and value of mechanical or arcane devices or constructs. This does not mean you know how to operate such a device if you have never handled it before.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Scion (HB)'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Scion (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I am deeply curious, always seeking to understand the world around me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Scion (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I am meticulous and organized in all my work.');
