INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Scion (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I''m fascinated by life and what makes something alive.');
