INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Scion (HB)'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Scion (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I am so consumed by my work that I often neglect my own health and relationships.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Scion (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I am reckless in my pursuit of knowledge, often putting myself and others in danger.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Scion (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I will do anything to protect my creations, even if it means harming others.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Scion (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I believe that I am above the laws and ethics that bind lesser individuals.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Scion (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I am haunted by the lifeless eyes of the beings I''ve created.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Scion (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I am desperate for recognition and fear obscurity more than death.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Worthless Husk (HB)', 'You were born into a world that saw you as utterly worthless, a waste of skin. Deemed talentless and devoid of potential, you were cast aside by those around you. As a Worthless Husk, you have learned to navigate the treacherous environment of the city, using your wits and endurance to survive. Given the chance to grow—a chance which you may never get—you could prove that even the most overlooked souls can make a difference in this twisted world.', 'Stealth, Survival', '', '', 'A set of common clothes, a small rusted knife, a key to an important location, a pouch containing 1 gold piece', (SELECT id FROM core_book_sources WHERE raw_source = 'Steinhardt''s Guide to the Eldritch Hunt p. 184 (Homebrew)'));
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Worthless Husk (HB)'), 'Feature: Bottom Dweller', 'Having been discarded by society, you''re familiar with desperation. You know the darkest corners of the city like the back of your hand. Whether it''s the dank sewers, abandoned buildings, or the shadowy back alleys, you can navigate these places with ease and can often find shelter or a safe passage where others would see only squalor and danger. You require half as much food and water as a regular member of your race to survive, and have advantage on all Survival checks made to maintain a Wretched lifestyle.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Worthless Husk (HB)'), 'Optional Feature: Worthless', 'With your GM''s approval you can pick this feature instead of Bottom Dweller.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Worthless Husk (HB)'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Worthless Husk (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I am quiet and withdrawn, preferring to remain in the background and observe.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Worthless Husk (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I am fiercely determined to prove my worth and show that I am more than just a waste of skin.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Worthless Husk (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I am quick to mistrust, always suspecting others'' motives due to my past experiences.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Worthless Husk (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I''ve developed a sharp sense of humor as a coping mechanism.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Worthless Husk (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I have a tendency to hoard items, never knowing when I might need them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Worthless Husk (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I am always scheming, to be ready for whatever life throws at me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Worthless Husk (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I often underestimate my worth and potential, always expecting to fail.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Worthless Husk (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I''ve grown to appreciate the little joys in life, as they are far and few between.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Worthless Husk (HB)'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Worthless Husk (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Perseverance: No matter how many times I''m knocked down, I will keep getting back up. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Worthless Husk (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Growth: I seek to find my hidden potential and make a difference in this harsh world. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Worthless Husk (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Independence: I will rely on no one but myself. I am all I need. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Worthless Husk (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Vengeance: I will show those who discarded me the error of their ways. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Worthless Husk (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Acceptance: I strive to find a place where I am accepted as I am. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Worthless Husk (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Redemption: I seek to prove that everyone, even someone like me, can change for the better. (Lawful)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Worthless Husk (HB)'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Worthless Husk (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I possess a crippling fear of abandonment, leading me to cling to unhealthy relationships even when they are detrimental to my well-being.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Worthless Husk (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'My self-destructive tendencies manifest in addictive behaviors, using substances or vices as a means to escape from my own perceived worthlessness.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Worthless Husk (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I have a hard time letting go of any perceived slights or insults, often holding grudges.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Worthless Husk (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'My survival instincts can make me selfish, often choosing my wellbeing over others.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Worthless Husk (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I tend to push people away, afraid that they will eventually discard me like the rest.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Worthless Husk (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I am often reckless, after all, I have nothing to lose.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Worthless Husk (HB)'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Worthless Husk (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I will protect the few friends I have made in this dark city, as they have shown me kindness when others would not.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Worthless Husk (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I am searching for a person who can help me unlock my true potential and find my place in this world.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Worthless Husk (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I am determined to create a better life for the other discarded souls in the city.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Worthless Husk (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I harbor a desire to confront those who cast me aside and show them how far I''ve come.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Worthless Husk (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'Precious memories, the only thing I have from my past, drives me to keep going.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Worthless Husk (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I''ve made a secret promise to myself that I''d rather die than stay worthless');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Cartographer (HB)', 'You are a master in the making and copying of maps, measuring and conveying the geography of various regions through your extensive studies. Whether apprenticed to a master or self-taught, you have made a name for yourself both for your unrivaled understanding of terrain changes and your ability to represent them in beautiful maps.', 'History, Survival', 'Cartographer''s tools and navigator''s tools', '', 'a set of cartographer''s tools, a set of traveler''s clothes and a belt pouch containing 15 gp.', (SELECT id FROM core_book_sources WHERE raw_source = 'Player''s Guide to Tanares p. 260 (Homebrew)'));
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Cartographer (HB)'), 'Feature: Dynamic Maps', 'You can recall the geographic features of all places you''ve visited, and spend 10 minutes to sketch an accurate map of one of these locations — as long as you have cartographer''s tools available.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Cartographer (HB)'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cartographer (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Community. I believe my gift should be used for society''s common good. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cartographer (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Tradition. Cartography is a timeless and delicate art which survived the greatest disasters, and must be preserved. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cartographer (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Greed. I am always after making money in any way I can, and this is the only reason why I dedicate myself to this activity. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cartographer (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Aspiration. I strive to be the best in what I do, and therefore I will offer the best of my effort at everything. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cartographer (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Beauty. More than geographical consultation, maps should convey the beauty and uniqueness of each place — so that we crave to explore those that are still uncharted. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cartographer (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Avant-garde. There are many ways to represent the world, and I will show it to the traditionalists. (Chaotic)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Cartographer (HB)'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cartographer (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I might never be able to repay all the good things someone has done for me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cartographer (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I left my family to pursue my calling, they never understood what it meant for me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cartographer (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I have deep admiration for a famous cartographer, and was accused many times of copying his style.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cartographer (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I learned my trade with someone who was first like a brother to me; he then became a rival. To this day, we compete at everything.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cartographer (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'My tools were stolen and I am set on recovering them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cartographer (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'My journey is meant for me to prove myself and earn a beloved person''s hand in marriage.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Cartographer (HB)'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cartographer (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I am certain I am destined for greatness, and I will stop at nothing to earn more recognition, and a nobility title.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cartographer (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I overindulge in earthly pleasures, and I will often spend my resources recklessly and selfishly if the occasion allows it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cartographer (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'Any sense of self-preservation I might have disappears when I am presented with the opportunity to know and explore new sites.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cartographer (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I see rivals and obstacles everywhere, and this hampers my personal growth and my ability to trust colleagues and adventurers.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cartographer (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I was disgraced for plagiarizing a piece of work. I am deeply ashamed of that and I will try to hide this fact at any cost.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cartographer (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I am a ruthless critic of the work of others, both within my field of work as in all other aspects of my life.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Community Leader (HB)', 'You are a natural leader, possessing a vision that inspires others to follow you. Whether you have built a community from scratch or have taken the helm of an existing one, your charisma and persuasiveness have earned the trust of the people under your care, and your leadership has brought about positive change.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Community Leader (HB)'), 'Feature: Cherished', 'By The People Your reputation precedes you as a respected leader who has earned the love and admiration of the people you serve. Those who consider you a leader are willing to follow your commands, provided that your actions align with their values and do not put them in harm''s way. Your status as a community leader also grants you access to resources, allies, and information that can aid you in your endeavors.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Community Leader (HB)'), 'Suggested Characteristics', 'Personality Trait', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Community Leader (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I wish to spread the model I implemented in my community to the whole world and see all peoples thrive.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Community Leader (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I avoid answering personal questions, deflecting attention with a subtle smile or a sharp change of subject.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Community Leader (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I despise religious dogma and superstition, knowing they distract people from true potential and stifle progress.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Community Leader (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I see the world in shades of gray, always searching for balance in the face of opposing forces and clashing');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Community Leader (HB)'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Community Leader (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'I always keep my word, knowing that trust is the foundation of all good relationships and key to effective leadership.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Community Leader (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'I feel the weight of my past successes, knowing that I must surpass them. I think it is difficult to live up to my own shadow.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Community Leader (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Equality. I stand up for the weak and the oppressed, fighting against tyranny and injustice. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Community Leader (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Charity. I seek out those in need and offer them aid. Any reward I receive from my work is to help them. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Community Leader (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Destiny. I am driven by the belief that I am destined for greatness, and nothing can stand in my way. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Community Leader (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Ambition. I know that people are like sheep, and I will be the shepherd, or I will be the wolf. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Community Leader (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Revolution. The laws should serve the community, not the other way around. I will unite the people to stand against oppression. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Community Leader (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Balance. I believe that good and evil are part of every person, and we must resist our urges and keep our conduct in check. (Neutral)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Community Leader (HB)'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Community Leader (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I believe my purpose is to protect the weak, so I ventured into the world, as my home town no longer needed me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Community Leader (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I suffered a great injustice and never received any reparation for it. This reflects on who I am and who I choose to associate with.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Community Leader (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I seek a lost (or fugitive) relative, unfortunately with no idea of his or her whereabouts.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Community Leader (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'Helping a community feels great, but it will never compare to the love I felt… and lost.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Community Leader (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'There is a secret reason for me to do all these things. No one must ever learn about it, and the chance that it might surface brings about the worst in me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Community Leader (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I admire a great leader from the past and everything I did stemmed from this admiration.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Community Leader (HB)'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Community Leader (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'Greed. I''m too focused on counting every penny, and it''s hard for me to part with my wealth, even when it''s for the greater good.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Community Leader (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'Trusting to a fault. I''m often betrayed or let down because I always assume the best of people and trust them too easily.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Community Leader (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'Obsession. I get too fixated on small issues or details, losing sight of the bigger picture and what''s truly important.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Community Leader (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'Comparative. I tend to compare everyone''s struggles to my own, making it hard for me to understand or empathize with others.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Community Leader (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'Arrogance. I can''t help but abuse the gratitude of those I help, letting it go to my head and treating others poorly as a result.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Community Leader (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'Rebel. I don''t respect authority and treat everyone as equals, which often gets me in trouble and makes me seem insolent.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Dragon Hunter (HB)', 'Your life is dedicated to the pursuit of the world''s greatest natural predators, the dragons. From the majesty of their wings to the ferocity of their flames, you have witnessed the chaos and destruction that these creatures can cause. Driven by a fierce determination to prevent them from spreading further terror, you have spent years studying every aspect of their existence. You have learned the nuances of their language, the intricacies of their customs, and the depths of their abilities. You have delved into the history of the world to better understand their influence, their origins, and their place in the tapestry of time.', 'Investigation, Nature', 'Vehicles (land)', 'Draconic', 'A dragon scale, a memento from your home region, a set of common clothes and a belt pouch containing 15 gp', (SELECT id FROM core_book_sources WHERE raw_source = 'Player''s Guide to Tanares p. 264 (Homebrew)'));
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Dragon Hunter (HB)'), 'Feature: Draconic Whispers', 'Through your long years of dragon hunting, you have built up a vast network of contacts. From magic scholars to local rangers and renegade dragonblades, you have gleaned valuable insights and knowledge about draconic activity. Your keen eye for detail and your experience in the field allow you to identify patterns and supernatural phenomena that others might miss.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Dragon Hunter (HB)'), 'Suggested Characteristics', 'Personality Trait', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Hunter (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I''ve never seen a dragon before. In fact, the mere thought of facing such an unrelenting force scares me out of my wits. Regardless, I know it is my mission.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Hunter (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I am relentlessly devoted to my mission and I will do whatever I can to accomplish it, ignoring any distractions.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Hunter (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I am always willing to acquire objects of draconic origin, sometimes paying much more than their worth.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Hunter (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Part of me dreams of taming a dragon and riding on its back.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Hunter (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I harbor a visceral, overpowering hatred for dragons and draconic creatures, even those who seem intelligent and civilized.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Hunter (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I usually make dozens of drawings of dragon anatomy, their lairs and symbols.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Dragon Hunter (HB)'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Hunter (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Preservation. I believe dragons are a fundamental part of the world. Though we must protect ourselves from them, indiscriminate hunt might irreparably harm nature''s balance. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Hunter (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Change. The evil dragons'' reign of terror must end, bringing a new era of freedom for humanity. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Hunter (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Fame. I wish to be known as the greatest dragonslayer the world has ever seen. Only then will I be truly satisfied.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Hunter (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Vengeance. No matter the consequences, I am ready to accept them if it means I will exact my revenge on a dragon.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Hunter (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Knowledge. I must learn all there is to know about dragons. Then I''ll be ready to face them. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Hunter (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Power. More than hunting dragons, what I wish is to capture one, using its power to take whatever I want. (Evil)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Dragon Hunter (HB)'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Hunter (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'A roving dragon destroyed my village, and that''s why I devote myself to hunting down these beasts.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Hunter (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I''m secretly in love for another hunter and I wish to prove myself to him/her before making my love known.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Hunter (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I have a master who taught me on the nature and power of the dragons.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Hunter (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I owe my life to my fellow hunters. I would never be able to betray or leave a single one of them behind.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Hunter (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I grew up in a small city in a dragon''s territory. One day I hope to return and free my people.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Hunter (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'When I was young, a great hero saved me from a dragon. I have followed his steps ever since in order to become a hero myself.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Dragon Hunter (HB)'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Hunter (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'My journey has severed my ties to any human relations I had. Nowadays I find it extremely hard to relate and social interactions are a true challenge.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Hunter (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I am always paranoid about an imminent dragon attack, 3 My study on dragons made me arrogant towards unlearned people. I have no patience for those who I deem ignorant.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Hunter (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I made a terrible mistake a couple years ago and this has cost a few lives. I hope to keep this mistake a secret from everyone.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Hunter (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I squander any spare money I have on drinking. Alcohol is my courage elixir for facing dragons… and forgetting the horrors I witnessed.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Dragon Hunter (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'Just like a dragon, I hoard treasures and have a peculiar taste for gold and precious stones.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Imperial Servant (HB)', 'You are a member of the privileged class of the Empire, given the esteemed position of overseeing lands and collecting taxes. Whether born into nobility or having earned your position through hard work, you hold a crucial role in the Empire''s functioning society.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Imperial Servant (HB)'), 'Feature: Old Friends', 'Your years of service to the Empire allowed you to build a vast network of contacts still working within its various departments. You have at least one ally in each department who can help you obtain meetings, information, or simple items such as an insignia or official document (with no gp value and not magical) like a pass to enter the Empire. These old friends may owe you a favor, or you may owe them one, but the relationship is mutually beneficial and can prove valuable in a variety of situations.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Imperial Servant (HB)'), 'Suggested Characteristics', 'Personality Trait', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Imperial Servant (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'The Empire taught the rigor that I want to forgot.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Imperial Servant (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I''m a servant and I''m good at it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Imperial Servant (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I''m a perfectionist, I like things the way they should be.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Imperial Servant (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Power without control is just a distraction. Everything in my reach must have my control.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Imperial Servant (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'The Empire was the greatest nation that ever existed. Do you understand the historical wealth we live in?');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Imperial Servant (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I am practical and direct, using force when necessary.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Imperial Servant (HB)'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Imperial Servant (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Charitable. After everything that happened, it''s time to help people. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Imperial Servant (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Mediator. The Empire still exists and its laws must prevail.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Imperial Servant (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Sadistic. Deep down, everyone knows what the Empire is like and that will continue. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Imperial Servant (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Perseverance. The imposition of the Empire is gone, now we can be free with our wills. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Imperial Servant (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Pathfinder. There is a world beyond the Empire to be known. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Imperial Servant (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Survivor. Only fools believe that the Empire has ceased to exist. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Imperial Servant (HB)'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Imperial Servant (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I lived the Empire, it''s part of me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Imperial Servant (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'My family are from the Empire. How can I abandon them?');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Imperial Servant (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'Nobody erases the past, so why should I unlink?');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Imperial Servant (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I remember everyone who suffered and I have to do something for them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Imperial Servant (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'The Emperor is still the most powerful man in Tanares and he is an example to live by.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Imperial Servant (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I will still be recognized by the Empire, and thus return to its greatness.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Imperial Servant (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I regret not taking more advantage of the power of the Empire.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Imperial Servant (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'The Empire was great, I blame myself for not having helped more.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Imperial Servant (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'The Empire destroyed a lot of people, I contributed to that unfortunately.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Imperial Servant (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I''ve done horrible things for the Empire, and it haunts me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Imperial Servant (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I want to get away from everything that relates to the Empire.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Imperial Servant (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'The Empire deserved to fall, just like me.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Occultist (HB)', 'You were once a member of a dark and forbidden cult, feared and reviled by all who knew of its existence. This cult may have worshiped one of the Ungods, aberrant deities of insanity, or the ancient god Bás, whose vengeful motivation and icy deathly powers are the stuff of legend. The cult''s practices were cruel and malevolent, invoking dark powers and twisted rituals that still haunt your dreams.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Occultist (HB)'), 'Feature: Hidden Contact', 'Your tenuous connection to the cult that you abandoned has borne fruit in the form of a valuable ally. You have a hidden contact, a member of the cult who remains loyal to you despite your departure. This contact may have once been a close friend or confidante, or may simply share your distaste for the cult''s methods and beliefs. Regardless of their history, you maintain good relations with them, even with little contact.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Occultist (HB)'), 'Suggested Characteristics', 'Personality Trait', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Occultist (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I prefer to keep to myself, rarely speaking unless necessary. The cult taught me the value of silence.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Occultist (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'The horrors that I witnessed during my time in the cult have left a lasting impact on me. I often have vivid nightmares and struggle with trust issues as a result.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Occultist (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'The cult instilled in me a healthy sense of caution. I am always on the lookout for potential threats and do not share sensitive information unless absolutely necessary.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Occultist (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'My knowledge of the cult''s procedures and practices is a valuable asset, and I am not afraid to use their tactics and ways to my advantage.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Occultist (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I have access to privileged information about the cult that most people do not. I am always on the lookout for any signs of their involvement in current events.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Occultist (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I have turned my back on the cult and its practices. I will do whatever it takes to stop them from causing any more harm, even if it means betraying my former comrades.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Occultist (HB)'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Occultist (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Redemption. I seek to atone for the sins of the cult and make amends for the harm I have caused. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Occultist (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Power. I have acquired knowledge and resources that give me great influence. I will use this power to further my own goals, regardless of who I must manipulate or harm to achieve them. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Occultist (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Salvation. It is my duty to prevent the cult from perpetrating any more atrocities. I will work within the confines of the law to bring them to justice and restore order. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Occultist (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Lunatic. The things I have seen have driven me to the brink of madness. I am convinced that the end of the world is near, and I will do whatever it takes to prepare for the apocalypse. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Occultist (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Responsibility. I have a solemn responsibility to prevent the cult from causing any more harm. I am prepared to take any action necessary to achieve this goal, regardless of the cost. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Occultist (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Shaveling. The cult''s dark practices and beliefs threaten to unravel the very fabric of reality. I will do whatever it takes to protect the world from their influence, even if it means resorting to unconventional or dangerous methods. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Occultist (HB)'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Occultist (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I have to make up for all the wrongs I did in the past.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Occultist (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'With the knowledge I''ve gained, I can prevent the worst.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Occultist (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I felt the pain I witnessed and now I want to cause it to the perpetrators.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Occultist (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I have a debt to the cult, but I can no longer go along with it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Occultist (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'Now I know their worst side and how they think and how to deal with them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Occultist (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I can no longer go back to the cult and I will make sure no one else does.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Occultist (HB)'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Occultist (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I am haunted by the atrocities that I participated in as a member of the cult. I struggle to forgive myself for the harm I caused and often feel overwhelmed by guilt and shame.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Occultist (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'Part of me is still loyal to my former cult and I am quick to defend their practices and beliefs, even when they contradict my own values. I struggle to separate my personal identity from that of the cult.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Occultist (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I am addicted to the sense of power that I once held as a member of the cult. I am willing to take risks and manipulate others to regain that power, even if it means sacrificing my morals.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Occultist (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I have lost loved ones to the cult''s practices and am consumed by a desire for vengeance. I am willing to take extreme measures to bring down the cult and avenge those I have lost, even if it means putting myself in danger.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Occultist (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I became obsessed with gaining knowledge and power through the cult, but the price for that power was too high. I struggle with a sense of regret and longing for a simpler life, but cannot shake the allure of the cult''s dark secrets.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Occultist (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I had no choices in the past and was forced to join the cult against my will. Now that I have left, I struggle to make my own decisions and am plagued by doubts and second-guessing. I fear making the wrong choices and being led astray once again.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Penumbral Survivor (HB)', 'Once in your life, you were lost in the dark and hostile Penumbral Plane. Perhaps you were lured in by whispers of forbidden knowledge, or maybe you stumbled into a portal or rift by accident. Whatever the reason, you found yourself alone in an unforgiving environment, surrounded by monstrous creatures and toxic fumes.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Penumbral Survivor (HB)'), 'Feature: Penumbral Walker', 'Your time spent in the Penumbral Plane has made you an expert in navigating its treacherous terrain. You have advantage on all Dexterity (Stealth) and Wisdom (Survival) skill checks while in the Penumbra.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Penumbral Survivor (HB)'), 'Suggested Characteristics', 'Personality Trait', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Penumbral Survivor (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I won''t speak about my experience in the Penumbra, as those horrors should be left there.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Penumbral Survivor (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'My mind constantly wanders somewhere else, making it hard to focus on a single task.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Penumbral Survivor (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I have a deep fascination with shadows and darkness, and feel most comfortable in dimly lit environments.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Penumbral Survivor (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I have a dry wit and a sharp tongue, often using humor as a defense mechanism.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Penumbral Survivor (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I am constantly haunted by my experience in the Penumbral Plane, making it difficult for me to trust others.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Penumbral Survivor (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I am restless and always seeking new challenges, no matter how dangerous they may be.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Penumbral Survivor (HB)'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Penumbral Survivor (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Guardian. I have a duty to protect others from the dangers of the Penumbral Plane. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Penumbral Survivor (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Balance. The Penumbra may be dangerous, but it is a necessary part of the natural order and must be preserved. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Penumbral Survivor (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Corrupted. The power and knowledge of the Penumbra are worth any price, no matter who or what is harmed in the process. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Penumbral Survivor (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Second Home. The Penumbra has become a part of me, and I will do whatever it takes to return to it and make a living there. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Penumbral Survivor (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Slayer. Penumbral creatures are abominations that must be destroyed to ensure the safety of Tanares. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Penumbral Survivor (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Eerie. The Penumbra is mysterious, and we need to go there to understand more about it. (Chaotic)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Penumbral Survivor (HB)'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Penumbral Survivor (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'When I returned to the Material Plane, my family was deeply worried, showing their love for me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Penumbral Survivor (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I had a master but, after returning from the Penumbra, I''ve never seen it again.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Penumbral Survivor (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I went to the Penumbra with my old team. I was the only survivor, and I fight to honor their memory.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Penumbral Survivor (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'My family was being shamed before I crossed to the Penumbra. Now I must do something to bring them honor.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Penumbral Survivor (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I crossed the portal as the result of a prank played by my childhood friends. I want to find them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Penumbral Survivor (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I glimpsed a fleeting image of someone I feel connected to in the Penumbral Plane, and now I am driven to find them before the darkness claims us both.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Penumbral Survivor (HB)'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Penumbral Survivor (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'The temptation to explore the Penumbra again is too great, even though it nearly cost my life the first time.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Penumbral Survivor (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I am obsessed with ending the penumbral influence in Tanares, to the point of recklessness and endangering others.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Penumbral Survivor (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'In order to escape the Penumbra, I was forced to abandon a companion or ally, and I am haunted by guilt and shame.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Penumbral Survivor (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'The penumbral influence has left me extremely paranoid, making it difficult to trust others and causing unnecessary conflicts.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Penumbral Survivor (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I unwittingly brought a penumbral monster with me to the Material Plane, and it has caused destruction and chaos wherever it goes.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Penumbral Survivor (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I willingly searched the Penumbra for an entity that could give me power, but what I found I dared not make any pact with, now it is after me.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Undercover (HB)', 'You live in the shadows, your true identity shrouded in secrecy, while another identity allows you to navigate the tumultuous world of Tanares with ease. Perhaps you pose as a humble farmer, a stoic soldier, or even something more sinister. You have spent so much time in this other skin that it feels like a part of you, and you are recognized for it even in other regions. With the growing tensions of the Cold War, your unique ability to move unnoticed between your two identities provides a distinct advantage, allowing you to gather invaluable information for espionage purposes. But as the stakes become higher, the lines between your true self and your other identity begin to blur, and the risk of being discovered looms ever larger.', 'Deception, Investigation', 'Disguise kit', '', 'Two sets of common clothes, two sets of documents for each identity, a sturdy bag, and a small pouch containing 15 gp', (SELECT id FROM core_book_sources WHERE raw_source = 'Player''s Guide to Tanares p. 270 (Homebrew)'));
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Undercover (HB)'), 'Feature: Another Life', 'You possess a second identity that you''ve developed over the years, complete with the necessary documentation to authenticate each persona. You are known by one identity or the other, depending on the region or location you''re in. Your extensive experience in living a double life has made you an expert in secrecy, and you must not need to make any ability check when trying to make people believe you are one of your two established identities.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Undercover (HB)'), 'Suggested Characteristics', 'Personality Trait', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Undercover (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I am constantly aware of my surroundings and the people around me, analyzing their every move and trying to anticipate their next actions.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Undercover (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I dislike my real identity and I secretly enjoy my cover-up identity and life more than my real one.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Undercover (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Empires rise and fall, and I want to be there to witness it all, in the heat of the political intrigue games.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Undercover (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'The weight of my double life can be overwhelming, causing me to withdraw from time to time and keep to myself as I struggle to maintain my two identities.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Undercover (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I am always looking for new information to gather and use for espionage purposes, willing to take risks and push boundaries to get what I need.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Undercover (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'My past is shrouded in mystery, and even I am not sure where my true loyalties lie. I must constantly remind myself of my mission and the greater good to stay on track.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Undercover (HB)'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Undercover (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'New Life. My new persona allows me to help those in need without the burden of my past mistakes. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Undercover (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'The Greater Good. The information I gather, even through questionable means, serves a greater purpose for the safety and stability of Tanares. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Undercover (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Power and Control. With my dual identities and knowledge of others'' secrets, I will hold immense power and control over those around me. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Undercover (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Freedom. My own freedom and success are my top priorities, even if it means betraying others. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Undercover (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Mercenary. The information I gather has immense value, and I will sell it to the highest bidder. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Undercover (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Survivor. I will do whatever it takes to survive and achieve my goals, even if it means sacrificing my true identity. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Undercover (HB)'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Undercover (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I have formed deep and meaningful connections with my contacts and will do anything to protect them and ensure their safety.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Undercover (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'The fate of two or more people rests on my ability to gather information and maintain my cover, and I will not let them down.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Undercover (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'My true loyalty lies with a specific organization or individual, and I will do whatever it takes to further their goals and protect their interests.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Undercover (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I have been given a specific mission by my organization or employer, and I will not rest until it is accomplished, even if it means sacrificing my personal safety.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Undercover (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I have fallen in love with someone who I must keep at a distance for fear of revealing my true identity, but I will do everything in my power to protect them from harm.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Undercover (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I have become emotionally attached to a civilian who knows me only as my false identity, and I must constantly remind myself that my feelings are based on a lie and could lead to danger if revealed.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Undercover (HB)'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Undercover (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I am haunted by my past mistakes and the people I''ve hurt while operating under my previous identity.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Undercover (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I have used my undercover persona for personal gain, sometimes at the expense of others.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Undercover (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I have developed close relationships with people who I must betray or distance myself from to maintain my cover.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Undercover (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I have obtained sensitive information through illegal or unethical means, making me vulnerable to being blackmailed or exposed.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Undercover (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'My two identities have become so intertwined that I struggle to remember which one is my true self.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Undercover (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'The power and freedom that comes with my new identity has made me arrogant and overconfident, causing me to take dangerous risks without considering the consequences.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Displaced (HB)', 'You don''t belong in this world. You come from an alien realm where magic doesn''t exist, technology is far more advanced, and the adventures you now face are confined only to fiction. You don''t know how you were transported to this world or why you were brought here, but you are trying to make the best of a perplexing situation.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Displaced (HB)'), 'Feature: Modern Knowledge', 'While you came into this unfamiliar world without a copper piece to your name, the advanced knowledge you brought with you has given you something of value to offer. You are well versed in a field that is farther advanced in your original world than the world you are in now. It could be medicine, mathematics, engineering, a craft, or anything else that makes sense for your character.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Displaced (HB)'), 'Variant Feature: I''ve read the handbook!', 'Because displaced characters are implied to have come from modern day Earth, there is a chance that they may have played Dungeons and Dragons as a game. This could potentially give an excuse for the character to know things about game mechanics and monsters that would otherwise seem like metagaming. Make sure your DM is comfortable with your character having this sort of knowledge in-game before characterizing them that way.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Displaced (HB)'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Displaced (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I''m taking this predicament as a chance to reinvent myself and be a better, stronger person.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Displaced (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I have trouble pronouncing all these weird fantasy names.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Displaced (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Back home I was nobody, so I''m amazed whenever I pull off something crazy and heroic.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Displaced (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'When someone asks me where I''m from, I tell the truth. They never believe me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Displaced (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I frequently make pop culture references that no one else understands.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Displaced (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I channel my favorite fictional hero in battle to give myself courage.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Displaced (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'Since I''m clearly the protagonist of an isekai anime, I frequently flirt with the natives to get my harem started.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Displaced (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I hate camping.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Displaced (HB)'), 'Suggested Characteristics', 'Ideal', '1d6');
