INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Displaced (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I want to build a life for myself that is as comfortable as what I used to have.');
