INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Displaced (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I owe my life to the kind people who took me in after I arrived in this world.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Displaced (HB)'), 'Suggested Characteristics', 'Flaw', '1d6');
