INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Displaced (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'My survival instincts leave much to be desired.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Displaced (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I haven''t adjusted to the lower standard of living in this world well, and I''m easily won over by the promise of luxuries.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Monster (HB)', 'You are a dragon who was permanently polymorphed, a vampire who was raised from the dead, a cured lycanthrope, or some other monster who has had their form changed into a living humanoid. Years of your life were spent as a being that haunts the nightmares of many. What were you? What terrible deeds did you commit? What magic transformed you into your new form?
